*/
let currentView = 'plot_solutions'; // 'plot_solutions', 'table', 'plot_turning_points'
let alphaCurrent = 0.039;

const solutionCurveData = generateSolutionCurveData(650);
// Pre-calculated turning points (picked along the curve to stand out)
const turningPointsData = generateTurningPoints([0.06, 0.16, 0.29, 0.43, 0.67, 0.84]);

/*
UI Elements
*/
const btnPlot = document.getElementById('btn-plot');
