const btnTurning = document.getElementById('btn-turning-points');
const sliderAlpha = document.getElementById('slider-alpha');
const alphaDisplay = document.getElementById('alpha-value-display');
const parameterControls = document.getElementById('parameter-controls');
const plotView = document.getElementById('plot-view');
const tableView = document.getElementById('table-view');
const tbody = document.getElementById('solutions-table-body');

/* Button handlers */
btnPlot.addEventListener('click', () => setView('plot_solutions'));
