}

/*
Generate a smooth multi-valued curve in (alpha, T2) plane plus auxiliary fields X1, X2, T1.
This is "pre-calculated" here at load time to be embedded with the page.
*/
function generateSolutionCurveData(N){
    const data = [];
    for(let i=0;i<N;i++){
        const t = i/(N-1);            // 0..1 parameter along the closed curve
        const th = 2*Math.PI*t;

        // alpha within [0,0.07] with folds
        const alpha = 0.035 + 0.025*Math.sin(th) + 0.01*Math.sin(2*th);

        // T2 within [0,8] with rich structure
        let T2 = 4 + 2.8*Math.sin(th + 0.4) + 1.6*Math.sin(3*th - 0.7);
        T2 = Math.max(0, Math.min(8, T2));

        // Auxiliary "coordinates" along the branch (0..1)
        const X1 = 0.02 + 0.96*(0.5 + 0.5*Math.sin(th + 1.2));
        const X2 = 0.02 + 0.96*(0.5 + 0.5*Math.sin(th + 3.7));
        const T1 = 0.3 + 7.2*(0.5 + 0.5*Math.sin(th + 2.1));

        data.push({alpha, x1:X1, x2:X2, t1:T1, t2:T2});
    }
    return data;
}

/* pick 6 points along the curve as "turning points" */
function generateTurningPoints(ts /* array in (0,1) */){
    const pts = [];
    ts.forEach(t=>{
        const th = 2*Math.PI*t;
        const alpha = 0.035 + 0.025*Math.sin(th) + 0.01*Math.sin(2*th);
        let T2 = 4 + 2.8*Math.sin(th + 0.4) + 1.6*Math.sin(3*th - 0.7);
        T2 = Math.max(0, Math.min(8, T2));
        pts.push({alpha, t2:T2});
    });
    return pts;
}

/*
Compute intersections of vertical line alpha = alpha0 with the polyline solutionCurveData.
We linearly interpolate all fields.
*/
function computeIntersections(alpha0){
    const eps = 1e-10;
    const intersections = [];
    for(let i=0;i<solutionCurveData.length-1;i++){
        const p1 = solutionCurveData[i];
        const p2 = solutionCurveData[i+1];

        const a1 = p1.alpha, a2 = p2.alpha;

        // handle vertical segments
        if(Math.abs(a2 - a1) < eps){
            if(Math.abs(alpha0 - a1) < 1e-5){
                // Same alpha: record both ends
                intersections.push({...p1});
                intersections.push({...p2});
            }
            continue;
        }

        if( (alpha0 >= Math.min(a1,a2)) && (alpha0 <= Math.max(a1,a2)) ){
            const f = (alpha0 - a1)/(a2 - a1);
            if(f >= -1e-6 && f <= 1+1e-6){
                const interp = (k)=> p1[k] + f*(p2[k]-p1[k]);
                intersections.push({
                    alpha: alpha0,
                    t2: interp('t2'),
                    x1: interp('x1'),
                    x2: interp('x2'),
                    t1: interp('t1')
                });
            }
        }
    }

    // sort by T2 and merge very-close duplicates
    intersections.sort((a,b)=>a.t2 - b.t2);
    const dedup = [];
    for(const q of intersections){
        if(dedup.length === 0 || Math.abs(q.t2 - dedup[dedup.length-1].t2) > 0.02){
            dedup.push(q);
        }
    }
    return dedup;
}

/*
