        tr.append(tdRoot, tdX1, tdX2, tdT1, tdT2);
        tbody.appendChild(tr);
    });
}

/*
p5.js visualization
*/
const sketch = (p)=>{
    let W=600, H=600;
    let margin = {left:70, right:25, top:35, bottom:65};
    let xMin=0, xMax=0.07;
    let yMin=0, yMax=8;

    p.setup = function(){
        const cnv = p.createCanvas(W, H);
        cnv.parent('plot-view');
        p.noLoop();
        p.textFont('Arial');
        p.textSize(12);
        p.pixelDensity(1);
    };

    function x2px(x){ return margin.left + (x - xMin) * (p.width - margin.left - margin.right) / (xMax - xMin); }
    function y2px(y){ return p.height - margin.bottom - (y - yMin) * (p.height - margin.top - margin.bottom) / (yMax - yMin); }

    function drawGridAndAxes(){
        // background
        p.background(255);

        // grid
        p.stroke(220);
        p.strokeWeight(1);
        for(let a=0; a<=0.07+1e-9; a+=0.01){
            const x = x2px(a);
            p.line(x, margin.top, x, p.height - margin.bottom);
        }
        for(let t=0; t<=8+1e-9; t+=1){
            const y = y2px(t);
            p.line(margin.left, y, p.width - margin.right, y);
        }

        // axes
        p.stroke(0); p.strokeWeight(1.2);
        // x-axis box
        p.line(margin.left, p.height - margin.bottom, p.width - margin.right, p.height - margin.bottom);
        p.line(margin.left, margin.top, margin.left, p.height - margin.bottom);

        // ticks and labels
        p.fill(0); p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        for(let a=0; a<=0.07+1e-9; a+=0.01){
            const x = x2px(a);
            p.stroke(0); p.line(x, p.height - margin.bottom, x, p.height - margin.bottom + 5);
            p.noStroke(); p.fill(80);
            p.text(a.toFixed(2), x, p.height - margin.bottom + 8);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for(let t=0; t<=8+1e-9; t+=1){
            const y = y2px(t);
            p.stroke(0); p.line(margin.left - 5, y, margin.left, y);
            p.noStroke(); p.fill(80);
            p.text(t.toFixed(0), margin.left - 8, y);
        }

        // axis labels
        p.push();
        p.fill(0);
        p.textAlign(p.CENTER, p.TOP);
        p.text('α', (margin.left + (p.width - margin.right))/2, p.height - margin.bottom + 34);
        p.pop();

        p.push();
        p.translate(22, (margin.top + (p.height - margin.bottom))/2);
        p.rotate(-Math.PI/2);
        p.textAlign(p.CENTER, p.TOP);
        p.text('T₂ (α)', 0, 0);
        p.pop();
    }

    function drawCurve(){
        p.noFill();
        p.stroke(255,0,0);
        p.strokeWeight(2);
        p.beginShape();
        for(const pt of solutionCurveData){
            p.vertex(x2px(pt.alpha), y2px(pt.t2));
        }
        p.endShape();
    }

    function drawDashedVertical(x, y1, y2, dash=7, gap=6){
        p.stroke(0,180,60);
        p.strokeWeight(2);
        let y = y1;
        while(y < y2){
            p.line(x, y, x, Math.min(y+dash, y2));
            y += dash + gap;
        }
    }

    function drawSolutionIntersections(){
        const xs = x2px(alphaCurrent);
        drawDashedVertical(xs, margin.top, p.height - margin.bottom, 7, 6);
        const sols = computeIntersections(alphaCurrent);

        p.noStroke();
        p.fill(0,90,255);
        for(const s of sols){
            p.circle(x2px(alphaCurrent), y2px(s.t2), 10);
        }
    }

    function drawTurningPoints(){
        p.noStroke();
        p.fill(80, 255, 80);
        for(const tp of turningPointsData){
            p.circle(x2px(tp.alpha), y2px(tp.t2), 10);
        }
    }

    p.draw = function(){
        drawGridAndAxes();
        drawCurve();
        if(currentView === 'plot_solutions'){
            drawSolutionIntersections();
        }else if(currentView === 'plot_turning_points'){
            drawTurningPoints();
        }
    };

    // expose for external redraw
    window.__sketch = p;
};

// boot p5
new p5(sketch);

// initial table build if needed (not visible by default)
if(currentView === 'table'){ updateTable(); }

// helpers for info button (optional)
document.getElementById('btn-info').addEventListener('click', ()=>{
    alert('Controls:\n• Switch views with the buttons.\n• Drag the α slider to update the vertical line and solutions.\n\nVisuals:\n• Red curve: pre-computed solution curve.\n• Plot view: blue dots are intersections at current α.\n• Turning points view: green dots indicate selected characteristic points.');
});
</script>
</body>
</html>
