<script>
(() => {
  // ---- State variables ----
  let spiralType = 'double'; // 'single' or 'double'
  let itemType = 'point';    // 'none' | 'point' | 'fox' | 'filled-fox'
  let spiralGrowth = 0.15;   // k

  // locator positions in world coordinates (px), y positive up
  let z0_loc = {x: 0, y: 0};           // for 'single'
  let z_plus_loc = {x: -100, y: 0};    // for 'double' (yellow)
  let z_minus_loc = {x: 100, y: 0};    // for 'double' (green)

  // dragging flags
  let dragging = { z0:false, zp:false, zm:false };

  // UI elements
  const btnTypeSingle = document.getElementById('btn-type-single');
  const btnTypeDouble = document.getElementById('btn-type-double');
  const btnItemNone = document.getElementById('btn-item-none');
  const btnItemPoint = document.getElementById('btn-item-point');
  const btnItemFox = document.getElementById('btn-item-fox');
  const btnItemFilledFox = document.getElementById('btn-item-filled-fox');
  const sliderGrowth = document.getElementById('slider-growth');
  const displayGrowth = document.getElementById('display-growth');
  const btnResetGrowth = document.getElementById('btn-reset-growth');

  // ---- Helper: manage button groups ----
  function activate(group, activeBtn) {
    [...group.querySelectorAll('button')].forEach(b => b.classList.remove('active'));
    activeBtn.classList.add('active');
  }

  // ---- Setup button interactions ----
  btnTypeSingle.addEventListener('click', () => {
    spiralType = 'single';
    activate(document.getElementById('type-controls'), btnTypeSingle);
  });

  btnTypeDouble.addEventListener('click', () => {
    spiralType = 'double';
    activate(document.getElementById('type-controls'), btnTypeDouble);
  });

  function setItem(type, activeBtn) {
    itemType = type;
    activate(document.getElementById('item-controls'), activeBtn);
  }

  btnItemNone.addEventListener('click', () => setItem('none', btnItemNone));
  btnItemPoint.addEventListener('click', () => setItem('point', btnItemPoint));
  btnItemFox.addEventListener('click', () => setItem('fox', btnItemFox));
  btnItemFilledFox.addEventListener('click', () => setItem('filled-fox', btnItemFilledFox));

  // Slider
  function updateGrowthFromSlider() {
    spiralGrowth = parseFloat(sliderGrowth.value);
    displayGrowth.textContent = spiralGrowth.toFixed(4);
  }
  sliderGrowth.addEventListener('input', updateGrowthFromSlider);
  updateGrowthFromSlider();

  // Reset button sets growth to 0.25 as required
  btnResetGrowth.addEventListener('click', () => {
    spiralGrowth = 0.25;
    sliderGrowth.value = spiralGrowth;
    displayGrowth.textContent = spiralGrowth.toFixed(4);
  });

  // ---- p5 Sketch (instance mode) ----
  const sketch = (p) => {
    let canvas;

    // Fox polygon vertices (facing right, centered near (0,0))
    const fox_vertices = [
      {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5},
      {x:5, y:-5}, {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6},
      {x:15, y:0}, {x:12, y:6}, {x:10, y:6}, {x:8, y:2},
      {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
    ];

    // ---- Complex helpers ----
    function C(x, y) { return {x, y}; }
    function add(a, b) { return C(a.x + b.x, a.y + b.y); }
    function sub(a, b) { return C(a.x - b.x, a.y - b.y); }
    function mul(a, b) { return C(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x); }
    function divc(a, b) {
      const den = b.x*b.x + b.y*b.y || 1e-9;
      return C((a.x*b.x + a.y*b.y)/den, (a.y*b.x - a.x*b.y)/den);
    }
    function mag(a) { return Math.hypot(a.x, a.y); }
    function argument(a) { return Math.atan2(a.y, a.x); }

    function w_of_t(t, k) {
      const r = Math.exp(k*t);
      return C(r*Math.cos(t), r*Math.sin(t));
    }

    // ---- Canvas size management ----
    function computeCanvasSize() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      return {w, h};
    }

    p.setup = () => {
      // Remove placeholder canvas (required by plan but not usable by p5)
      const placeholder = document.getElementById('p5-canvas');
      if (placeholder) placeholder.remove();

      const {w, h} = computeCanvasSize();
      canvas = p.createCanvas(w, h);
      canvas.parent('canvas-container');
      canvas.id('p5-canvas');

      p.textFont('Arial, Helvetica, sans-serif');
    };

    p.windowResized = () => {
      const {w, h} = computeCanvasSize();
      p.resizeCanvas(w, h);
    };

    // ---- Interaction utilities ----
    function mouseWorld() {
      // Convert p.mouseX/Y to world coordinates: origin at center, +y up
      return C(p.mouseX - p.width/2, (p.height/2 - p.mouseY));
    }
    function isInsideCanvas() {
      return p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height;
    }

    p.mousePressed = () => {
      if (!isInsideCanvas()) return;

      const m = mouseWorld();
      const hitRadius = 12; // px in world units

      if (spiralType === 'single') {
        if (Math.hypot(m.x - z0_loc.x, m.y - z0_loc.y) <= hitRadius) dragging.z0 = true;
      } else {
        if (Math.hypot(m.x - z_plus_loc.x, m.y - z_plus_loc.y) <= hitRadius) dragging.zp = true;
        else if (Math.hypot(m.x - z_minus_loc.x, m.y - z_minus_loc.y) <= hitRadius) dragging.zm = true;
      }
    };

    p.mouseDragged = () => {
      if (!isInsideCanvas()) return;
      const m = mouseWorld();
      if (dragging.z0) z0_loc = {x:m.x, y:m.y};
      if (dragging.zp) z_plus_loc = {x:m.x, y:m.y};
      if (dragging.zm) z_minus_loc = {x:m.x, y:m.y};
    };

    p.mouseReleased = () => { dragging = { z0:false, zp:false, zm:false }; };

    // ---- Drawing routines ----
    function drawLocators() {
      p.push();
      p.translate(p.width/2, p.height/2);
      p.scale(1, -1);

      // Styles
      p.stroke(0);
      p.strokeWeight(1);

      const drawLocator = (pos, col, labelText=null) => {
        p.push();
        p.fill(col[0], col[1], col[2], 200);
        p.circle(pos.x, pos.y, 20);
        p.stroke(0);
        // crosshair
        p.line(pos.x - 6, pos.y, pos.x + 6, pos.y);
        p.line(pos.x, pos.y - 6, pos.x, pos.y + 6);
        p.pop();

        if (labelText) {
          p.push();
          // flip back for text
          p.scale(1, -1);
          p.noStroke();
          p.fill(0);
          p.textSize(14);
          const sx = pos.x + 10;
          const sy = -pos.y - 10;
          p.text(labelText, sx, sy);
          p.pop();
        }
      };

      if (spiralType === 'single') {
        drawLocator(z0_loc, [255,255,0], "z0Loc");
      } else {
        drawLocator(z_plus_loc, [255,255,0], null);
        drawLocator(z_minus_loc, [0,255,0], null);
        const mid = C((z_plus_loc.x + z_minus_loc.x)/2, (z_plus_loc.y + z_minus_loc.y)/2);
        drawLocator(mid, [255,255,255,0], "z0Loc"); // invisible dot, only text
      }

      p.pop();
    }

    function drawCurveAndItems() {
      const k = spiralGrowth;

      const blue = p.color('#4A6DA8');
      p.stroke(blue);
      p.strokeWeight(2);
      p.noFill();

      const T = 8*Math.PI;
      const epsilon = 0.01;
      const dt = 0.02; // base sampling step for curve

      p.push();
      p.translate(p.width/2, p.height/2);
      p.scale(1, -1);

      const drawBranch = (tStart, tEnd) => {
        p.beginShape();
        for (let t=tStart; (tStart < tEnd) ? t<=tEnd : t>=tEnd; t += (tStart < tEnd ? dt : -dt)) {
          let pt;
          if (spiralType === 'single') {
            const r = Math.exp(k*t);
            const x = z0_loc.x + r*Math.cos(t);
            const y = z0_loc.y + r*Math.sin(t);
            pt = C(x, y);
          } else {
            const w = w_of_t(t, k);
            const pz = z_plus_loc, qz = z_minus_loc;
            const numerator = sub(mul(pz, w), qz);
            const denom = sub(w, C(1,0));
            pt = divc(numerator, denom);
          }
          p.vertex(pt.x, pt.y);
        }
        p.endShape();
      };

      if (spiralType === 'single') {
        drawBranch(-T, T);
      } else {
        // left branch (<0) and right branch (>0) to avoid t=0 pole
        drawBranch(-T, -epsilon);
        drawBranch(epsilon, T);
      }

      // Decorations
      if (itemType !== 'none') {
        const pointColor = p.color('#CB3335');

        function drawPoint(pt) {
          p.push();
          p.noStroke();
          p.fill(pointColor);
          p.circle(pt.x, pt.y, 5);
          p.pop();
        }

        function drawFox(pt, angle, scaleFactor, filled) {
          p.push();
          p.translate(pt.x, pt.y);
          p.rotate(angle);
          p.scale(scaleFactor, scaleFactor);
          if (filled) {
            p.noStroke();
            p.fill('#D6292D');
          } else {
            p.noFill();
            p.stroke('#D6292D');
            p.strokeWeight(1);
          }
          p.beginShape();
          fox_vertices.forEach(v => p.vertex(v.x, v.y));
          p.endShape(p.CLOSE);
          p.pop();
        }

        const stepItem = Math.PI / 6; // item interval along t
        const tSets = [];
        if (spiralType === 'single') {
          tSets.push({start: -T, end: T});
        } else {
          tSets.push({start: -T, end: -epsilon});
          tSets.push({start: epsilon, end: T});
        }

        for (const seg of tSets) {
          for (let t = seg.start; (seg.start < seg.end) ? t <= seg.end : t >= seg.end; t += (seg.start < seg.end ? stepItem : -stepItem)) {
            // compute position and derivative
            let zt, zt_prime;
            if (spiralType === 'single') {
              const w = w_of_t(t, k);
              zt = add(z0_loc, w);
              zt_prime = C(k*w.x - w.y, k*w.y + w.x); // (k + i)*w
            } else {
              const w = w_of_t(t, k);
              const pz = z_plus_loc, qz = z_minus_loc;
              const denom = sub(w, C(1,0));
              const numerator = sub(mul(pz, w), qz);
              zt = divc(numerator, denom);

              // derivative from plan: (p - q) * (k+i) * w / (w - 1)^2
              const numPrime = mul(sub(pz, qz), C(k*w.x - w.y, k*w.y + w.x)); // (p - q) * (k+i) * w
              const denomSq = mul(denom, denom);
              zt_prime = divc(numPrime, denomSq);
            }

            if (itemType === 'point') {
              drawPoint(zt);
            } else {
              // Orientation angle: atan2(y', x'), but with world y-up, our coords already in world orientation here
              let ang = argument(zt_prime);
              // Scale based on speed magnitude, clamped
              const speed = mag(zt_prime);
              const s = Math.min(36, Math.max(6, speed * 0.06)) / 12; // convert to a reasonable polygon scale
              drawFox(zt, ang, s, itemType === 'filled-fox');
            }
          }
        }
      }

      p.pop();
    }

    p.draw = () => {
      p.clear();
      // white background
      p.background('#ffffff');

      drawCurveAndItems();
      drawLocators();
    };
  };

  new p5(sketch);
})();
</script>
</body>
</html>
