    </div>
  </div>

  <div id="viz-container">
    <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
    <div id="canvas-container"></div>
  </div>
</div>

<script>
  // ------------------- Data and State -------------------
  const vertices = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p'];
  const edgesPairs = [
    // Horizontal rows (top to bottom)
    ['m','n'], ['n','o'], ['o','p'],
    ['i','j'], ['j','k'], ['k','l'],
    ['e','f'], ['f','g'], ['g','h'],
    ['a','b'], ['b','c'], ['c','d'],
    // Vertical columns (left to right)
    ['m','i'], ['i','e'], ['e','a'],
    ['n','j'], ['j','f'], ['f','b'],
    ['o','k'], ['k','g'], ['g','c'],
    ['p','l'], ['l','h'], ['h','d']
  ];

  const titleEl = document.getElementById('title-display');
  const nextBtn = document.getElementById('next-step-btn');

  // Graph structures
  let edgeWeights = {};        // key "a-b" (sorted)
  let adjacency = {};          // neighbors map
  let coords = {};             // vertex -> {x,y}

  // Algorithm state
  let startVertex = 'm';
  let distances = {};
  let visited = {};
  let predecessors = {};
  let visitedCount = 0;

  // p5 instance holder
  let P5;

  // ------------------- Helpers -------------------
  function key(u,v){ return (u < v) ? `${u}-${v}` : `${v}-${u}`; }

  function computeCoords() {
    // 600x600 canvas, margins
    const W = 600, H = 600, M = 70, step = (W - 2*M) / 3;
    coords = {};
    vertices.forEach((v, idx) => {
      const row = Math.floor(idx / 4);   // 0 bottom ... 3 top
      const col = idx % 4;
      const x = M + col * step;
      const y = H - (M + row * step);
      coords[v] = { x, y };
    });
  }

  function rebuildAdjacency() {
    adjacency = {};
    vertices.forEach(v => adjacency[v] = []);
    edgesPairs.forEach(([u,v]) => {
      const w = edgeWeights[key(u,v)];
      adjacency[u].push({ v, w });
      adjacency[v].push({ v: u, w });
    });
  }

  function setFixedExampleWeights() {
    const fixed = {
      'm-n':8, 'n-o':7, 'o-p':7,
      'i-j':9, 'j-k':7, 'k-l':1,
      'e-f':1, 'f-g':7, 'g-h':6,
      'a-b':10, 'b-c':4, 'c-d':8,
      'm-i':6, 'i-e':9, 'e-a':9,
      'n-j':7, 'j-f':6, 'f-b':5,
      'o-k':1, 'k-g':8, 'g-c':8,
      'p-l':2, 'l-h':1, 'h-d':7
    };
    edgeWeights = {};
    edgesPairs.forEach(([u,v]) => { edgeWeights[key(u,v)] = fixed[key(u,v)]; });
    rebuildAdjacency();
  }

  function randomizeWeights() {
    edgeWeights = {};
    edgesPairs.forEach(([u,v]) => {
      edgeWeights[key(u,v)] = 1 + Math.floor(Math.random()*10);
    });
    rebuildAdjacency();
  }

  function resetAlgorithm(start) {
    startVertex = start;
    distances = {}; visited = {}; predecessors = {};
    vertices.forEach(v => { distances[v] = Infinity; visited[v] = false; predecessors[v] = null; });
    distances[start] = 0;
    visitedCount = 0;
    nextBtn.disabled = false;
    updateTitle();
    if (P5) P5.redraw();
  }

  function updateTitle() {
    titleEl.textContent = `Dijkstra's algorithm starting at vertex ${startVertex}`;
  }

  function allVisited() { return visitedCount >= vertices.length; }

  function smallestUnvisited() {
    let minV = null, minD = Infinity;
    for (const v of vertices) {
      if (!visited[v] && distances[v] < minD) { minD = distances[v]; minV = v; }
    }
    if (minV === null) {
      // unreachable remaining, pick alphabetically
      const remaining = vertices.filter(v => !visited[v]).sort();
      minV = remaining.length ? remaining[0] : null;
    }
    return minV;
  }

  function doNextStep() {
    if (allVisited()) return;
    const u = smallestUnvisited();
    if (u === null) return;
    visited[u] = true;
    visitedCount++;

    // Relax neighbors
    adjacency[u].forEach(({v, w}) => {
      if (!visited[v]) {
        const nd = (isFinite(distances[u]) ? distances[u] : Infinity) + w;
        if (nd < distances[v]) {
          distances[v] = nd;
          predecessors[v] = u;
        }
      }
    });

    if (allVisited()) nextBtn.disabled = true;
    if (P5) P5.redraw();
  }

  // ------------------- UI Wiring -------------------
  function setActiveVertexButton(letter) {
    // Remove from all
    document.querySelectorAll('#vertex-grid button').forEach(b => b.classList.remove('active'));
    const btn = document.getElementById('btn-' + letter);
    if (btn) btn.classList.add('active');
  }

  function wireVertexButtons() {
    const letters = ['m','n','o','p','i','j','k','l','e','f','g','h','a','b','c','d'];
    letters.forEach(letter => {
      const btn = document.getElementById('btn-' + letter);
      btn.addEventListener('click', () => {
        setActiveVertexButton(letter);
        resetAlgorithm(letter);
      });
    });
  }

  function startOver() {
    setFixedExampleWeights();
    setActiveVertexButton('m');
    resetAlgorithm('m');
  }

  // ------------------- p5 Sketch -------------------
  function makeSketch() {
    return new p5(p => {
      p.setup = function() {
        const cnv = p.createCanvas(600, 600);
        cnv.parent('canvas-container');
        p.textFont('Arial');
        p.noLoop();
        p.pixelDensity(2); // crisper text on HiDPI
        drawGraph();
      };

      p.draw = function() { drawGraph(); };

      function drawWeightLabel(mx, my, txt) {
        p.textSize(14);
        p.textAlign(p.CENTER, p.CENTER);
        const w = p.textWidth(txt);
        p.noStroke();
        p.fill(255);
        p.rect(mx - (w + 10)/2, my - 10, w + 10, 18, 3);
        p.fill(0);
        p.text(txt, mx, my);
      }

      function drawGraph() {
        p.clear();
        p.background(255);

        // Base edges
        p.stroke(0);
        p.strokeWeight(1.4);
        edgesPairs.forEach(([u,v]) => {
          const a = coords[u], b = coords[v];
          p.line(a.x, a.y, b.x, b.y);

          const mx = (a.x + b.x) / 2;
          const my = (a.y + b.y) / 2;
          drawWeightLabel(mx, my, String(edgeWeights[key(u,v)]));
        });

        // Shortest path tree edges (visited vertex with predecessor)
        p.stroke(0, 90, 255);
        p.strokeWeight(6);
        vertices.forEach(v => {
          const u = predecessors[v];
          if (u && visited[v]) {
            const a = coords[u], b = coords[v];
            p.line(a.x, a.y, b.x, b.y);
          }
        });

        // Vertices and labels
        vertices.forEach(v => {
          const pos = coords[v];
          // Node dot
          p.noStroke();
          p.fill(visited[v] ? p.color(220, 0, 0) : p.color(0));
          p.circle(pos.x, pos.y, 8);

          // Distance label above node
          const distTxt = isFinite(distances[v]) ? `(${distances[v]})` : '(∞)';
          p.textSize(14);
          p.textAlign(p.CENTER, p.BOTTOM);
          p.fill(visited[v] ? p.color(220,0,0) : p.color(0));
          p.text(distTxt, pos.x, pos.y - 12);

          // Vertex letter near dot
          p.textSize(16);
          p.textAlign(p.LEFT, p.TOP);
          p.fill(0);
          p.text(v, pos.x + 6, pos.y + 4);
        });
      }
    });
  }

  // ------------------- Initialization -------------------
  document.addEventListener('DOMContentLoaded', () => {
    computeCoords();
    setFixedExampleWeights();
    wireVertexButtons();
    setActiveVertexButton('m');
    resetAlgorithm('m');

    // Buttons
    document.getElementById('start-over-btn').addEventListener('click', startOver);
    document.getElementById('fixed-example-btn').addEventListener('click', () => {
      setFixedExampleWeights();
      resetAlgorithm(startVertex);
    });
    document.getElementById('random-example-btn').addEventListener('click', () => {
      randomizeWeights();
      resetAlgorithm(startVertex);
    });
    nextBtn.addEventListener('click', doNextStep);

    // Create p5 after DOM is ready
    P5 = makeSketch();
  });
</script>
</body>
</html>
