</head>
<body>
  <div id="app-container">
    <div id="controls-panel">
      <div class="control-group">
        <label for="slider-sphere-diameter">diameter of sphere</label>
        <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">
      </div>

      <div class="control-group">
        <label>polyhedron vertices</label>
        <div id="polyhedron-buttons">
          <button id="btn-cube">Cube</button>
          <button id="btn-dodecahedron">Dodecahedron</button>
          <button id="btn-icosahedron">Icosahedron</button>
          <button id="btn-octahedron" class="active">Octahedron</button>
          <button id="btn-tetrahedron">Tetrahedron</button>
        </div>
      </div>

      <div class="control-group">
        <label for="slider-cone-height">height</label>
        <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">
      </div>

      <div class="control-group">
        <label for="slider-cone-base">base</label>
        <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
      </div>

      <div class="footer-note">
        Tip: Drag to rotate, scroll to zoom, right-drag to pan.
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

  <!-- three.js and OrbitControls -->
  <script src="https://unpkg.com/three@0.158.0/build/three.min.js"></script>
  <script src="https://unpkg.com/three@0.158.0/examples/js/controls/OrbitControls.js"></script>

  <script>
    // DOM elements
    const container = document.getElementById('canvas-container');
    const sliderSphereDiameter = document.getElementById('slider-sphere-diameter');
    const sliderConeHeight = document.getElementById('slider-cone-height');
    const sliderConeBase = document.getElementById('slider-cone-base');

    const btnCube = document.getElementById('btn-cube');
    const btnDodecahedron = document.getElementById('btn-dodecahedron');
    const btnIcosahedron = document.getElementById('btn-icosahedron');
    const btnOctahedron = document.getElementById('btn-octahedron');
    const btnTetrahedron = document.getElementById('btn-tetrahedron');
    const buttonsContainer = document.getElementById('polyhedron-buttons');

    // three.js core
    let scene, camera, renderer, controls;
    let sphere;
    let cones = [];
    let coneMaterial;

    // Constants and initial state
    const DEFAULT_SPHERE_DIAMETER = 1.5;
    const DEFAULT_SPHERE_RADIUS = DEFAULT_SPHERE_DIAMETER / 2; // 0.75
    const INITIAL_POLYHEDRON = 'octahedron';

    init();
    animate();

    function init() {
      // Scene
      scene = new THREE.Scene();

      // Renderer
      renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
      renderer.setPixelRatio(window.devicePixelRatio || 1);
      resizeRenderer();
      renderer.setClearColor(0xf0f0f0);
      container.appendChild(renderer.domElement);

      // Camera
      camera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
      camera.position.set(0, 0, 8);

      // Controls
      controls = new THREE.OrbitControls(camera, renderer.domElement);
      controls.enableDamping = true;
      controls.dampingFactor = 0.05;

      // Lights
      const ambient = new THREE.AmbientLight(0x404040, 1.0);
      scene.add(ambient);
      const dir1 = new THREE.DirectionalLight(0xffffff, 0.85);
      dir1.position.set(1, 1, 1);
      scene.add(dir1);
      const dir2 = new THREE.DirectionalLight(0xffffff, 0.45);
      dir2.position.set(-1, -1, -1);
      scene.add(dir2);

      // Sphere
      const sphereGeo = new THREE.SphereGeometry(DEFAULT_SPHERE_RADIUS, 48, 32);
      const sphereMat = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });
      sphere = new THREE.Mesh(sphereGeo, sphereMat);
      scene.add(sphere);

      // Cones material
      coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });

      // Initial cones (Octahedron)
      setPolyhedron(INITIAL_POLYHEDRON);

      // UI events
      sliderSphereDiameter.addEventListener('input', onSphereDiameterChange);
      sliderConeHeight.addEventListener('input', onConeHeightChange);
      sliderConeBase.addEventListener('input', onConeBaseChange);

      btnCube.addEventListener('click', () => setPolyhedron('cube'));
      btnDodecahedron.addEventListener('click', () => setPolyhedron('dodecahedron'));
      btnIcosahedron.addEventListener('click', () => setPolyhedron('icosahedron'));
      btnOctahedron.addEventListener('click', () => setPolyhedron('octahedron'));
      btnTetrahedron.addEventListener('click', () => setPolyhedron('tetrahedron'));

      window.addEventListener('resize', onWindowResize);
    }

    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }

    function onWindowResize() {
      resizeRenderer();
    }

    function resizeRenderer() {
      const w = container.clientWidth;
      const h = container.clientHeight;
      if (renderer) renderer.setSize(w, h, false);
      if (camera) {
        camera.aspect = w / h;
        camera.updateProjectionMatrix();
      }
    }

    function currentSphereRadius() {
      return DEFAULT_SPHERE_RADIUS * sphere.scale.x;
    }

    function onSphereDiameterChange() {
      const newDiameter = parseFloat(sliderSphereDiameter.value);
      const scale = newDiameter / DEFAULT_SPHERE_DIAMETER;
      sphere.scale.set(scale, scale, scale);
      updateConePositions(); // depends on sphere radius
    }

    function onConeHeightChange() {
      const newHeight = parseFloat(sliderConeHeight.value);
      const base = parseFloat(sliderConeBase.value);
      updateConeGeometries(newHeight, base);
      updateConePositions(); // base must remain on sphere surface
    }

    function onConeBaseChange() {
      const height = parseFloat(sliderConeHeight.value);
      const newBase = parseFloat(sliderConeBase.value);
      updateConeGeometries(height, newBase);
      // positions unchanged
    }

    function clearCones() {
      for (const c of cones) {
        scene.remove(c);
        if (c.geometry) c.geometry.dispose();
      }
      cones = [];
    }

    function updateConeGeometries(height, baseRadius) {
      for (const c of cones) {
        if (c.geometry) c.geometry.dispose();
        c.geometry = new THREE.ConeGeometry(baseRadius, height, 32);
      }
    }

    function updateConePositions() {
      const sphereR = currentSphereRadius();
      const height = parseFloat(sliderConeHeight.value);
      for (const c of cones) {
        const dir = c.userData.direction; // unit vector
        const centerPos = dir.clone().multiplyScalar(sphereR + height / 2);
        c.position.copy(centerPos);
        // orientation remains the same
      }
    }

    function setPolyhedron(type) {
      setActiveButton(type);

      const directions = getVertexDirections(type);
      const height = parseFloat(sliderConeHeight.value);
      const baseRadius = parseFloat(sliderConeBase.value);
      const sphereR = currentSphereRadius();

      clearCones();

      for (const dir of directions) {
        const cone = new THREE.Mesh(new THREE.ConeGeometry(baseRadius, height, 32), coneMaterial);
        // orient: default cone axis +Y -> align with direction
        cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), dir);
        const centerPos = dir.clone().multiplyScalar(sphereR + height / 2);
        cone.position.copy(centerPos);
        cone.userData.direction = dir.clone(); // store for updates
        scene.add(cone);
        cones.push(cone);
      }
    }

    function setActiveButton(type) {
      // clear active
      for (const btn of buttonsContainer.querySelectorAll('button')) btn.classList.remove('active');
      switch (type) {
        case 'cube': btnCube.classList.add('active'); break;
        case 'dodecahedron': btnDodecahedron.classList.add('active'); break;
        case 'icosahedron': btnIcosahedron.classList.add('active'); break;
        case 'octahedron': btnOctahedron.classList.add('active'); break;
        case 'tetrahedron': btnTetrahedron.classList.add('active'); break;
      }
    }

    function getVertexDirections(type) {
      let geometry;
      switch (type) {
        case 'cube':
          geometry = new THREE.BoxGeometry(2, 2, 2); // corners at ±1
          break;
        case 'dodecahedron':
          geometry = new THREE.DodecahedronGeometry(1);
          break;
        case 'icosahedron':
          geometry = new THREE.IcosahedronGeometry(1);
          break;
        case 'octahedron':
          geometry = new THREE.OctahedronGeometry(1);
          break;
        case 'tetrahedron':
          geometry = new THREE.TetrahedronGeometry(1);
          break;
        default:
          geometry = new THREE.OctahedronGeometry(1);
          break;
      }

      const pos = geometry.getAttribute('position');
      const map = new Map();
      const directions = [];
      for (let i = 0; i < pos.count; i++) {
        const v = new THREE.Vector3(pos.getX(i), pos.getY(i), pos.getZ(i)).normalize();
        // Deduplicate by rounding
        const key = `${round(v.x)},${round(v.y)},${round(v.z)}`;
        if (!map.has(key)) {
          map.set(key, true);
          directions.push(v);
        }
      }
      geometry.dispose();
      return directions;

      function round(n) {
        return Math.round(n * 100000) / 100000;
      }
    }

    // Set initial values explicitly (in case browser restores)
    (function initUIToDefaults() {
      sliderSphereDiameter.value = "1.5";
      sliderConeHeight.value = "1.5";
      sliderConeBase.value = "0.4";
      onSphereDiameterChange(); // ensures cone positions are synced with sphere scale
    })();
  </script>
</body>
</html>
