      <div class="row">
        <input type="checkbox" id="checkbox-snapshots" checked>
        <label for="checkbox-snapshots">show sun snapshots</label>
      </div>
      <div class="row">
        <input type="checkbox" id="checkbox-scales" checked>
        <label for="checkbox-scales">show scales</label>
      </div>
    </div>
    <div class="small-note">Tip: pick a planet for a preset, or enable "choose orbit manually" to adjust sliders.</div>
  </div>

  <!-- Right Column (Visualization Area) -->
  <div id="visualization-area">
    <canvas id="analemma-canvas"></canvas>
  </div>
</div>

<script>
/* Predefined Planet Data (as specified) */
const planetData = {
  "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
  "Mars":  { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
};

/* UI Elements */
const ui = {
  selectPlanet: document.getElementById('select-planet'),
  checkboxManual: document.getElementById('checkbox-manual'),
  sliderTilt: document.getElementById('slider-tilt'),
  sliderEquinox: document.getElementById('slider-equinox'),
  sliderEcc: document.getElementById('slider-eccentricity'),
  checkboxSnapshots: document.getElementById('checkbox-snapshots'),
  checkboxScales: document.getElementById('checkbox-scales'),
};

function setSlidersEnabled(enabled) {
  ui.sliderTilt.disabled = !enabled;
  ui.sliderEquinox.disabled = !enabled;
  ui.sliderEcc.disabled = !enabled;
}

function toRadians(deg) { return deg * Math.PI / 180; }
function toDegrees(rad) { return rad * 180 / Math.PI; }
function wrapToPi(a) {
  // Wrap angle to (-π, π]
  a = (a + Math.PI) % (2 * Math.PI);
  if (a < 0) a += 2 * Math.PI;
  return a - Math.PI;
}

/* State and redraw orchestration */
let needsRecalc = true; // recalc analemma model or just redraw
let pointCache = null;  // store {xr, yr} arrays in radians
let lastParamsKey = '';

function getParams() {
  return {
    tiltDeg: parseFloat(ui.sliderTilt.value),
    ecc: parseFloat(ui.sliderEcc.value),
    equinoxDeg: parseFloat(ui.sliderEquinox.value)
  };
}
function makeParamsKey(p) {
  return [p.tiltDeg, p.ecc, p.equinoxDeg].join('|');
}

function applyPreset(name) {
  const data = planetData[name];
  if (!data) return;
  // Set sliders from preset
  ui.sliderTilt.value = data.tilt;
  ui.sliderEcc.value = data.eccentricity;
  ui.sliderEquinox.value = data.equinox;
  // Disable manual controls
  ui.checkboxManual.checked = false;
  setSlidersEnabled(false);
  needsRecalc = true;
  if (window.redraw) window.redraw();
}

function enableManual() {
  ui.checkboxManual.checked = true;
  ui.selectPlanet.value = "manual";
  setSlidersEnabled(true);
  needsRecalc = true;
  if (window.redraw) window.redraw();
}

/* UI Event Wiring */
ui.selectPlanet.addEventListener('change', (e) => {
  const val = e.target.value;
  if (val === 'manual') {
    enableManual();
  } else {
    applyPreset(val);
  }
});

ui.checkboxManual.addEventListener('change', (e) => {
  const enabled = e.target.checked;
  if (enabled) {
    ui.selectPlanet.value = 'manual';
  }
  setSlidersEnabled(enabled);
  needsRecalc = true;
  if (window.redraw) window.redraw();
});

['input', 'change'].forEach(evt => {
  ui.sliderTilt.addEventListener(evt, () => { needsRecalc = true; if (window.redraw) window.redraw(); });
  ui.sliderEquinox.addEventListener(evt, () => { needsRecalc = true; if (window.redraw) window.redraw(); });
  ui.sliderEcc.addEventListener(evt, () => { needsRecalc = true; if (window.redraw) window.redraw(); });
});

ui.checkboxSnapshots.addEventListener('change', () => { /* display only */ if (window.redraw) window.redraw(); });
ui.checkboxScales.addEventListener('change', () => { /* display only */ if (window.redraw) window.redraw(); });

/* p5 Sketch (global mode) */
let cnv;
let plot = {
  margin: 28,
  topOrbitH: 90
};

function setup() {
  // Remove placeholder canvas so p5 can create one with the same ID
  const placeholder = document.getElementById('analemma-canvas');
  if (placeholder && placeholder.parentElement) {
    placeholder.remove();
  }
  const area = document.getElementById('visualization-area');
  const w = area.clientWidth;
  const h = area.clientHeight;
  cnv = createCanvas(w, h);
  cnv.parent('visualization-area');
  cnv.id('analemma-canvas'); // keep the required ID
  pixelDensity(window.devicePixelRatio || 1);
  noLoop(); // redraw only on interactions
  needsRecalc = true;
  redraw();
}

function windowResized() {
  const area = document.getElementById('visualization-area');
  resizeCanvas(area.clientWidth, area.clientHeight);
  redraw();
}

function computeAnalemmaPoints(params) {
  const tilt = toRadians(params.tiltDeg);
  const ecc = params.ecc;
  const eqx = toRadians(params.equinoxDeg);

  const pts = [];
  const days = 365; // approximate
  for (let d = 0; d <= days; d++) {
    const M0 = 2 * Math.PI * d / 365.25;
    const M = wrapToPi(M0);

    // Kepler's equation: M = E - e sin E (Newton's method)
    let E = M + ecc * Math.sin(M) + 0.5 * ecc * ecc * Math.sin(2 * M); // decent initial guess
    for (let i = 0; i < 6; i++) {
      const f = E - ecc * Math.sin(E) - M;
      const fp = 1 - ecc * Math.cos(E);
      E = E - f / fp;
    }

    const sqrt1pe = Math.sqrt(1 + ecc);
    const sqrt1me = Math.sqrt(1 - ecc);
    const v = 2 * Math.atan2(sqrt1pe * Math.sin(E / 2), sqrt1me * Math.cos(E / 2)); // true anomaly
    const lambda = wrapToPi(v + eqx); // ecliptic longitude shifted by equinox

    // Declination (y)
    const yAng = Math.asin(Math.sin(tilt) * Math.sin(lambda));

    // Right ascension (alpha)
    const alpha = Math.atan2(Math.cos(tilt) * Math.sin(lambda), Math.cos(lambda));

    // Equation of time proxy (difference)
    let eot = wrapToPi(M - alpha);

    pts.push({ xr: eot, yr: yAng });
  }
  return pts;
}

function drawAxes(centerX, centerY, C) {
  if (!ui.checkboxScales.checked) return;

  push();
  stroke(120);
  strokeWeight(1);
  noFill();

  // Draw a light bounding box for the plot region
  const left = centerX - (width - 2 * plot.margin) / 2;
  const right = centerX + (width - 2 * plot.margin) / 2;
  const top = plot.topOrbitH + plot.margin;
  const bottom = height - plot.margin;
  drawingContext.setLineDash([4, 4]);
  rectMode(CORNERS);
  rect(left, top, right, bottom);
  drawingContext.setLineDash([]);

  // Axes (through origin of plotting coords)
  translate(centerX, centerY);
  // x-axis
  line(-width, 0, width, 0);
  // y-axis
  line(0, -height, 0, height);

  // Ticks every 20 degrees from -60 to 60 (labels in degrees)
  textAlign(CENTER, TOP);
  textSize(12);
  fill(90);
  noStroke();

  const degTicks = [-60,-40,-20,0,20,40,60];

  // X-axis ticks
  for (const deg of degTicks) {
    const x = toRadians(deg) * C;
    stroke(120);
    line(x, -4, x, 4);
    noStroke();
    if (Math.abs(deg) > 0) {
      text(deg, x, 6);
    } else {
      text('0', x, 6);
    }
  }

  // Y-axis ticks
  textAlign(RIGHT, CENTER);
  for (const deg of degTicks) {
    const y = -toRadians(deg) * C;
    stroke(120);
    line(-4, y, 4, y);
    noStroke();
    text(deg, -8, y);
  }
  pop();
}

function drawOrbitEllipse(ecc) {
  // Draw at top center as a small brown ellipse
  const cx = width / 2;
  const cy = plot.topOrbitH / 2 + 10;
  const base = 26;
  const rx = base * (1 + ecc);               // horizontal radius increases with eccentricity
  const ry = base * Math.sqrt(1 - ecc*ecc);  // vertical radius shrinks with eccentricity

  push();
  noFill();
  stroke(120, 85, 50);
  strokeWeight(2);
  ellipse(cx, cy, rx * 2, ry * 2);
  pop();
}

function draw() {
  background(255);

  // Center of analytic plot (0,0 at center of main plot area)
  const plotCenter = {
    x: width / 2,
    y: (plot.topOrbitH + (height - plot.topOrbitH) / 2)
  };

  // Scaling (radians to pixels)
  // Single scale C for both axes as required
  const availableH = (height - plot.topOrbitH) - 2 * plot.margin;
  const availableW = width - 2 * plot.margin;
  const C = Math.min(availableW, availableH) / 1.8; // tuned so shapes fit comfortably

  // Read current parameters
  const params = getParams();
  const paramsKey = makeParamsKey(params);

  // Recompute model if needed
  if (needsRecalc || paramsKey !== lastParamsKey || !pointCache) {
    pointCache = computeAnalemmaPoints(params);
    lastParamsKey = paramsKey;
    needsRecalc = false;
  }

  // Draw orbit indicator at top
  drawOrbitEllipse(params.ecc);

  // Draw axes and ticks if requested
  drawAxes(plotCenter.x, plotCenter.y, C);

  // Prepare to draw analemma with origin at plot center
  push();
  translate(plotCenter.x, plotCenter.y);

  // Analemma curve
  noFill();
  stroke('orange');
  strokeWeight(3);
  beginShape();
  for (let i = 0; i < pointCache.length; i++) {
    const pt = pointCache[i];
    const x = pt.xr * C;
    const y = -pt.yr * C; // screen Y inverted
    vertex(x, y);
  }
  endShape();

  // Sun snapshots (every ~20th point)
  if (ui.checkboxSnapshots.checked) {
    noStroke();
    fill('orange');
    for (let i = 0; i < pointCache.length; i += 20) {
      const pt = pointCache[i];
      const x = pt.xr * C;
      const y = -pt.yr * C;
      circle(x, y, 8);
    }
  }

  pop();
}
</script>
