  <script>
    // ====== Utility data and helpers ======
    const letters = ['A','B','C','D','E','F','G'];
    const labelToIndex = Object.fromEntries(letters.map((ch, i) => [ch, i]));

    function cloneMatrix(M) {
      return M.map(row => row.slice());
    }

    function makeFixedGraph() {
      const positions = [
        {x:200, y:60},   // A
        {x:80,  y:120},  // B
        {x:60,  y:240},  // C
        {x:150, y:340},  // D
        {x:250, y:340},  // E
        {x:340, y:240},  // F
        {x:320, y:120},  // G
      ];
      const W = [
        [null,11,16,44,30,44,23],
        [11,null,12,12,10,30,25],
        [16,12,null,43,32,36,44],
        [44,12,43,null,41,30,35],
        [30,10,32,41,null,31,20],
        [44,30,36,30,31,null,18],
        [23,25,44,35,20,18,null],
      ];
      return { type: 'fixed', positions, W };
    }

    function makeRandomGraph() {
      // Positions on a circle for clarity
      const N = 7;
      const center = {x: 230, y: 215};
      const R = 150;
      const positions = [];
      for (let i=0;i<N;i++) {
        const ang = -Math.PI/2 + i * 2*Math.PI/N;
        positions.push({x: center.x + R*Math.cos(ang), y: center.y + R*Math.sin(ang)});
      }
      const W = Array.from({length:N}, () => Array(N).fill(null));
      for (let i=0;i<N;i++) {
        for (let j=i+1;j<N;j++) {
          const w = Math.floor(10 + Math.random()*41); // 10..50
          W[i][j] = W[j][i] = w;
        }
      }
      return { type: 'random', positions, W };
    }

    function idxToLabel(i){ return letters[i]; }

    function formatEdge(i,j){
      return `{${idxToLabel(i)}, ${idxToLabel(j)}}`;
    }

    function edgeWeight(W, i, j){ return W[i][j]; }

    function pathWeight(W, pathIdx) {
      let sum = 0;
      for (let k=0;k<pathIdx.length-1;k++) sum += W[pathIdx[k]][pathIdx[k+1]];
      return sum;
    }

    // Brute force optimal Hamiltonian circuit (fix start at 0 to avoid duplicates)
    function optimalByBruteforce(W) {
      const N = W.length;
      const perm = [];
      for (let i=1;i<N;i++) perm.push(i);
      let best = Infinity;
      let bestPath = null;

      function swap(a,b){ const t=perm[a]; perm[a]=perm[b]; perm[b]=t; }
      function permute(l){
        if (l === perm.length) {
          const trial = [0, ...perm, 0];
          let total = 0;
          for (let i=0;i<N;i++) total += W[trial[i]][trial[i+1]];
          if (total < best) { best = total; bestPath = trial.slice(); }
          return;
        }
        for (let i=l;i<perm.length;i++){
          swap(l,i);
          permute(l+1);
          swap(l,i);
        }
      }
      permute(0);
      return { pathIdx: bestPath, total: best };
    }

    // Pre-calculated optimal for the fixed graph (provided in instructions)
    function fixedOptimal(W) {
      const pathIdx = [labelToIndex.A, labelToIndex.C, labelToIndex.B, labelToIndex.D, labelToIndex.F, labelToIndex.G, labelToIndex.E, labelToIndex.A];
      const total = pathWeight(W, pathIdx);
      return { pathIdx, total };
    }

    // ====== App State ======
    const state = {
      currentAlgorithm: 'nearest-neighbor', // 'nearest-neighbor' or 'cheapest-link'
      graph: makeFixedGraph(),
      optimal: null, // { pathIdx, total }
      algo: {
        started: false,
        finished: false,
        startIndex: null,
        currentIndex: null,
        visited: new Set(),
        edgesPicked: [], // [ [i,j], ... ]
        totalWeight: 0,
        // For cheapest link
        sortedEdges: [],
        sortedCursor: 0,
        degree: [],
      }
    };

    // ====== p5 sketch helpers ======
    const GraphRenderer = {
      drawGraph(p, positions, W) {
        p.push();
        p.background(255);
        p.stroke(120);
        p.strokeWeight(1);
        p.textSize(12);
        p.fill(0);
        // Draw all edges with weights
        for (let i=0;i<W.length;i++){
          for (let j=i+1;j<W.length;j++){
            const a = positions[i], b = positions[j];
            p.stroke(160);
            p.line(a.x, a.y, b.x, b.y);
            // Weight label with white background near midpoint, slightly offset
            const midx = (a.x + b.x)/2;
            const midy = (a.y + b.y)/2;
            const dx = b.x - a.x, dy = b.y - a.y;
            const len = Math.max(1, Math.hypot(dx,dy));
            const off = 10;
            const tx = midx - (dy/len)*off;
            const ty = midy + (dx/len)*off;
            const label = String(W[i][j]);
            const tw = p.textWidth(label);
            p.noStroke();
            p.fill(255);
            p.rect(tx - tw/2 - 3, ty - 9, tw + 6, 14);
            p.fill(0);
            p.textAlign(p.CENTER, p.CENTER);
            p.text(label, tx, ty);
          }
        }
        // Draw vertices
        for (let i=0;i<positions.length;i++){
          const v = positions[i];
          p.stroke(0);
          p.fill(255);
          p.ellipse(v.x, v.y, 28, 28);
          p.fill(0);
          p.noStroke();
          p.textAlign(p.CENTER, p.CENTER);
          p.text(idxToLabel(i), v.x, v.y);
        }
        p.pop();
      },
      drawHighlightedEdges(p, positions, edges, color='#ff7f0e') {
        p.push();
        p.stroke(color);
        p.strokeWeight(3);
        edges.forEach(([i,j]) => {
          const a = positions[i], b = positions[j];
          p.line(a.x, a.y, b.x, b.y);
        });
        p.pop();
      }
    };

    // ====== p5 Instances ======
    let algoSketchInstance = null;
    let optimalSketchInstance = null;

    function initAlgoSketch() {
      const sketch = (p) => {
        p.setup = function() {
          const cnv = p.createCanvas(460, 430);
          cnv.parent('canvas-algo-wrapper');
          p.noLoop();
          p.pixelDensity(1);
        };
        p.draw = function() {
          GraphRenderer.drawGraph(p, state.graph.positions, state.graph.W);
          GraphRenderer.drawHighlightedEdges(p, state.graph.positions, state.algo.edgesPicked, '#ff7f0e');
        };
      };
      algoSketchInstance = new p5(sketch);
    }

    function initOptimalSketchIfNeeded() {
      if (optimalSketchInstance) return;
      const sketch = (p) => {
        p.setup = function() {
          const cnv = p.createCanvas(460, 430);
          cnv.parent('canvas-optimal-wrapper');
          p.noLoop();
          p.pixelDensity(1);
        };
        p.draw = function() {
          GraphRenderer.drawGraph(p, state.graph.positions, state.graph.W);
          // Convert pathIdx to edge list
          if (!state.optimal) return;
          const edges = [];
          const path = state.optimal.pathIdx;
          for (let k=0;k<path.length-1;k++) edges.push([path[k], path[k+1]]);
          GraphRenderer.drawHighlightedEdges(p, state.graph.positions, edges, '#1f77b4');
        };
      };
      optimalSketchInstance = new p5(sketch);
    }

    // ====== Algorithm Implementations ======
    function resetAlgoState() {
      state.algo.started = false;
      state.algo.finished = false;
      state.algo.startIndex = null;
      state.algo.currentIndex = null;
      state.algo.visited = new Set();
      state.algo.edgesPicked = [];
      state.algo.totalWeight = 0;
      state.algo.sortedEdges = [];
      state.algo.sortedCursor = 0;
      state.algo.degree = Array(letters.length).fill(0);
    }

    // Build sorted edge list for cheapest link
    function buildSortedEdges() {
      const W = state.graph.W;
      const list = [];
      for (let i=0;i<W.length;i++){
        for (let j=i+1;j<W.length;j++){
          list.push({i,j,w:W[i][j]});
        }
      }
      list.sort((a,b) => {
        if (a.w !== b.w) return a.w - b.w;
        const na = idxToLabel(a.i) + idxToLabel(a.j);
        const nb = idxToLabel(b.i) + idxToLabel(b.j);
        return na.localeCompare(nb);
      });
      state.algo.sortedEdges = list;
      state.algo.sortedCursor = 0;
    }

    // Disjoint Set Union for cycle detection
    class DSU {
      constructor(n){
        this.p = Array.from({length:n}, (_,i)=>i);
        this.r = Array(n).fill(0);
      }
      find(x){ return this.p[x]===x?x:(this.p[x]=this.find(this.p[x])); }
      union(a,b){
        a=this.find(a); b=this.find(b);
        if (a===b) return false;
        if (this.r[a]<this.r[b]) [a,b]=[b,a];
        this.p[b]=a;
        if (this.r[a]===this.r[b]) this.r[a]++;
        return true;
      }
    }

    function canAddCheapestEdge(i,j) {
      const N = letters.length;
      const deg = state.algo.degree;
      if (deg[i] >= 2 || deg[j] >= 2) return false;

      const edges = state.algo.edgesPicked;
      if (edges.length < N-1) {
        // Use DSU to check if i and j are already connected => premature cycle
        const dsu = new DSU(N);
        for (const [a,b] of edges) dsu.union(a,b);
        if (dsu.find(i) === dsu.find(j)) return false;
      } else if (edges.length === N-1) {
        // Only allow if both vertices have degree 1 to close the cycle
        if (!(deg[i] === 1 && deg[j] === 1)) return false;
      }
      return true;
    }

    function stepCheapestLink() {
      if (state.algo.finished) return;
      if (state.algo.sortedEdges.length === 0) buildSortedEdges();
      const list = state.algo.sortedEdges;
      while (state.algo.sortedCursor < list.length) {
        const e = list[state.algo.sortedCursor++];
        if (canAddCheapestEdge(e.i, e.j)) {
          state.algo.edgesPicked.push([e.i, e.j]);
          state.algo.degree[e.i]++; state.algo.degree[e.j]++;
          state.algo.totalWeight += e.w;
          break;
        }
      }
      if (state.algo.edgesPicked.length === letters.length) {
        state.algo.finished = true;
      }
    }

    function stepNearestNeighbor() {
      if (state.algo.finished || !state.algo.started) return;
      const W = state.graph.W;
      const N = letters.length;
      const cur = state.algo.currentIndex;

      // If all visited, connect back to start.
      if (state.algo.visited.size === N) {
        const s = state.algo.startIndex;
        state.algo.edgesPicked.push([cur, s]);
        state.algo.totalWeight += W[cur][s];
        state.algo.finished = true;
        return;
      }

      // Find nearest unvisited neighbor
      let bestJ = null, bestW = Infinity;
      for (let j=0;j<N;j++) {
        if (j === cur) continue;
        if (state.algo.visited.has(j)) continue;
        const w = W[cur][j];
        if (w < bestW) {
          bestW = w; bestJ = j;
        } else if (w === bestW && bestJ !== null) {
          if (idxToLabel(j) < idxToLabel(bestJ)) bestJ = j;
        }
      }
      if (bestJ !== null) {
        state.algo.edgesPicked.push([cur, bestJ]);
        state.algo.totalWeight += W[cur][bestJ];
        state.algo.currentIndex = bestJ;
        state.algo.visited.add(bestJ);
        // If now all visited, next step will close the circuit
        if (state.algo.visited.size === N) {
          // Optionally, auto-close on same step? Spec says final step adds edge returning to the start when button pressed again.
          // We'll wait for next click to add the closing edge.
        }
      }
    }

    // ====== UI update helpers ======
    function setExampleTypeTexts() {
      const t = state.graph.type === 'fixed' ? 'fixed example' : 'random example';
      document.getElementById('text-example-type-algo').textContent = t;
      document.getElementById('text-example-type-optimal').textContent = t;
    }

    function clearTableAndTotals() {
      document.getElementById('table-title').textContent = '';
      document.getElementById('table-edge-list').innerHTML = '';
      document.getElementById('text-total-weight').textContent = '';
    }

    function updateTableAndTotals() {
      const edges = state.algo.edgesPicked;
      const W = state.graph.W;
      if (edges.length === 0) {
        clearTableAndTotals();
        algoSketchInstance && algoSketchInstance.redraw();
        return;
      }
      document.getElementById('table-title').textContent = 'Edges in the order selected';
      const table = document.getElementById('table-edge-list');
      const rows = [];
      rows.push('<tr><th>edge</th><th>weight</th></tr>');
      edges.forEach(([i,j]) => {
        rows.push(`<tr><td>${formatEdge(i,j)}</td><td>${W[i][j]}</td></tr>`);
      });
      table.innerHTML = rows.join('');

      let message = `The total weight of the edges selected so far is ${state.algo.totalWeight}.`;
      if (state.algo.finished) {
        if (state.currentAlgorithm === 'nearest-neighbor') {
          const sLabel = idxToLabel(state.algo.startIndex);
          message = `The nearest neighbor algorithm starting at vertex ${sLabel} found a Hamilton circuit of weight ${state.algo.totalWeight}.`;
        } else {
          message = `The cheapest link algorithm found a Hamilton circuit of weight ${state.algo.totalWeight}.`;
        }
      }
      document.getElementById('text-total-weight').textContent = message;

      algoSketchInstance && algoSketchInstance.redraw();
    }

    function refreshOptimal() {
      // Ensure state.optimal is set
      if (!state.optimal) {
        if (state.graph.type === 'fixed') {
          state.optimal = fixedOptimal(state.graph.W);
        } else {
          state.optimal = optimalByBruteforce(state.graph.W);
        }
      }
      if (optimalSketchInstance) optimalSketchInstance.redraw();
      document.getElementById('text-optimal-weight').textContent =
        `An optimal solution has a total weight of ${state.optimal.total}.`;
    }

    function setVertexButtonsEnabled(enabled) {
      const btns = document.querySelectorAll('.vertex-btn');
      btns.forEach(b => b.disabled = !enabled);
    }
    function clearVertexButtonSelection() {
      document.querySelectorAll('.vertex-btn').forEach(b => b.classList.remove('selected'));
    }

    function setNextStepEnabled(enabled) {
      document.getElementById('btn-next-step').disabled = !enabled;
    }

    function updateTitle() {
      const titleEl = document.getElementById('title-algo');
      if (state.currentAlgorithm === 'nearest-neighbor') {
        if (state.algo.started && state.algo.startIndex !== null) {
          titleEl.textContent = `The nearest neighbor algorithm starting at vertex ${idxToLabel(state.algo.startIndex)}`;
        } else {
          titleEl.textContent = 'Select a starting vertex';
        }
      } else {
        titleEl.textContent = 'The cheapest link algorithm';
      }
    }

    function fullRedraw() {
      setExampleTypeTexts();
      updateTitle();
      algoSketchInstance && algoSketchInstance.redraw();
      if (document.getElementById('checkbox-optimal').checked) {
        refreshOptimal();
      }
    }

    // ====== Control Logic ======
    function startOver() {
      resetAlgoState();
      clearTableAndTotals();
      setExampleTypeTexts();

      // Optimal pre-computation for display when toggled
      state.optimal = null;

      if (state.currentAlgorithm === 'nearest-neighbor') {
        document.getElementById('starting-vertex-selector').style.display = 'block';
        setVertexButtonsEnabled(true);
        clearVertexButtonSelection();
        setNextStepEnabled(false);
      } else {
        document.getElementById('starting-vertex-selector').style.display = 'none';
        setVertexButtonsEnabled(false);
        setNextStepEnabled(true);
      }
      updateTitle();
      fullRedraw();
    }

    // Load example graph handlers
    function loadFixedExample() {
      state.graph = makeFixedGraph();
      startOver();
    }
    function loadRandomExample() {
      state.graph = makeRandomGraph();
      startOver();
    }

    // ====== Event Listeners ======
    document.getElementById('algo-cheapest-link').addEventListener('change', (e) => {
      if (e.target.checked) {
        state.currentAlgorithm = 'cheapest-link';
        startOver();
      }
    });
    document.getElementById('algo-nearest-neighbor').addEventListener('change', (e) => {
      if (e.target.checked) {
        state.currentAlgorithm = 'nearest-neighbor';
        startOver();
      }
    });

    document.getElementById('btn-start-over').addEventListener('click', startOver);
    document.getElementById('btn-fixed-example').addEventListener('click', loadFixedExample);
    document.getElementById('btn-random-example').addEventListener('click', loadRandomExample);

    // Starting vertex buttons
    document.getElementById('vertex-buttons-container').addEventListener('click', (e) => {
      const btn = e.target.closest('.vertex-btn');
      if (!btn) return;
      if (state.currentAlgorithm !== 'nearest-neighbor') return;
      if (btn.disabled) return;

      // select starting vertex
      clearVertexButtonSelection();
      btn.classList.add('selected');
      const label = btn.dataset.vertex;
      const idx = labelToIndex[label];
      state.algo.startIndex = idx;
      state.algo.currentIndex = idx;
      state.algo.visited = new Set([idx]);
      state.algo.started = true;
      setVertexButtonsEnabled(false);
      setNextStepEnabled(true);
      updateTitle();
      fullRedraw();
    });

    document.getElementById('btn-next-step').addEventListener('click', () => {
      if (state.currentAlgorithm === 'nearest-neighbor') {
        stepNearestNeighbor();
      } else {
        stepCheapestLink();
      }
      if (state.algo.finished) setNextStepEnabled(false);
      updateTableAndTotals();
    });

    document.getElementById('checkbox-optimal').addEventListener('change', (e) => {
      const cont = document.getElementById('optimal-result-container');
      if (e.target.checked) {
        cont.style.display = 'block';
        initOptimalSketchIfNeeded();
        refreshOptimal();
      } else {
        cont.style.display = 'none';
      }
    });

    // ====== Initialize ======
    window.addEventListener('DOMContentLoaded', () => {
      initAlgoSketch();

      // Initial state per specification:
      // - Fixed example loaded already (default)
      // - title "The nearest neighbor algorithm"
      // - example type text "fixed example"
      // - next-step disabled and vertex buttons disabled until "start over" or example buttons
      setExampleTypeTexts();
      document.getElementById('title-algo').textContent = 'The nearest neighbor algorithm';
      setVertexButtonsEnabled(false);
      setNextStepEnabled(false);
      clearTableAndTotals();
      algoSketchInstance.redraw();
    });
  </script>
</body>
</html>
