<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Ullman's Theorem in Two Dimensions</title>
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.5.0/math.js"></script>
  <style>
    html, body {
      margin: 0;
      height: 100%;
      font-family: Arial, Helvetica, sans-serif;
      color: #333;
    }
    #main-container {
      display: flex;
      width: 100vw;
      height: 100vh;
      box-sizing: border-box;
      overflow: hidden;
    }
    #control-panel {
      width: 260px;
      min-width: 260px;
      padding: 10px 12px;
      background: #f5f5f5;
      border-right: 1px solid #ddd;
      box-sizing: border-box;
      overflow-y: auto;
    }
    .slider-group {
      display: grid;
      grid-template-columns: 50px 1fr 56px;
      align-items: center;
      gap: 8px;
      margin: 10px 0;
    }
    .slider-group label {
      font-size: 13px;
      color: #555;
    }
    .slider-group input[type="range"] {
      width: 100%;
    }
    .slider-group span {
      font-family: Consolas, Menlo, monospace;
      font-size: 12px;
      text-align: right;
      color: #444;
    }
    #canvas-container {
      flex: 1;
      position: relative;
      background: #ffffff;
    }
    .footer {
      margin-top: 8px;
      font-size: 12px;
      color: #777;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div class="slider-group">
        <label for="slider-au">A.u</label>
        <input type="range" id="slider-au" min="-2" max="2" step="0.01" value="0.8">
        <span id="value-au">0.80</span>
      </div>
      <div class="slider-group">
        <label for="slider-bu">B.u</label>
        <input type="range" id="slider-bu" min="-2" max="2" step="0.01" value="-0.5">
        <span id="value-bu">-0.50</span>
      </div>
      <div class="slider-group">
        <label for="slider-av">A.v</label>
        <input type="range" id="slider-av" min="-2" max="2" step="0.01" value="1.2">
        <span id="value-av">1.20</span>
      </div>
      <div class="slider-group">
        <label for="slider-bv">B.v</label>
        <input type="range" id="slider-bv" min="-2" max="2" step="0.01" value="1.0">
        <span id="value-bv">1.00</span>
      </div>
      <div class="slider-group">
        <label for="slider-aw">A.w</label>
        <input type="range" id="slider-aw" min="-2" max="2" step="0.01" value="1.1">
        <span id="value-aw">1.10</span>
      </div>
      <div class="slider-group">
        <label for="slider-bw">B.w</label>
        <input type="range" id="slider-bw" min="-2" max="2" step="0.01" value="-0.2">
        <span id="value-bw">-0.20</span>
      </div>
      <div class="slider-group">
        <label for="slider-x-scale">x scale</label>
        <input type="range" id="slider-x-scale" min="20" max="200" step="1" value="80">
        <span id="value-x-scale">80</span>
      </div>
      <div class="slider-group">
        <label for="slider-y-scale">y scale</label>
        <input type="range" id="slider-y-scale" min="20" max="200" step="1" value="80">
        <span id="value-y-scale">80</span>
      </div>
      <div class="footer">
        Ullman's Theorem (2D) demo. Move sliders to change dot products and scales.
      </div>
    </div>
    <div id="canvas-container"></div>
  </div>

  <script>
    // Global state
    let xScale = 80;
    let yScale = 80;

    let u = [1, 0]; // camera axes (computed)
    let v = [0, 1];
    let w = [1, 1];

    let A = [1, 0]; // points (computed)
    let B = [0, 1];

    let validState = false; // set true when computation succeeds
    let lastError = "";

    function getSliderNumber(id) {
      return parseFloat(document.getElementById(id).value);
    }

    function setValueSpan(id, value, digits = 2) {
      const el = document.getElementById(id);
      if (!el) return;
      if (Number.isInteger(value)) {
        el.textContent = String(value);
      } else {
        el.textContent = value.toFixed(digits);
      }
    }

    function attachSlider(id, valueSpanId, onInput) {
      const slider = document.getElementById(id);
      const isScale = id === 'slider-x-scale' || id === 'slider-y-scale';
      slider.addEventListener('input', () => {
        const val = parseFloat(slider.value);
        setValueSpan(valueSpanId, val, isScale ? 0 : 2);
        onInput && onInput(val);
      });
    }

    // Linear algebra helpers
    function norm2(vec) {
      return Math.hypot(vec[0], vec[1]);
    }
    function normalize(vec) {
      const n = norm2(vec);
      if (n === 0) return [0, 0];
      return [vec[0] / n, vec[1] / n];
    }
    function scaleVec(s, vec) {
      return [s * vec[0], s * vec[1]];
    }

    function computeFromSliders() {
      lastError = "";
      try {
        const au = getSliderNumber('slider-au');
        const bu = getSliderNumber('slider-bu');
        const av = getSliderNumber('slider-av');
        const bv = getSliderNumber('slider-bv');
        const aw = getSliderNumber('slider-aw');
        const bw = getSliderNumber('slider-bw');

        // 1) Measurement matrix M (3x2)
        const M = [
          [au, bu],
          [av, bv],
          [aw, bw]
        ];

        // 2) SVD: M = U * S * V^T
        const svd = math.svd(math.matrix(M));
        const Ufull = svd.U.toArray(); // 3x3
        const Sfull = svd.S.toArray(); // 3x2 (diagonal)
        const V = svd.V.toArray();     // 2x2

        // take first two columns of U to form S_hat (3x2)
        const U2 = Ufull.map(row => [row[0], row[1]]);

        // singular values s1, s2
        const s1 = (Sfull[0] && Sfull[0][0]) ? Sfull[0][0] : 0;
        const s2 = (Sfull[1] && Sfull[1][1]) ? Sfull[1][1] : 0;
        const D2 = [
          [s1, 0],
          [0, s2]
        ];

        // 3) Build K and solve K * l = [1,1,1] for l = [l1, l2, l3]
        const K = U2.map(r => {
          const x = r[0], y = r[1];
          return [x * x, 2 * x * y, y * y];
        });
        const b = [1, 1, 1];

        // Solve using LU
        const lvecMat = math.lusolve(math.matrix(K), math.matrix(b));
        const lvec = lvecMat.toArray(); // [[l1],[l2],[l3]]
        const l1 = lvec[0][0], l2 = lvec[1][0], l3 = lvec[2][0];

        // 4) Check positive definiteness of L
        const detL = l1 * l3 - l2 * l2;
        if (!(l1 > 0 && detL > 0)) {
          validState = false;
          lastError = "L is not positive definite.";
          return;
        }

        // 5) Cholesky (lower triangular) L = Q * Q^T
        const q11 = Math.sqrt(l1);
        if (!isFinite(q11) || q11 <= 0) {
          validState = false;
          lastError = "Cholesky failed (q11).";
          return;
        }
        const q21 = l2 / q11;
        const tmp = l3 - q21 * q21;
        if (!(tmp > 0)) {
          validState = false;
          lastError = "Cholesky failed (tmp <= 0).";
          return;
        }
        const q22 = Math.sqrt(tmp);
        const Q = [
          [q11, 0],
          [q21, q22]
        ];

        // 6) Final motion and shape matrices
        const S = math.multiply(math.matrix(U2), math.matrix(Q)).toArray(); // 3x2
        const invQ = math.inv(math.matrix(Q));
        const P = math.multiply(math.multiply(invQ, math.matrix(D2)), math.transpose(math.matrix(V))).toArray(); // 2x2

        // 7) Extract vectors
        u = normalize(S[0]);
        v = normalize(S[1]);
        w = normalize(S[2]);

        A = [P[0][0], P[1][0]];
        B = [P[0][1], P[1][1]];

        validState = true;
      } catch (err) {
        validState = false;
        lastError = "Computation error: " + (err && err.message ? err.message : String(err));
      }
    }

    // p5.js sketch
    let cnv;
    function setup() {
      const container = document.getElementById('canvas-container');
      const rect = container.getBoundingClientRect();
      cnv = createCanvas(rect.width, rect.height);
      cnv.parent('canvas-container');
      noLoop();

      // Attach sliders
      attachSlider('slider-au', 'value-au', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-bu', 'value-bu', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-av', 'value-av', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-bv', 'value-bv', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-aw', 'value-aw', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-bw', 'value-bw', () => { computeFromSliders(); redraw(); });
      attachSlider('slider-x-scale', 'value-x-scale', (val) => { xScale = val; redraw(); });
      attachSlider('slider-y-scale', 'value-y-scale', (val) => { yScale = val; redraw(); });

      // Initialize values and draw
      xScale = getSliderNumber('slider-x-scale');
      yScale = getSliderNumber('slider-y-scale');
      computeFromSliders();
      redraw();
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      const rect = container.getBoundingClientRect();
      resizeCanvas(rect.width, rect.height);
      redraw();
    }

    function drawAxis(dir, strokeCol, label) {
      const n = normalize(dir);
      const L = 6; // half-length in world units
      const p1 = [-n[0] * L, -n[1] * L];
      const p2 = [ n[0] * L,  n[1] * L];

      stroke(strokeCol);
      strokeWeight(3 / ((xScale + yScale) / 2) * 80); // keep line visually similar across scales
      line(p1[0], p1[1], p2[0], p2[1]);
      noStroke();
      fill(strokeCol);
      textSize(14 / ((xScale + yScale) / 2) * 80);
      text(label, p2[0] + 0.1, p2[1] + 0.1);
    }

    function drawPoint(P, col, label) {
      noStroke();
      fill(col);
      const r = 0.12; // world radius
      ellipse(P[0], P[1], r, r);
      fill(col);
      textSize(14 / ((xScale + yScale) / 2) * 80);
      text(label, P[0] + 0.15, P[1] + 0.15);
    }

    function drawProjectionFromPoint(P, n, dotVal) {
      const nUnit = normalize(n);
      const proj = scaleVec(dotVal, nUnit);
      // connector
      stroke('#808080');
      strokeWeight(1 / ((xScale + yScale) / 2) * 80);
      line(P[0], P[1], proj[0], proj[1]);
      // green small marker
      noStroke();
      fill('#00FF00');
      const r = 0.08;
      ellipse(proj[0], proj[1], r, r);
    }

    function draw() {
      // Background
      background('#F0F0F0');

      // Transform to Cartesian coordinates and apply anisotropic scaling
      push();
      translate(width / 2, height / 2);
      scale(xScale, -yScale);

      if (!validState) {
        pop();
        // draw message in pixel (screen) space
        fill('#cc0000');
        noStroke();
        textSize(14);
        text("Invalid configuration: " + lastError, 16, 24);
        return;
      }

      // Axes
      drawAxis(u, '#0000CD', 'u');
      drawAxis(v, '#6495ED', 'v');
      drawAxis(w, '#0000CD', 'w');

      // Projections for A and B using slider-provided dot products
      const au = getSliderNumber('slider-au');
      const av = getSliderNumber('slider-av');
      const aw = getSliderNumber('slider-aw');

      const bu = getSliderNumber('slider-bu');
      const bv = getSliderNumber('slider-bv');
      const bw = getSliderNumber('slider-bw');

      // Origin
      drawPoint([0,0], '#FF0000', 'O');

      // Connectors and projection markers
      stroke('#808080');
      drawProjectionFromPoint(A, u, au);
      drawProjectionFromPoint(A, v, av);
      drawProjectionFromPoint(A, w, aw);

      drawProjectionFromPoint(B, u, bu);
      drawProjectionFromPoint(B, v, bv);
      drawProjectionFromPoint(B, w, bw);

      // Main points
      drawPoint(A, '#FF0000', 'A');
      drawPoint(B, '#FFA500', 'B');

      pop();
    }
  </script>
</body>
</html>
