      <div id="controls-3d">
        <h3>3D</h3>
        <div id="buttons-3d-container">
          <button id="btn-3A1p">²A₁(+)</button>
          <button id="btn-3A1m">²A₁(-)</button>
          <button id="btn-3A2">²A₂</button>
          <button id="btn-3A3">²A₃</button>
          <button id="btn-3B2p">²B₂(+)</button>
          <button id="btn-3B2m">²B₂(-)</button>
          <button id="btn-3B3">²B₃</button>
          <button id="btn-3C3p">²C₃⁺</button>
          <button id="btn-3C3m">²C₃⁻</button>
        </div>
      </div>

      <div id="time-control-container">
        <label for="time-pad">time</label>
        <div id="time-pad">
          <div id="time-handle"></div>
        </div>
        <div id="time-readout">t1: 0.00, t2: 0.00</div>
      </div>
    </div>

    <div id="visualization-container"></div>
  </div>

  <!-- three.js and controls -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
  <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/geometries/ParametricGeometry.js"></script>

  <script>
  (function() {
    // Global state
    let currentType = '2A2';
    let currentDimension = '2D';
    let t1 = 0, t2 = 0; // in [-1, 1]
    const readout = document.getElementById('time-readout');

    // THREE.js essentials
    const container = document.getElementById('visualization-container');
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
    container.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    // Lights (remain in the scene)
    const amb = new THREE.AmbientLight(0xffffff, 0.55);
    const dir = new THREE.DirectionalLight(0xffffff, 0.9);
    dir.position.set(5, 8, 6);
    scene.add(amb, dir);

    let camera = null;
    let controls = null;
    let currentGroup = null;

    function setRendererSize() {
      const w = container.clientWidth;
      const h = container.clientHeight;
      renderer.setSize(w, h, false);
      if (camera) {
        if (camera.isPerspectiveCamera) {
          camera.aspect = w / h;
        } else if (camera.isOrthographicCamera) {
          const aspect = w / h;
          const size = 6; // world units half-height
          camera.left   = -size * aspect;
          camera.right  =  size * aspect;
          camera.top    =  size;
          camera.bottom = -size;
        }
        camera.updateProjectionMatrix();
      }
    }

    function setDimension(dim) {
      currentDimension = dim;

      // Dispose previous controls
      if (controls) {
        controls.dispose();
        controls = null;
      }

      // Create camera appropriate for the dimension
      const w = container.clientWidth || 800;
      const h = container.clientHeight || 600;
      if (dim === '2D') {
        const aspect = (w || 1) / (h || 1);
        const size = 6;
        camera = new THREE.OrthographicCamera(-size * aspect, size * aspect, size, -size, -100, 100);
        camera.position.set(0, 0, 10);
        camera.up.set(0, 1, 0);
        camera.lookAt(new THREE.Vector3(0, 0, 0));
      } else {
        camera = new THREE.PerspectiveCamera(45, (w || 1) / (h || 1), 0.1, 1000);
        camera.position.set(8, 6, 10);
        camera.lookAt(0, 0, 0);
        controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.08;
      }
      setRendererSize();
    }

    function clearCurrent() {
      if (currentGroup) {
        scene.remove(currentGroup);
        disposeObject3D(currentGroup);
        currentGroup = null;
      }
    }

    function disposeObject3D(obj) {
      obj.traverse(child => {
        if (child.geometry) {
          child.geometry.dispose();
        }
        if (child.material) {
          if (Array.isArray(child.material)) {
            child.material.forEach(m => m.dispose());
          } else {
            child.material.dispose();
          }
        }
      });
    }

    // 2D parametric curves
    function build2DCurve(type) {
      let Umin = -2, Umax = 2, steps = 250;
      if (type === '2A2') { Umin = -1.5; Umax = 1.5; }
      const positions = new Float32Array((steps+1) * 3);

      const T1 = t1 * 1.5;
      const T2 = t2 * 1.5;

      let i = 0;
      for (let s = 0; s <= steps; s++) {
        const U = Umin + (Umax - Umin) * (s / steps);
        let x, y;
        if (type === '2A1') {
          x = U; y = U*U;
        } else if (type === '2A2') {
          x = 2*Math.pow(U,3); y = 3*Math.pow(U,2);
        } else if (type === '2B2') {
          x = U*U; y = Math.pow(U,4);
        } else {
          x = U; y = 0;
        }
        positions[i++] = x + T1;
        positions[i++] = y + T2;
        positions[i++] = 0;
      }

      const geometry = new THREE.BufferGeometry();
      geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
      const material = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
      const line = new THREE.Line(geometry, material);
      return line;
    }

    // 3D parametric surfaces
    function getParametricFunction(type) {
      // Map [0,1] to [a,b]
      const map = (x, a, b) => a + (b - a) * x;
      const T1 = t1 * 1.5;
      const T2 = t2 * 1.5;

      if (type === '3A1p' || type === '3A1m' || type === '3B2p' || type === '3B2m' || type === '3B3' || type === '3C3p' || type === '3C3m') {
        return function(u, v, target) {
          const U = map(u, -2, 2);
          const V = map(v, -2, 2);
          let x, y, z;
          if (type === '3A1p') {
            x = U; y = V; z = 0.3*(U*U + V*V) + T1*U + T2*V;
          } else if (type === '3A1m') {
            x = U; y = V; z = -0.3*(U*U + V*V) + T1*U + T2*V;
          } else if (type === '3B2p') {
            x = U; y = V; z = Math.pow((U*U + T1), 2) + V*V + T2;
          } else if (type === '3B2m') {
            x = U; y = V; z = -(Math.pow(U,4) + T1*2*U*U + V*V - T2);
          } else if (type === '3B3') {
            x = U; y = V; z = Math.pow(U,3) + T1*U + V*V + T2;
          } else if (type === '3C3p') {
            x = U; y = V; z = Math.pow(U,3) - 3*U*V*V + T1*2*(U*U + V*V) + T2*4*U;
          } else { // 3C3m
            x = U; y = V; z = Math.pow(U,3) + Math.pow(V,3) + T1*4*U + T2*4*V;
          }
          target.set(x, y, z);
        };
      }

      if (type === '3A2' || type === '3A3') {
        return function(u, v, target) {
          const U = (type === '3A2' || type === '3A3') ? (type === '3A2' ? (u * 3 - 1.5) : (u * 3 - 1.5)) : (u * 4 - 2);
          const V = (type === '3A2') ? (v * 4 - 2) : (v * 8 - 4);
          let x, y, z;
          if (type === '3A2') {
            x = 2*Math.pow(U,3) + T1;
            y = 3*Math.pow(U,2) + T2;
            z = V;
          } else { // 3A3 (swallowtail)
            x = 3*Math.pow(U,4) + (U*U)*V;
            y = -4*Math.pow(U,3) - 2*U*V + T1*2;
            z = V + T2*2;
          }
          target.set(x, y, z);
        };
      }

      // Fallback
      return function(u, v, target) {
        const U = map(u, -2, 2);
        const V = map(v, -2, 2);
        target.set(U, V, 0);
      };
    }

    function build3DSurface(type) {
      const func = getParametricFunction(type);
      const slices = 60, stacks = 60;
      const geometry = new THREE.ParametricGeometry(func, slices, stacks);
      geometry.computeVertexNormals();

      const mat = new THREE.MeshPhongMaterial({
        color: 0xffc34d,
        shininess: 60,
        specular: 0x333333,
        side: THREE.DoubleSide,
        flatShading: false
      });
      const mesh = new THREE.Mesh(geometry, mat);

      // Wireframe overlay (helps perception; required for A3, ok for others as well)
      const wire = new THREE.WireframeGeometry(geometry);
      const line = new THREE.LineSegments(wire, new THREE.LineBasicMaterial({
        color: 0x000000, linewidth: 1, transparent: true, opacity: 0.35
      }));

      const group = new THREE.Group();
      group.add(mesh);
      group.add(line);
      return group;
    }

    function updateVisualization() {
      clearCurrent();

      if (currentDimension === '2D') {
        currentGroup = new THREE.Group();
        const line = build2DCurve(currentType);
        currentGroup.add(line);
        scene.add(currentGroup);

        // 2D camera framing: center near latest curve's bounds
        if (line.geometry.boundingBox === null) line.geometry.computeBoundingBox();
        const bb = line.geometry.boundingBox;
        const cx = (bb.max.x + bb.min.x) * 0.5;
        const cy = (bb.max.y + bb.min.y) * 0.5;
        const extentX = (bb.max.x - bb.min.x) * 0.6 + 3; // some padding
        const extentY = (bb.max.y - bb.min.y) * 0.6 + 3;
        if (camera && camera.isOrthographicCamera) {
          const w = container.clientWidth;
          const h = container.clientHeight;
          const aspect = w / h;
          const size = Math.max(extentY, extentX / aspect);
          camera.left = -size * aspect + cx;
          camera.right = size * aspect + cx;
          camera.top = size + cy;
          camera.bottom = -size + cy;
          camera.near = -100;
          camera.far = 100;
          camera.updateProjectionMatrix();
          camera.position.set(0, 0, 10);
          camera.lookAt(new THREE.Vector3(cx, cy, 0));
        }
      } else {
        currentGroup = build3DSurface(currentType);
        scene.add(currentGroup);

        // Fit camera roughly to object
        if (currentGroup.children[0] && currentGroup.children[0].geometry.boundingSphere === null) {
          currentGroup.children[0].geometry.computeBoundingSphere();
        }
        const bs = currentGroup.children[0].geometry.boundingSphere;
        const R = (bs && bs.radius) ? bs.radius : 4;
        const center = (bs && bs.center) ? bs.center : new THREE.Vector3();
        if (controls) {
          controls.target.copy(center);
          const distance = Math.max(R * 2.5, 6);
          const dirVec = new THREE.Vector3(1.2, 1.0, 1.5).normalize();
          const pos = center.clone().addScaledVector(dirVec, distance);
          camera.position.copy(pos);
          camera.near = 0.1;
          camera.far = Math.max(1000, distance * 10);
          camera.updateProjectionMatrix();
          controls.update();
        }
      }
      renderOnce();
    }

    function renderOnce() {
      renderer.render(scene, camera);
    }

    // Animation loop for interactive 3D controls (and safe to run also for 2D)
    function animate() {
      requestAnimationFrame(animate);
      if (controls) controls.update();
      renderer.render(scene, camera);
    }

    // Button wiring
    const buttonMap = {
      'btn-2A1': { type: '2A1', dim: '2D' },
      'btn-2A2': { type: '2A2', dim: '2D' },
      'btn-2B2': { type: '2B2', dim: '2D' },

      'btn-3A1p': { type: '3A1p', dim: '3D' },
      'btn-3A1m': { type: '3A1m', dim: '3D' },
      'btn-3A2':  { type: '3A2', dim: '3D' },
      'btn-3A3':  { type: '3A3', dim: '3D' },
      'btn-3B2p': { type: '3B2p', dim: '3D' },
      'btn-3B2m': { type: '3B2m', dim: '3D' },
      'btn-3B3':  { type: '3B3', dim: '3D' },
      'btn-3C3p': { type: '3C3p', dim: '3D' },
      'btn-3C3m': { type: '3C3m', dim: '3D' }
    };

    function setupButtons() {
      const allButtons = [
        ...document.querySelectorAll('#buttons-2d-container button'),
        ...document.querySelectorAll('#buttons-3d-container button')
      ];
      allButtons.forEach(btn => {
        btn.addEventListener('click', () => {
          const info = buttonMap[btn.id];
          if (!info) return;
          // Update active style
          allButtons.forEach(b => b.classList.remove('active'));
          btn.classList.add('active');

          const dimChanged = (currentDimension !== info.dim);
          currentType = info.type;

          if (dimChanged) setDimension(info.dim);

          updateVisualization();
        });
      });
    }

    // Time pad interactions
    function setupTimePad() {
      const pad = document.getElementById('time-pad');
      const handle = document.getElementById('time-handle');

      let dragging = false;

      function padRect() {
        return pad.getBoundingClientRect();
      }

      function setHandleFromT() {
        const rect = padRect();
        const cx = rect.width / 2;
        const cy = rect.height / 2;
        const x = cx + t1 * cx;
        const y = cy - t2 * cy; // invert Y (top positive)
        handle.style.left = `${x}px`;
        handle.style.top = `${y}px`;
        readout.textContent = `t1: ${t1.toFixed(2)}, t2: ${t2.toFixed(2)}`;
      }

      function updateTFromEvent(e) {
        const rect = padRect();
        const px = (e.clientX !== undefined) ? e.clientX : (e.touches ? e.touches[0].clientX : 0);
        const py = (e.clientY !== undefined) ? e.clientY : (e.touches ? e.touches[0].clientY : 0);

        const x = Math.max(0, Math.min(px - rect.left, rect.width));
        const y = Math.max(0, Math.min(py - rect.top, rect.height));

        const cx = rect.width / 2;
        const cy = rect.height / 2;

        let nx = (x - cx) / cx;
        let ny = (cy - y) / cy; // invert Y

        nx = Math.max(-1, Math.min(1, nx));
        ny = Math.max(-1, Math.min(1, ny));

        t1 = nx;
        t2 = ny;

        setHandleFromT();
        updateVisualization(); // live update
      }

      handle.addEventListener('mousedown', (e) => {
        dragging = true;
        handle.style.cursor = 'grabbing';
        e.preventDefault();
      });
      window.addEventListener('mousemove', (e) => {
        if (!dragging) return;
        updateTFromEvent(e);
      });
      window.addEventListener('mouseup', () => {
        if (dragging) {
          dragging = false;
          handle.style.cursor = 'grab';
        }
      });

      // Click or drag anywhere on the pad to jump
      pad.addEventListener('mousedown', (e) => {
        dragging = true;
        updateTFromEvent(e);
      });

      // Touch support
      handle.addEventListener('touchstart', (e) => { dragging = true; e.preventDefault(); }, { passive: false });
      pad.addEventListener('touchstart', (e) => { dragging = true; updateTFromEvent(e); e.preventDefault(); }, { passive: false });
      window.addEventListener('touchmove', (e) => { if (dragging) { updateTFromEvent(e); e.preventDefault(); }}, { passive: false });
      window.addEventListener('touchend', () => { dragging = false; }, { passive: true });

      // Initialize position
      setHandleFromT();
    }

    // Initialize
    function init() {
      // Ensure container has some size before setting cameras
      setRendererSize();
      setupButtons();
      setupTimePad();
      setDimension('2D'); // default
      updateVisualization();
      animate();
    }

    window.addEventListener('resize', () => {
      setRendererSize();
      renderOnce();
    });

    // Kick off
    init();
  })();
  </script>
</body>
</html>
