    btnUp = document.getElementById('btn-feed-up');
    btnDown = document.getElementById('btn-feed-down');

    sliderFR = document.getElementById('slider-feed-rate');
    inputFR  = document.getElementById('input-feed-rate');
    sliderUA = document.getElementById('slider-ua');
    inputUA  = document.getElementById('input-ua');
    sliderDH1 = document.getElementById('slider-dh1');
    inputDH1  = document.getElementById('input-dh1');
    sliderDH2 = document.getElementById('slider-dh2');
    inputDH2  = document.getElementById('input-dh2');
    legendDiv = document.getElementById('legend-overlay');

    // Dropdown
    selPlot.value = state.selectPlot;
    selPlot.addEventListener('change', () => {
      state.selectPlot = selPlot.value;
      updateLegend();
      runAndRedraw();
    });

    // Feed cycler
    const updateFeedLabel = () => { lblFeed.textContent = feedOptions[feedIndex]; state.feedConfig = feedOptions[feedIndex]; updateLegend(); runAndRedraw(); };
    btnUp.addEventListener('click', () => { feedIndex = (feedIndex + 1) % feedOptions.length; updateFeedLabel(); });
    btnDown.addEventListener('click', () => { feedIndex = (feedIndex - 1 + feedOptions.length) % feedOptions.length; updateFeedLabel(); });
    updateFeedLabel();

    // Sync helpers
    function bindSliderNumber(slider, number, key, parser = parseFloat){
      const syncFromSlider = () => {
        number.value = slider.value;
        state[key] = parser(slider.value);
        runAndRedraw();
      };
      const syncFromNumber = () => {
        const min = parseFloat(number.min), max = parseFloat(number.max);
        let v = parser(number.value);
        if (isNaN(v)) v = parser(slider.value);
        v = clamp(v, min, max);
        number.value = v;
        slider.value = v;
        state[key] = v;
        runAndRedraw();
      };
      slider.addEventListener('input', syncFromSlider);
      number.addEventListener('input', syncFromNumber);
    }

    bindSliderNumber(sliderFR, inputFR, 'feedRate', parseFloat);
    bindSliderNumber(sliderUA, inputUA, 'UA', parseFloat);
    bindSliderNumber(sliderDH1, inputDH1, 'dH1', parseFloat);
    bindSliderNumber(sliderDH2, inputDH2, 'dH2', parseFloat);

    MathJax.typesetPromise(); // typeset labels in control panel
  }

  function updateLegend(){
    const lineSVG = (color, dashed=false) => {
      if (dashed){
        return `<svg class="svg-swatch" viewBox="0 0 60 10" xmlns="http://www.w3.org/2000/svg">
                  <line x1="0" y1="5" x2="60" y2="5" stroke="${color}" stroke-width="3" stroke-dasharray="8,6" stroke-linecap="round"/>
                </svg>`;
      } else {
        return `<svg class="svg-swatch" viewBox="0 0 60 10" xmlns="http://www.w3.org/2000/svg">
                  <line x1="0" y1="5" x2="60" y2="5" stroke="${color}" stroke-width="3" stroke-linecap="round"/>
                </svg>`;
      }
    };

    if (state.selectPlot === 'selectivity'){
      legendDiv.innerHTML = `
        <div class="legend-item">
          ${lineSVG('#800080', false)}
          <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
        </div>`;
    } else {
      legendDiv.innerHTML = `
        <div class="legend-item">${lineSVG('#2ca02c', true)}<span>\\(N_a\\)</span></div>
        <div class="legend-item">${lineSVG('#000000', true)}<span>\\(N_b\\)</span></div>
        <div class="legend-item">${lineSVG('#1f77b4', false)}<span>\\(N_d\\)</span></div>
        <div class="legend-item">${lineSVG('#d62728', false)}<span>\\(N_u\\)</span></div>
      `;
    }
    MathJax.typesetPromise([legendDiv]);
  }

  // ODE system
  function rates(T, Na, Nb){
    const k1 = k1_0 * Math.exp(-E1 / (R*T));
    const k2 = k2_0 * Math.exp(-E2 / (R*T));
    const Ca = Na / V;
    const Cb = Nb / V;
    const r1 = k1 * Ca * Cb * Cb; // mol/L/h
    const r2 = k2 * Ca * Cb;
    return {k1, k2, r1, r2};
  }

  function derivs(t, Y, params){
    // Y=[Na, Nb, Nd, Nu, T]
    let [Na, Nb, Nd, Nu, T] = Y;
    const { UA, dH1, dH2, fr, tf, mode, feedTo } = params;

    const { r1, r2 } = rates(T, Na, Nb);

    let dNa, dNb;
    if (mode === 'batch'){
      dNa = -(r1 + r2) * V;
      dNb = -(r1 + r2) * V;
    } else { // semibatch
      if (feedTo === 'AtoB'){ // feed A into B
        const FA0 = (t < tf) ? fr : 0;
        dNa = FA0 - (r1 + r2) * V;
        dNb = -(r1 + r2) * V;
      } else { // feed B into A
        const FB0 = (t < tf) ? fr : 0;
        dNa = -(r1 + r2) * V;
        dNb = FB0 - (r1 + r2) * V;
      }
    }
    const dNd = r1 * V;
    const dNu = r2 * V;
    const dT = ( UA * (T_coolant - T) + (-dH1 * r1 - dH2 * r2) * V ) / Cp_total;

    return [dNa, dNb, dNd, dNu, dT];
  }

  function rk4Step(t, Y, h, deriv, params){
    const k1 = deriv(t, Y, params);
    const Y2 = Y.map((v,i)=> v + 0.5*h*k1[i]);
    const k2 = deriv(t + 0.5*h, Y2, params);
    const Y3 = Y.map((v,i)=> v + 0.5*h*k2[i]);
    const k3 = deriv(t + 0.5*h, Y3, params);
    const Y4 = Y.map((v,i)=> v + h*k3[i]);
    const k4 = deriv(t + h, Y4, params);
    return Y.map((v,i)=> v + h*(k1[i] + 2*k2[i] + 2*k3[i] + k4[i])/6.0);
  }

  function simulate(mode, feedConfig, fr, UA, dH1, dH2){
    // returns arrays of t, Na,Nb,Nd,Nu,T
    let Y;
    let params = { UA, dH1, dH2, fr, tf: 100.0/fr, mode, feedTo: (feedConfig === 'feed A to B') ? 'AtoB' : 'BtoA' };

    if (mode === 'batch'){
      Y = [100, 100, 0, 0, T_initial];
    } else { // semibatch
      if (feedConfig === 'feed A to B'){
        Y = [0, 100, 0, 0, T_initial];
      } else {
        Y = [100, 0, 0, 0, T_initial];
      }
    }

    const nSteps = Math.round((tEnd - tStart)/dt);
    const tArr = new Array(nSteps+1);
    const NaArr = new Array(nSteps+1);
    const NbArr = new Array(nSteps+1);
    const NdArr = new Array(nSteps+1);
    const NuArr = new Array(nSteps+1);
    const TArr  = new Array(nSteps+1);

    let t = tStart;
    for (let i=0; i<=nSteps; i++){
      tArr[i] = t;
      NaArr[i] = Y[0]; NbArr[i] = Y[1]; NdArr[i] = Y[2]; NuArr[i] = Y[3]; TArr[i] = Y[4];
      if (i < nSteps){
        Y = rk4Step(t, Y, dt, derivs, params);
        // prevent tiny negative drifts
        Y[0] = Math.max(Y[0], 0);
        Y[1] = Math.max(Y[1], 0);
        t += dt;
      }
    }
    return { t: tArr, Na: NaArr, Nb: NbArr, Nd: NdArr, Nu: NuArr, T: TArr };
  }

  function simulateSelectivity(feedConfig, fr, UA, dH1, dH2){
    const batch = simulate('batch', feedConfig, fr, UA, dH1, dH2);
    const semi  = simulate('semibatch', feedConfig, fr, UA, dH1, dH2);

    const t = batch.t.slice();
    const Sratio = new Array(t.length);
    let yMin = Infinity, yMax = -Infinity;

    for (let i=1; i<t.length; i++){ // start from dt to avoid 0/0 initially
      const Sb = (batch.Nd[i] > 0 && batch.Nu[i] > 0) ? (batch.Nd[i] / batch.Nu[i]) : NaN;
      const Ss = (semi.Nd[i]  > 0 && semi.Nu[i]  > 0) ? (semi.Nd[i]  / semi.Nu[i])  : NaN;
      const Sr = (isFinite(Sb) && isFinite(Ss) && Sb !== 0) ? (Ss / Sb) : NaN;
      Sratio[i] = Sr;
      if (isFinite(Sr)){ yMin = Math.min(yMin, Sr); yMax = Math.max(yMax, Sr); }
    }
    // Handle t=0
    Sratio[0] = NaN;
    if (!isFinite(yMin) || !isFinite(yMax) || yMax === yMin){
      yMin = 0.9; yMax = 1.1;
    }
    const pad = 0.1*(yMax - yMin);
    yMin -= pad; yMax += pad;

    return { t, Sratio, yMin, yMax };
  }

  function runSimulation(){
    const { selectPlot, feedConfig, feedRate, UA, dH1, dH2 } = state;

    if (selectPlot === 'selectivity'){
      const res = simulateSelectivity(feedConfig, feedRate, UA, dH1, dH2);
      data.t = res.t;
      data.Sratio = res.Sratio;
      data.yMin = res.yMin;
      data.yMax = res.yMax;
      data.yLabel = "selectivity ratio";
      data.Na = data.Nb = data.Nd = data.Nu = [];
    } else {
      const res = simulate(selectPlot, feedConfig, feedRate, UA, dH1, dH2);
      data.t = res.t;
      data.Na = res.Na; data.Nb = res.Nb; data.Nd = res.Nd; data.Nu = res.Nu; data.T = res.T;
      data.yMin = 0; data.yMax = 100;
      data.yLabel = "moles (mol)";
      data.Sratio = [];
    }
  }

  // p5 sketch
  function initApp(){
    initControls();
    updateLegend();
    runSimulation();

    const sketch = (p)=>{
      let cw = Math.min(920, Math.floor(window.innerWidth*0.95));
      let ch = Math.round(cw * 0.55);
      const margins = { left: 70, right: 20, top: 60, bottom: 60 }; // extra top for legend space

      p.setup = function(){
        const cnv = p.createCanvas(cw, ch);
        cnv.id('plot-canvas');
        p.noLoop();
        p.pixelDensity(1);
        p.strokeCap(p.ROUND);
      };

      function innerWidth(){ return p.width - margins.left - margins.right; }
      function innerHeight(){ return p.height - margins.top - margins.bottom; }
      function xMap(t){ return margins.left + (t - tStart) / (tEnd - tStart) * innerWidth(); }
      function yMap(y, yMin, yMax){
        const h = innerHeight();
        return margins.top + (1 - (y - yMin)/(yMax - yMin)) * h;
      }

      function drawGridAxes(){
        p.clear();
        p.background('#F0F0F0');

        // Plot area border
        p.noFill();
        p.stroke('#bbbbbb');
        p.rect(margins.left, margins.top, innerWidth(), innerHeight());

        // Axes
        p.stroke('#666666');
        p.strokeWeight(1);

        // Ticks and grid - X
        p.textAlign(p.CENTER, p.TOP);
        p.textSize(12);
        p.fill('#333');
        const xTickStep = 0.5;
        for (let x = 0; x <= (tEnd + 1e-9); x += xTickStep){
          const px = xMap(x);
          p.stroke('#e0e0e0');
          p.line(px, margins.top, px, margins.top + innerHeight());
          p.stroke('#666');
          p.line(px, margins.top + innerHeight(), px, margins.top + innerHeight() + 5);
          p.noStroke();
          p.text(x.toFixed(1), px, margins.top + innerHeight() + 8);
        }
        p.text('time (h)', margins.left + innerWidth()/2, p.height - 22);

        // Y axis ticks depend on plot
        let yMin = data.yMin, yMax = data.yMax;
        if (state.selectPlot === 'selectivity'){
          // choose ~5 ticks
          const N=5;
          const range = yMax - yMin;
          const rawStep = range/N;
          const mag = Math.pow(10, Math.floor(Math.log10(rawStep)));
          const norm = rawStep/mag;
          let nice;
          if (norm < 1.5) nice = 1;
          else if (norm < 3) nice = 2;
          else if (norm < 7) nice = 5;
          else nice = 10;
          const step = nice*mag;
          let start = Math.ceil(yMin/step)*step;
          p.textAlign(p.RIGHT, p.CENTER);
          for (let y = start; y <= yMax + 1e-9; y += step){
            const py = yMap(y, yMin, yMax);
            p.stroke('#e0e0e0'); p.line(margins.left, py, margins.left + innerWidth(), py);
            p.stroke('#666'); p.line(margins.left - 5, py, margins.left, py);
            p.noStroke(); p.fill('#333'); p.text(y.toFixed(2), margins.left - 8, py);
          }
        } else {
          p.textAlign(p.RIGHT, p.CENTER);
          for (let y = 0; y <= 100; y += 20){
            const py = yMap(y, 0, 100);
            p.stroke('#e0e0e0'); p.line(margins.left, py, margins.left + innerWidth(), py);
            p.stroke('#666'); p.line(margins.left - 5, py, margins.left, py);
            p.noStroke(); p.fill('#333'); p.text(y.toFixed(0), margins.left - 8, py);
          }
        }
        p.push();
        p.translate(18, margins.top + innerHeight()/2);
        p.rotate(-Math.PI/2);
        p.textAlign(p.CENTER, p.BOTTOM);
        p.fill('#333'); p.text(data.yLabel, 0, 0);
        p.pop();
      }

      function drawSeries(){
        const tArr = data.t;
        if (!tArr || tArr.length === 0) return;

        if (state.selectPlot === 'selectivity'){
          const yMin = data.yMin, yMax = data.yMax;
          const col = p.color('#800080');
          p.stroke(col); p.strokeWeight(2); p.noFill();

          p.beginShape();
          for (let i=0; i<tArr.length; i++){
            const y = data.Sratio[i];
            if (!isFinite(y)) continue;
            p.vertex(xMap(tArr[i]), yMap(y, yMin, yMax));
          }
          p.endShape();
          return;
        }

        // Helpers
        function drawPolyline(xs, ys, color, dashed=false){
          p.strokeWeight(2);
          p.noFill();
          p.stroke(color);

          if (dashed){
            // use canvas dash for efficiency but wrap as a helper
            const ctx = p.drawingContext;
            ctx.save();
            ctx.setLineDash([8, 6]);
            p.beginShape();
            for (let i=0; i<xs.length; i++){
              p.vertex(xs[i], ys[i]);
            }
            p.endShape();
            ctx.restore();
          } else {
            p.beginShape();
            for (let i=0; i<xs.length; i++){
              p.vertex(xs[i], ys[i]);
            }
            p.endShape();
          }
        }

        // Pre-map
        const xs = new Array(tArr.length);
        const ya = new Array(tArr.length);
        const yb = new Array(tArr.length);
        const yd = new Array(tArr.length);
        const yu = new Array(tArr.length);
        for (let i=0; i<tArr.length; i++){
          xs[i] = xMap(tArr[i]);
          ya[i] = yMap(data.Na[i], 0, 100);
          yb[i] = yMap(data.Nb[i], 0, 100);
          yd[i] = yMap(data.Nd[i], 0, 100);
          yu[i] = yMap(data.Nu[i], 0, 100);
        }

        drawPolyline(xs, ya, '#2ca02c', true);
        drawPolyline(xs, yb, '#000000', true);
        drawPolyline(xs, yd, '#1f77b4', false);
        drawPolyline(xs, yu, '#d62728', false);
      }

      p.draw = function(){
        drawGridAxes();
        drawSeries();
      };

      // resize handler
      p.windowResized = function(){
        const newW = Math.min(920, Math.floor(window.innerWidth*0.95));
        const newH = Math.round(newW * 0.55);
        p.resizeCanvas(newW, newH);
        p.redraw();
      };
    };

    const p5instance = new p5(sketch, document.getElementById('plot-area'));

    function redraw(){
      p5instance.redraw();
    }

    // expose functions to outer scope of initApp
    window.runAndRedraw = function(){
      runSimulation();
      updateLegend();
      redraw();
    };

    // Initial render
    runAndRedraw();
  }

})();
</script>
</body>
</html>
