<script>
(function(){
  // State and configuration
  const W = 500, H = 500;
  const wallThickness = 20; // geometric thickness (collision); drawing uses strokeWeight per spec
  const drawStrokeWeight = 8;
  const nodeRadius = 3; // visual
  const startDefault = { x: 50, y: 250 };
  const goalDefault = { x: 450, y: 450 };
  const stepSize = 12; // for RRT / RRT*
  const rewireRadius = 35;

  const state = {
    algorithm: 'rt', // 'rt' | 'rrt' | 'rrt*'
    obstacleType: 'narrow-passage',
    nodes: [],
    start: { x: startDefault.x, y: startDefault.y },
    goal: { x: goalDefault.x, y: goalDefault.y },
    draggingGoal: false,
    goalRadius: 1, // slider units, pixels = * 10
    bias: 0,
    goalReached: false,
    bestGoalNode: null,
    obstacles: [] // rectangles {x,y,w,h} as SOLID filled for collision tests
  };

  // Helpers
  function dist2(a,b){ const dx=a.x-b.x, dy=a.y-b.y; return dx*dx+dy*dy; }
  function distance(a,b){ return Math.sqrt(dist2(a,b)); }
  function clamp(v, min, max){ return Math.max(min, Math.min(max, v)); }
  function pxGoalRadius(){ return state.goalRadius * 10; }

  function pointInRect(x,y,r){
    return (x >= r.x && x <= r.x + r.w && y >= r.y && y <= r.y + r.h);
  }
  function pointInAnyObstacle(x,y){
    for(const r of state.obstacles){
      if(pointInRect(x,y,r)) return true;
    }
    return false;
  }
  function lineLineIntersect(x1,y1,x2,y2, x3,y3,x4,y4) {
    const den = (x1-x2)*(y3-y4) - (y1-y2)*(x3-x4);
    if (den === 0) return false;
    const t = ((x1-x3)*(y3-y4) - (y1-y3)*(x3-x4)) / den;
    const u = -((x1-x2)*(y1-y3) - (y1-y2)*(x1-x3)) / den;
    return (t>=0 && t<=1 && u>=0 && u<=1);
  }
  function lineIntersectsRect(x1,y1,x2,y2, r){
    if (pointInRect(x1,y1,r) || pointInRect(x2,y2,r)) return true;
    const rx=r.x, ry=r.y, rw=r.w, rh=r.h;
    if (lineLineIntersect(x1,y1,x2,y2, rx,ry, rx+rw,ry)) return true; // top
    if (lineLineIntersect(x1,y1,x2,y2, rx,ry+rh, rx+rw,ry+rh)) return true; // bottom
    if (lineLineIntersect(x1,y1,x2,y2, rx,ry, rx,ry+rh)) return true; // left
    if (lineLineIntersect(x1,y1,x2,y2, rx+rw,ry, rx+rw,ry+rh)) return true; // right
    return false;
  }
  function segmentHitsAnyObstacle(a,b){
    for(const r of state.obstacles){
      if (lineIntersectsRect(a.x,a.y,b.x,b.y, r)) return true;
    }
    return false;
  }

  function nearestNode(p){
    let best = state.nodes[0];
    let bestD2 = dist2(best, p);
    for(let i=1;i<state.nodes.length;i++){
      const d2 = dist2(state.nodes[i], p);
      if(d2<bestD2){ best=state.nodes[i]; bestD2=d2; }
    }
    return best;
  }
  function randomPoint(){
    // keep sampling until not in obstacle
    for (let tries=0; tries<1000; tries++){
      const x = Math.random()*W;
      const y = Math.random()*H;
      if (!pointInAnyObstacle(x,y)) return {x,y};
    }
    // fallback somewhere near start
    return {x: state.start.x + Math.random()*30, y: state.start.y + Math.random()*30};
  }

  function buildBorders(rects) {
    // solid rectangles representing the frame
    const t = wallThickness;
    rects.push({x:0, y:0, w:W, h:t});               // top
    rects.push({x:0, y:H-t, w:W, h:t});             // bottom
    rects.push({x:0, y:0, w:t, h:H});               // left
    rects.push({x:W-t, y:0, w:t, h:H});             // right
  }

  function generateObstacles() {
    const rects = [];
    buildBorders(rects); // borders always present to match visuals
    if (state.obstacleType === 'narrow-passage') {
      const t = wallThickness;
      const innerTop = t, innerBottom = H - t;
      const x1 = Math.round(W/3);     // left vertical wall
      const x2 = Math.round(2*W/3);   // right vertical wall

      // Left wall with a gap near the bottom
      rects.push({x:x1 - t/2, y:innerTop, w:t, h:(innerBottom - 140) - innerTop}); // top segment
      rects.push({x:x1 - t/2, y:innerBottom - 40, w:t, h:40}); // short bottom segment

      // Right wall with a gap near the top
      rects.push({x:x2 - t/2, y:innerTop, w:t, h:60}); // short top segment
      rects.push({x:x2 - t/2, y:innerTop + 120, w:t, h:innerBottom - (innerTop + 120)}); // bottom segment
    }
    else if (state.obstacleType === 'random-rects') {
      // Several randomly placed rectangles, avoiding start/goal
      const count = 8;
      let attempts = 0;
      while (attempts < 300 && rects.length < count + 4) { // +4 because borders already added
        attempts++;
        const w = 40 + Math.random()*120;
        const h = 40 + Math.random()*100;
        const x = wallThickness + Math.random()*(W - 2*wallThickness - w);
        const y = wallThickness + Math.random()*(H - 2*wallThickness - h);
        const r = {x,y,w,h};
        // ensure start/goal not blocked
        if (pointInRect(state.start.x, state.start.y, r)) continue;
        if (pointInRect(state.goal.x, state.goal.y, r)) continue;
        // keep minimal spacing from start/goal
        const cx = x + w/2, cy = y + h/2;
        const dStart = Math.hypot(cx - state.start.x, cy - state.start.y);
        const dGoal  = Math.hypot(cx - state.goal.x,  cy - state.goal.y);
        if (dStart < 60 || dGoal < 60) continue;
        rects.push(r);
      }
    }
    state.obstacles = rects;
  }

  function resetNodesAndPath() {
    state.nodes = [];
    const root = {x: state.start.x, y: state.start.y, parent: null, cost: 0, children: []};
    state.nodes.push(root);
    state.goalReached = false;
    state.bestGoalNode = null;
  }

  function fullReset({toDefaults=false, keepGoal=false} = {}) {
    if (toDefaults) {
      // Reset controls to default initial state
      setActiveAlgoButton('rt');
      state.algorithm = 'rt';
      state.obstacleType = 'narrow-passage';
      document.getElementById('select-obstacle').value = 'narrow-passage';
      state.bias = 0;
      document.getElementById('slider-bias').value = '0';
      document.getElementById('bias-value-label').innerText = '0';
      state.goalRadius = 1;
      document.getElementById('slider-radius').value = '1';
      document.getElementById('radius-value-label').innerText = '1';
      if (!keepGoal) {
        state.start = {x: startDefault.x, y: startDefault.y};
        state.goal  = {x: goalDefault.x,  y: goalDefault.y};
      }
    }
    if (!keepGoal && !toDefaults) {
      // keep current goal, but if intended can reset to defaults elsewhere
    }
    generateObstacles();
    resetNodesAndPath();
    updateBiasSliderEnabled();
    updateStatusText();
    if (pInst) pInst.redraw();
  }

  function setActiveAlgoButton(algo){
    const map = {rt: 'btn-rt', rrt:'btn-rrt', 'rrt*':'btn-rrt-star'};
    document.getElementById('btn-rt').classList.remove('active');
    document.getElementById('btn-rrt').classList.remove('active');
    document.getElementById('btn-rrt-star').classList.remove('active');
    document.getElementById(map[algo]).classList.add('active');
  }

  function updateBiasSliderEnabled(){
    const s = document.getElementById('slider-bias');
    const lbl = document.getElementById('bias-value-label');
    if (state.algorithm === 'rt') {
      s.disabled = true;
      s.classList.add('disabled');
      lbl.classList.add('disabled');
    } else {
      s.disabled = false;
      s.classList.remove('disabled');
      lbl.classList.remove('disabled');
    }
  }

  function updateStatusText(){
    const n = state.nodes.length;
    let txt = `${n} node${n===1?'':'s'}, `;
    if (!state.goalReached) txt += 'goal not yet reached';
    else {
      const L = (state.bestGoalNode ? state.bestGoalNode.cost : 0);
      txt += `path length ${L.toFixed(2)}`;
    }
    document.getElementById('status-text').innerText = txt;
  }

  function addNodes(count){
    let added = 0;
    let attempts = 0;
    const maxAttempts = count * 400; // to avoid infinite loop in heavy obstacle cases
    while (added < count && attempts < maxAttempts){
      if (addOneNode()) added++;
      attempts++;
    }
    updateBestGoalAfterChanges();
    updateStatusText();
    if (pInst) pInst.redraw();
  }

  function addOneNode(){
    if (state.algorithm === 'rt') return addNodeRT();
    if (state.algorithm === 'rrt') return addNodeRRT(false);
    if (state.algorithm === 'rrt*') return addNodeRRT(true);
    return false;
  }

  function isValidNewEdge(a,b){
    if (pointInAnyObstacle(b.x,b.y)) return false;
    if (segmentHitsAnyObstacle(a,b)) return false;
    return true;
  }

  function addNodeRT(){
    // Sample a collision-free random point; connect by straight line to nearest if segment is feasible.
    for (let tries=0; tries<200; tries++){
      const p = randomPoint();
      const nn = nearestNode(p);
      if (!isValidNewEdge(nn, p)) continue;
      const node = {x: p.x, y: p.y, parent: nn, cost: nn.cost + distance(nn,p), children: []};
      nn.children.push(node);
      state.nodes.push(node);
      checkGoalReach(node);
      return true;
    }
    return false;
  }

  function stepToward(from, to, step){
    const dx = to.x - from.x;
    const dy = to.y - from.y;
    const d = Math.hypot(dx,dy);
    if (d === 0) return {x: from.x, y: from.y};
    const t = Math.min(step/d, 1);
    return {x: from.x + dx*t, y: from.y + dy*t};
  }

  function addNodeRRT(isStar){
    // choose sample
    let sample;
    if (Math.random() < state.bias) {
      sample = {x: state.goal.x, y: state.goal.y};
    } else {
      sample = randomPoint();
    }
    const nn = nearestNode(sample);
    const newPos = stepToward(nn, sample, stepSize);
    if (!isValidNewEdge(nn, newPos)) return false;

    let parent = nn;
    let cost = nn.cost + distance(nn, newPos);
    const node = {x: newPos.x, y: newPos.y, parent: null, cost: cost, children: []};

    if (isStar){
      // find near nodes
      let bestParent = nn;
      let bestCost = cost;
      const nearNodes = getNearNodes(newPos, rewireRadius);
      for (const q of nearNodes){
        const tmpCost = q.cost + distance(q, newPos);
        if (tmpCost < bestCost && isValidNewEdge(q, newPos)){
          bestCost = tmpCost;
          bestParent = q;
        }
      }
      parent = bestParent;
      node.parent = parent;
      node.cost = bestCost;
      parent.children.push(node);
      state.nodes.push(node);

      // Rewire neighbors through this node if it helps
      for (const q of nearNodes){
        const viaCost = node.cost + distance(node, q);
        if (viaCost + 1e-9 < q.cost && isValidNewEdge(node, q)){
          // rewire q to node
          if (q.parent){
            const idx = q.parent.children.indexOf(q);
            if (idx>=0) q.parent.children.splice(idx,1);
          }
          q.parent = node;
          node.children.push(q);
          const delta = viaCost - q.cost;
          propagateCost(q, delta);
        }
      }
    } else {
      node.parent = parent;
      parent.children.push(node);
      state.nodes.push(node);
    }
    checkGoalReach(node);
    return true;
  }

  function getNearNodes(pos, radius){
    const rr2 = radius*radius;
    const res = [];
    for (const n of state.nodes){
      if (dist2(n, pos) <= rr2) res.push(n);
    }
    return res;
  }

  function propagateCost(root, delta){
    // Update cost of root and descendants by adding delta
    const stack = [root];
    while (stack.length){
      const n = stack.pop();
      n.cost += delta;
      for (const c of n.children) stack.push(c);
    }
  }

  function checkGoalReach(node){
    const d = Math.hypot(node.x - state.goal.x, node.y - state.goal.y);
    if (d <= pxGoalRadius()){
      if (!state.goalReached){
        state.goalReached = true;
        state.bestGoalNode = node;
      } else {
        if (state.algorithm === 'rrt*' && node.cost < (state.bestGoalNode ? state.bestGoalNode.cost : Infinity)){
          state.bestGoalNode = node;
        }
      }
    }
  }

  function updateBestGoalAfterChanges(){
    if (!state.goalReached) {
      // if radius change made it reachable now
      let candidate = null;
      for (const n of state.nodes){
        const d = Math.hypot(n.x - state.goal.x, n.y - state.goal.y);
        if (d <= pxGoalRadius()){
          if (!candidate || n.cost < candidate.cost) candidate = n;
        }
      }
      if (candidate){
        state.goalReached = true;
        state.bestGoalNode = candidate;
      }
    } else if (state.algorithm === 'rrt*'){
      // if goal already reached, keep shortest
      let best = state.bestGoalNode;
      for (const n of state.nodes){
        const d = Math.hypot(n.x - state.goal.x, n.y - state.goal.y);
        if (d <= pxGoalRadius()){
          if (!best || n.cost < best.cost) best = n;
        }
      }
      state.bestGoalNode = best;
    }
  }

  // p5 Sketch
  let pInst = null;
  const sketch = (p)=>{
    p.setup = function(){
      const cnv = p.createCanvas(W, H);
      cnv.parent('canvas-container');
      p.noLoop();
      fullReset({toDefaults:true});
    };

    p.draw = function(){
      p.background(255);

      // Draw obstacles (as thick blue rectangles outlines)
      p.push();
      p.noFill();
      p.stroke('blue');
      p.strokeWeight(drawStrokeWeight);
      for (const r of state.obstacles){
        p.rect(r.x, r.y, r.w, r.h);
      }
      p.pop();

      // Draw goal area (yellow translucent) + crosshair
      p.push();
      p.noStroke();
      p.fill(255, 255, 0, 100);
      p.circle(state.goal.x, state.goal.y, pxGoalRadius()*2);
      p.pop();
      // crosshair
      p.push();
      p.stroke(0);
      p.strokeWeight(1.2);
      const gr = pxGoalRadius();
      p.line(state.goal.x - gr*0.6, state.goal.y, state.goal.x + gr*0.6, state.goal.y);
      p.line(state.goal.x, state.goal.y - gr*0.6, state.goal.x, state.goal.y + gr*0.6);
      p.pop();

      // Draw edges for RRT* (thin)
      if (state.algorithm === 'rrt*'){
        p.push();
        p.stroke(0);
        p.strokeWeight(0.5);
        for (let i=1;i<state.nodes.length;i++){
          const n = state.nodes[i];
          if (n.parent){
            p.line(n.x, n.y, n.parent.x, n.parent.y);
          }
        }
        p.pop();
      }

      // Draw all tree nodes
      p.push();
      p.noStroke();
      p.fill('red');
      for (let i=1;i<state.nodes.length;i++){
        const n = state.nodes[i];
        p.circle(n.x, n.y, nodeRadius*2);
      }
      p.pop();

      // Start node: red if only start exists, otherwise green
      p.push();
      p.noStroke();
      p.fill(state.nodes.length>1 ? 'green' : 'red');
      p.circle(state.start.x, state.start.y, nodeRadius*2+2);
      p.pop();

      // If goal reached, draw final path as thick green line
      if (state.goalReached && state.bestGoalNode){
        p.push();
        p.stroke('green');
        p.strokeWeight(3);
        let n = state.bestGoalNode;
        while (n && n.parent){
          p.line(n.x, n.y, n.parent.x, n.parent.y);
          n = n.parent;
        }
        p.pop();
      }
    };

    // Mouse handling for draggable goal
    p.mousePressed = function(){
      if (p.mouseX < 0 || p.mouseX > W || p.mouseY < 0 || p.mouseY > H) return;
      const d = Math.hypot(p.mouseX - state.goal.x, p.mouseY - state.goal.y);
      if (d <= pxGoalRadius()*0.9) {
        state.draggingGoal = true;
        // reset simulation due to goal relocation
        resetNodesAndPath();
        updateStatusText();
        p.redraw();
      }
    };
    p.mouseDragged = function(){
      if (!state.draggingGoal) return;
      // clamp goal inside canvas and not inside obstacles
      state.goal.x = clamp(p.mouseX, wallThickness+2, W - wallThickness - 2);
      state.goal.y = clamp(p.mouseY, wallThickness+2, H - wallThickness - 2);
      updateStatusText();
      p.redraw();
    };
    p.mouseReleased = function(){ state.draggingGoal = false; };
  };

  pInst = new p5(sketch);

  // UI wiring
  function setAlgorithm(algo){
    state.algorithm = algo;
    setActiveAlgoButton(algo);
    resetNodesAndPath();
    updateBiasSliderEnabled();
    updateStatusText();
    if (pInst) pInst.redraw();
  }

  document.getElementById('btn-rt').addEventListener('click', ()=> setAlgorithm('rt'));
  document.getElementById('btn-rrt').addEventListener('click', ()=> setAlgorithm('rrt'));
  document.getElementById('btn-rrt-star').addEventListener('click', ()=> setAlgorithm('rrt*'));

  document.getElementById('select-obstacle').addEventListener('change', (e)=>{
    state.obstacleType = e.target.value;
    generateObstacles();
    resetNodesAndPath();
    updateStatusText();
    if (pInst) pInst.redraw();
  });

  document.getElementById('btn-add-1').addEventListener('click', ()=> addNodes(1));
  document.getElementById('btn-add-10').addEventListener('click', ()=> addNodes(10));
  document.getElementById('btn-add-100').addEventListener('click', ()=> addNodes(100));
  document.getElementById('btn-add-200').addEventListener('click', ()=> addNodes(200));
  document.getElementById('btn-add-500').addEventListener('click', ()=> addNodes(500));

  document.getElementById('slider-bias').addEventListener('input', (e)=>{
    state.bias = parseFloat(e.target.value);
    document.getElementById('bias-value-label').innerText = e.target.value;
  });

  document.getElementById('slider-radius').addEventListener('input', (e)=>{
    state.goalRadius = parseFloat(e.target.value);
    document.getElementById('radius-value-label').innerText = e.target.value;
    updateBestGoalAfterChanges();
    updateStatusText();
    if (pInst) pInst.redraw();
  });

  document.getElementById('btn-reset').addEventListener('click', ()=>{
    fullReset({toDefaults:true, keepGoal:false});
  });

})();
</script>
</body>
</html>
