    }

    function createPotentialSurface() {
      if (potentialMesh) {
        scene.remove(potentialMesh);
        potentialMesh.geometry.dispose();
        potentialMesh.material.dispose();
      }

      const paramFunc = function(u, v, target) {
        const rho = 0.4 + u * 2.1;
        const theta = v * Math.PI * 2.0;
        const x = rho * Math.cos(theta);
        const y_surf = rho * rho + 1.0 / (rho * rho); // vertical mapped to Y
        const z_surf = -rho * Math.sin(theta);        // orientation to match snapshot
        target.set(x, y_surf, z_surf);
      };

      const geometry = new THREE.ParametricGeometry(paramFunc, 160, 64);
      geometry.computeVertexNormals();

      const material = new THREE.MeshStandardMaterial({
        color: 0xE69943,
        metalness: 0.4,
        roughness: 0.5,
        side: THREE.DoubleSide
      });

      potentialMesh = new THREE.Mesh(geometry, material);
      scene.add(potentialMesh);
    }

    function createCenterCylinder() {
      if (centerCylinder) {
        scene.remove(centerCylinder);
        centerCylinder.geometry.dispose();
        centerCylinder.material.dispose();
      }
      const cylGeo = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
      const cylMat = new THREE.MeshStandardMaterial({
        color: 0xcccccc,
        metalness: 0.1,
        roughness: 0.9
      });
      centerCylinder = new THREE.Mesh(cylGeo, cylMat);
      centerCylinder.position.set(0, 1.25, 0); // raise so base ~ y=0
      scene.add(centerCylinder);
    }

    function onWindowResize() {
      const w = container.clientWidth;
      const h = container.clientHeight;
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
