    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }

    // RK4 integrator for the specified ODE system
    function rk4Step(derivFunc, s, Y, h, params) {
      // Y is array [rho, rhoDot, phi]
      const k1 = derivFunc(s, Y, params);
      const Y2 = [ Y[0] + 0.5*h*k1[0], Y[1] + 0.5*h*k1[1], Y[2] + 0.5*h*k1[2] ];
      const k2 = derivFunc(s + 0.5*h, Y2, params);
      const Y3 = [ Y[0] + 0.5*h*k2[0], Y[1] + 0.5*h*k2[1], Y[2] + 0.5*h*k2[2] ];
      const k3 = derivFunc(s + 0.5*h, Y3, params);
      const Y4 = [ Y[0] + h*k3[0], Y[1] + h*k3[1], Y[2] + h*k3[2] ];
      const k4 = derivFunc(s + h, Y4, params);

      return [
        Y[0] + (h/6)*(k1[0] + 2*k2[0] + 2*k3[0] + k4[0]),
        Y[1] + (h/6)*(k1[1] + 2*k2[1] + 2*k3[1] + k4[1]),
        Y[2] + (h/6)*(k1[2] + 2*k2[2] + 2*k3[2] + k4[2])
      ];
    }

    function derivs(s, Y, params) {
      // Y = [rho, rhoDot, phi]
      const rhoMin = 1e-4; // avoid singularities
      let rho = Math.max(rhoMin, Y[0]);
      const rhoDot = Y[1];
      const phi = Y[2];
      const e0 = params.e0;
      const lambda = params.lambda;

      // dρ/ds
      const d1 = rhoDot;
      // d²ρ/ds² = -(2*ρ - 2/ρ³) - 4*λ²*ρ + 4*e₀/ρ³
      const d2 = -(2*rho - 2/(rho**3)) - 4*(lambda**2)*rho + 4*e0/(rho**3);
      // dφ/ds = 2*(e₀/ρ² - λ)
      const dphi = 2*(e0/(rho**2) - lambda);

      return [d1, d2, dphi];
    }

    function computeIonPath(params) {
      const ds = 0.05;
      const steps = Math.max(1, Math.ceil(params.t / ds));
      let s = 0;

      // Initial conditions: Y0 = [ρ0, 0, 0]
      let Y = [params.rho0, 0.0, 0.0];

      const pts = [];
      for (let i = 0; i <= steps; i++) {
        const rho = Math.max(1e-6, Y[0]);
        const phi = Y[2];

        // Cartesian from cylindrical (x,y) in the trap plane
        const x_i = rho * Math.cos(phi);
        const y_i = rho * Math.sin(phi);

        // Vertical z from potential difference
        const u_rho = rho*rho + 1.0/(rho*rho);
        const sqrt_arg = params.e0 - u_rho;
        const amp = Math.sqrt(Math.max(0, sqrt_arg));
        const z_i = amp * Math.sin(Math.SQRT2 * params.nu * s + params.zeta0);

        // Mapping note:
        // We map (x, y, z_calc) -> Three.js (x, z_calc, -y) to match the specified orientation.
        pts.push(new THREE.Vector3(x_i, z_i, -y_i));

        if (i < steps) {
          Y = rk4Step(derivs, s, Y, ds, { e0: params.e0, lambda: params.lambda });
          s += ds;
        }
      }
      return pts;
    }

    function removeExistingPath() {
      const existing = scene.getObjectByName(ION_PATH_NAME);
      if (existing) {
        scene.remove(existing);
        if (existing.geometry) existing.geometry.dispose();
        if (existing.material) existing.material.dispose();
      }
    }

    function addIonPath(points) {
      if (!points || points.length < 2) return;
      const curve = new THREE.CatmullRomCurve3(points);
      const tubeGeo = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
      const mat = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
      const mesh = new THREE.Mesh(tubeGeo, mat);
      mesh.name = ION_PATH_NAME;
      scene.add(mesh);
    }

    // Read inputs and update visualization
    function updateVisualization() {
      const t = parseFloat(document.getElementById('input-t').value);
      const rho0 = parseFloat(document.getElementById('input-rho0').value);
      const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
      const e0 = parseFloat(document.getElementById('input-e0').value);
      const nu = parseFloat(document.getElementById('input-v').value);      // v -> nu
      const lambda = parseFloat(document.getElementById('input-lambda').value);

      const params = { t, rho0, zeta0, e0, nu, lambda };

      const points = computeIonPath(params);

      removeExistingPath();
      addIonPath(points);
    }

    // Link range and number inputs
    function bindPair(rangeId, numId, isInteger=false) {
      const r = document.getElementById(rangeId);
      const n = document.getElementById(numId);

      const clamp = (val, min, max) => Math.min(Math.max(val, min), max);

      function syncFromRange() {
        const val = isInteger ? parseInt(r.value) : parseFloat(r.value);
        n.value = isInteger ? String(val) : String(val);
        updateVisualization();
      }
      function syncFromNumber() {
        const min = parseFloat(n.min);
        const max = parseFloat(n.max);
        let val = isInteger ? parseInt(n.value) : parseFloat(n.value);
        if (Number.isNaN(val)) val = isInteger ? parseInt(r.value) : parseFloat(r.value);
        val = clamp(val, min, max);
        // Snap to step
        const step = parseFloat(n.step || r.step || "0.01");
        val = isInteger ? Math.round(val/step)*step : Math.round(val/step)*step;
        n.value = isInteger ? String(Math.round(val)) : val.toFixed(Math.max(0, (step.toString().split('.')[1] || '').length));
        r.value = n.value;
        updateVisualization();
      }

      r.addEventListener('input', syncFromRange);
      n.addEventListener('input', syncFromNumber);

      // Initialize sync
      syncFromRange();
    }

    // Initialize after DOM and MathJax
    function initUI() {
      // Ensure MathJax renders labels
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise();
      }

      // Bind pairs
      bindPair('slider-t', 'input-t', true);
      bindPair('slider-rho0', 'input-rho0', false);
      bindPair('slider-zeta0', 'input-zeta0', false);
      bindPair('slider-e0', 'input-e0', false);
      bindPair('slider-v', 'input-v', false);
      bindPair('slider-lambda', 'input-lambda', false);
    }

    // Boot
    window.addEventListener('DOMContentLoaded', () => {
      initScene();
      initUI();
      // Perform initial render with defaults
      updateVisualization();
    });
  </script>
</body>
</html>
