<body>
  <div id="app-container">
    <!-- Control Panel Section -->
    <div id="control-panel">
      <label for="slider-r">R (Å)</label>
      <input id="slider-r" type="range" min="0.001" max="4.0" step="0.001" value="0.001" />
      <output id="slider-r-value">0.001</output>
    </div>

    <!-- Potential Energy Plot Section -->
    <div id="potential-energy-plot-container"></div>

    <!-- Electron Density Plots Section -->
    <div id="density-plots-container">
      <div id="bonding-plot-container"></div>
      <div id="antibonding-plot-container"></div>
    </div>
  </div>

  <script>
    // Constants
    const ANGSTROM_PER_BOHR = 0.529177; // a0 in Å
    const BOHR_PER_ANGSTROM = 1 / ANGSTROM_PER_BOHR;

    // DOM elements
    const slider = document.getElementById('slider-r');
    const sliderValue = document.getElementById('slider-r-value');

    // Create a fixed XY grid for the 3D density surfaces
    const GRID_MIN = -3, GRID_MAX = 3, GRID_N = 60;
    const xGrid = linspace(GRID_MIN, GRID_MAX, GRID_N);
    const yGrid = linspace(GRID_MIN, GRID_MAX, GRID_N);

    // Initialize everything on load
    init();

    function init() {
      // 1) Static potential energy plot
      plotPotentialEnergy();

      // 2) Initial electron density surfaces at default R
      const R_angstroms = parseFloat(slider.value);
      sliderValue.value = format3(R_angstroms);
      const R_au = R_angstroms * BOHR_PER_ANGSTROM; // Å -> bohr
      const { zBonding, zAntibonding } = computeDensities(R_au, xGrid, yGrid);

      plot3DSurface('bonding-plot-container', xGrid, yGrid, zBonding, {
        title: 'bonding',
        colorscale: 'YlOrBr'
      });
      plot3DSurface('antibonding-plot-container', xGrid, yGrid, zAntibonding, {
        title: 'antibonding',
        colorscale: 'YlOrBr'
      });

      // 3) Slider interaction
      slider.addEventListener('input', onSlider);
      window.addEventListener('resize', () => {
        Plotly.Plots.resize('potential-energy-plot-container');
        Plotly.Plots.resize('bonding-plot-container');
        Plotly.Plots.resize('antibonding-plot-container');
      });
    }

    function onSlider(e) {
      const R_angstroms = parseFloat(e.target.value);
      sliderValue.value = format3(R_angstroms);
      const R_au = R_angstroms * BOHR_PER_ANGSTROM;

      const { zBonding, zAntibonding } = computeDensities(R_au, xGrid, yGrid);

      // Update only the Z data to keep interactions smooth
      Plotly.restyle('bonding-plot-container', { z: [zBonding] });
      Plotly.restyle('antibonding-plot-container', { z: [zAntibonding] });
    }

    // --------- Plot 1: Potential Energy (static) ----------
    function plotPotentialEnergy() {
      // R in bohr from ~0.2 to 15
      const R_au = linspace(0.2, 15.0, 300);
      const xAngstrom = R_au.map(r => r * ANGSTROM_PER_BOHR);
      const Eb = [];
      const Ea = [];
      for (const R of R_au) {
        const Sab = overlapS(R);
        const J = (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
        const K = (1 + R) * Math.exp(-R);
        // Guard against extreme values near Sab -> 1
        const denomB = 1 + Sab;
        const denomA = Math.max(1 - Sab, 1e-6);
        Eb.push((J + K) / denomB);
        Ea.push((J - K) / denomA);
      }

      const traceB = {
        x: xAngstrom, y: Eb,
        mode: 'lines',
        line: { color: 'blue', width: 3 },
        name: 'bonding PE'
      };
      const traceA = {
        x: xAngstrom, y: Ea,
        mode: 'lines',
        line: { color: 'red', width: 3 },
        name: 'antibonding PE'
      };

      const layout = {
        title: { text: 'potential energy', x: 0.02, y: 0.95, font: { size: 14 } },
        margin: { l: 50, r: 20, t: 30, b: 40 },
        xaxis: {
          title: 'R',
          range: [0, 8],
          zeroline: false
        },
        yaxis: {
          range: [-0.6, 0.4],
          zeroline: false
        },
        showlegend: true
      };

      Plotly.newPlot('potential-energy-plot-container', [traceB, traceA], layout, {
        displayModeBar: false,
        responsive: true
      });
    }

    // --------- Plot 2 & 3: 3D Electron Density (dynamic) ----------
    function plot3DSurface(containerId, x, y, z, opts = {}) {
      const trace = {
        type: 'surface',
        x: x,
        y: y,
        z: z,
        colorscale: opts.colorscale || 'Viridis',
        showscale: false,
        contours: {
          x: { show: true, highlight: false, color: 'rgba(0,0,0,0.2)' },
          y: { show: true, highlight: false, color: 'rgba(0,0,0,0.2)' }
        }
      };

      const layout = {
        showlegend: false,
        margin: { l: 0, r: 0, t: 0, b: 0 },
        scene: {
          xaxis: {
            range: [GRID_MIN, GRID_MAX],
            showgrid: false, zeroline: false, showbackground: false, title: ''
          },
          yaxis: {
            range: [GRID_MIN, GRID_MAX],
            showgrid: false, zeroline: false, showbackground: false, title: ''
          },
          zaxis: {
            range: [0, 0.5],
            showgrid: false, zeroline: false, showbackground: false, title: ''
          },
          aspectmode: 'manual',
          aspectratio: { x: 1, y: 1, z: 0.5 },
          camera: {
            eye: { x: 1.35, y: 1.35, z: 0.9 }
          }
        }
      };

      Plotly.newPlot(containerId, [trace], layout, {
        displayModeBar: false,
        responsive: true
      });
    }

    // --------- Physics helpers ----------
    function overlapS(R) {
      // Overlap integral S_ab for 1s-1s (R in bohr)
      return (1 + R + (R * R) / 3) * Math.exp(-R);
    }

    function computeDensities(R, xArr, yArr) {
      const Sab = overlapS(R);
      // Guard against Sab ~ 1 at very small R to avoid divergence in antibonding normalization
      const Nb = 1 / Math.sqrt(2 * Math.max(1 + Sab, 1e-10));
      const Na = 1 / Math.sqrt(2 * Math.max(1 - Sab, 1e-10));

      // Nuclei positions along x
      const Ax = -R / 2, Ay = 0;
      const Bx = +R / 2, By = 0;

      const nx = xArr.length, ny = yArr.length;
      const zBonding = new Array(ny);
      const zAntibonding = new Array(ny);

      for (let j = 0; j < ny; j++) {
        const y = yArr[j];
        const rowB = new Array(nx);
        const rowA = new Array(nx);
        for (let i = 0; i < nx; i++) {
          const x = xArr[i];
          const distA = Math.hypot(x - Ax, y - Ay);
          const distB = Math.hypot(x - Bx, y - By);
          const phiA = Math.exp(-distA);
          const phiB = Math.exp(-distB);

          const psiB = Nb * (phiA + phiB);
          const psiA = Na * (phiA - phiB);

          rowB[i] = psiB * psiB;
          rowA[i] = psiA * psiA;
        }
        zBonding[j] = rowB;
        zAntibonding[j] = rowA;
      }

      return { zBonding, zAntibonding };
    }

    // --------- Utilities ----------
    function linspace(a, b, n) {
      if (n === 1) return [a];
      const arr = new Array(n);
      const step = (b - a) / (n - 1);
      for (let i = 0; i < n; i++) arr[i] = a + i * step;
      return arr;
    }
    function format3(v) {
      return (+v).toFixed(3);
    }
  </script>
</body>
</html>
