<script>
  // --- Simulation state ---
  let timeH = 0;                 // hours, 0..672
  const ORBIT_PERIOD = 672;      // hours (28 days)
  const EARTH_ROT_PERIOD = 24;   // hours
  let isPlaying = false;
  let hoursPerSecond = 12;       // playback speed

  // Rotation mode multipliers relative to synchronous (1.0)
  const RotationMode = { HALF: 'half', NORMAL: 'normal', DOUBLE: 'double', NONE: 'none' };
  let rotationMode = RotationMode.NORMAL;
  const rotMultiplier = {
    [RotationMode.NORMAL]: 1.0,
    [RotationMode.HALF]: 2.0,
    [RotationMode.DOUBLE]: 0.5,
    [RotationMode.NONE]: 0.0
  };

  // Display flags
  let showDayNight = false;
  let showNearSide = true;
  let showLine = true;
  let moonOnly = false;

  // DOM elements
  const sliderTime = document.getElementById('slider-time');
  const displayTime = document.getElementById('display-time');
  const btnPlayPause = document.getElementById('btn-play-pause');
  const btnStepBack = document.getElementById('btn-step-back');
  const btnReset = document.getElementById('btn-reset');
  const btnStepForward = document.getElementById('btn-step-forward');
  const rotButtons = {
    half: document.getElementById('btn-rot-half'),
    normal: document.getElementById('btn-rot-normal'),
    double: document.getElementById('btn-rot-double'),
    none: document.getElementById('btn-rot-none'),
  };
  const chkDayNight = document.getElementById('check-day-night');
  const chkNearSide = document.getElementById('check-near-side');
  const chkLine = document.getElementById('check-earth-moon-line');
  const chkMoonOnly = document.getElementById('check-moon-only');

  // Utilities
  function wrapHours(h){
    h %= ORBIT_PERIOD;
    if (h < 0) h += ORBIT_PERIOD;
    return h;
  }
  function updateTimeText(){
    const d = Math.floor(timeH / 24);
    const h = Math.floor(timeH % 24);
    displayTime.textContent = `${d} d  ${h} h`;
  }
  function syncSlider(){
    sliderTime.value = Math.round(timeH);
  }
  function setRotationMode(mode){
    rotationMode = mode;
    Object.values(rotButtons).forEach(b => b.classList.remove('active'));
    rotButtons[mode].classList.add('active');
  }

  // Event listeners
  sliderTime.addEventListener('input', () => {
    timeH = wrapHours(parseInt(sliderTime.value, 10));
    updateTimeText();
