    syncSlider(); updateTimeText();
  });
  btnStepForward.addEventListener('click', () => {
    timeH = wrapHours(timeH + 1);
    syncSlider(); updateTimeText();
  });
  btnReset.addEventListener('click', () => {
    timeH = 0;
    syncSlider(); updateTimeText();
  });

  rotButtons.half.addEventListener('click', () => setRotationMode(RotationMode.HALF));
  rotButtons.normal.addEventListener('click', () => setRotationMode(RotationMode.NORMAL));
  rotButtons.double.addEventListener('click', () => setRotationMode(RotationMode.DOUBLE));
  rotButtons.none.addEventListener('click', () => setRotationMode(RotationMode.NONE));

  chkDayNight.addEventListener('change', () => { showDayNight = chkDayNight.checked; });
  chkNearSide.addEventListener('change', () => { showNearSide = chkNearSide.checked; });
  chkLine.addEventListener('change', () => { showLine = chkLine.checked; });
  chkMoonOnly.addEventListener('change', () => { moonOnly = chkMoonOnly.checked; });

  // --- p5.js sketch (global mode) ---
  let canvas, vizFrameEl;
  let W = 800, H = 600;

  // Visual constants (in pixels)
  const EARTH_R = 80;
  const MOON_R  = 25;
  const ORBIT_R = 250;

  // For orientation zero so the "near side" faces Earth at t=0
  const SPIN_BASE = Math.PI / 2; // angle to Earth from Moon at t=0

  function setup(){
    // Replace placeholder canvas with p5 canvas that has the same id
    const placeholder = document.getElementById('main-canvas');
    vizFrameEl = document.getElementById('viz-frame');
    if (placeholder) placeholder.remove();

    const rect = vizFrameEl.getBoundingClientRect();
    W = rect.width; H = rect.height;
    canvas = createCanvas(W, H);
    canvas.id('main-canvas');
    canvas.parent('viz-frame');
    frameRate(60);
    angleMode(RADIANS);

    // Defaults from spec
    setRotationMode(RotationMode.NORMAL);
    showDayNight = chkDayNight.checked;
    showNearSide = chkNearSide.checked;
    showLine = chkLine.checked;
    moonOnly = chkMoonOnly.checked;

    updateTimeText();
  }

  function windowResized(){
    const rect = vizFrameEl.getBoundingClientRect();
    W = rect.width; H = rect.height;
    resizeCanvas(W, H);
  }

  function draw(){
    background(255);
    translate(width/2, height/2);

    // Time progression when playing
    if (isPlaying){
      timeH = wrapHours(timeH + hoursPerSecond * (deltaTime / 1000));
      syncSlider();
      updateTimeText();
    }

    // Angles
    const earthRot = (timeH / EARTH_ROT_PERIOD) * TWO_PI; // rotation about axis
    const moonOrbit = (timeH / ORBIT_PERIOD) * TWO_PI;    // orbital angle
    const spinAngle = SPIN_BASE + rotMultiplier[rotationMode] * (timeH / ORBIT_PERIOD) * TWO_PI;

    // Moon position
    let a = moonOrbit - HALF_PI; // start at top
    let mx = ORBIT_R * Math.cos(a);
    let my = ORBIT_R * Math.sin(a);
    if (moonOnly){ mx = 0; my = 0; }

    // Draw Earth if not moon-only
    if (!moonOnly){
      push();
      // Base planet
      stroke(0); strokeWeight(3);
      fill('#3B82F6');
      circle(0, 0, EARTH_R*2);

      // Landmasses + stick figure rotate with Earth
      push();
      rotate(earthRot);

      // Simple landmasses
      noStroke(); fill('#16a34a');
      beginShape();
        vertex(-30, -10); vertex(-10, -25); vertex(20, -20); vertex(15, -5);
        vertex(35, 10); vertex(10, 20); vertex(-10, 15);
      endShape(CLOSE);
      beginShape();
        vertex(-50, 20); vertex(-35, 35); vertex(-15, 30); vertex(-20, 10);
      endShape(CLOSE);

      // Red stick figure at top of Earth
      const sx = 0, sy = -EARTH_R;
      stroke('#ef4444'); strokeWeight(3);
      fill('#ef4444');
      circle(sx, sy - 10, 8); // head
      // body
      line(sx, sy - 6, sx, sy + 12);
      // arms
      line(sx, sy + 2, sx - 8, sy + 6);
      line(sx, sy + 2, sx + 8, sy + 6);
      // legs
      line(sx, sy + 12, sx - 6, sy + 22);
      line(sx, sy + 12, sx + 6, sy + 22);

      pop(); // end rotation for Earth features

      // Day/night on Earth (left half shaded — sun from +x)
      if (showDayNight){
        noStroke();
        fill(0, 0, 0, 128);
        arc(0, 0, EARTH_R*2+0.01, EARTH_R*2+0.01, HALF_PI, 3*HALF_PI, PIE);
      }
      pop();
    }

    // Earth–Moon line
    if (showLine && !moonOnly){
      push();
      stroke(0); strokeWeight(2);
      drawingContext.setLineDash([6, 6]);
      line(0, 0, mx, my);
      drawingContext.setLineDash([]);
      pop();
    }

    // Draw Moon
    push();
    translate(mx, my);
    stroke(0); strokeWeight(3);
    fill('#808080');
    circle(0, 0, MOON_R*2);

    // Day/night on Moon
    if (showDayNight){
      noStroke();
      fill(0, 0, 0, 128);
      arc(0, 0, MOON_R*2+0.01, MOON_R*2+0.01, HALF_PI, 3*HALF_PI, PIE);
    }

    if (showNearSide){
      // Near-side marker and magenta figure rotate with the Moon spinAngle
      push();
      rotate(spinAngle);
      // Green semicircle arc facing +x
      noFill();
      stroke('#22C55E'); strokeWeight(4);
      arc(0, 0, MOON_R*2+1, MOON_R*2+1, -HALF_PI, HALF_PI);
      // Magenta stick figure at rightmost point
      const px = MOON_R + 6, py = 0;
      stroke('#D946EF'); fill('#D946EF'); strokeWeight(3);
      circle(px, py - 8, 8);           // head
      line(px, py - 4, px, py + 12);   // body
      line(px, py + 2, px - 7, py + 6);// arm
      line(px, py + 2, px + 7, py + 6);// arm
      line(px, py + 12, px - 6, py + 22);// legs
      line(px, py + 12, px + 6, py + 22);
      pop();
    }
    pop();
  }
</script>
</body>
</html>
