    <script>
        // Constants
        const nu0 = 1000; // cm^-1 band origin
        const Bp = 0.98, Ap = 1.95;  // upper state
        const Bpp = 1.0, App = 2.0;  // lower state
        const zeta = 0.2;
        const T = 300;     // K
        const kB = 0.695;  // cm^-1/K
        const J_max = 60;  // calculation limit
        const BAR_WIDTH = 0.12;

        // DOM
        const plotDiv = document.getElementById('plot-div');

        // Groups
        const groupK = document.getElementById('k-selector-group');
        const groupSubType = document.getElementById('subband-type-group');
        const groupBranch = document.getElementById('branch-selector-group');

        // Radios
        const radioViewFull = document.getElementById('radio-view-full');
        const radioViewSub  = document.getElementById('radio-view-sub');

        const radioSubFull = document.getElementById('radio-subband-full');
        const radioSubPos  = document.getElementById('radio-subband-positive');
        const radioSubNeg  = document.getElementById('radio-subband-negative');

        const radioBranchAll = document.getElementById('radio-branch-all');
        const radioBranchP   = document.getElementById('radio-branch-p');
        const radioBranchQ   = document.getElementById('radio-branch-q');
        const radioBranchR   = document.getElementById('radio-branch-r');

        // Zoom sliders
        const sliderLower = document.getElementById('slider-x-lower');
        const sliderUpper = document.getElementById('slider-x-upper');
        const spanLower = document.getElementById('span-x-lower-value');
        const spanUpper = document.getElementById('span-x-upper-value');

        function getSelectedK(){
            for (let i=0;i<=5;i++){
                const el = document.getElementById('radio-k-' + i);
                if (el.checked) return i;
            }
            return 0;
        }
        function getSelectedSubType(){
            if (radioSubPos.checked) return 'positive';
            if (radioSubNeg.checked) return 'negative';
            return 'full';
        }
        function getSelectedBranch(){
            if (radioBranchP.checked) return 'P';
            if (radioBranchQ.checked) return 'Q';
            if (radioBranchR.checked) return 'R';
            return 'all';
        }
        function setGroupDisabled(groupEl, disabled){
            if (disabled) groupEl.classList.add('disabled');
            else groupEl.classList.remove('disabled');
        }
        function setBranchGroupVisible(visible){
            if (visible){
                groupBranch.classList.remove('hidden');
            } else {
                groupBranch.classList.add('hidden');
            }
        }

        // Energy for a symmetric top (approx, cm^-1)
        function E(A, B, J, K){
            return B*J*(J+1) + (A - B)*K*K;
        }

        // Hönl-London-like factors (simple smooth approximations)
        function honlLondonApprox(branch, J, K){
            // All non-negative and smoothly varying, scaled to ~[0,1]
            if (branch === 'P'){ // ΔJ = -1
                if (J < 1) return 0;
                return Math.max(0, (J - K + 1) / (2*J + 1));
            }
            if (branch === 'Q'){ // ΔJ = 0
                return Math.min(1, 0.6 * (K + 1) / (J + 1));
            }
            if (branch === 'R'){ // ΔJ = +1
                return Math.max(0, (J + K + 1) / (2*J + 3));
            }
            return 1;
        }

        function boltzmann(J, K){
            const Erot = E(App, Bpp, J, K);
            return (2*J + 1) * Math.exp(-Erot / (kB * T));
        }

        // Coriolis-like small offset for Q branch (for visual realism)
        function coriolisShift(type, K){
            const sign = (type === 'positive') ? +1 : -1;
            return sign * zeta * (Ap - Bp) * Math.max(1, (K + 1)/2);
        }

        // Generate transitions for a given sub-band type and K''
        // type: 'positive' or 'negative'
        // branches: array of 'P','Q','R'
        function generateSubband(K, type, branches){
            const traces = [];
            const colorMap = {
                positive: '#b22222',  // red
                negative: '#1e4bd8'   // blue
            };
            const color = colorMap[type];

            branches.forEach(br => {
                const xs=[], ys=[], ws=[];
                const Kpp = K;                       // lower-state K''
                const Kp  = (type === 'positive') ? Kpp + 1 : Kpp - 1; // upper-state K'
                if (Kp < 0) {
                    // invalid subband
                    traces.push({x:[], y:[], width:[], type:'bar', marker:{color:color}, hoverinfo:'skip', name: `${type}-${br}`});
                    return;
                }
                for (let Jpp = Math.max(Kpp, 0); Jpp <= J_max; Jpp++){
                    let Jp;
                    if (br === 'P'){ Jp = Jpp - 1; if (Jp < 0) continue; }
                    if (br === 'Q'){ Jp = Jpp; }
                    if (br === 'R'){ Jp = Jpp + 1; }
                    // Angular momentum constraints J' ≥ K'
                    if (Jp < Kp) continue;

                    const ErotLower = E(App, Bpp, Jpp, Kpp);
                    const ErotUpper = E(Ap,  Bp,  Jp,  Kp );
                    // Q-branch small Coriolis-like visual offset
                    const add = (br === 'Q') ? coriolisShift(type, Kpp) : 0;
                    const nu = nu0 + (ErotUpper - ErotLower) + add;

                    const intensity = boltzmann(Jpp, Kpp) * honlLondonApprox(br, Jpp, Kpp);
                    if (intensity > 0){
                        xs.push(nu);
                        ys.push(intensity);
                        ws.push(BAR_WIDTH);
                    }
                }

                traces.push({
                    x: xs, y: ys, width: ws, type:'bar',
                    marker:{color:color},
                    hovertemplate: `ν = %{x:.2f} cm⁻¹<br>I = %{y:.3f}<extra>${br}</extra>`,
                    name: `${type}-${br}`
                });
            });

            return traces;
        }

        // Generate "full spectrum" (all K=0..5, both sub-bands, all branches), in black
        function generateFullSpectrum(){
            const x=[], y=[], w=[];
            for (let K=0; K<=5; K++){
                ['positive','negative'].forEach(type => {
                    const Kp = (type==='positive') ? K+1 : K-1;
                    if (Kp < 0) return;
                    ['P','Q','R'].forEach(br => {
                        for (let Jpp = Math.max(K, 0); Jpp <= J_max; Jpp++){
                            let Jp;
                            if (br==='P'){ Jp = Jpp - 1; if (Jp < 0) continue; }
                            if (br==='Q'){ Jp = Jpp; }
                            if (br==='R'){ Jp = Jpp + 1; }
                            if (Jp < Kp) continue;
                            const nu = nu0 + (E(Ap, Bp, Jp, Kp) - E(App, Bpp, Jpp, K)) + ((br==='Q')? coriolisShift(type, K) : 0);
                            const intensity = boltzmann(Jpp, K) * honlLondonApprox(br, Jpp, K);
                            if (intensity>0){
                                x.push(nu); y.push(intensity); w.push(BAR_WIDTH);
                            }
                        }
                    });
                });
            }
            return [{
                x:x, y:y, width:w, type:'bar',
                marker:{color:'#111'},
                hovertemplate: 'ν = %{x:.2f} cm⁻¹<br>I = %{y:.3f}<extra></extra>',
                name:'full'
            }];
        }

        // Combine / filter according to UI
        function buildTraces(){
            const view = radioViewSub.checked ? 'sub' : 'full';
            if (view === 'full'){
                return generateFullSpectrum();
            }
            const K = getSelectedK();
            const subtype = getSelectedSubType();
            if (subtype === 'full'){
                const pos = generateSubband(K, 'positive', ['P','Q','R']);
                const neg = (K===0) ? [] : generateSubband(K, 'negative', ['P','Q','R']);
                // Merge branches within each subband into a single colored trace
                function mergeTraces(list){
                    const x=[], y=[], w=[];
                    list.forEach(t => { x.push(...t.x); y.push(...t.y); w.push(...t.width); });
                    return [{
                        x:x, y:y, width:w, type:'bar',
                        marker:{color: list.length? list[0].marker.color : '#000'},
                        hovertemplate: 'ν = %{x:.2f} cm⁻¹<br>I = %{y:.3f}<extra></extra>'
                    }];
                }
                return [...mergeTraces(pos), ...mergeTraces(neg)];
            }else{
                // Single subband, optionally filter by branch
                const branchSel = getSelectedBranch();
                const branches = (branchSel === 'all') ? ['P','Q','R'] : [branchSel];
                return generateSubband(K, subtype, branches);
            }
        }

        // Scale intensities to fit y-range nicely
        function scaleTraces(traces){
            let maxY = 0;
            traces.forEach(t => {
                t.y.forEach(v => { if (v > maxY) maxY = v; });
            });
            if (maxY <= 0) return traces;
            const s = 1.05 / maxY;
            traces.forEach(t => { t.y = t.y.map(v => v*s); });
            return traces;
        }

        function currentTitle(){
            if (radioViewFull.checked){
                return 'Rovibronic Spectrum';
            }
            const K = getSelectedK();
            const subtype = getSelectedSubType();
            if (subtype === 'full'){
                return `K = ${K} sub-band`;
            }
            const subLabel = subtype === 'positive' ? '"positive" sub-band' : '"negative" sub-band';
            return `K = ${K} sub-band\n${subLabel}`;
        }

        function currentXRange(){
            return [Number(sliderLower.value), Number(sliderUpper.value)];
        }

        function drawPlot(){
            const traces = scaleTraces(buildTraces());
            const layout = {
                title: {text: currentTitle(), font:{size:16}},
                xaxis: {title: 'wavenumber (cm⁻¹)', range: currentXRange()},
                yaxis: {title: 'absorbance', range: [0, 1.2]},
                showlegend: false,
                margin: {l:60, r:20, t:60, b:60},
                bargap: 0
            };
            Plotly.react(plotDiv, traces, layout, {displayModeBar:false, responsive:true});
        }

        // UI logic handlers
        function onViewChanged(){
            if (radioViewFull.checked){
                setGroupDisabled(groupK, true);
                setGroupDisabled(groupSubType, true);
                setGroupDisabled(groupBranch, true);
                setBranchGroupVisible(false);
            } else {
                setGroupDisabled(groupK, false);
                setGroupDisabled(groupSubType, false);
                // Branch group visibility depends on subtype
                const subtype = getSelectedSubType();
                const showBranch = (subtype === 'positive' || subtype === 'negative');
                setBranchGroupVisible(showBranch);
                setGroupDisabled(groupBranch, !showBranch);
                // K=0 disables negative subband
                enforceK0Constraint();
            }
            drawPlot();
        }

        function enforceK0Constraint(){
            const K = getSelectedK();
            if (K === 0){
                radioSubNeg.disabled = true;
                if (radioSubNeg.checked){
                    // Prefer full sub-band when negative is invalid
                    radioSubFull.checked = true;
                    radioSubPos.checked = false;
                }
            } else {
                radioSubNeg.disabled = false;
            }
        }

        function onSubTypeChanged(){
            const subtype = getSelectedSubType();
            const showBranch = (subtype === 'positive' || subtype === 'negative');
            setBranchGroupVisible(showBranch);
            setGroupDisabled(groupBranch, !showBranch);
            drawPlot();
        }

        function onKChanged(){
            enforceK0Constraint();
            drawPlot();
        }

        // Zoom handlers
        function updateZoomFromSliders(){
            let low = Number(sliderLower.value);
            let high = Number(sliderUpper.value);
            // enforce bounds
            if (low >= high){
                high = low + 1;
                sliderUpper.value = high;
            }
            if (high <= low){
                low = high - 1;
                sliderLower.value = low;
            }
            spanLower.textContent = low.toFixed(0) + '.';
            spanUpper.textContent = high.toFixed(0) + '.';
            Plotly.relayout(plotDiv, {'xaxis.range': [low, high]});
        }

        // Wire events
        radioViewFull.addEventListener('change', onViewChanged);
        radioViewSub.addEventListener('change', onViewChanged);

        for (let i=0;i<=5;i++){
            document.getElementById('radio-k-' + i).addEventListener('change', onKChanged);
        }
        radioSubFull.addEventListener('change', onSubTypeChanged);
        radioSubPos.addEventListener('change', onSubTypeChanged);
        radioSubNeg.addEventListener('change', onSubTypeChanged);

