<script type="module">
import * as THREE from 'three';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
import { ParametricGeometry } from 'three/addons/geometries/ParametricGeometry.js';

const container = document.getElementById('canvas-container');

/* -------------------------------------------------------
   Scene / Camera / Renderer
------------------------------------------------------- */
const scene = new THREE.Scene();
scene.background = new THREE.Color('#f0f0f0');

const camera = new THREE.PerspectiveCamera(40, container.clientWidth / container.clientHeight, 0.1, 100);
camera.position.set(4.5, 4.5, 3.6);
camera.lookAt(0,0,0);

const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
renderer.setSize(container.clientWidth, container.clientHeight);
renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
container.appendChild(renderer.domElement);

/* Orbit Controls */
const controls = new OrbitControls(camera, renderer.domElement);
controls.enableDamping = true;
controls.dampingFactor = 0.06;
controls.target.set(0,0,0);

/* Lighting */
scene.add(new THREE.AmbientLight(0xffffff, 0.55));
const dir1 = new THREE.DirectionalLight(0xffffff, 0.7);
dir1.position.set(5, 3, 6);
scene.add(dir1);
const dir2 = new THREE.DirectionalLight(0xffffff, 0.6);
dir2.position.set(-4, -2, 4);
scene.add(dir2);

/* -------------------------------------------------------
   Radiation Pattern Graph (Hierarchy for rotations)
   Order: strike (world Z) -> dip (local X) -> rake (local Y)
------------------------------------------------------- */
const patternRoot = new THREE.Object3D();
scene.add(patternRoot);

const strikeGroup = new THREE.Object3D(); // world Z rotation
const dipGroup = new THREE.Object3D();    // local X rotation
const rakeGroup = new THREE.Object3D();   // local Y rotation

patternRoot.add(strikeGroup);
strikeGroup.add(dipGroup);
dipGroup.add(rakeGroup);

/* Bounding Box Helper (attached to the hierarchy root for the mesh) */
const boxHelper = new THREE.BoxHelper(rakeGroup, 0x666666);
boxHelper.visible = false;
scene.add(boxHelper);

/* -------------------------------------------------------
   Compass Overlay (in-scene)
------------------------------------------------------- */
const compassGroup = new THREE.Object3D();
scene.add(compassGroup);

const compassRadius = 2.3;

// dashed outer circle
{
    const pts = [];
    const steps = 256;
    for(let i=0;i<=steps;i++){
        const a = (i/steps) * Math.PI * 2;
        pts.push(new THREE.Vector3(compassRadius*Math.cos(a), compassRadius*Math.sin(a), 0));
    }
    const g = new THREE.BufferGeometry().setFromPoints(pts);
    const m = new THREE.LineDashedMaterial({ color: 0x999999, dashSize: 0.2, gapSize: 0.15 });
    const circle = new THREE.LineLoop(g, m);
    circle.computeLineDistances();
    compassGroup.add(circle);
}

// helper: text sprite maker
function makeTextSprite(text, {font='bold 36px Arial', fillStyle='#222', opacity=1, padding=8, bg=null, scale=0.25}={}){
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    ctx.font = font;
    const metrics = ctx.measureText(text);
    const w = Math.ceil(metrics.width + 2*padding);
    const h = Math.ceil(36 + 2*padding);
    canvas.width = w; canvas.height = h;
    ctx.font = font;
    ctx.textBaseline = 'middle';
    ctx.textAlign = 'center';
    if(bg){
        ctx.fillStyle = bg;
        ctx.fillRect(0,0,w,h);
    }
    ctx.fillStyle = fillStyle;
    ctx.globalAlpha = opacity;
    ctx.fillText(text, w/2, h/2);
    const tex = new THREE.CanvasTexture(canvas);
    tex.minFilter = THREE.LinearFilter;
    const mat = new THREE.SpriteMaterial({ map: tex, transparent: true, depthTest: false });
    const spr = new THREE.Sprite(mat);
    const aspect = w/h;
    spr.scale.set(scale*aspect, scale, 1);
    spr.renderOrder = 999;
    return spr;
}

// place cardinal letters
const cardScale = 0.35;
const N = makeTextSprite('N', {font:'bold 44px Arial', scale: cardScale});
N.position.set(0, compassRadius + 0.35, 0);
const S = makeTextSprite('S', {font:'bold 44px Arial', scale: cardScale});
S.position.set(0, -compassRadius - 0.35, 0);
const E = makeTextSprite('E', {font:'bold 44px Arial', scale: cardScale});
E.position.set(compassRadius + 0.35, 0, 0);
const W = makeTextSprite('W', {font:'bold 44px Arial', scale: cardScale});
W.position.set(-compassRadius - 0.35, 0, 0);
compassGroup.add(N, S, E, W);

// degree markings at 30,60,...,330 (azimuth clockwise from North)
const degs = [30,60,120,150,210,240,300,330];
degs.forEach(d=>{
    const spr = makeTextSprite(d + '°', {font:'bold 30px Arial', fillStyle:'#777', scale:0.28});
    const angleFromX = THREE.MathUtils.degToRad(90 - d);
    const r = compassRadius + 0.12;
    spr.position.set(r*Math.cos(angleFromX), r*Math.sin(angleFromX), 0);
    compassGroup.add(spr);
});

// Up arrow with 'U'
const upDir = new THREE.Vector3(0,0,1);
const upArrow = new THREE.ArrowHelper(upDir, new THREE.Vector3(0,0,0), 1.15, 0x000000, 0.18, 0.10);
scene.add(upArrow);
const uLabel = makeTextSprite('U', {font:'bold 40px Arial', scale:0.35});
uLabel.position.copy(upArrow.position).add(new THREE.Vector3(0,0,1.22));
scene.add(uLabel);

/* -------------------------------------------------------
   Radiation Pattern Mesh creation
------------------------------------------------------- */
let currentWaveType = 'P'; // default

function amplitude(type, theta, phi){
    switch(type){
        case 'P':  return Math.abs(Math.sin(2*theta) * Math.sin(phi));
        case 'SV': return Math.abs(Math.cos(2*theta) * Math.sin(phi));
        case 'SH': return Math.abs(Math.cos(theta) * Math.cos(phi));
        default: return 0;
    }
}

function createPatternMesh(type){
    const func = (u,v,target)=>{
        const phi = u * Math.PI * 2;   // 0..2π
        const theta = v * Math.PI;     // 0..π

        const rBase = amplitude(type, theta, phi);
        // scale and avoid exact zeros for better shading
        const r = Math.max(rBase, 0.0005) * 1.25;

        const sinT = Math.sin(theta);
        const cosT = Math.cos(theta);
        const cosP = Math.cos(phi);
        const sinP = Math.sin(phi);

        const x = r * sinT * cosP;
        const y = r * sinT * sinP;
        const z = r * cosT;

        target.set(x, y, z);
    };
    const geom = new ParametricGeometry(func, 120, 60);
    geom.computeVertexNormals();
    const mat = new THREE.MeshStandardMaterial({
        color: 0xD2B48C,
        metalness: 0.15,
        roughness: 0.55,
        side: THREE.DoubleSide
    });
    const mesh = new THREE.Mesh(geom, mat);
    return mesh;
}

// holder for current mesh
let patternMesh = createPatternMesh(currentWaveType);
rakeGroup.add(patternMesh);

/* -------------------------------------------------------
   Orientation from sliders (ZYX: Z strike, X dip, Y rake)
------------------------------------------------------- */
function degToRad(d){ return d * Math.PI / 180; }

function applyOrientation(){
    const strike = Number(document.getElementById('slider-strike').value);
    const dip    = Number(document.getElementById('slider-dip').value);
    const rake   = Number(document.getElementById('slider-rake').value);

    strikeGroup.rotation.set(0,0, degToRad(strike)); // world Z
    dipGroup.rotation.set(degToRad(dip), 0, 0);      // local X
    rakeGroup.rotation.set(0, degToRad(rake), 0);    // local Y

    boxHelper.update();
}

/* -------------------------------------------------------
   UI wiring - sliders, buttons, checkbox
------------------------------------------------------- */
const sliderStrike = document.getElementById('slider-strike');
const sliderDip    = document.getElementById('slider-dip');
const sliderRake   = document.getElementById('slider-rake');

const strikeValue = document.getElementById('strike-value');
const dipValue    = document.getElementById('dip-value');
const rakeValue   = document.getElementById('rake-value');

// initialize slider attributes as specified
sliderStrike.min = "0";   sliderStrike.max = "360";  sliderStrike.step = "1";   sliderStrike.value = "180";
sliderDip.min    = "0";   sliderDip.max    = "90";   sliderDip.step    = "1";   sliderDip.value    = "60";
sliderRake.min   = "-180";sliderRake.max   = "180";  sliderRake.step   = "1";   sliderRake.value   = "90";

function onSliderInput(e){
    strikeValue.textContent = sliderStrike.value;
    dipValue.textContent    = sliderDip.value;
    rakeValue.textContent   = sliderRake.value;
    applyOrientation();
}

sliderStrike.addEventListener('input', onSliderInput);
sliderDip.addEventListener('input', onSliderInput);
sliderRake.addEventListener('input', onSliderInput);

// Wave type buttons
const btnP  = document.getElementById('btn-p');
const btnSV = document.getElementById('btn-sv');
const btnSH = document.getElementById('btn-sh');

function setActiveButton(button){
    [btnP, btnSV, btnSH].forEach(b => b.classList.remove('active'));
    button.classList.add('active');
}

function rebuildPattern(type){
    // remove old mesh
    if(patternMesh){
        rakeGroup.remove(patternMesh);
        patternMesh.geometry.dispose();
        patternMesh.material.dispose();
    }
    // create new and orient
    patternMesh = createPatternMesh(type);
    rakeGroup.add(patternMesh);
    applyOrientation();
}

btnP.addEventListener('click', ()=>{
    currentWaveType = 'P';
    setActiveButton(btnP);
    rebuildPattern(currentWaveType);
});
btnSV.addEventListener('click', ()=>{
    currentWaveType = 'SV';
    setActiveButton(btnSV);
    rebuildPattern(currentWaveType);
});
btnSH.addEventListener('click', ()=>{
    currentWaveType = 'SH';
    setActiveButton(btnSH);
    rebuildPattern(currentWaveType);
});

// Checkbox for box helper
const checkboxBox = document.getElementById('checkbox-box');
checkboxBox.checked = false;
checkboxBox.addEventListener('change', ()=>{
    boxHelper.visible = checkboxBox.checked;
});

/* -------------------------------------------------------
   Resize handling
------------------------------------------------------- */
function onResize(){
    const w = container.clientWidth;
    const h = container.clientHeight;
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    renderer.setSize(w, h);
}
window.addEventListener('resize', onResize);

/* -------------------------------------------------------
   Initial apply + MathJax
------------------------------------------------------- */
onSliderInput();
if(window.MathJax && MathJax.typesetPromise){
    MathJax.typesetPromise();
}else if(window.MathJax && MathJax.typeset){
    MathJax.typeset();
}

/* -------------------------------------------------------
   Animation loop
------------------------------------------------------- */
function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
}
animate();
</script>
</body>
</html>
