<body>

<!-- Main container for the whole demo -->
<div id="demo-container">

    <!-- Left side: Control Panel -->
    <div id="control-panel">

        <!-- Share 1 Control -->
        <div class="share-control">
            <label for="joystick-share1">share 1</label>
            <div id="joystick-share1" class="joystick-container">
                <div id="joystick-handle1" class="joystick-handle"></div>
            </div>
        </div>

        <!-- Share 2 Control -->
        <div class="share-control">
            <label for="joystick-share2">share 2</label>
            <div id="joystick-share2" class="joystick-container">
                <div id="joystick-handle2" class="joystick-handle"></div>
            </div>
        </div>

    </div>

    <!-- Right side: Visualization -->
    <div id="visualization-area">
        <!-- The p5.js canvas will be attached here -->
    </div>

    <!-- Reset Button -->
    <button id="btn-reset">+</button>

</div>

<!-- CDN for p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
/* ---------------------------
   State and DOM references
----------------------------*/
const joystick1 = document.getElementById('joystick-share1');
const joystick2 = document.getElementById('joystick-share2');
const handle1   = document.getElementById('joystick-handle1');
const handle2   = document.getElementById('joystick-handle2');
const btnReset  = document.getElementById('btn-reset');

const initialPercent1 = { x: 0.75, y: 0.25 }; // left 75%, top 25%
const initialPercent2 = { x: 0.25, y: 0.75 }; // left 25%, top 75%

let percent1 = { ...initialPercent1 };
let percent2 = { ...initialPercent2 };

let dragging = null; // { handle, container, which: 1|2 }

function setHandlePercent(handle, percent){
  handle.style.left = (percent.x * 100).toFixed(2) + '%';
  handle.style.top  = (percent.y * 100).toFixed(2) + '%';
}

// initialize handle positions per spec
setHandlePercent(handle1, percent1);
setHandlePercent(handle2, percent2);

/* ---------------------------
   Joystick dragging logic
----------------------------*/
function startDrag(e, which){
  e.preventDefault();
  const handle = which === 1 ? handle1 : handle2;
  const container = which === 1 ? joystick1 : joystick2;
  dragging = { handle, container, which };
}

function updateDrag(e){
  if(!dragging) return;

  const container = dragging.container;
  const rect = container.getBoundingClientRect();
  const clientX = (e.touches && e.touches[0]) ? e.touches[0].clientX : e.clientX;
  const clientY = (e.touches && e.touches[0]) ? e.touches[0].clientY : e.clientY;

  let relX = (clientX - rect.left) / rect.width;
  let relY = (clientY - rect.top)  / rect.height;

  relX = Math.min(1, Math.max(0, relX));
  relY = Math.min(1, Math.max(0, relY));

  const p = { x: relX, y: relY };

  dragging.handle.style.left = (p.x * 100) + '%';
  dragging.handle.style.top  = (p.y * 100) + '%';

  if(dragging.which === 1){
    percent1 = p;
  }else{
    percent2 = p;
  }
}

function endDrag(){
  dragging = null;
}

handle1.addEventListener('mousedown', e => startDrag(e, 1));
handle2.addEventListener('mousedown', e => startDrag(e, 2));
handle1.addEventListener('touchstart', e => startDrag(e, 1), {passive:false});
handle2.addEventListener('touchstart', e => startDrag(e, 2), {passive:false});

document.addEventListener('mousemove', updateDrag);
document.addEventListener('touchmove', updateDrag, {passive:false});
document.addEventListener('mouseup', endDrag);
document.addEventListener('touchend', endDrag);

/* ---------------------------
   Reset button
----------------------------*/
btnReset.addEventListener('click', () => {
  percent1 = { ...initialPercent1 };
  percent2 = { ...initialPercent2 };
  setHandlePercent(handle1, percent1);
  setHandlePercent(handle2, percent2);
});

/* ---------------------------
   p5.js visualization
----------------------------*/
let share1_graphic, share2_graphic;
let canvasW = 520, canvasH = 380;
let shareDisplaySize = 256; // how big each share is drawn on the main canvas

// computed positions mapped from joystick percentages
let drawPos1 = { x: 0, y: 0 };
let drawPos2 = { x: 0, y: 0 };

function percentToCanvasPos(p){
  const rangeX = canvasW - shareDisplaySize;
  const rangeY = canvasH - shareDisplaySize;
  return {
    x: p.x * rangeX,
    y: p.y * rangeY
  };
}

/* Build a binary (0/1) smiley face as a procedural 2D array */
function makeSmileyBinary(w, h){
  const arr = new Array(h);
  const cx = (w-1)/2;
  const cy = (h-1)/2;

  const R  = Math.min(w,h) * 0.45;  // outer face radius
  const ringT = 2.6;                 // thickness for ring outline
  const eyeR = Math.min(w,h) * 0.06;
  const eyeOffsetX = w * 0.18;
  const eyeOffsetY = -h * 0.12;

  const mouthCy = h * 0.06;          // mouth circle center Y
  const mouthR  = Math.min(w,h) * 0.28;
  const mouthT  = 2.8;

  for(let j=0;j<h;j++){
    arr[j] = new Array(w).fill(0);   // white background by default
    for(let i=0;i<w;i++){
      const x = i - cx;
      const y = j - cy;

      let black = 0;

      // face ring
      const d = Math.hypot(x, y);
      if(Math.abs(d - R) <= ringT) black = 1;

      // eyes
      const lx = x + eyeOffsetX;
      const ly = y + eyeOffsetY;
      const rx = x - eyeOffsetX;
      const ry = y + eyeOffsetY;
      if(Math.hypot(lx, ly) <= eyeR || Math.hypot(rx, ry) <= eyeR) black = 1;

      // smile arc (lower half of a circle arc)
      const mx = x;
      const my = y - (mouthCy - cy);
      const md = Math.hypot(mx, my);
      if(my > 0 && Math.abs(md - mouthR) <= mouthT){
        // angle constraint to keep it as a smile arc
        const ang = Math.atan2(my, mx); // -pi..pi
        const deg = ang * 180 / Math.PI;
        if(deg > 20 && deg < 160) black = 1;
      }

      arr[j][i] = black ? 1 : 0;
    }
  }
  return arr;
}

/* Create visual-cryptography shares based on the binary image */
function generateShares(){
  const secretW = 64, secretH = 64;
  const secret = makeSmileyBinary(secretW, secretH);

  share1_graphic = createGraphics(secretW*2, secretH*2);
  share2_graphic = createGraphics(secretW*2, secretH*2);

  share1_graphic.clear();
  share2_graphic.clear();

  share1_graphic.noStroke();
  share2_graphic.noStroke();
  share1_graphic.noSmooth();
  share2_graphic.noSmooth();

  // Patterns: diag A and diag B across a 2x2 subpixel block
  // A: (0,0) and (1,1) black
  // B: (1,0) and (0,1) black
  function drawPattern(g, x0, y0, pat){
    g.fill(0);
    if(pat === 0){
      g.rect(x0+0, y0+0, 1, 1);
      g.rect(x0+1, y0+1, 1, 1);
    }else{
      g.rect(x0+1, y0+0, 1, 1);
      g.rect(x0+0, y0+1, 1, 1);
    }
  }

  for(let j=0;j<secretH;j++){
    for(let i=0;i<secretW;i++){
      const x0 = i*2;
      const y0 = j*2;

      const randPattern = Math.random() < 0.5 ? 0 : 1;
      if(secret[j][i] === 0){
        // white pixel: both shares get the SAME pattern
        drawPattern(share1_graphic, x0, y0, randPattern);
        drawPattern(share2_graphic, x0, y0, randPattern);
      }else{
        // black pixel: complementary patterns
        drawPattern(share1_graphic, x0, y0, randPattern);
        drawPattern(share2_graphic, x0, y0, 1 - randPattern);
      }
    }
  }
}

/* p5 sketch (global mode per library on the page) */
let _cnv;
function setup(){
  _cnv = createCanvas(canvasW, canvasH);
  _cnv.parent('visualization-area');
  pixelDensity(1);
  noSmooth();

  generateShares();
}

function draw(){
  // Update mapped positions from current joystick percentages
  drawPos1 = percentToCanvasPos(percent1);
  drawPos2 = percentToCanvasPos(percent2);

  background(255);
  image(share1_graphic, drawPos1.x, drawPos1.y, shareDisplaySize, shareDisplaySize);
  image(share2_graphic, drawPos2.x, drawPos2.y, shareDisplaySize, shareDisplaySize);
}

/* Optional: keep canvas responsive if container gets very small (simple approach) */
window.addEventListener('resize', () => {
  const vis = document.getElementById('visualization-area');
  const pad = 0;
  const maxW = Math.max(320, Math.min(720, vis.clientWidth - pad));
  const maxH = Math.max(280, Math.min(520, vis.clientHeight - pad));
  // keep aspect similar
  const newW = Math.floor(maxW);
  const newH = Math.floor(maxH);
  if(newW !== canvasW || newH !== canvasH){
    canvasW = newW;
    canvasH = newH;
    resizeCanvas(canvasW, canvasH);
  }
});
</script>
</body>
</html>
