        <label for="cpr-radios" class="group-label">circles per ring</label>
        <div id="cpr-radios" class="radio-container">
          <input type="radio" name="cpr" value="4" id="cpr-4" />
          <label for="cpr-4">4</label>
          <input type="radio" name="cpr" value="6" id="cpr-6" />
          <label for="cpr-6">6</label>
          <input type="radio" name="cpr" value="8" id="cpr-8" checked />
          <label for="cpr-8">8</label>
          <input type="radio" name="cpr" value="10" id="cpr-10" />
          <label for="cpr-10">10</label>
          <input type="radio" name="cpr" value="12" id="cpr-12" />
          <label for="cpr-12">12</label>
          <input type="radio" name="cpr" value="14" id="cpr-14" />
          <label for="cpr-14">14</label>
        </div>
      </div>

      <div id="inverse-control-group">
        <label for="checkbox-inverse">inverse</label>
        <input type="checkbox" id="checkbox-inverse" />
      </div>
    </div>

    <div id="canvas-container">
      <!-- Placeholder canvas as specified; replaced at runtime by p5 and re-created with the same id -->
      <canvas id="p5-canvas"></canvas>
    </div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // Internal state variables
    let rings = 1;
    let cpr = 8;
    let inverseMode = false;

    let locators = null;      // Array of two p5.Vector (set in setup)
    let draggedLocator = -1;  // -1 => none

    let baseCircles = [];     // Array of {x, y, r}
    let outerCenters = [];    // For guidelines

    let canvasEl = null;

    // Utility to read current UI values
    function readUI() {
      const ringsInput = document.querySelector('input[name="rings"]:checked');
      const cprInput = document.querySelector('input[name="cpr"]:checked');
      rings = ringsInput ? parseInt(ringsInput.value, 10) : 1;
      cpr = cprInput ? parseInt(cprInput.value, 10) : 8;
    }

    function addControlListeners(sketch) {
      const radios = document.querySelectorAll('input[name="rings"], input[name="cpr"]');
      radios.forEach(el => {
        el.addEventListener('change', () => {
          readUI();
          computeBaseGeometry(sketch);
          sketch.redraw();
        });
      });

      const inverseCheckbox = document.getElementById('checkbox-inverse');
      inverseCheckbox.addEventListener('change', (e) => {
        inverseMode = e.target.checked;
        toggleCanvasDragCursor();
        // Stop continuous draw unless dragging; redraw once with new mode
        sketch.noLoop();
        sketch.redraw();
      });
    }

    function toggleCanvasDragCursor() {
      if (!canvasEl) return;
      if (inverseMode) canvasEl.addClass('drag-available');
      else canvasEl.removeClass('drag-available');
    }

    function computeBaseGeometry(sketch) {
      baseCircles = [];
      outerCenters = [];

      const N = cpr;
      const M = rings;
      const R_max = sketch.width / 2 * 0.9;
      const sinTerm = Math.sin(Math.PI / N);
      const k = (1 - sinTerm) / (1 + sinTerm);
      const R_c = R_max / (1 + sinTerm);
      const r0 = R_c * sinTerm;

      // Save outer ring centers for guidelines
      for (let j = 0; j < N; j++) {
        const angle = j * (2 * Math.PI / N);
        const x = R_c * Math.cos(angle);
        const y = R_c * Math.sin(angle);
        outerCenters.push({ x, y });
      }

      for (let i = 0; i < M; i++) {
        const scale = Math.pow(k, i);
        const Rc_i = R_c * scale;
        const r_i = r0 * scale;

        for (let j = 0; j < N; j++) {
          const angle = j * (2 * Math.PI / N);
          const x = Rc_i * Math.cos(angle);
          const y = Rc_i * Math.sin(angle);
          baseCircles.push({ x, y, r: r_i });
        }
      }
    }

    // p5 sketch (global mode)
    new p5((sketch) => {
      sketch.setup = function () {
        // Remove placeholder canvas and create a proper p5 canvas with the same id
        const placeholder = document.getElementById('p5-canvas');
        if (placeholder) placeholder.remove();

        const c = sketch.createCanvas(550, 550);
        c.id('p5-canvas');
        c.parent('canvas-container');
        canvasEl = c;

        sketch.angleMode(sketch.RADIANS);
        sketch.noLoop();

        // Initial locators (p5.Vector objects) relative to center
        locators = [sketch.createVector(-50, 0), sketch.createVector(50, 0)];
        draggedLocator = -1;

        readUI();
        computeBaseGeometry(sketch);
        addControlListeners(sketch);
        toggleCanvasDragCursor();

        sketch.redraw();
      };

      sketch.draw = function () {
        // Background and coordinate transform
        sketch.background('#C1D4E6');
        sketch.push();
        sketch.translate(sketch.width / 2, sketch.height / 2);

        if (!inverseMode) {
          drawNormal(sketch);
        } else {
          drawInverse(sketch);
        }

        sketch.pop();
      };

      function drawNormal(sketch) {
        // Guidelines from center to outermost ring centers
        sketch.stroke('#A0A0A0');
        sketch.strokeWeight(1);
        outerCenters.forEach(c => {
          sketch.line(0, 0, c.x, c.y);
        });

        // Circles: grey fill and black outline
        sketch.stroke(0);
        sketch.fill('#8C929D');
        baseCircles.forEach(c => {
          sketch.circle(c.x, c.y, c.r * 2);
        });
      }

      function drawInverse(sketch) {
        // Inversion circle from two draggable locators
        const pA = locators[0];
        const pB = locators[1];
        const C_inv = p5.Vector.add(pA, pB).mult(0.5);
        const R_inv = p5.Vector.dist(pA, pB) * 0.5;

        // Connecting line between locators
        sketch.stroke('#FF0000');
        sketch.strokeWeight(1.2);
        sketch.line(pA.x, pA.y, pB.x, pB.y);

        // Inversion circle (semi-transparent red)
        sketch.stroke(0);
        sketch.fill('rgba(255, 0, 0, 0.3)');
        sketch.circle(C_inv.x, C_inv.y, R_inv * 2);

        // Locators (red dots)
        sketch.noStroke();
        sketch.fill('#FF0000');
        sketch.circle(pA.x, pA.y, 8);
        sketch.circle(pB.x, pB.y, 8);

        // Invert each original circle
        baseCircles.forEach(circ => {
          drawInvertedCircleOrLine(sketch, circ, C_inv, R_inv);
        });
      }

      function drawInvertedCircleOrLine(sketch, circ, C_inv, R_inv) {
        const C_orig = sketch.createVector(circ.x, circ.y);
        const r_orig = circ.r;

        const v = p5.Vector.sub(C_orig, C_inv);
        const d = v.mag();
        const eps = sketch.width * 0.001;

        if (R_inv < eps) {
          // Degenerate inversion radius; nothing to draw
          return;
        }

        if (Math.abs(d - r_orig) < eps) {
          // Inverse is a line: perpendicular to (C_inv -> C_orig), at distance s from C_inv
          if (d < eps) return; // Avoid undefined direction
          const s = (R_inv * R_inv) / (2 * d);
          const vHat = v.copy().mult(1 / d);
          const p0 = p5.Vector.add(C_inv, vHat.copy().mult(s)); // closest point to C_inv on the line
          const t = sketch.createVector(-vHat.y, vHat.x); // direction of the line (perpendicular to vHat)

          const L = sketch.width * 2; // long segment spanning canvas
          const a = p5.Vector.add(p0, t.copy().mult(L));
          const b = p5.Vector.sub(p0, t.copy().mult(L));

          sketch.noFill();
          sketch.stroke(0);
          sketch.strokeWeight(1);
          sketch.line(a.x, a.y, b.x, b.y);
        } else {
          // Inverse is a circle
          const denom = (d * d - r_orig * r_orig);
          const factor = (R_inv * R_inv) / denom;

          const C_new = p5.Vector.add(C_inv, v.copy().mult(factor));
          const r_new = Math.abs(factor) * r_orig;

          sketch.stroke(0);
          sketch.strokeWeight(1);
          sketch.fill('rgba(220, 100, 220, 0.5)');
          sketch.circle(C_new.x, C_new.y, r_new * 2);
        }
      }

      // Mouse interactions for dragging locators in inverse mode
      sketch.mousePressed = function () {
        if (!inverseMode) return;
        const m = sketch.createVector(sketch.mouseX - sketch.width / 2, sketch.mouseY - sketch.height / 2);
        const hitRadius = 10;
        for (let i = 0; i < 2; i++) {
          if (p5.Vector.dist(m, locators[i]) <= hitRadius) {
            draggedLocator = i;
            sketch.loop(); // Start continuous updates while dragging
            return;
          }
        }
      };

      sketch.mouseDragged = function () {
        if (!inverseMode) return;
        if (draggedLocator >= 0) {
          const m = sketch.createVector(sketch.mouseX - sketch.width / 2, sketch.mouseY - sketch.height / 2);
          locators[draggedLocator].set(m.x, m.y);
          // In continuous draw mode due to loop(); nothing else needed
        }
      };

      sketch.mouseReleased = function () {
        if (!inverseMode) return;
        if (draggedLocator >= 0) {
          draggedLocator = -1;
          sketch.noLoop();  // Stop continuous updates
          sketch.redraw();  // Final render
        }
      };
    });

  </script>
</body>
</html>
