<body>
  <div id="app">
    <!-- Control Panel -->
    <div id="control-panel">
      <div class="slider-group">
        <label for="slider-logCa"><span>\(C_a\)</span></label>
        <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
        <div class="value" id="val-logCa"></div>
      </div>
      <div class="slider-group">
        <label for="slider-pka"><span>\(pK_a\)</span></label>
        <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
        <div class="value" id="val-pka"></div>
      </div>
    </div>

    <!-- Visualization Area -->
    <div id="viz-container">
      <!-- MathJax formula overlays -->
      <div id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
      <div id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
    </div>
  </div>

  <script>
    // State variables from sliders
    let logCa = -1;     // slider-logCa
    let pKa = 1.2;      // slider-pka

    // Plot ranges
    const xMin = -4, xMax = 7;   // pKa
    const yMin = 0,  yMax = 7;   // pH

    // Layout margins inside canvas
    let M = {left: 70, right: 26, top: 28, bottom: 48};

    // Cached exact curve points (recomputed when logCa changes)
    let curvePoints = [];

    // DOM references
    const sliderLogCa = document.getElementById('slider-logCa');
    const sliderPka   = document.getElementById('slider-pka');
    const valLogCa    = document.getElementById('val-logCa');
    const valPka      = document.getElementById('val-pka');
    const vizContainer= document.getElementById('viz-container');
    const fStrong     = document.getElementById('formula-strong');
    const fWeak       = document.getElementById('formula-weak');

    // Utility: 10^x
    const pow10 = x => Math.pow(10, x);
    const clamp = (v, lo, hi) => Math.max(lo, Math.min(hi, v));

    // Format helpers
    function fmt(n, d=2){ return Number(n).toFixed(d); }

    function updateValueDisplays(){
      const Ca = pow10(logCa);
      valLogCa.textContent = `log10(C_a) = ${fmt(logCa,2)}    |    C_a = ${fmt(Ca,3)} M`;
      valPka.textContent   = `pK_a = ${fmt(pKa,2)}    |    K_a = ${fmt(pow10(-pKa),3)}`;
    }

    // Exact [H+] and pH
    function exact_pH(pKaVal, logCaVal){
      const Ka = pow10(-pKaVal);
      const Ca = pow10(logCaVal);
      // Positive root of H^2 + Ka*H - Ka*Ca = 0
      const disc = Math.sqrt(Ka*Ka + 4*Ka*Ca);
      const H = 0.5 * (-Ka + disc);
      const pH = -Math.log10(H);
      return pH;
    }

    function recomputeCurve(){
      curvePoints = [];
      const step = 0.02;   // fine resolution
      for(let x = xMin; x <= xMax; x += step){
        let y = exact_pH(x, logCa);
        // restrict to plotting range but keep endpoints for continuity
        y = clamp(y, yMin, yMax);
        curvePoints.push([x, y]);
      }
    }

    // p5.js sketch (global mode)
    let cnv;
    function setup(){
      // Create canvas sized to container width with a pleasant height
      const w = vizContainer.clientWidth - 16; // account for container padding
      const h = Math.max(360, Math.round(w*0.55));
      cnv = createCanvas(w, h);
      cnv.parent(vizContainer);
      pixelDensity(1);
      noLoop();
      recomputeCurve();
      updateValueDisplays();
      redraw(); // first draw
    }

    function windowResized(){
      const w = vizContainer.clientWidth - 16;
      const h = Math.max(360, Math.round(w*0.55));
      resizeCanvas(w, h);
      redraw();
    }

    // Coordinate transforms
    function xToPx(x){
      return map(x, xMin, xMax, M.left, width - M.right);
    }
    function yToPx(y){
      return map(y, yMin, yMax, height - M.bottom, M.top);
    }

    function drawAxes(){
      stroke(74); strokeWeight(1);
      fill(60); textSize(12); textAlign(CENTER, CENTER);

      // Axis lines crossing at pH=0 (x-axis) and pKa=0 (y-axis)
      const y0 = yToPx(0);
      const x0 = xToPx(0);
      // x-axis
      line(M.left, y0, width - M.right, y0);
      // y-axis
      line(x0, height - M.bottom, x0, M.top);

      // Ticks for x (pKa)
      for(let x=Math.ceil(xMin); x<=Math.floor(xMax); x+=1){
        const px = xToPx(x);
        line(px, y0-4, px, y0+4);
        if (x !== 0 && x % 1 === 0){
          noStroke(); fill(80);
          text(x, px, y0+16);
          stroke(74);
        }
      }
      // Ticks for y (pH)
      textAlign(RIGHT, CENTER);
      for(let y=0; y<=7; y+=1){
        const py = yToPx(y);
        line(x0-4, py, x0+4, py);
        if (y !== 0){
          noStroke(); fill(80);
          text(y, x0-10, py);
          stroke(74);
        }
      }

      // Axis labels
      noStroke(); fill(70); textSize(13);
      textAlign(CENTER, BOTTOM);
      text("pH", x0, M.top - 6);
      textAlign(RIGHT, CENTER);
      text("pK_a", width - M.right - 6, height - M.bottom + 24);
    }

    function drawStrongLine(){
      const y = -logCa;
      const py = yToPx(y);
      stroke(color('maroon')); strokeWeight(3);
      // full horizontal line within domain (clip to plot area)
      const yClamped = clamp(y, yMin, yMax);
      const pyClamped = yToPx(yClamped);
      line(M.left, pyClamped, width - M.right, pyClamped);
    }

    function drawWeakLine(){
      stroke(color('green')); strokeWeight(3);
      const x1 = xMin, x2 = xMax;
      const y1 = 0.5*(x1 - logCa);
      const y2 = 0.5*(x2 - logCa);
      const p1 = {x:xToPx(x1), y:yToPx(y1)};
      const p2 = {x:xToPx(x2), y:yToPx(y2)};
      line(p1.x, p1.y, p2.x, p2.y);
    }

    function drawExactCurve(){
      noFill();
      stroke(color('#1773e6')); // blue
      strokeWeight(4);
      beginShape();
      for(const [x, y] of curvePoints){
        const px = xToPx(x);
        const py = yToPx(y);
        vertex(px, py);
      }
      endShape();
    }

    function drawIndicator(){
      const y = exact_pH(pKa, logCa);
      const px = xToPx(pKa);
      const py = yToPx(clamp(y, yMin, yMax));
      stroke(60); strokeWeight(1.5);
      fill(color('#ff9a2b'));
      circle(px, py, 12);
    }

    function draw(){
      background(255);
      // Border rectangle for plot area (optional subtle)
      noFill(); stroke(220); rect(M.left, M.top, width - M.left - M.right, height - M.top - M.bottom);

      drawAxes();
      drawStrongLine();
      drawWeakLine();
      drawExactCurve();
      drawIndicator();
      positionFormulas(); // sync overlay positions
    }

    function positionFormulas(){
      // Position strong-acid formula near left of the red line
      const xs = -2.6;
      let ys = -logCa + 0.15;
      ys = clamp(ys, yMin + 0.4, yMax - 0.4);
      const sLeft = xToPx(xs), sTop = yToPx(ys);
      // Offset a bit so text does not sit exactly on the line
      fStrong.style.left = (sLeft - 70) + "px";
      fStrong.style.top  = (sTop - 16) + "px";

      // Position weak-acid formula roughly along its line at x ~ 4
      const xw = 4;
      let yw = 0.5*(xw - logCa) + 0.5; // slightly above the line
      yw = clamp(yw, yMin + 0.5, yMax - 0.5);
      const wLeft = xToPx(xw), wTop = yToPx(yw);
      fWeak.style.left = (wLeft - 10) + "px";
      fWeak.style.top  = (wTop - 20) + "px";
    }

    // Slider interactions
    sliderLogCa.addEventListener('input', (e)=>{
      logCa = parseFloat(e.target.value);
      updateValueDisplays();
      recomputeCurve();    // strong + weak + exact curves change with C_a
      redraw();
    });

    sliderPka.addEventListener('input', (e)=>{
      pKa = parseFloat(e.target.value);
      updateValueDisplays();
      // Only the indicator position depends on pKa; we still redraw the canvas.
      redraw();
    });

    // Initial value labels (in case p5 hasn't run yet)
    updateValueDisplays();
  </script>
</body>
</html>
