    border:1px solid #bbb;
    box-shadow:0 1px 2px rgba(0,0,0,.08);
    position:absolute;
    transform:translate(-50%,-50%);
    cursor:grab;
  }
  .joystick-handle.dragging{
    cursor:grabbing;
  }
  select, input[type="range"]{
    width:100%;
    box-sizing:border-box;
  }
  /* Nice radios */
  input[type="radio"]{
    accent-color: var(--accent);
  }

  /* Canvas inner frame outline (p5 draws content; this is decorative) */
  #canvas-inner-frame{
    position:absolute;
    inset:18px;
    border:1px solid #cfcfcf;
    border-radius:2px;
    pointer-events:none;
  }
</style>
</head>
<body>

<div id="main-container">

