  <!-- p5.js -->
  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
  <script>
    // DOM references
    const sliderQ = document.getElementById('slider-source-charge');
    const sliderq = document.getElementById('slider-test-charge');
    const labelQ  = document.getElementById('label-source-charge');
    const labelq  = document.getElementById('label-test-charge');

    const pE  = document.getElementById('display-E-mag');
    const pEx = document.getElementById('display-Ex');
    const pEy = document.getElementById('display-Ey');
    const pX  = document.getElementById('display-x-pos');
    const pY  = document.getElementById('display-y-pos');
    const pF  = document.getElementById('display-force');

    // Simulation state
    let Q = parseFloat(sliderQ.value);          // source charge
    let q = parseFloat(sliderq.value);          // test charge
    const k = 1.0;                              // Coulomb constant (normalized)
    let pos;                                    // position of test charge (world units, meters)

    // Canvas and rendering parameters
    const W = 600, H = 500;
    const worldRange = 7;          // +/- world units visible
    const s = 40;                  // pixels per meter (scale)
    const vectorScalePx = 400;     // pixels per (N/C) used to draw vectors
    const arrowWorldScale = vectorScalePx / s; // convert vector to world units under current transform
    const locatorRadiusPx = 8;
    const sourceRadiusPx = 7;

    let dragging = false;

    // Format helpers
    function fmt(n, d) {
      // Standard rounding with fixed decimals
      return Number(n).toFixed(d);
    }
    function clamp(v, a, b) { return Math.max(a, Math.min(b, v)); }

    // World/screen conversion (without relying on current transform matrix)
    function worldToScreen(v) {
      return createVector(W/2 + v.x * s, H/2 - v.y * s);
    }
    function screenToWorld(mx, my) {
      return createVector((mx - W/2)/s, (H/2 - my)/s);
    }

    // Physics
    function computeEAt(posVec) {
      const r2 = posVec.x*posVec.x + posVec.y*posVec.y;
      if (r2 < 1e-9) return createVector(0, 0);
      const r = Math.sqrt(r2);
      const factor = k * Q / (r*r*r); // Q * r_vec / r^3
      return createVector(posVec.x * factor, posVec.y * factor);
    }

    // Draw an arrow in WORLD coordinates (after world transform is applied)
    function drawArrowWorld(base, vec, colorStr) {
      const end = p5.Vector.add(base, vec);
      stroke(colorStr);
      strokeWeight(2);
      fill(colorStr);

      // shaft
      line(base.x, base.y, end.x, end.y);

      // arrowhead geometry in world units (convert from px to world)
      const headLen = 10 / s;
      const headWidth = 7 / s;

      const v = vec.copy();
      const L = v.mag();
      if (L < 1e-6) return;

      const n = v.copy().mult(1 / L); // unit vector along arrow
      const perp = createVector(-n.y, n.x);

      const tip = end;
      const left = p5.Vector.add(p5.Vector.sub(tip, p5.Vector.mult(n, headLen)), p5.Vector.mult(perp, headWidth));
      const right = p5.Vector.add(p5.Vector.sub(tip, p5.Vector.mult(n, headLen)), p5.Vector.mult(perp, -headWidth));

      noStroke();
      triangle(tip.x, tip.y, left.x, left.y, right.x, right.y);
    }

    // p5.js sketch
    let canvas;
    function setup() {
      canvas = createCanvas(W, H);
      canvas.parent('canvas-container');
      pos = createVector(2, 3);

      // Slider handlers
      sliderQ.addEventListener('input', () => {
        Q = parseFloat(sliderQ.value);
        labelQ.textContent = Q.toFixed(1);
      });
      sliderq.addEventListener('input', () => {
        q = parseFloat(sliderq.value);
        labelq.textContent = q.toFixed(4);
      });
    }

    function drawGridAndAxes() {
      push();
      translate(width/2, height/2);
      scale(s, -s); // world meters -> pixels, y up

      // minor grid (every 1 unit)
      stroke(220);
      strokeWeight(1 / s);
      for (let x = -worldRange; x <= worldRange; x += 1) {
        line(x, -worldRange, x, worldRange);
      }
      for (let y = -worldRange; y <= worldRange; y += 1) {
        line(-worldRange, y, worldRange, y);
      }

      // major grid (every 2 units)
      stroke(190);
      strokeWeight(1.5 / s);
      for (let x = -worldRange; x <= worldRange; x += 2) {
        line(x, -worldRange, x, worldRange);
      }
      for (let y = -worldRange; y <= worldRange; y += 2) {
        line(-worldRange, y, worldRange, y);
      }

      // axes
      stroke(120);
      strokeWeight(2 / s);
      line(-worldRange, 0, worldRange, 0);
      line(0, -worldRange, 0, worldRange);

      pop();

      // Axis ticks and numbers in screen space for crisp text
      textSize(12);
      textAlign(CENTER, TOP);
      fill(80);
      noStroke();
      for (let x = -worldRange; x <= worldRange; x++) {
        const pt = worldToScreen(createVector(x, 0));
        // small tick marks
        stroke(150);
        line(pt.x, H/2 - 3, pt.x, H/2 + 3);
        noStroke();
        if (x !== 0 && x % 1 === 0) text(x, pt.x, H/2 + 6);
      }
      textAlign(LEFT, CENTER);
      for (let y = -worldRange; y <= worldRange; y++) {
        const pt = worldToScreen(createVector(0, y));
        stroke(150);
        line(W/2 - 3, pt.y, W/2 + 3, pt.y);
        noStroke();
        if (y !== 0 && y % 1 === 0) text(y, W/2 + 6, pt.y - 6);
      }
    }

    function drawScene() {
      background(255);

      drawGridAndAxes();

      // Draw source charge at origin and test charge locator (world space)
      push();
      translate(width/2, height/2);
      scale(s, -s);

      // Source charge (origin)
      noStroke();
      fill(32, 124, 238); // blue
      const srcR = sourceRadiusPx / s;
      circle(0, 0, 2 * srcR);

      // Test charge locator
      fill(0);
      const locR = locatorRadiusPx / s;
      circle(pos.x, pos.y, 2 * locR);

      // Compute vectors
      const E = computeEAt(pos);
      const F = createVector(E.x * q, E.y * q);

      // Draw vectors from locator
      const E_draw = E.copy().mult(arrowWorldScale);
      const F_draw = F.copy().mult(arrowWorldScale);

      if (E_draw.magSq() > 0) {
        drawArrowWorld(pos, E_draw, '#d33'); // red for E
      }
      if (Math.abs(q) > 1e-10 && F_draw.magSq() > 0) {
        drawArrowWorld(pos, F_draw, '#1f76d2'); // blue for F
      }

      pop();

      // Update info panel
      const Emag = Math.hypot(E.x, E.y);
      const Fmag = Math.abs(q) * Emag;

      pE.textContent  = `|E| = ${fmt(Emag, 3)} N/C`;
      pEx.innerHTML   = `E<sub>x</sub> component = ${fmt(E.x, 3)} N/C`;
      pEy.innerHTML   = `E<sub>y</sub> component = ${fmt(E.y, 3)} N/C`;
      pX.textContent  = `x position = ${fmt(pos.x, 2)} m`;
      pY.textContent  = `y position = ${fmt(pos.y, 2)} m`;
      pF.textContent  = `Electric force = ${fmt(Fmag, 3)} N`;
    }

    function draw() {
      drawScene();
    }

    // Interaction: dragging the locator
    function mousePressed() {
      // Only start drag if mouse is over the locator and within canvas
      if (mouseX < 0 || mouseX > W || mouseY < 0 || mouseY > H) return;
      const screenPos = worldToScreen(pos);
      const d = dist(mouseX, mouseY, screenPos.x, screenPos.y);
      if (d <= locatorRadiusPx + 3) {
        dragging = true;
      }
    }

    function mouseDragged() {
      if (!dragging) return;
      const w = screenToWorld(mouseX, mouseY);
      // Clamp to logical bounds
      pos.x = clamp(w.x, -worldRange, worldRange);
      pos.y = clamp(w.y, -worldRange, worldRange);
    }

    function mouseReleased() {
      dragging = false;
    }
  </script>
</body>
</html>
