      <label for="checkbox-gaussian">Gaussian</label>
    </div>

    <div id="scale-selector">
      <label>axes</label>
      <button id="btn-linear-linear" class="active">linear-linear</button>
      <button id="btn-log-linear">log-linear</button>
      <button id="btn-log-log">log-log</button>
    </div>

    <div id="sliders-container">
      <div id="slider-r-control">
        <label for="slider-r">radius r</label>
        <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
        <span id="span-r-value">2.5</span>
      </div>
      <div id="slider-k-control">
        <label for="slider-k">wave number k</label>
        <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
        <span id="span-k-value">50</span>
      </div>
    </div>
  </div>

  <p id="hover-info">(Hover over the curves to see math labels.)</p>

  <div id="plots-container">
    <div id="plot-left"></div>
    <div id="plot-right"></div>
  </div>

  <script>
    // Utility
    function linspace(start, end, n) {
      if (n < 2) return [start];
      const arr = new Array(n);
      const step = (end - start) / (n - 1);
      for (let i = 0; i < n; i++) arr[i] = start + i * step;
      return arr;
    }

    // Real-space functions
    function fHertz(r) { return (r <= 1) ? Math.pow(1 - r*r, 2.5) : 0; }
    function fOverlap(r) { return (r <= 1) ? Math.pow(1 - r, 2) : 0; }
    function fGaussian(r) { return Math.exp(-Math.PI * r * r); }

    // Fourier transforms (3D, analytical). Handle k=0 limits explicitly.
    function FH(k){
      if (k === 0) return Math.PI; // limit
      return 15 * Math.PI * ( (3 - k*k) * Math.sin(k) - 3 * k * Math.cos(k) ) / Math.pow(k,5);
    }
    // Note: using a numerically stable (finite at k=0) form for overlap
    function FO(k){
      if (k === 0) return 2 * Math.PI; // limit
      // Finite expression; avoids divergence and permits log scaling
      return 8 * Math.PI * (2 + k*k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k,4);
    }
    function FG(k){
      return (1/Math.sqrt(Math.PI)) * Math.exp(- (k*k) / (4*Math.PI));
    }

    // Hover templates with LaTeX
    const hoverLeft = {
      hertz: 'Hertz<br>$f_H(r) = (1 - r^2)^{5/2},\\ r \\le 1$<br>r=%{x:.3g}, f=%{y:.3g}<extra></extra>',
      overlap: 'overlap<br>$f_O(r) = (1 - r)^2,\\ r \\le 1$<br>r=%{x:.3g}, f=%{y:.3g}<extra></extra>',
      gaussian: 'Gaussian<br>$f_G(r) = e^{-\\pi r^2}$<br>r=%{x:.3g}, f=%{y:.3g}<extra></extra>'
    };
    const hoverRight = {
      hertz: 'Hertz<br>$F_H(k) = 15\\pi\\, \\dfrac{(3-k^2)\\sin k - 3k\\cos k}{k^5}$<br>k=%{x:.3g}, F=%{y:.3g}<extra></extra>',
      overlap: 'overlap<br>$F_O(k) = 8\\pi\\, \\dfrac{2 + k^2 - 2\\cos k - 2k\\sin k}{k^4}$<br>k=%{x:.3g}, F=%{y:.3g}<extra></extra>',
      gaussian: 'Gaussian<br>$F_G(k) = \\dfrac{1}{\\sqrt{\\pi}}\\, e^{-k^2/(4\\pi)}$<br>k=%{x:.3g}, F=%{y:.3g}<extra></extra>'
    };

    // Colors
    const colors = {
      hertz: '#ff0000',
      overlap: '#000000',
      gaussian: '#0000ff'
    };

    function activeScale() {
      if (document.getElementById('btn-log-log').classList.contains('active')) return 'log-log';
      if (document.getElementById('btn-log-linear').classList.contains('active')) return 'log-linear';
      return 'linear-linear';
    }

    function init() {
      // Attach event listeners
      document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
      document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
      document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

      document.getElementById('btn-linear-linear').addEventListener('click', () => { setScaleButton('btn-linear-linear'); updatePlots(); });
      document.getElementById('btn-log-linear').addEventListener('click', () => { setScaleButton('btn-log-linear'); updatePlots(); });
      document.getElementById('btn-log-log').addEventListener('click', () => { setScaleButton('btn-log-log'); updatePlots(); });

      document.getElementById('slider-r').addEventListener('input', updatePlots);
      document.getElementById('slider-k').addEventListener('input', updatePlots);

      // Create empty plots first to ensure layout, then update
      Plotly.newPlot('plot-left', [], {
        title: {text: 'function', font: {color: '#b0b0b0'}},
        xaxis: {title: 'r', zeroline: false},
        yaxis: {rangemode: 'tozero', zeroline: false},
        margin: {l: 50, r: 20, t: 40, b: 40},
        showlegend: false
      }, {displayModeBar: false, responsive: true});

      Plotly.newPlot('plot-right', [], {
        title: {text: '3D Fourier transform (real part)', font: {color: '#b0b0b0'}},
        xaxis: {title: 'k', zeroline: false},
        yaxis: {type: 'linear', zeroline: false},
        margin: {l: 50, r: 20, t: 40, b: 40},
        showlegend: false
      }, {displayModeBar: false, responsive: true});

      updatePlots();
    }

    function setScaleButton(activeId){
      ['btn-linear-linear','btn-log-linear','btn-log-log'].forEach(id=>{
        document.getElementById(id).classList.toggle('active', id === activeId);
      });
    }

    function updatePlots(){
      const showH = document.getElementById('checkbox-hertz').checked;
      const showO = document.getElementById('checkbox-overlap').checked;
      const showG = document.getElementById('checkbox-gaussian').checked;

      const scaleMode = activeScale();

      const rMax = parseFloat(document.getElementById('slider-r').value);
      const kMax = parseFloat(document.getElementById('slider-k').value);

      document.getElementById('span-r-value').textContent = rMax.toFixed(1);
      document.getElementById('span-k-value').textContent = kMax.toFixed(2);

      // Left plot data (real space)
      const rVals = linspace(0, rMax, 200);
      const yH = showH ? rVals.map(r => fHertz(r)) : [];
      const yO = showO ? rVals.map(r => fOverlap(r)) : [];
      const yG = showG ? rVals.map(r => fGaussian(r)) : [];

      const tracesLeft = [
        {x: showH ? rVals : [], y: yH, mode:'lines', line:{color:colors.hertz, width:2}, hovertemplate: hoverLeft.hertz, name: 'Hertz'},
        {x: showO ? rVals : [], y: yO, mode:'lines', line:{color:colors.overlap, width:2}, hovertemplate: hoverLeft.overlap, name: 'overlap'},
        {x: showG ? rVals : [], y: yG, mode:'lines', line:{color:colors.gaussian, width:2}, hovertemplate: hoverLeft.gaussian, name: 'Gaussian'}
      ];

      const layoutLeft = {
        title: {text: 'function', font: {color: '#b0b0b0'}},
        xaxis: {title: 'r', range: [0, rMax], zeroline:false},
        yaxis: {rangemode: 'tozero', zeroline:false},
        margin: {l: 50, r: 20, t: 40, b: 40},
        showlegend: false
      };

      Plotly.react('plot-left', tracesLeft, layoutLeft, {displayModeBar:false, responsive:true});

      // Right plot data (Fourier space)
      const xType = (scaleMode === 'log-log') ? 'log' : 'linear';
      const yType = (scaleMode === 'linear-linear') ? 'linear' : 'log';

      // avoid x=0 when using log scale on x
      const kStart = (xType === 'log') ? 1e-3 : 0;
      const kVals = linspace(kStart, kMax, 200);

      const YH = showH ? kVals.map(k => FH(k)) : [];
      const YO = showO ? kVals.map(k => FO(k)) : [];
      const YG = showG ? kVals.map(k => FG(k)) : [];

      const tracesRight = [
        {x: showH ? kVals : [], y: YH, mode:'lines', line:{color:colors.hertz, width:2}, hovertemplate: hoverRight.hertz, name:'Hertz'},
        {x: showO ? kVals : [], y: YO, mode:'lines', line:{color:colors.overlap, width:2}, hovertemplate: hoverRight.overlap, name:'overlap'},
        {x: showG ? kVals : [], y: YG, mode:'lines', line:{color:colors.gaussian, width:2}, hovertemplate: hoverRight.gaussian, name:'Gaussian'}
      ];

      const layoutRight = {
        title: {text: '3D Fourier transform (real part)', font: {color: '#b0b0b0'}},
        xaxis: {title: 'k', type: xType, range: (xType === 'linear') ? [0, kMax] : undefined, zeroline:false},
        yaxis: {type: yType, zeroline:false},
        margin: {l: 50, r: 20, t: 40, b: 40},
        showlegend: false
      };

      Plotly.react('plot-right', tracesRight, layoutRight, {displayModeBar:false, responsive:true});

      // Re-typeset tooltips (MathJax)
      if (window.MathJax && MathJax.typeset) {
        // Use a small delay to allow Plotly to place hover labels
        setTimeout(()=>MathJax.typeset(), 0);
      }
    }

    window.addEventListener('DOMContentLoaded', init);
  </script>
</body>
