    <label for="slider-compensation-cost">compensation costs per ton of CO₂ ($/ton)</label>
    <div class="row">
      <input type="range" id="slider-compensation-cost" min="5" max="100" step="1" value="15">
      <span id="value-compensation-cost" class="value">15</span>
    </div>

    <label for="slider-fractional-compensation">fractional compensation (%)</label>
    <div class="row">
      <input type="range" id="slider-fractional-compensation" min="0" max="100" step="1" value="50">
      <span id="value-fractional-compensation" class="value">50</span>
    </div>

    <label for="slider-constant-compensation">constant compensation (%)</label>
    <div class="row">
      <input type="range" id="slider-constant-compensation" min="0" max="100" step="1" value="25">
      <span id="value-constant-compensation" class="value">25</span>
    </div>
  </div>

  <div id="vis-panel">
    <div id="climate-neutral-buttons">
      <button id="btn-neutral-now">now</button>
      <button id="btn-neutral-2030">2030</button>
      <button id="btn-neutral-2040" class="active">2040</button>
      <button id="btn-neutral-2050">2050</button>
      <button id="btn-neutral-never">never</button>
    </div>

    <div id="main-plot-container"></div>
    <div id="budget-plot-container"></div>
  </div>

</div>

<script>
  // Global state
  const years = [];
  for(let y=2020;y<=2050;y++) years.push(y);

  const state = {
    scale: 'individual',
    populationK: 250,
    type: 'emissions',
    initial_emissions_person: 14,
    reduction_half_time: 20,
    annual_budget_person: 1.5,
    start_year: 2020,
    cost_per_ton: 15,
    fractional_comp_pct: 50,
    constant_comp_pct: 25,
    neutral_year: 2040
  };

  // References to p5 instances
  let p5Main = null;
  let p5Budget = null;

  // Computed model data shared by both sketches
  let modelData = null;

  function setNeutralYear(y){
    state.neutral_year = y;
    const btns = [
      ['btn-neutral-now',2020],
      ['btn-neutral-2030',2030],
      ['btn-neutral-2040',2040],
      ['btn-neutral-2050',2050],
      ['btn-neutral-never',null]
    ];
    btns.forEach(([id,val])=>{
      const b = document.getElementById(id);
      if((val===null && state.neutral_year===null) || val===state.neutral_year){
        b.classList.add('active');
      }else{
        b.classList.remove('active');
      }
    });
    update();
  }

  // UI wiring
  function wireControls(){
    // scale
    document.getElementById('scale-population').addEventListener('change', () => {
      state.scale = 'population';
      document.getElementById('slider-population').disabled = false;
      update();
    });
    document.getElementById('scale-individual').addEventListener('change', () => {
      state.scale = 'individual';
      document.getElementById('slider-population').disabled = true;
      update();
    });
    document.getElementById('slider-population').addEventListener('input', (e)=>{
      state.populationK = +e.target.value;
      document.getElementById('value-population').textContent = e.target.value;
      update();
    });

    // type
    document.getElementById('type-emissions').addEventListener('change', ()=>{
      state.type = 'emissions'; update();
    });
    document.getElementById('type-costs').addEventListener('change', ()=>{
      state.type = 'costs'; update();
    });

    // sliders
    document.getElementById('slider-annual-emissions').addEventListener('input', (e)=>{
      state.initial_emissions_person = +e.target.value;
      document.getElementById('value-annual-emissions').textContent = (+e.target.value).toFixed(1);
      update();
    });
    document.getElementById('slider-reduction-time').addEventListener('input', (e)=>{
      state.reduction_half_time = +e.target.value;
      document.getElementById('value-reduction-time').textContent = e.target.value;
      update();
    });
    document.getElementById('slider-annual-budget').addEventListener('input', (e)=>{
      state.annual_budget_person = +e.target.value;
      document.getElementById('value-annual-budget').textContent = (+e.target.value).toFixed(1);
      update();
    });
    document.getElementById('slider-start-year').addEventListener('input', (e)=>{
      state.start_year = +e.target.value;
      document.getElementById('value-start-year').textContent = e.target.value;
      update();
    });
    document.getElementById('slider-compensation-cost').addEventListener('input', (e)=>{
      state.cost_per_ton = +e.target.value;
      document.getElementById('value-compensation-cost').textContent = e.target.value;
      update();
    });
    document.getElementById('slider-fractional-compensation').addEventListener('input', (e)=>{
      state.fractional_comp_pct = +e.target.value;
      document.getElementById('value-fractional-compensation').textContent = e.target.value;
      update();
    });
    document.getElementById('slider-constant-compensation').addEventListener('input', (e)=>{
      state.constant_comp_pct = +e.target.value;
      document.getElementById('value-constant-compensation').textContent = e.target.value;
      update();
    });

    // climate neutral buttons
    document.getElementById('btn-neutral-now').addEventListener('click', ()=>setNeutralYear(2020));
    document.getElementById('btn-neutral-2030').addEventListener('click', ()=>setNeutralYear(2030));
    document.getElementById('btn-neutral-2040').addEventListener('click', ()=>setNeutralYear(2040));
    document.getElementById('btn-neutral-2050').addEventListener('click', ()=>setNeutralYear(2050));
    document.getElementById('btn-neutral-never').addEventListener('click', ()=>setNeutralYear(null));
  }

  function fmt(n, d=1){
    const sign = n<0 ? '-' : '';
    const abs = Math.abs(n);
    return sign + abs.toFixed(d);
  }

  function computeModel(){
    const N = years.length;
    const real_person = new Array(N);
    const comp_person = new Array(N);
    const eff_person = new Array(N);
    const annual_costs_person = new Array(N);

    // Per-person series
    for(let i=0;i<N;i++){
      const t = years[i];
      let real = (t < state.start_year) ? state.initial_emissions_person
        : state.initial_emissions_person * Math.pow(0.5, (t - state.start_year)/state.reduction_half_time);

      const fractional = real * (state.fractional_comp_pct/100);
      const constant = state.initial_emissions_person * (state.constant_comp_pct/100);
      const compensated = fractional + constant;
      const effective = real - compensated;

      real_person[i] = real;
      comp_person[i] = compensated;
      eff_person[i] = effective;
      annual_costs_person[i] = compensated * state.cost_per_ton;
    }

    // Scale conversion for display in emissions plots and budget (units)
    const scaleFactor = (state.scale === 'population') ? (state.populationK * 1000) / 1_000_000 : 1; // display in mil tons for population
    const yLabelEmissions = (state.scale === 'population')
      ? 'annual city emission of CO₂ (mil tons)'
      : 'annual CO₂ emissions per person (tons)';
    const budgetTitle = (state.scale === 'population')
      ? 'population climate budget'
      : 'individual climate budget';
    const budgetUnit = (state.scale === 'population') ? 'mil tons CO₂' : 'tons CO₂';

    const real_display = real_person.map(v => v * scaleFactor);
    const eff_display  = eff_person.map(v => v * scaleFactor);
    const comp_display = comp_person.map(v => v * scaleFactor);

    // Cumulative effective for display units
    const cumulative_display = {};
    let running = 0;
    for(let i=0;i<N;i++){
      running += eff_person[i]*scaleFactor; // sum in display units
      cumulative_display[years[i]] = running;
    }

    // Costs
    const total_costs = annual_costs_person.reduce((a,b)=>a+b,0);
    const avg_annual_costs = total_costs / N;

    return {
      real_person, comp_person, eff_person,
      real_display, comp_display, eff_display,
      annual_costs_person, total_costs, avg_annual_costs,
      scaleFactor, yLabelEmissions, budgetTitle, budgetUnit,
      cumulative_display
    };
  }

  function update(){
    modelData = computeModel();
    if(p5Main) p5Main.redraw();
    if(p5Budget) p5Budget.redraw();
  }

  // Helper: dashed line drawing
  function drawDashedLine(p, x1, y1, x2, y2, dash=6, gap=4){
    const dx = x2-x1, dy = y2-y1;
    const len = Math.hypot(dx,dy);
    const steps = Math.floor(len/(dash+gap));
    const ux = dx/len, uy=dy/len;
    let sx=x1, sy=y1;
    for(let i=0;i<steps;i++){
      const ex = sx + ux*dash, ey = sy + uy*dash;
      p.line(sx,sy,ex,ey);
      sx = ex + ux*gap;
      sy = ey + uy*gap;
    }
    p.line(sx,sy,x2,y2);
  }

  // p5 Sketch: Main Plot
  const mainSketch = (p)=>{
    let W=600, H=440;
    p.setup = function(){
      const cW = document.getElementById('main-plot-container').clientWidth - 12;
      W = Math.max(360, cW);
      H = Math.max(420, document.getElementById('main-plot-container').clientHeight);
      p.createCanvas(W, H).parent('main-plot-container');
      p.noLoop();
    };
    p.windowResized = function(){
      const cW = document.getElementById('main-plot-container').clientWidth - 12;
      W = Math.max(360, cW);
      H = Math.max(420, document.getElementById('main-plot-container').clientHeight);
      p.resizeCanvas(W, H);
      p.redraw();
    };
    function plotArea(){
      const m = {l:76, r:18, t:24, b:48};
      return {x:m.l, y:m.t, w:W-m.l-m.r, h:H-m.t-m.b, m};
    }
    function mapX(x, range){
      return p.map(x, 2020, 2050, range.x, range.x+range.w);
    }
    function mapY(y, range, yMin, yMax){
      return p.map(y, yMin, yMax, range.y+range.h, range.y);
    }
    function drawGrid(range, yMin, yMax){
      p.push(); p.stroke(230); p.strokeWeight(1);
      // vertical years each 5 years
      for(let y=2020; y<=2050; y+=5){
        const xx = mapX(y, range);
        p.line(xx, range.y, xx, range.y+range.h);
        p.noStroke(); p.fill(80);
        p.textAlign(p.CENTER, p.TOP);
        p.text(y, xx, range.y+range.h+6);
        p.stroke(230);
      }
      // horizontal grid 6 lines
      const n=6;
      for(let i=0;i<=n;i++){
        const val = p.lerp(yMin, yMax, i/n);
        const yy = mapY(val, range, yMin, yMax);
        p.stroke(230);
        p.line(range.x, yy, range.x+range.w, yy);
        p.noStroke(); p.fill(100);
        p.textAlign(p.RIGHT, p.CENTER);
        p.text(val.toFixed( (Math.abs(yMax-yMin)<10)?1:0 ), range.x-6, yy);
      }
      p.pop();
    }
    function drawFilledBetween(range, xs, y1, y2, yMin, yMax, col){
      p.push();
      p.noStroke(); p.fill(col);
      p.beginShape();
      for(let i=0;i<xs.length;i++){
        p.vertex(mapX(xs[i], range), mapY(y1[i], range, yMin, yMax));
      }
      for(let i=xs.length-1;i>=0;i--){
        p.vertex(mapX(xs[i], range), mapY(y2[i], range, yMin, yMax));
      }
      p.endShape(p.CLOSE);
      p.pop();
    }
    p.draw = function(){
      p.clear();
      if(!modelData) return;
      const range = plotArea();
      p.push();
      p.stroke(200); p.noFill();
      p.rect(range.x, range.y, range.w, range.h, 6);
      p.pop();

      if(state.type === 'emissions'){
        // Determine y range
        const yMax = Math.max(
          Math.max(...modelData.real_display),
          Math.max(...modelData.eff_display),
          0
        ) * 1.12 + 0.0001;
        const yMin = Math.min(
          Math.min(...modelData.eff_display),
          0
        ) - Math.abs(yMax)*0.12;

        drawGrid(range, yMin, yMax);

        // Shaded between real (red) and effective (black): compensated CO2
        drawFilledBetween(range, years, modelData.real_display, modelData.eff_display, yMin, yMax, p.color(70,140,220,90));

        // Shaded effective vs 0 (red or green)
        for(let i=0;i<years.length-1;i++){
          const xs=[years[i], years[i+1]];
          const eff=[modelData.eff_display[i], modelData.eff_display[i+1]];
          const mid=(eff[0]+eff[1])/2;
          const col = (mid>=0) ? p.color(232,76,60,90) : p.color(60,170,90,110);
          p.noStroke(); p.fill(col);
          const x1 = mapX(xs[0],range), x2=mapX(xs[1],range);
          const y1 = mapY(eff[0], range, yMin, yMax);
          const y2 = mapY(eff[1], range, yMin, yMax);
          const y0 = mapY(0, range, yMin, yMax);
          p.beginShape();
          p.vertex(x1,y0); p.vertex(x1,y1); p.vertex(x2,y2); p.vertex(x2,y0);
          p.endShape(p.CLOSE);
        }

        // Lines
        p.noFill();

        // Real emissions (red)
        p.stroke(231,76,60); p.strokeWeight(2.2);
        p.beginShape();
        years.forEach((yr,i)=>{
          p.vertex(mapX(yr,range), mapY(modelData.real_display[i], range, yMin, yMax));
        });
        p.endShape();

        // Effective emissions (black)
        p.stroke(20); p.strokeWeight(2.2);
        p.beginShape();
        years.forEach((yr,i)=>{
          p.vertex(mapX(yr,range), mapY(modelData.eff_display[i], range, yMin, yMax));
        });
        p.endShape();

        // Labels
        p.fill(231,76,60); p.noStroke(); p.textAlign(p.RIGHT, p.TOP);
        p.text("real CO₂ emissions", mapX(2050,range)-6, mapY(modelData.real_display.at(-1), range, yMin, yMax)-14);
        p.fill(60);
        p.textAlign(p.LEFT, p.CENTER);
        const midIdx = 6;
        p.text("effective\nCO₂ emissions",
               mapX(years[midIdx],range)-10, mapY(modelData.eff_display[midIdx], range, yMin, yMax)-6);
        p.fill(50,120,200);
        p.textAlign(p.LEFT, p.CENTER);
        p.text("compensated CO₂", mapX(2021,range)+6, mapY((modelData.real_display[1]+modelData.eff_display[1])/2, range, yMin, yMax));

        // Axes labels
        p.fill(60); p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        p.text("year", range.x+range.w/2, range.y+range.h+28);
        p.push(); p.translate(range.x-56, range.y+range.h/2); p.rotate(-Math.PI/2);
        p.text(modelData.yLabelEmissions, 0, 0);
        p.pop();

        // Climate neutral marker & cumulative dashed lines
        const y0 = mapY(0, range, yMin, yMax);
        if(state.neutral_year !== null){
          const xN = mapX(state.neutral_year, range);
          p.push();
          p.stroke(80); p.strokeWeight(1.5);
          drawDashedLine(p, xN, range.y, xN, range.y+range.h, 6,4);
          p.noStroke(); p.fill(40);
          p.textAlign(p.LEFT, p.CENTER);
          p.text("climate neutral\n" + state.neutral_year, xN+6, p.constrain(y0-20, range.y+16, range.y+range.h-30));
          p.pop();
        }

        // Horizontal dashed lines for cumulative emissions annotations
        const cumNeutral = (state.neutral_year===null) ? null : modelData.cumulative_display[state.neutral_year];
        const cum2050 = modelData.cumulative_display[2050];
        const yDash1 = range.y + range.h - 0.15*range.h;
        const yDash2 = range.y + range.h - 0.25*range.h;

        p.push();
        if(cumNeutral !== null){
          p.stroke(231,76,60); p.strokeWeight(1.5);
          drawDashedLine(p, range.x, yDash2, range.x+range.w, yDash2, 8,6);
          p.noStroke(); p.fill(231,76,60);
          p.textAlign(p.LEFT, p.BOTTOM);
          p.text("CO₂ emissions until " + state.neutral_year + " = " + fmt(cumNeutral,1) + " " + (state.scale==='population'?'mil ':'') + "tons CO₂",
            range.x+8, yDash2-4);
        }
        p.stroke(20); p.strokeWeight(1.5);
        drawDashedLine(p, range.x, yDash1, range.x+range.w, yDash1, 8,6);
        p.noStroke(); p.fill(30,120,60);
        p.textAlign(p.LEFT, p.BOTTOM);
        p.text("CO₂ emissions until 2050 = " + fmt(cum2050,1) + " " + (state.scale==='population'?'mil ':'') + "tons CO₂",
          range.x+8, yDash1-4);
        p.pop();

      } else { // Costs plot
        const series = modelData.annual_costs_person;
        const yMax = Math.max(...series) * 1.15 + 0.0001;
        const yMin = 0;

        drawGrid(range, yMin, yMax);

        // Area under line
        p.noStroke(); p.fill(70,140,220,90);
        p.beginShape();
        p.vertex(mapX(2020,range), mapY(0, range, yMin, yMax));
        years.forEach((yr,i)=>{
          p.vertex(mapX(yr,range), mapY(series[i], range, yMin, yMax));
        });
        p.vertex(mapX(2050,range), mapY(0, range, yMin, yMax));
        p.endShape(p.CLOSE);

        // Line
        p.stroke(60,120,220); p.strokeWeight(2.2); p.noFill();
        p.beginShape();
        years.forEach((yr,i)=>{
          p.vertex(mapX(yr,range), mapY(series[i], range, yMin, yMax));
        });
        p.endShape();

        // Average dashed line
        const yAvg = mapY(modelData.avg_annual_costs, range, yMin, yMax);
        p.push();
        p.stroke(50,80,120); p.strokeWeight(1.5);
        drawDashedLine(p, range.x, yAvg, range.x+range.w, yAvg, 8,6);
        p.noStroke(); p.fill(40,60,90);
        p.textAlign(p.LEFT, p.BOTTOM);
        p.text("annual personal compensation costs (average) = $" + modelData.avg_annual_costs.toFixed(2) + " per year",
               range.x+8, yAvg-4);
        p.pop();

        // Total costs label inside
        p.noStroke(); p.fill(40,60,90); p.textAlign(p.LEFT, p.TOP);
        p.text("total personal compensation costs (until 2050) = $" + modelData.total_costs.toFixed(2),
               range.x+10, range.y+range.h*0.45);

        // Axes labels
        p.fill(60); p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        p.text("year", range.x+range.w/2, range.y+range.h+28);
        p.push(); p.translate(range.x-56, range.y+range.h/2); p.rotate(-Math.PI/2);
        p.text("annual personal compensation costs ($)", 0, 0);
        p.pop();
      }
    };
  };

  // p5 Sketch: Budget Circles
  const budgetSketch = (p)=>{
    let W=600, H=180;
    p.setup = function(){
      const cW = document.getElementById('budget-plot-container').clientWidth - 12;
      W = Math.max(360, cW);
      H = Math.max(160, document.getElementById('budget-plot-container').clientHeight);
      p.createCanvas(W, H).parent('budget-plot-container');
      p.noLoop();
    };
    p.windowResized = function(){
      const cW = document.getElementById('budget-plot-container').clientWidth - 12;
      W = Math.max(360, cW);
      H = Math.max(160, document.getElementById('budget-plot-container').clientHeight);
      p.resizeCanvas(W,H);
      p.redraw();
    };
    function dashedCircle(cx, cy, r){
      p.push();
      p.noFill(); p.stroke(0); p.strokeWeight(1.5);
      const dashDeg = 12, gapDeg = 10;
      for(let a=0; a<360; a+=dashDeg+gapDeg){
        p.arc(cx, cy, 2*r, 2*r, p.radians(a), p.radians(a+dashDeg));
      }
      p.pop();
    }
    p.draw = function(){
      p.clear();
      if(!modelData) return;
      p.noStroke(); p.fill(40); p.textAlign(p.CENTER, p.TOP);
      p.text(modelData.budgetTitle, W/2, 6);

      // Years for circles
      const Ys = [2020,2030,2040,2050];
      const total_budget_display = 30 * state.annual_budget_person * modelData.scaleFactor; // in display units
      // Values and scaling for radii
      const values = Ys.map(Y=>{
        const val = total_budget_display - modelData.cumulative_display[Y];
        return val;
      });
      const maxAbs = Math.max(1e-6, ...values.map(v=>Math.abs(v)));
      const k = Math.min(60, (W-120)/8) / Math.sqrt(maxAbs); // dynamic scaling

      const slots = Ys.length;
      const gap = W/(slots+1);
      for(let i=0;i<Ys.length;i++){
        const cx = gap*(i+1);
        const cy = H*0.58;
        const val = values[i];
        const r = Math.max(14, k * Math.sqrt(Math.abs(val)));
        // Fill
        p.noStroke();
        p.fill(70,170,100);
        p.circle(cx, cy, r*2);

        // Edge style based on instantaneous effective emissions (per-person condition)
        // If effective_emissions_person(Y) <= 0 -> solid; else dashed outline
        const idx = years.indexOf(Ys[i]);
        const effInstant = modelData.eff_person[idx]; // per-person
        if(effInstant>0){
          dashedCircle(cx, cy, r);
        }else{
          p.noFill(); p.stroke(0); p.strokeWeight(1.5); p.circle(cx, cy, r*2);
        }

        // Text inside
        p.noStroke(); p.fill(25);
        p.textAlign(p.CENTER, p.CENTER);
        const txt = fmt(val,1) + " " + modelData.budgetUnit;
        p.text(txt, cx, cy);

        // Year label above
        p.fill(40); p.textAlign(p.CENTER, p.BOTTOM);
        p.text(Ys[i].toString(), cx, cy - r - 8);
      }
    };
  };

  // Initialize p5 instances
  function initSketches(){
    p5Main = new p5(mainSketch);
    p5Budget = new p5(budgetSketch);
  }

  // Initialize UI and render
  window.addEventListener('load', ()=>{
    wireControls();
    initSketches();
    update(); // initial draw
  });

  window.addEventListener('resize', ()=>{
    if(p5Main) p5Main.redraw();
    if(p5Budget) p5Budget.redraw();
  });
</script>
</body>
</html>
