<!-- Three.js + OrbitControls via CDN -->
<script src="https://unpkg.com/three@0.155.0/build/three.min.js"></script>
<script src="https://unpkg.com/three@0.155.0/examples/js/controls/OrbitControls.js"></script>
<script>
(function(){
  // DOM
  const container = document.getElementById('canvas-container');
  const slider = document.getElementById('slider-fold');
  const btnReset = document.getElementById('btn-reset-view');
  const btn1 = document.getElementById('btn-solid-1');
  const btn2 = document.getElementById('btn-solid-2');
  const btn3 = document.getElementById('btn-solid-3');
  const solidButtons = [btn1, btn2, btn3];

  // Three.js core
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xf0f0f0);

  const camera = new THREE.PerspectiveCamera(45, 1, 0.01, 1000);
  camera.position.set(3, 3, 5);

  const renderer = new THREE.WebGLRenderer({ antialias: true });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  renderer.setSize(container.clientWidth, container.clientHeight);
  container.appendChild(renderer.domElement);

  const controls = new THREE.OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;

  // Lights
  scene.add(new THREE.AmbientLight(0xffffff, 0.7));
  const dirLight = new THREE.DirectionalLight(0xffffff, 0.7);
  dirLight.position.set(3, 4, 6);
  scene.add(dirLight);

  // State
  let currentSolidGroup = null;
  let hinges = []; // {pivot, finalAngle}
  let faces = [];  // {mesh, container, vertices:[Vector3...], foldedColor:Color}
  const unfoldedColor = new THREE.Color('#F5E0E0');
  const edgeColor = new THREE.Color('#333333');
  let currentSolidIndex = 1;

  // Utilities
  function addEdges(mesh, color = edgeColor) {
    const eGeom = new THREE.EdgesGeometry(mesh.geometry);
    const eMat = new THREE.LineBasicMaterial({ color });
    const lines = new THREE.LineSegments(eGeom, eMat);
    mesh.add(lines);
  }

  function makeTriGeometryFromPoints(a,b,c){
    const g = new THREE.BufferGeometry();
    const pos = new Float32Array([
      a.x,a.y,a.z, b.x,b.y,b.z, c.x,c.y,c.z
    ]);
    g.setAttribute('position', new THREE.BufferAttribute(pos, 3));
    g.setIndex([0,1,2]);
    g.computeVertexNormals();
    return g;
  }

  function triangleOnHinge(len=1, side=1){
    const h = Math.sqrt(3)/2 * len;
    const A = new THREE.Vector3(0,0,0);
    const B = new THREE.Vector3(len,0,0);
    const C = new THREE.Vector3(len*0.5, side*h, 0);
    return { geom: makeTriGeometryFromPoints(A,B,C), verts:[A,B,C] };
  }

  function quadFromPoints(p0,p1,p2,p3){
    const g = new THREE.BufferGeometry();
    const pos = new Float32Array([
      p0.x,p0.y,p0.z,
      p1.x,p1.y,p1.z,
      p2.x,p2.y,p2.z,
      p3.x,p3.y,p3.z
    ]);
    g.setAttribute('position', new THREE.BufferAttribute(pos, 3));
    g.setIndex([0,1,2, 0,2,3]);
    g.computeVertexNormals();
    return g;
  }

  function meshForGeometry(geom, color) {
    const mat = new THREE.MeshStandardMaterial({
      color: unfoldedColor.clone(), side: THREE.DoubleSide, flatShading: true, roughness: 0.9, metalness: 0.0
    });
    const mesh = new THREE.Mesh(geom, mat);
    addEdges(mesh);
    // store folded target color
    mesh.userData.foldColor = new THREE.Color(color);
    return mesh;
  }

  function createPivotOnEdge(parentContainer, A, B){
    const pivot = new THREE.Object3D();
    // Axis setup
    const X = new THREE.Vector3().subVectors(B, A).normalize(); // along edge
    const Z = new THREE.Vector3(0,0,1); // parent's face normal in its own local space
    const Y = new THREE.Vector3().crossVectors(Z, X).normalize(); // right-handed
    const basis = new THREE.Matrix4().makeBasis(X, Y, Z);
    pivot.position.copy(A);
    pivot.setRotationFromMatrix(basis);
    parentContainer.add(pivot);
    return pivot;
  }

  function frameObject(object, offset=1.25){
    const box = new THREE.Box3().setFromObject(object);
    const size = box.getSize(new THREE.Vector3());
    const center = box.getCenter(new THREE.Vector3());

    // Compute distance for camera so object fits in view
    const maxSize = Math.max(size.x, size.y, size.z);
    const fitHeightDistance = maxSize / (2 * Math.tan(THREE.MathUtils.degToRad(camera.fov * 0.5)));
    const distance = fitHeightDistance * offset;

    const dir = new THREE.Vector3(2,2,2).normalize();
    camera.position.copy(center.clone().add(dir.multiplyScalar(distance)));
    camera.near = distance/100;
    camera.far = distance*100;
    camera.updateProjectionMatrix();

    controls.target.copy(center);
    controls.update();
    controls.saveState();
  }

  function setActiveButton(index){
    solidButtons.forEach((b,i)=>{
      if(i === index-1) b.classList.add('active'); else b.classList.remove('active');
    });
  }

  function clearCurrent(){
    if(currentSolidGroup){
      scene.remove(currentSolidGroup);
      currentSolidGroup.traverse(obj => {
        if(obj.geometry) obj.geometry.dispose();
        if(obj.material) {
          if(Array.isArray(obj.material)) obj.material.forEach(m=>m.dispose());
          else obj.material.dispose();
        }
      });
    }
    currentSolidGroup = null;
    hinges = [];
    faces = [];
  }

  function applyFolding(t){
    // Rotate hinges
    for(const h of hinges){
      // rotate around local X (hinge axis)
      h.pivot.rotation.x = t * h.finalAngle;
    }
    // Interpolate colors
    for(const f of faces){
      const target = f.mesh.userData.foldColor || new THREE.Color(0xffffff);
      const c = new THREE.Color().lerpColors(unfoldedColor, target, t);
      f.mesh.material.color.copy(c);
    }
  }

  // SOLID BUILDERS
  function buildSolid1(){
    const group = new THREE.Group();

    // A "strip" of 6 equilateral triangles connected by hinges to form a fan/chain
    const s = 1.1;
    const h = Math.sqrt(3)/2 * s;

    // Base face (triangle)
    const A0 = new THREE.Vector3(0,0,0);
    const B0 = new THREE.Vector3(s,0,0);
    const C0 = new THREE.Vector3(s/2, h, 0);
    const baseGeom = makeTriGeometryFromPoints(A0,B0,C0);
    const baseMesh = meshForGeometry(baseGeom, '#E47A55'); // orange
    const baseContainer = new THREE.Group();
    baseContainer.add(baseMesh);
    group.add(baseContainer);

    faces.push({ mesh: baseMesh, container: baseContainer, vertices:[A0,B0,C0] });

    // Attach successive triangles along edge [1,2] each time, alternating sides and angles
    let parent = { mesh: baseMesh, container: baseContainer, vertices:[A0,B0,C0] };
    const sides = [-1, +1, -1, +1, -1]; // place alternating above/below the strip
    const angles = [2.2, -2.2, 2.2, -2.2, 2.2]; // radians, alternating fold directions
    const colors = ['#A4C6E3','#F3C76E','#C6E3A4','#B79BD9','#F2A0A0'];
    for(let i=0;i<5;i++){
      const iA = 1, iB = 2; // edge [B,C] in parent's local vertices
      const A = parent.vertices[iA].clone();
      const B = parent.vertices[iB].clone();
      const pivot = createPivotOnEdge(parent.container, A, B);

      const tri = triangleOnHinge(A.distanceTo(B), sides[i]);
      const mesh = meshForGeometry(tri.geom, colors[i%colors.length]);
      pivot.add(mesh);

      faces.push({ mesh, container: pivot, vertices: tri.verts });
      hinges.push({ pivot, finalAngle: angles[i] });

      // next parent becomes the triangle just created
      parent = { mesh, container: pivot, vertices: tri.verts };
    }

    // Slight rotate the whole strip to mimic screenshot tilt
    group.rotation.z = -Math.PI/8;

    return group;
  }

  function buildSolid2(){
    // Square base with four triangular sides -> a pyramid-like folding
    const group = new THREE.Group();

    const s = 1.4;
    const v0 = new THREE.Vector3(0,0,0);
    const v1 = new THREE.Vector3(s,0,0);
    const v2 = new THREE.Vector3(s,s,0);
    const v3 = new THREE.Vector3(0,s,0);
    const baseGeom = quadFromPoints(v0,v1,v2,v3);
    const baseMesh = meshForGeometry(baseGeom, '#9FC9EB'); // light blue base target
    const baseContainer = new THREE.Group();
    baseContainer.add(baseMesh);
    group.add(baseContainer);

    faces.push({ mesh: baseMesh, container: baseContainer, vertices:[v0,v1,v2,v3] });

    const edges = [
      [v0,v1, '#E47A55'],
      [v1,v2, '#F3C76E'],
      [v2,v3, '#C6E3A4'],
      [v3,v0, '#B79BD9']
    ];
    const finalAngle = 1.35; // ~77 degrees fold upward

    edges.forEach((e,i)=>{
      const A = e[0].clone(), B = e[1].clone();
      const color = e[2];
      const pivot = createPivotOnEdge(baseContainer, A, B);
      const tri = triangleOnHinge(A.distanceTo(B), +1); // outside the square
      const mesh = meshForGeometry(tri.geom, color);
      pivot.add(mesh);
      faces.push({ mesh, container: pivot, vertices: tri.verts });
      hinges.push({ pivot, finalAngle: finalAngle });
    });

    // offset for a nicer framing
    group.position.set(-s*0.5, -s*0.5, 0);
    return group;
  }

  function buildSolid3(){
    // Tetrahedron from 4 equilateral triangles: one base + 3 surrounding
    const group = new THREE.Group();
    const s = 1.25;
    const h = Math.sqrt(3)/2 * s;

    const A0 = new THREE.Vector3(0,0,0);
    const B0 = new THREE.Vector3(s,0,0);
    const C0 = new THREE.Vector3(s/2, h, 0);
    const baseGeom = makeTriGeometryFromPoints(A0,B0,C0);
    const baseMesh = meshForGeometry(baseGeom, '#8BBCE8');
    const baseContainer = new THREE.Group();
    baseContainer.add(baseMesh);
    group.add(baseContainer);
    faces.push({ mesh: baseMesh, container: baseContainer, vertices:[A0,B0,C0] });

    const edges = [
      { ids:[0,1], side:+1, color:'#C97A6C' },
      { ids:[1,2], side:+1, color:'#F3C76E' },
      { ids:[2,0], side:+1, color:'#A4C6E3' }
    ];
    const finalAngle = 2.094; // ~120 degrees

    edges.forEach(e=>{
      const A = [A0,B0,C0][e.ids[0]].clone();
      const B = [A0,B0,C0][e.ids[1]].clone();
      const pivot = createPivotOnEdge(baseContainer, A, B);
      const tri = triangleOnHinge(A.distanceTo(B), e.side);
      const mesh = meshForGeometry(tri.geom, e.color);
      pivot.add(mesh);
      faces.push({ mesh, container: pivot, vertices: tri.verts });
      hinges.push({ pivot, finalAngle: finalAngle });
    });

    return group;
  }

  function createSolid(index){
    clearCurrent();
    let group;
    if(index === 1) group = buildSolid1();
    else if(index === 2) group = buildSolid2();
    else group = buildSolid3();
    currentSolidGroup = group;
    scene.add(group);
    // Apply slider's current value to newly created object
    applyFolding(parseFloat(slider.value));
    // Frame camera and save state for reset button
    frameObject(group);
  }

  // Events
  slider.addEventListener('input', () => {
    applyFolding(parseFloat(slider.value));
  });

  btnReset.addEventListener('click', () => {
    controls.reset(); // returns to saved state from frameObject()
  });

  btn1.addEventListener('click', () => {
    if(currentSolidIndex!==1){
      currentSolidIndex = 1;
      setActiveButton(1);
      createSolid(1);
    }
  });
  btn2.addEventListener('click', () => {
    if(currentSolidIndex!==2){
      currentSolidIndex = 2;
      setActiveButton(2);
      createSolid(2);
    }
  });
  btn3.addEventListener('click', () => {
    if(currentSolidIndex!==3){
      currentSolidIndex = 3;
      setActiveButton(3);
      createSolid(3);
    }
  });

  // Resize handling
  function onResize(){
    const w = container.clientWidth;
    const h = container.clientHeight;
    renderer.setSize(w, h);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
  }
  window.addEventListener('resize', onResize);

  // Render loop
  function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }

  // Initial load: Solid 1, slider at 0 (fully unfolded)
  createSolid(1);
  animate();
  onResize();

  // Tooltips: pointer instructions (optional overlay in console)
  console.log('Mouse controls: Left-drag to rotate, Right-drag to pan, Scroll to zoom.');
})();
</script>
</body>
</html>
