<body>
  <div id="main-container">
    <div id="control-panel">
      <h3>relative volatility</h3>
      <button id="btn-reset">+</button>

      <div class="slider-row">
        <label for="slider-ac">between A and C</label>
        <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
        <span id="value-ac">0.60</span>
      </div>

      <div class="slider-row">
        <label for="slider-bc">between B and C</label>
        <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
        <span id="value-bc">3.93</span>
      </div>
    </div>

    <div id="visualization-container">
      <!-- Placeholder canvas (will be replaced with p5 canvas with the same id) -->
      <canvas id="ternary-plot-canvas" width="600" height="550"></canvas>
    </div>
  </div>

<script>
(function() {
  const DEFAULTS = { ac: 0.60, bc: 3.93 };
  const sliders = {
    ac: document.getElementById('slider-ac'),
    bc: document.getElementById('slider-bc')
  };
  const values = {
    ac: document.getElementById('value-ac'),
    bc: document.getElementById('value-bc')
  };

  // Ensure initial label formatting
  function fmt2(x){ return Number(x).toFixed(2); }
  function setDisplays(){
    values.ac.textContent = fmt2(sliders.ac.value);
    values.bc.textContent = fmt2(sliders.bc.value);
  }
  setDisplays();

  // p5 sketch in instance mode
  let triggerRedraw = function(){};

  const sketch = (p) => {
    const W = 600, H = 550, N = 10;

    // Triangle geometry
    let A = {x:0,y:0}, B = {x:0,y:0}, C = {x:0,y:0};
    let side = 0, topMargin = 40, bottomMargin = 70, sideMargin = 60;

    // Color gradient stops
    const stops = [
      {t:0.00, c: '#2c7bb6'},
      {t:0.25, c: '#abd9e9'},
      {t:0.50, c: '#ffffbf'},
      {t:0.75, c: '#fdae61'},
      {t:1.00, c: '#d7191c'}
    ];
    const stopRGB = stops.map(s => ({t:s.t, rgb:hexToRgb(s.c)}));

    p.setup = function() {
      // Remove placeholder canvas (if present) to avoid duplicate ids, then create our canvas with same id
      const old = document.getElementById('ternary-plot-canvas');
      if (old) old.remove();
      const cnv = p.createCanvas(W, H);
      cnv.parent('visualization-container');
      cnv.id('ternary-plot-canvas');

      p.pixelDensity(1);
      p.noLoop();
      computeTriangleGeometry();
      triggerRedraw = () => p.redraw();
    };

    function computeTriangleGeometry(){
      const maxSideByWidth = W - 2*sideMargin;
      const maxSideByHeight = (H - topMargin - bottomMargin) * 2 / Math.sqrt(3);
      side = Math.min(maxSideByWidth, maxSideByHeight);

      const triHeight = side * Math.sqrt(3) / 2;
      const centerX = W/2;
      const topY = (H - triHeight)/2 - 4 + 10; // small visual tweak
      const bottomY = topY + triHeight;

      A = {x: centerX + side/2, y: bottomY};
      C = {x: centerX - side/2, y: bottomY};
      B = {x: centerX, y: topY};
    }

    p.draw = function() {
      p.background(255);
      p.stroke(0);
      p.strokeWeight(0.6);

      const alpha1 = parseFloat(sliders.ac.value); // α_13
      const alpha2 = parseFloat(sliders.bc.value); // α_23
      const alpha3 = 1.0;

      // Draw filled small triangles
      for (let i=0; i<=N-1; i++){
        for (let j=0; j<=N-1-i; j++){
          // Upward triangle: (i,j), (i+1,j), (i,j+1)
          {
            const b1 = baryFromIJ(i, j, N);
            const b2 = baryFromIJ(i+1, j, N);
            const b3 = baryFromIJ(i, j+1, N);
            const bc = baryAverage([b1,b2,b3]);
            const col = mapValToColor(Fvalue(bc, alpha1, alpha2, alpha3));
            p.fill(col.r, col.g, col.b);
            drawTri(b1, b2, b3);
          }
          // Downward triangle: (i+1,j), (i+1,j+1), (i,j+1) if valid
          if (i + j <= N-2){
            const b1 = baryFromIJ(i+1, j, N);
            const b2 = baryFromIJ(i+1, j+1, N);
            const b3 = baryFromIJ(i, j+1, N);
            const bc = baryAverage([b1,b2,b3]);
            const col = mapValToColor(Fvalue(bc, alpha1, alpha2, alpha3));
            p.fill(col.r, col.g, col.b);
            drawTri(b1, b2, b3);
          }
        }
      }

      // Draw outer triangle border
      p.stroke(0);
      p.strokeWeight(1.5);
      p.noFill();
      p.beginShape();
      p.vertex(A.x, A.y);
      p.vertex(B.x, B.y);
      p.vertex(C.x, C.y);
      p.endShape(p.CLOSE);

      // Labels A, B, C
      p.noStroke();
      p.fill(0);
      p.textSize(20);
      p.textStyle(p.ITALIC);
      p.textAlign(p.LEFT, p.BOTTOM);
      p.text('A', A.x + 12, A.y + 6);
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text('B', B.x, B.y - 10);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text('C', C.x - 12, C.y + 6);

      // Tick numbers on three sides
      p.textStyle(p.NORMAL);
      p.textSize(12);
      drawTicks();
    };

    // Helpers

    function drawTicks(){
      p.fill(30);
      p.noStroke();
      // bottom axis (C -> A), label 0.0 ... 1.0
      p.textAlign(p.CENTER, p.TOP);
      for (let k=0; k<=10; k++){
        let t = k/10;
        const P = xyFromBary({a:t, b:0, c:1-t});
        p.text(t.toFixed(1), P.x, P.y + 8);
      }

      // right axis (A -> B)
      p.textAlign(p.LEFT, p.CENTER);
      for (let k=0; k<=10; k++){
        let t = k/10; // fraction from A (0) to B (1)
        const P = xyFromBary({a:1-t, b:t, c:0});
        p.text(t.toFixed(1), P.x + 10, P.y);
      }

      // left axis (C -> B)
      p.textAlign(p.RIGHT, p.CENTER);
      for (let k=0; k<=10; k++){
        let t = k/10;
        const P = xyFromBary({a:0, b:t, c:1-t});
        p.text(t.toFixed(1), P.x - 10, P.y);
      }
    }

    function drawTri(b1, b2, b3){
      const p1 = xyFromBary(b1);
      const p2 = xyFromBary(b2);
      const p3 = xyFromBary(b3);
      p.beginShape();
      p.vertex(p1.x, p1.y);
      p.vertex(p2.x, p2.y);
      p.vertex(p3.x, p3.y);
      p.endShape(p.CLOSE);
    }

    function baryFromIJ(i, j, N){
      // Using base vectors from C->A (i) and C->B (j)
      const a = i / N;
      const b = j / N;
      const c = 1 - a - b;
      return {a:a, b:b, c:c};
    }

    function baryAverage(arr){
      let a=0,b=0,c=0;
      for (const v of arr){ a+=v.a; b+=v.b; c+=v.c; }
      const n = arr.length;
      return {a:a/n, b:b/n, c:c/n};
    }

    function xyFromBary(b){
      const x = b.a*A.x + b.b*B.x + b.c*C.x;
      const y = b.a*A.y + b.b*B.y + b.c*C.y;
      return {x,y};
    }

    function Fvalue(b, alpha1, alpha2, alpha3){
      const x1 = b.a, x2 = b.b, x3 = b.c;
      const a1 = alpha1, a2 = alpha2, a3 = alpha3;
      const D = a1*x1 + a2*x2 + a3*x3;
      const y1 = (a1*x1)/D;
      const y2 = (a2*x2)/D;
      const y3 = (a3*x3)/D;
      const F = (x1 - y1)*(x1 - y1) + (x2 - y2)*(x2 - y2) + (x3 - y3)*(x3 - y3);
      // Normalize to [0,1] with max = 2.0
      let v = F / 2.0;
      if (v < 0) v = 0;
      if (v > 1) v = 1;
      return v;
    }

    function mapValToColor(t){
      // Linear interpolation among stops
      if (t <= stopRGB[0].t) return stopRGB[0].rgb;
      if (t >= stopRGB[stopRGB.length-1].t) return stopRGB[stopRGB.length-1].rgb;

      let i=0;
      while (i < stopRGB.length-1 && !(t >= stopRGB[i].t && t <= stopRGB[i+1].t)) i++;
      const s0 = stopRGB[i], s1 = stopRGB[i+1];
      const u = (t - s0.t) / (s1.t - s0.t);
      return {
        r: Math.round(lerp(s0.rgb.r, s1.rgb.r, u)),
        g: Math.round(lerp(s0.rgb.g, s1.rgb.g, u)),
        b: Math.round(lerp(s0.rgb.b, s1.rgb.b, u))
      };
    }

    function lerp(a,b,t){ return a + (b-a)*t; }

    function hexToRgb(hex){
      const m = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
      return m ? { r: parseInt(m[1],16), g: parseInt(m[2],16), b: parseInt(m[3],16) } : {r:0,g:0,b:0};
    }
  };

  const p5instance = new p5(sketch);

  // Slider bindings
  const onInput = () => {
    setDisplays();
    triggerRedraw();
  };
  sliders.ac.addEventListener('input', onInput);
  sliders.bc.addEventListener('input', onInput);

  // Reset button
  document.getElementById('btn-reset').addEventListener('click', () => {
    sliders.ac.value = DEFAULTS.ac;
    sliders.bc.value = DEFAULTS.bc;
    setDisplays();
    triggerRedraw();
  });
})();
</script>
</body>
</html>
