  <script>
    // Element references
    const sliderWSO = document.getElementById('slider-wso');
    const sliderN   = document.getElementById('slider-n');
    const sliderBX  = document.getElementById('slider-bx');
    const sliderBY  = document.getElementById('slider-by');
    const sliderBZ  = document.getElementById('slider-bz');

    const valueWSO = document.getElementById('value-wso');
    const valueN   = document.getElementById('value-n');
    const valueBX  = document.getElementById('value-bx');
    const valueBY  = document.getElementById('value-by');
    const valueBZ  = document.getElementById('value-bz');

    function formatVal(val, step) {
      // Integers: no decimals; otherwise, one decimal place
      if (Number(step) % 1 === 0) return String(Math.round(Number(val)));
      return Number(val).toFixed(1);
    }

    function updateValueDisplays() {
      valueWSO.textContent = formatVal(sliderWSO.value, sliderWSO.step);
      valueN.textContent   = formatVal(sliderN.value, sliderN.step);
      valueBX.textContent  = formatVal(sliderBX.value, sliderBX.step);
      valueBY.textContent  = formatVal(sliderBY.value, sliderBY.step);
      valueBZ.textContent  = formatVal(sliderBZ.value, sliderBZ.step);
    }

    function generateKArray(kmin, kmax, step) {
      const k = [];
      for (let x = kmin; x <= kmax + 1e-9; x += step) k.push(Number(x.toFixed(5)));
      return k;
    }

    function computeBands(k, n, wso, BX, BY, BZ) {
      const base = 2 * n + 1;
      const Eplus = [];
      const Eminus = [];
      for (let i = 0; i < k.length; i++) {
        const ki = k[i];
        const split = Math.sqrt(Math.pow(BZ + wso * ki, 2) + BX * BX + BY * BY);
        const parab = base + ki * ki;
        Eplus.push(parab + split);
        Eminus.push(parab - split);
      }
      return { Eplus, Eminus };
    }

    function buildTitle(n, wso, BX, BY, BZ) {
      const parts = [`n = ${n}`];
      // Only include non-zero parameters
      if (BX !== 0) parts.push(`BX = ${BX.toFixed(1)}`);
      if (BY !== 0) parts.push(`BY = ${BY.toFixed(1)}`);
      if (BZ !== 0) parts.push(`BZ = ${BZ.toFixed(1)}`);
      if (wso !== 0) parts.push(`wso = ${wso.toFixed(1)}`);
      return parts.join(', ');
    }

    function updatePlot() {
      const n   = parseInt(sliderN.value, 10);
      const wso = parseFloat(sliderWSO.value);
      const BX  = parseFloat(sliderBX.value);
      const BY  = parseFloat(sliderBY.value);
      const BZ  = parseFloat(sliderBZ.value);

      const k = generateKArray(-5, 5, 0.1);
      const { Eplus, Eminus } = computeBands(k, n, wso, BX, BY, BZ);

      const degenerate = (wso === 0 && BX === 0 && BY === 0 && BZ === 0);

      const traces = [];

      if (degenerate) {
        traces.push({
          x: k,
          y: Eplus, // same as Eminus in this case
          mode: 'lines',
          name: 'E',
          line: { color: 'red', dash: 'solid', width: 2 }
        });
      } else {
        traces.push(
          {
            x: k,
            y: Eplus,
            mode: 'lines',
            name: 'E+',
            line: { color: 'red', dash: 'solid', width: 2 }
          },
          {
            x: k,
            y: Eminus,
            mode: 'lines',
            name: 'E-',
            line: { color: 'red', dash: 'dash', width: 2 }
          }
        );
      }

      const layout = {
        title: buildTitle(n, wso, BX, BY, BZ),
        margin: { l: 60, r: 20, t: 40, b: 50 },
        xaxis: {
          title: 'normalized k length (lwky)',
          range: [-5, 5],
          zeroline: true
        },
        yaxis: {
          title: '2 E / \u210F\u03C9',
          range: [0, 40],
          zeroline: true
        },
        showlegend: false
      };

      Plotly.react('plot-container', traces, layout, { displayModeBar: false, responsive: true });
    }

    // Event bindings
    [sliderWSO, sliderN, sliderBX, sliderBY, sliderBZ].forEach(slider => {
      slider.addEventListener('input', () => {
        updateValueDisplays();
        updatePlot();
      });
    });

    // Initialize
    window.addEventListener('DOMContentLoaded', () => {
      updateValueDisplays();
      updatePlot();
    });
  </script>
</body>
