    <div id="plot-canvas-1"></div>
    <div id="plot-canvas-2"></div>
    <div id="plot-canvas-3"></div>
    <div id="plot-canvas-4"></div>
    <div id="plot-canvas-5"></div>
    <div id="plot-canvas-6"></div>
  </div>
</div>

<script>
  // Global state arrays
  let P = [1,0,0];      // precipitation (3)
  let U = [1,5,3,1];    // unit hydrograph (4)
  let C1 = [0,0,0,0];
  let C2 = [0,0,0,0];
  let C3 = [0,0,0,0];
  let Q  = [0,0,0,0,0,0]; // composite (6)

  // Utility: read sliders, compute convolution, redraw
  function update(){
    P = [
      parseInt(document.getElementById('slider-precip-1').value,10),
      parseInt(document.getElementById('slider-precip-2').value,10),
      parseInt(document.getElementById('slider-precip-3').value,10)
    ];
    U = [
      parseInt(document.getElementById('slider-uh-1').value,10),
      parseInt(document.getElementById('slider-uh-2').value,10),
      parseInt(document.getElementById('slider-uh-3').value,10),
      parseInt(document.getElementById('slider-uh-4').value,10)
    ];

    // Components
    C1 = U.map(v => v * P[0]);
    C2 = U.map(v => v * P[1]);
    C3 = U.map(v => v * P[2]);

    // Composite Q (length 6) via explicit formula
    Q = new Array(6).fill(0);
    Q[0] = P[0]*U[0];
    Q[1] = P[0]*U[1] + P[1]*U[0];
    Q[2] = P[0]*U[2] + P[1]*U[1] + P[2]*U[0];
    Q[3] = P[0]*U[3] + P[1]*U[2] + P[2]*U[1];
    Q[4] = P[1]*U[3] + P[2]*U[2];
    Q[5] = P[2]*U[3];

    // Redraw all plots
    sketches.forEach(s => s.redraw());
  }

  // Attach events to sliders and set value displays
  const sliderValuePairs = [
    ['slider-precip-1','value-precip-1'],
    ['slider-precip-2','value-precip-2'],
    ['slider-precip-3','value-precip-3'],
    ['slider-uh-1','value-uh-1'],
    ['slider-uh-2','value-uh-2'],
    ['slider-uh-3','value-uh-3'],
    ['slider-uh-4','value-uh-4'],
  ];
  sliderValuePairs.forEach(([sid,vid])=>{
    const s = document.getElementById(sid);
    const v = document.getElementById(vid);
    const handler = () => { v.textContent = parseInt(s.value,10); update(); };
    s.addEventListener('input', handler);
    v.textContent = s.value;
  });

  // Toggle controls visibility
  document.getElementById('toggle-controls').addEventListener('click', ()=>{
    const area = document.getElementById('sliders-area');
    area.style.display = (area.style.display === 'none') ? 'block' : 'none';
  });

  // Reusable plot drawing with p5 instance
  function drawPlot(p, data, xLabel, yLabel, xMax, yMax, xTick, yTick, offset){
    const M = {l:48, r:10, t:14, b:34};
    const left = M.l, top = M.t;
    const w = p.width - M.l - M.r;
    const h = p.height - M.t - M.b;

    p.background(255);
    p.stroke(0); p.strokeWeight(1); p.noFill();

    // Axes
    p.line(left, top+h, left+w, top+h); // x
    p.line(left, top, left, top+h);     // y

    p.textSize(11);
    p.fill(0);
    p.noStroke();

    // Ticks: X
    p.textAlign(p.CENTER, p.TOP);
    for(let x=0; x<=xMax+1e-9; x+=xTick){
      const px = left + (x/xMax)*w;
      p.stroke(0); p.line(px, top+h, px, top+h+4);
      p.noStroke(); p.text(x.toString(), px, top+h+6);
    }
    // Ticks: Y
    p.textAlign(p.RIGHT, p.CENTER);
    for(let y=0; y<=yMax+1e-9; y+=yTick){
      const py = top + h - (y/yMax)*h;
      p.stroke(0); p.line(left-4, py, left, py);
      p.noStroke(); p.text(y.toString(), left-6, py);
    }

    // Axis labels
    p.textAlign(p.CENTER, p.BOTTOM);
    p.text(xLabel, left + w/2, top + h + 28);
    p.push();
    p.translate(14, top + h/2);
    p.rotate(-p.HALF_PI);
    p.text(yLabel, 0, 0);
    p.pop();

    // Plot step chart
    const xToPx = (t)=> left + (t/xMax)*w;
    const yToPx = (y)=> top + h - (Math.max(0,Math.min(y,yMax))/yMax)*h;

    // Area fill (step)
    p.noStroke();
    p.fill('#d4e6f1');
    p.beginShape();
    p.vertex(xToPx(offset), yToPx(0));
    if(data.length>0){
      p.vertex(xToPx(offset), yToPx(data[0]));
    }
    for(let i=0;i<data.length;i++){
      p.vertex(xToPx(offset+i),   yToPx(data[i]));
      p.vertex(xToPx(offset+i+1), yToPx(data[i]));
    }
    p.vertex(xToPx(offset+data.length), yToPx(0));
    p.endShape(p.CLOSE);

    // Step outlines
    p.noFill();
    p.stroke('#2e86c1');
    p.strokeWeight(2);
    for(let i=0;i<data.length;i++){
      const y = data[i];
      // horizontal
      p.line(xToPx(offset+i), yToPx(y), xToPx(offset+i+1), yToPx(y));
      // vertical connector
      if(i < data.length-1){
        p.line(xToPx(offset+i+1), yToPx(y), xToPx(offset+i+1), yToPx(data[i+1]));
      }
    }
  }

  // Create p5 sketches for each plot
  const sketches = [];
  function createSketch(containerId, getData, xLabel, yLabel, xMax, yMax, xTick, yTick, offset){
    const sketch = (p)=>{
      let parent, cnv;
      p.setup = function(){
        parent = document.getElementById(containerId);
        const w = parent.clientWidth || 280;
        const h = parent.clientHeight || 220;
        cnv = p.createCanvas(w, h);
        cnv.parent(parent);
        p.noLoop();
      };
      p.draw = function(){
        drawPlot(p, getData(), xLabel, yLabel, xMax, yMax, xTick, yTick, offset);
      };
      p.windowResized = function(){
        if(!parent) parent = document.getElementById(containerId);
        const w = parent.clientWidth || 280;
        const h = parent.clientHeight || 220;
        p.resizeCanvas(w, h);
        p.redraw();
      };
    };
    const instance = new p5(sketch);
    return instance;
  }

  // Build all six plots
