<script>
  // Global state of payoffs
  const state = {
    A: {a11: -5, a12: -3, a21: -1, a22: -4},
    B: {b11: -5, b12: -3, b21: -1, b22: -4}
  };

  // Utility: fraction formatting using continued fractions
  function toFraction(x, tol = 1e-6, maxDen = 256) {
    // Handle NaN or out of bounds
    if (!isFinite(x)) return '\\text{undefined}';
    const sgn = x < 0 ? -1 : 1;
    x = Math.abs(x);
    if (Math.abs(x - Math.round(x)) < tol) return (sgn * Math.round(x)).toString();
    let h1 = 1, h2 = 0, k1 = 0, k2 = 1, b = x;
    do {
      const a = Math.floor(b);
      const h = a * h1 + h2;
      const k = a * k1 + k2;
      if (k > maxDen) {
        // fallback to last
        const num = Math.round(x * k1);
        const den = k1;
        return (sgn < 0 ? '-' : '') + num + '/' + den;
      }
      const approx = h / k;
      if (Math.abs(x - approx) <= tol) {
        if (k === 1) return (sgn < 0 ? '-' : '') + h;
        return (sgn < 0 ? '-' : '') + h + '/' + k;
      }
      h2 = h1; h1 = h; k2 = k1; k1 = k;
      b = 1 / (b - a);
    } while (true);
  }

  function formatPoint(x, y) {
    return '(' + toFraction(x) + ', ' + toFraction(y) + ')';
  }

  // Build payoff table values
  function updatePayoffTable() {
    const A = state.A, B = state.B;
    document.getElementById('cell-11').textContent = `${A.a11}, ${B.b11}`;
    document.getElementById('cell-12').textContent = `${A.a12}, ${B.b12}`;
    document.getElementById('cell-21').textContent = `${A.a21}, ${B.b21}`;
    document.getElementById('cell-22').textContent = `${A.a22}, ${B.b22}`;
  }

  // Collect slider bindings
  const sliderDefs = [
    {id:'a11', group:'A', def:-5},
    {id:'a12', group:'A', def:-3},
    {id:'a21', group:'A', def:-1},
    {id:'a22', group:'A', def:-4},
    {id:'b11', group:'B', def:-5},
    {id:'b12', group:'B', def:-3},
    {id:'b21', group:'B', def:-1},
    {id:'b22', group:'B', def:-4},
  ];

  function clamp(val, min, max) { return Math.min(max, Math.max(min, val)); }

  function initSliders() {
    sliderDefs.forEach(s => {
      const slider = document.getElementById('slider-' + s.id);
      const minus = document.getElementById('btn-minus-' + s.id);
      const plus  = document.getElementById('btn-plus-' + s.id);
      const valueSpan = document.getElementById('value-' + s.id);

      const step = Number(slider.step || 1);
      const min = Number(slider.min), max = Number(slider.max);

      function apply(val) {
        slider.value = val;
        valueSpan.textContent = val;
        state[s.group][s.id] = Number(val);
        onParametersChanged();
      }
      slider.addEventListener('input', () => apply(slider.value));
      minus.addEventListener('click', () => apply(clamp(Number(slider.value) - step, min, max)));
      plus.addEventListener('click',  () => apply(clamp(Number(slider.value) + step, min, max)));

      // initialize display
      valueSpan.textContent = slider.value;
      state[s.group][s.id] = Number(slider.value);
    });
  }

  // Geometric helpers
  function nearlyEqual(a, b, eps = 1e-9) { return Math.abs(a - b) < eps; }
  function segment(x1, y1, x2, y2, orientation) { return {x1, y1, x2, y2, orientation}; }

  // Best response construction
  function bestResponseBlue(A) {
    // Player 1 best response correspondence as segments
    const segs = [];
    const den = (A.a11 - A.a12 - A.a21 + A.a22);
    const eps = 1e-12;
    const yIndiff = (A.a22 - A.a12) / den;

    function addVertical(x, ylo, yhi) {
      segs.push(segment(x, Math.max(0, Math.min(1, ylo)), x, Math.max(0, Math.min(1, yhi)), 'v'));
    }
    function addHorizontal(xlo, xhi, y) {
      segs.push(segment(Math.max(0, Math.min(1, xlo)), y, Math.max(0, Math.min(1, xhi)), y, 'h'));
    }

    if (Math.abs(den) < eps) {
      const intercept = (A.a12 - A.a22);
      if (intercept > 0) {
        addVertical(1, 0, 1);
      } else if (intercept < 0) {
        addVertical(0, 0, 1);
      } else {
        // completely indifferent: allow both pure extremes
        addVertical(0, 0, 1);
        addVertical(1, 0, 1);
        addHorizontal(0, 1, 0); // faint hint of the whole set; intersections will handle it
        addHorizontal(0, 1, 1);
      }
    } else if (yIndiff > 0 && yIndiff < 1) {
      // Horizontal line at y = yIndiff
      addHorizontal(0, 1, yIndiff);
      if (den > 0) {
        addVertical(0, 0, yIndiff);
        addVertical(1, yIndiff, 1);
      } else {
        addVertical(0, yIndiff, 1);
        addVertical(1, 0, yIndiff);
      }
    } else {
      // Dominant pure in the mixed extension
      // Check preference at y = 0.5
      const diff = den * 0.5 + (A.a12 - A.a22);
      if (diff >= 0) addVertical(1, 0, 1);
      else addVertical(0, 0, 1);
    }
    return segs;
  }

  function bestResponseGreen(B) {
    // Player 2 best response correspondence as segments
    const segs = [];
    const den = (B.b11 - B.b12 - B.b21 + B.b22);
    const eps = 1e-12;
    const xIndiff = (B.b22 - B.b21) / den;

    function addVertical(x, ylo, yhi) {
      segs.push(segment(x, Math.max(0, Math.min(1, ylo)), x, Math.max(0, Math.min(1, yhi)), 'v'));
    }
    function addHorizontal(xlo, xhi, y) {
      segs.push(segment(Math.max(0, Math.min(1, xlo)), y, Math.max(0, Math.min(1, xhi)), y, 'h'));
    }

    if (Math.abs(den) < eps) {
      const intercept = (B.b21 - B.b22);
      if (intercept > 0) {
        addHorizontal(0, 1, 1); // y=1 always
      } else if (intercept < 0) {
        addHorizontal(0, 1, 0); // y=0 always
      } else {
        // completely indifferent
        addHorizontal(0, 1, 0);
        addHorizontal(0, 1, 1);
        addVertical(0, 0, 1);
        addVertical(1, 0, 1);
      }
    } else if (xIndiff > 0 && xIndiff < 1) {
      // Vertical line at x = xIndiff
      addVertical(xIndiff, 0, 1);
      if (den > 0) {
        addHorizontal(0, xIndiff, 0);
        addHorizontal(xIndiff, 1, 1);
      } else {
        addHorizontal(0, xIndiff, 1);
        addHorizontal(xIndiff, 1, 0);
      }
    } else {
      // Dominant in mixed extension; check x=0.5
      const diff = den * 0.5 + (B.b21 - B.b22);
      if (diff >= 0) addHorizontal(0, 1, 1);
      else addHorizontal(0, 1, 0);
    }
    return segs;
  }

  // Intersection computation between blue and green polylines
  function computeIntersections(blueSegs, greenSegs) {
    const res = [];
    const eps = 1e-9;
    function addPoint(x, y) { res.push({type:'point', x, y}); }
    function addSeg(x1, y1, x2, y2) { res.push({type:'segment', x1, y1, x2, y2}); }

    for (const b of blueSegs) {
      const bxmin = Math.min(b.x1, b.x2), bxmax = Math.max(b.x1, b.x2);
      const bymin = Math.min(b.y1, b.y2), bymax = Math.max(b.y1, b.y2);
      for (const g of greenSegs) {
        const gxmin = Math.min(g.x1, g.x2), gxmax = Math.max(g.x1, g.x2);
        const gymin = Math.min(g.y1, g.y2), gymax = Math.max(g.y1, g.y2);

        if (b.orientation === 'v' && g.orientation === 'h') {
          const x = b.x1, y = g.y1;
          if (x >= gxmin - eps && x <= gxmax + eps && y >= bymin - eps && y <= bymax + eps) addPoint(x, y);
        } else if (b.orientation === 'h' && g.orientation === 'v') {
          const x = g.x1, y = b.y1;
          if (x >= bxmin - eps && x <= bxmax + eps && y >= gymin - eps && y <= gymax + eps) addPoint(x, y);
        } else if (b.orientation === 'v' && g.orientation === 'v') {
          if (nearlyEqual(b.x1, g.x1, eps)) {
            const lo = Math.max(bymin, gymin), hi = Math.min(bymax, gymax);
            if (hi >= lo - eps) addSeg(b.x1, lo, b.x1, hi);
          }
        } else if (b.orientation === 'h' && g.orientation === 'h') {
          if (nearlyEqual(b.y1, g.y1, eps)) {
            const lo = Math.max(bxmin, gxmin), hi = Math.min(bxmax, gxmax);
            if (hi >= lo - eps) addSeg(lo, b.y1, hi, b.y1);
          }
        }
      }
    }
    return res;
  }

  // p5.js sketch
  let blueSegments = [];
  let greenSegments = [];
  let intersections = [];

  let canvasW = 600, canvasH = 520;
  const xw0 = -0.1, xw1 = 1.1, yw0 = -0.1, yw1 = 1.1;
  const margin = {left: 50, right: 20, top: 20, bottom: 54};

  function sx() { return (canvasW - margin.left - margin.right) / (xw1 - xw0); }
  function sy() { return (canvasH - margin.top - margin.bottom) / (yw1 - yw0); }

  function worldToScreen(x, y) {
    const px = margin.left + (x - xw0) * sx();
    const py = canvasH - margin.bottom - (y - yw0) * sy();
    return [px, py];
  }

  function setWorldTransform() {
    translate(margin.left, canvasH - margin.bottom);
    scale(sx(), -sy());
    translate(-xw0, -yw0);
  }

  function setup() {
    const cnv = createCanvas(canvasW, canvasH);
    cnv.parent('canvas-container');
    noLoop();
    onParametersChanged();
  }

  function drawAxesAndGrid() {
    // Axes and grid under world transform
    push();
    setWorldTransform();

    // Light grid 0..1 step 0.2
    stroke(220);
    strokeWeight(1 / sx());
    for (let t = 0; t <= 1.0001; t += 0.2) {
      line(0, t, 1, t);
      line(t, 0, t, 1);
    }

    // Axes
    stroke(120);
    strokeWeight(2 / sx());
    line(0, 0, 1, 0);
    line(0, 0, 0, 1);

    pop();

    // Tick labels and axis labels (screen space)
    textAlign(CENTER, TOP);
    textSize(12);
    fill(60);
    for (let t = 0; t <= 1.0001; t += 0.2) {
      let [tx, ty0] = worldToScreen(t, 0);
      let [tx2, ty1] = worldToScreen(0, t);
      // tick marks (short)
      stroke(120);
      line(tx, ty0, tx, ty0 + 5);
      line(tx2 - 5, ty1, tx2, ty1);

      noStroke();
      text(Number(t.toFixed(1)), tx, ty0 + 6);
      text(Number(t.toFixed(1)), tx2 - 12, ty1 - 6);
    }
    // Axis labels
    noStroke();
    textSize(14);
    const [xl, yl] = worldToScreen(1.04, -0.02);
    text('x₁', xl, yl);
    textAlign(RIGHT, BOTTOM);
    const [ylx, yly] = worldToScreen(-0.04, 1.04);
    text('y₁', ylx, yly);
  }

  function drawSegments(segs, colorStr, w = 3) {
    push();
    setWorldTransform();
    stroke(colorStr);
    strokeWeight(w / sx()); // scale invariant
    for (const s of segs) {
      line(s.x1, s.y1, s.x2, s.y2);
    }
    pop();
  }

  function drawIntersections(ints) {
    // Thick red segments and vertices
    push();
    setWorldTransform();
    stroke('#d00');
    strokeWeight(4 / sx());
    for (const it of ints) {
      if (it.type === 'segment') {
        line(it.x1, it.y1, it.x2, it.y2);
      } else if (it.type === 'point') {
        // draw as a tiny segment to allow strokeWeight apply
        point(it.x, it.y);
      }
    }
    pop();

    // draw endpoints as red filled circles
    const vertices = collectVertices(ints);
    fill('#d00');
    noStroke();
    for (const v of vertices) {
      const [px, py] = worldToScreen(v[0], v[1]);
      circle(px, py, 8);
    }
  }

  function collectVertices(ints) {
    let pts = [];
    for (const it of ints) {
      if (it.type === 'point') pts.push([it.x, it.y]);
      else {
        pts.push([it.x1, it.y1], [it.x2, it.y2]);
      }
    }
    // Deduplicate
    const out = [];
    const eps = 1e-9;
    for (const p of pts) {
      if (!out.some(q => Math.abs(q[0] - p[0]) < eps && Math.abs(q[1] - p[1]) < eps)) {
        out.push(p);
      }
    }
    // Sort for a stable display
    out.sort((p, q) => (p[0] - q[0]) || (p[1] - q[1]));
    return out;
  }

  function draw() {
    background(255);
    // frame
    stroke(220);
    noFill();
    rect(0, 0, width, height);

    drawAxesAndGrid();
    drawSegments(blueSegments, '#1f77b4', 3);  // blue
    drawSegments(greenSegments, '#2ca02c', 3); // green
    drawIntersections(intersections);
  }

  function updateEquilibriaDisplay() {
    const verts = collectVertices(intersections);
    let content;
    if (verts.length === 0) {
      content = '\\( \\{\\} \\)';
    } else {
      const items = verts.map(v => '(' + toFraction(v[0]) + ', ' + toFraction(v[1]) + ')' );
      content = '\\( \\{ ' + items.join(', ') + ' \\} \\)';
    }
    const el = document.getElementById('equilibria-display');
    el.innerHTML = content;
    if (window.MathJax && MathJax.typesetPromise) {
      MathJax.typesetPromise([el]);
    }
  }

  function onParametersChanged() {
    updatePayoffTable();
    blueSegments = bestResponseBlue(state.A);
    greenSegments = bestResponseGreen(state.B);
    intersections = computeIntersections(blueSegments, greenSegments);
    updateEquilibriaDisplay();
    redraw();
  }

  // Initialize after DOM ready
  window.addEventListener('DOMContentLoaded', () => {
    initSliders();
    updatePayoffTable();
    if (window.MathJax && MathJax.typesetPromise) {
      MathJax.typesetPromise();
    }
  });
</script>

</body>
