  #slider-group, #checkbox-group {
    display: flex;
    align-items: center;
    gap: 8px;
  }
  #slider-group label, #checkbox-group label {
    font-size: 14px;
    color: #444;
  }
  #slider-t {
    width: 320px;
  }
  #input-t {
    width: 70px;
  }
  #btn-reset {
    border: 1px solid #bdbdbd;
    background: #f5f5f5;
    color: #444;
    padding: 4px 8px;
    border-radius: 6px;
    cursor: pointer;
  }
  #btn-reset:hover {
    background: #eee;
  }
  #canvas-container {
    background: #fff;
    border: 1px solid #ddd;
    border-radius: 6px;
    padding: 8px;
  }
  .value-note {
    font-variant-numeric: tabular-nums;
    color: #555;
    font-size: 13px;
  }
</style>
</head>
<body>
  <div id="demo-container">
    <div id="controls-container">
      <div id="slider-group">
        <label for="slider-t">choose quadratic</label>
        <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
        <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
        <button id="btn-reset" style="font-family: 'Courier New', monospace;">&#x21BB;</button>
      </div>
      <div id="checkbox-group">
        <input type="checkbox" id="checkbox-family">
        <label for="checkbox-family">family</label>
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

<script>
/*
  Cubic with osculating quadratic visualizer
  - p5.js rendering in #canvas-container
  - Four draggable locators define a cubic y = a x^3 + b x^2 + c x + d
  - Osculating quadratic at x = t is g(x) = p x^2 + q x + r with:
      p = 3 a t + b
      q = -3 a t^2 + c
      r = a t^3 + d
  - Optional "family" view fills many quadratics across t in [-5,5]
*/

// DOM elements
const sliderT = document.getElementById('slider-t');
const inputT  = document.getElementById('input-t');
const chkFamily = document.getElementById('checkbox-family');
const btnReset = document.getElementById('btn-reset');

// State (non-p5)
const T_MIN = -5, T_MAX = 5, T_STEP = 0.01, T_DEFAULT = 0.5;

// p5 globals
let cnv;
const CANVAS_W = 600, CANVAS_H = 500;
const SCALE = 50; // pixels per unit
const PAD = 8;

// World visible ranges derived from canvas size and scale
const WORLD_X_MIN = - (CANVAS_W/2) / SCALE;
const WORLD_X_MAX =   (CANVAS_W/2) / SCALE;
const WORLD_Y_MIN = - (CANVAS_H/2) / SCALE;
const WORLD_Y_MAX =   (CANVAS_H/2) / SCALE;

// Locators (world coordinates)
const initialLocators = [
  {x: -4, y:  1},
  {x: -2, y:  1.8},
  {x:  1, y: -1},
  {x:  4, y: -1}
];
let locators = JSON.parse(JSON.stringify(initialLocators));
let draggingIndex = -1;

// Polynomial coefficients
let cubic = {a:0, b:0, c:0, d:0};
let quad  = {p:0, q:0, r:0};
let tValue = T_DEFAULT;

// Family palette (RGB)
const palette = [
  [239,83,80],   // red
  [255,167,38],  // orange
  [255,238,88],  // yellow
  [102,187,106], // green
  [66,165,245],  // blue
  [171,71,188],  // purple
  [0,188,212],   // cyan
  [141,110,99],  // brown
  [255,112,67],  // deep orange
  [124,179,66],  // light green
  [57,73,171],   // indigo
  [0,121,107]    // teal
];

// Utility: solve 4x4 linear system with partial pivoting
function solveLinearSystem(M, b) {
  // Deep copy
  let A = M.map(row => row.slice());
  let x = b.slice();
  const n = 4;

  for (let i = 0; i < n; i++) {
    // Pivot
    let maxRow = i;
    let maxVal = Math.abs(A[i][i]);
    for (let r = i+1; r < n; r++) {
      const v = Math.abs(A[r][i]);
      if (v > maxVal) { maxVal = v; maxRow = r; }
    }
    if (maxVal < 1e-12) {
      // Singular-ish, return zeros
      return [0,0,0,0];
    }
    if (maxRow !== i) {
      const tmpR = A[i]; A[i] = A[maxRow]; A[maxRow] = tmpR;
      const tmpb = x[i]; x[i] = x[maxRow]; x[maxRow] = tmpb;
    }
    // Eliminate below
    for (let r = i+1; r < n; r++) {
      const factor = A[r][i] / A[i][i];
      if (!isFinite(factor)) continue;
      for (let c = i; c < n; c++) {
        A[r][c] -= factor * A[i][c];
      }
      x[r] -= factor * x[i];
    }
  }
  // Back substitution
  const sol = new Array(n).fill(0);
  for (let i = n-1; i >= 0; i--) {
    let sum = x[i];
    for (let c = i+1; c < n; c++) sum -= A[i][c] * sol[c];
    sol[i] = sum / A[i][i];
  }
  return sol;
}

function solveCubicCoefficients(points) {
  // points: array of objects {x, y} length 4
  const M = [];
  const b = [];
  for (let i = 0; i < 4; i++) {
    const xi = points[i].x;
    const yi = points[i].y;
    M.push([xi*xi*xi, xi*xi, xi, 1]);
    b.push(yi);
  }
  const [a,b2,c,d] = solveLinearSystem(M, b);
  return {a:a, b:b2, c:c, d:d};
}

function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
  const {a,b,c,d} = cubicCoeffs;
  return {
    p: 3*a*t + b,
    q: -3*a*t*t + c,
    r: a*t*t*t + d
  };
}

function clamp(v, lo, hi) { return Math.max(lo, Math.min(hi, v)); }

// p5 sketch
new p5((p) => {
  p.setup = function() {
    const parent = document.getElementById('canvas-container');
    cnv = p.createCanvas(CANVAS_W, CANVAS_H);
    cnv.parent(parent);
    p.pixelDensity(1);
    p.noLoop();

    // Sync controls
    sliderT.value = T_DEFAULT;
    inputT.value = T_DEFAULT.toFixed(2);
    chkFamily.checked = false;

    // Event listeners
    sliderT.addEventListener('input', () => {
      tValue = clamp(parseFloat(sliderT.value), T_MIN, T_MAX);
      inputT.value = tValue.toFixed(2);
      quad = getOsculatingQuadraticCoeffs(tValue, cubic);
      p.redraw();
    });
    inputT.addEventListener('change', () => {
      let v = parseFloat(inputT.value);
      if (isNaN(v)) v = tValue;
      v = clamp(v, T_MIN, T_MAX);
      v = Math.round(v / T_STEP) * T_STEP;
      tValue = v;
      sliderT.value = v;
      inputT.value = v.toFixed(2);
      quad = getOsculatingQuadraticCoeffs(tValue, cubic);
      p.redraw();
    });
    chkFamily.addEventListener('change', () => {
      p.redraw();
    });
    btnReset.addEventListener('click', () => {
      // Reset everything
      locators = JSON.parse(JSON.stringify(initialLocators));
      tValue = T_DEFAULT;
      sliderT.value = T_DEFAULT;
      inputT.value = T_DEFAULT.toFixed(2);
      chkFamily.checked = false;
      cubic = solveCubicCoefficients(locators);
      quad = getOsculatingQuadraticCoeffs(tValue, cubic);
      p.redraw();
    });

    // Initial coefficients
    cubic = solveCubicCoefficients(locators);
    quad  = getOsculatingQuadraticCoeffs(tValue, cubic);

    p.redraw();
  };

  // World <-> Screen
  function worldToScreen(x, y) {
    const cx = CANVAS_W / 2;
    const cy = CANVAS_H / 2;
    const sx = cx + x * SCALE;
    const sy = cy - y * SCALE;
    return {x: sx, y: sy};
  }
  function screenToWorld(px, py) {
    const cx = CANVAS_W / 2;
    const cy = CANVAS_H / 2;
    const x = (px - cx) / SCALE;
    const y = (cy - py) / SCALE;
    return {x, y};
  }

  // Drawing helpers
  function drawAxes() {
    p.push();
    p.stroke(180);
    p.strokeWeight(1);

    // X axis
    let s0 = worldToScreen(WORLD_X_MIN, 0);
    let s1 = worldToScreen(WORLD_X_MAX, 0);
    p.line(s0.x, s0.y, s1.x, s1.y);

    // Y axis
    s0 = worldToScreen(0, WORLD_Y_MIN);
    s1 = worldToScreen(0, WORLD_Y_MAX);
    p.line(s0.x, s0.y, s1.x, s1.y);

    // Tick marks and labels
    p.textAlign(p.CENTER, p.TOP);
    p.fill(120);
    p.noStroke();

    // X ticks
    for (let x = Math.ceil(WORLD_X_MIN); x <= Math.floor(WORLD_X_MAX); x++) {
      if (x === 0) continue;
      const pt = worldToScreen(x, 0);
      p.stroke(200);
      p.line(pt.x, pt.y - 4, pt.x, pt.y + 4);
      p.noStroke();
      p.text(x.toString(), pt.x, pt.y + 6);
    }

    // Y ticks
    p.textAlign(p.RIGHT, p.CENTER);
    for (let y = Math.ceil(WORLD_Y_MIN); y <= Math.floor(WORLD_Y_MAX); y++) {
      if (y === 0) continue;
      const pt = worldToScreen(0, y);
      p.stroke(200);
      p.line(pt.x - 4, pt.y, pt.x + 4, pt.y);
      p.noStroke();
      p.text(y.toString(), pt.x - 6, pt.y - 1);
    }
    p.pop();
  }

  function drawLocator(pt, highlight=false) {
    const s = worldToScreen(pt.x, pt.y);
    p.push();
    p.stroke(0);
    p.strokeWeight(2);
    p.fill(255);
    p.circle(s.x, s.y, 16);
    p.line(s.x - 6, s.y, s.x + 6, s.y);
    p.line(s.x, s.y - 6, s.x, s.y + 6);
    if (highlight) {
      p.stroke(50, 100, 200);
      p.noFill();
      p.circle(s.x, s.y, 22);
    }
    p.pop();
  }

  function drawCubicCurve(coeffs) {
    const {a,b,c,d} = coeffs;
    p.push();
    p.noFill();
    p.stroke('#3f51b5');
    p.strokeWeight(3);

    p.beginShape();
    for (let xPixel = 0; xPixel <= CANVAS_W; xPixel += 1) {
      const x = (xPixel - CANVAS_W/2) / SCALE;
      const y = ((a*x + b)*x + c)*x + d; // ax^3 + bx^2 + cx + d
      const s = worldToScreen(x, y);
      p.vertex(s.x, s.y);
    }
    p.endShape();
    p.pop();
  }

  function drawQuadraticCurve(coeffs, style={stroke:'#f57c00', weight:3}) {
    const {p:pa, q, r} = coeffs;
    p.push();
    p.noFill();
    p.stroke(style.stroke || '#f57c00');
    p.strokeWeight(style.weight || 3);

    p.beginShape();
    for (let xPixel = 0; xPixel <= CANVAS_W; xPixel += 1) {
      const x = (xPixel - CANVAS_W/2) / SCALE;
      const y = (pa*x + q)*x + r; // px^2 + qx + r
      const s = worldToScreen(x, y);
      p.vertex(s.x, s.y);
    }
    p.endShape();
    p.pop();
  }

  function drawFilledQuadratic(coeffs, colRGB) {
    const {p:pa, q, r} = coeffs;
    const up = pa > 0;
    const yBoundary = up ? WORLD_Y_MAX : WORLD_Y_MIN;

    p.push();
    p.noStroke();
    p.fill(colRGB[0], colRGB[1], colRGB[2], 95);

    p.beginShape();
    // Left boundary vertex
    let s = worldToScreen(WORLD_X_MIN, yBoundary);
    p.vertex(s.x, s.y);

    // Curve vertices from left to right
    const stepPx = 3; // sampling step in pixels
    for (let xPixel = 0; xPixel <= CANVAS_W; xPixel += stepPx) {
      const x = (xPixel - CANVAS_W/2) / SCALE;
      const y = (pa*x + q)*x + r;
      s = worldToScreen(x, y);
      p.vertex(s.x, s.y);
    }
    // Right boundary vertex
    s = worldToScreen(WORLD_X_MAX, yBoundary);
    p.vertex(s.x, s.y);

    p.endShape(p.CLOSE);
    p.pop();
  }

  function drawFamilyOfQuadratics() {
    const steps = 21; // from -5 to 5 inclusive -> 21 samples step 0.5
    const tStep = (T_MAX - T_MIN) / (steps - 1);

    for (let i = 0; i < steps; i++) {
      const t = T_MIN + i * tStep;
      const coeffs = getOsculatingQuadraticCoeffs(t, cubic);
      const col = palette[i % palette.length];
      drawFilledQuadratic(coeffs, col);
      // Also draw a subtle outline
      p.push();
      p.stroke(col[0], col[1], col[2], 160);
      p.strokeWeight(1.2);
      drawQuadraticCurve(coeffs, {stroke: p.color(col[0], col[1], col[2], 160), weight:1.2});
      p.pop();
    }
  }

  function drawAll() {
    p.clear();
    p.background(255);
    // Outer border for canvas area (optional subtle)
    p.push();
    p.noFill();
    p.stroke(230);
    p.rect(0.5, 0.5, CANVAS_W-1, CANVAS_H-1, 6);
    p.pop();

    drawAxes();

    // Family vs cubic
    if (chkFamily.checked) {
      drawFamilyOfQuadratics();
    } else {
      drawCubicCurve(cubic);
    }

    // Highlighted osculating quadratic on top
    drawQuadraticCurve(quad, {stroke:'#f57c00', weight:3});

    // Locators
    for (let i = 0; i < locators.length; i++) {
      drawLocator(locators[i], i === draggingIndex);
    }
  }

  p.draw = function() {
    drawAll();
  };

  // Interaction: dragging locators
  function hitTestLocator(mx, my) {
    // returns index or -1
    for (let i = locators.length - 1; i >= 0; i--) {
      const s = worldToScreen(locators[i].x, locators[i].y);
      const d = Math.hypot(mx - s.x, my - s.y);
      if (d <= 12) return i;
    }
    return -1;
  }

  p.mousePressed = function() {
    const mx = p.mouseX, my = p.mouseY;
    if (mx < 0 || my < 0 || mx > CANVAS_W || my > CANVAS_H) return;
    draggingIndex = hitTestLocator(mx, my);
    if (draggingIndex !== -1) p.redraw();
  };

  p.mouseDragged = function() {
    if (draggingIndex === -1) return;
    const mx = p.mouseX, my = p.mouseY;
    const w = screenToWorld(mx, my);
    // Clamp to visible world a bit beyond the view
    locators[draggingIndex].x = clamp(w.x, WORLD_X_MIN*1.2, WORLD_X_MAX*1.2);
    locators[draggingIndex].y = clamp(w.y, WORLD_Y_MIN*1.2, WORLD_Y_MAX*1.2);
    cubic = solveCubicCoefficients(locators);
    quad  = getOsculatingQuadraticCoeffs(tValue, cubic);
    p.redraw();
  };

  p.mouseReleased = function() {
    if (draggingIndex !== -1) {
      draggingIndex = -1;
      p.redraw();
    }
  };

}, window.document);
</script>
</body>
</html>
