<script>
(() => {
  // State
  const state = {
    v0: 211,                 // volume at 273.15 K
    T_units: 'C',            // 'C' or 'K' (display units)
    T_slider_value: 270,     // value shown on the slider (in current units)
    T_K: 270 + 273.15,       // absolute temperature in Kelvin
    k: 211 / 273.15,         // proportionality constant
    V: 0                     // current volume
  };

  // Elements
  const elV0 = document.getElementById('slider-v0');
  const elV0Label = document.getElementById('label-v0');
  const elT = document.getElementById('slider-T');
  const elTLabel = document.getElementById('label-T');
  const radioK = document.getElementById('radio-kelvin');
  const radioC = document.getElementById('radio-celsius');
  const plotDiv = document.getElementById('plot-container');

  // Colorscale for plot and marker
  const colorScale = [
    [0,   'rgb(128,0,128)'],   // 0 K  -> deep purple
    [0.5, 'rgb(255,255,0)'],   // 500 K -> yellow
    [1,   'rgb(255,0,0)']      // 1000 K -> red
  ];

  function clamp(x, a, b) { return Math.min(Math.max(x, a), b); }
  function cToK(tc) { return tc + 273.15; }
  function kToC(tk) { return tk - 273.15; }

  function computeColorForTK(TK) {
    // Piecewise interpolation: 0K->purple, 500K->yellow, 1000K->red
    const t = clamp(TK, 0, 1000);
    let r, g, b;
    if (t <= 500) {
      const u = t / 500;
      // purple (128,0,128) to yellow (255,255,0)
      r = Math.round(128 + (255 - 128) * u);
      g = Math.round(0   + (255 - 0)   * u);
      b = Math.round(128 + (0   - 128) * u);
    } else {
      const u = (t - 500) / 500;
      // yellow (255,255,0) to red (255,0,0)
      r = 255;
      g = Math.round(255 + (0 - 255) * u);
      b = 0;
    }
    return `rgb(${r},${g},${b})`;
  }

  function recalc() {
    state.k = state.v0 / 273.15;
    state.V = state.k * state.T_K;
  }

  function setTempSliderForUnits(units, preservePhysicalTemperature = true) {
    state.T_units = units;
    if (units === 'C') {
      elT.min = -273; elT.max = 727; elT.step = 1;
      if (preservePhysicalTemperature) {
        state.T_slider_value = Math.round(kToC(state.T_K));
      }
    } else {
      elT.min = 0; elT.max = 1000; elT.step = 1;
      if (preservePhysicalTemperature) {
        state.T_slider_value = Math.round(state.T_K);
      }
    }
    // Clamp to range and push to element
    const min = Number(elT.min), max = Number(elT.max);
    state.T_slider_value = clamp(state.T_slider_value, min, max);
    elT.value = state.T_slider_value;
    updateTFromSlider();
  }

  function updateTFromSlider() {
    state.T_slider_value = Number(elT.value);
    state.T_K = (state.T_units === 'C') ? cToK(state.T_slider_value) : state.T_slider_value;
    recalc();
    elTLabel.textContent = state.T_units === 'C'
      ? `${state.T_slider_value} °C`
      : `${state.T_slider_value} K`;
    updatePlot();
    // p5 sketch reads state continuously; nothing else to do here.
  }

  function updateV0FromSlider() {
    state.v0 = Number(elV0.value);
    elV0Label.textContent = state.v0.toFixed(0);
    recalc();
    updatePlot();
  }

  function getLineTrace() {
    const N = 200;
    const x = new Array(N);
    const y = new Array(N);
    const colors = new Array(N);
    for (let i = 0; i < N; i++) {
      const t = i / (N - 1);
      let T_K, Tx;
      if (state.T_units === 'K') {
        Tx = 0 + t * (1000 - 0);
        T_K = Tx;
      } else {
        Tx = -273 + t * (727 - (-273));
        T_K = Tx + 273.15;
      }
      x[i] = Tx;
      y[i] = state.k * T_K;
      colors[i] = T_K;
    }
    return {
      x, y,
      mode: 'lines+markers',
      hoverinfo: 'skip',
      line: { width: 2, color: 'rgba(60,60,60,0.2)' },
      marker: {
        size: 4,
        color: colors,
        colorscale: colorScale,
        cmin: 0,
        cmax: 1000,
        showscale: false
      },
      name: 'Charles line'
    };
  }

  function getMarkerTrace() {
    const xVal = (state.T_units === 'K') ? state.T_K : state.T_K - 273.15;
    return {
      x: [xVal],
      y: [state.V],
      mode: 'markers',
      name: 'current',
      marker: {
        size: 12,
        color: computeColorForTK(state.T_K),
        line: { width: 1, color: '#222' }
      },
      hovertemplate:
        `T: %{x:.1f} ${state.T_units === 'K' ? 'K' : '°C'}<br>` +
        'V: %{y:.1f}<extra></extra>'
    };
  }

  function getLayout() {
    const xTitle = state.T_units === 'K' ? 'temperature K' : 'temperature °C';
    const xRange = state.T_units === 'K' ? [0, 1000] : [-273, 727];
    return {
      margin: { l: 60, r: 20, t: 10, b: 50 },
      xaxis: {
        title: xTitle,
        range: xRange,
        zeroline: false,
        gridcolor: '#eaeaea'
      },
      yaxis: {
        title: 'volume',
        range: [0, 2000],
        gridcolor: '#eaeaea'
      },
      showlegend: false,
      paper_bgcolor: 'rgba(0,0,0,0)',
      plot_bgcolor: '#fff',
      height: Math.max(360, plotDiv.clientHeight)
    };
  }

  function updatePlot() {
    const traces = [getLineTrace(), getMarkerTrace()];
    const layout = getLayout();
    const config = { responsive: true, displayModeBar: false };
    Plotly.react(plotDiv, traces, layout, config);
  }

  // Initialize controls and plot
  function init() {
    // Initial slider setup (Celsius default with value 270)
    elV0.value = state.v0;
    elV0Label.textContent = String(state.v0);
    radioC.checked = true;
    radioK.checked = false;

    setTempSliderForUnits('C', true); // sets min/max/step and syncs the label and plot

    // Event listeners
    elV0.addEventListener('input', updateV0FromSlider);
    elT.addEventListener('input', updateTFromSlider);

    radioK.addEventListener('change', () => {
      if (radioK.checked) {
        setTempSliderForUnits('K', true);
      }
    });
    radioC.addEventListener('change', () => {
      if (radioC.checked) {
        setTempSliderForUnits('C', true);
      }
    });

    window.addEventListener('resize', () => {
      Plotly.Plots.resize(plotDiv);
    });
  }

  // p5 piston sketch
  const pistonSketch = (p) => {
    const W = 300, H = 320;
    const wall = 16;
    const innerW = 150;
    const innerX = (W - innerW) / 2;
    const baseY = H - 30;
    const baseH = 16;
    const innerH = baseY - 20; // internal cavity usable height
    const plateH = 14;

    let colorLow, colorMid, colorHigh;

    p.setup = function() {
      const cnv = p.createCanvas(W, H);
      cnv.parent('piston-container');
      colorLow = p.color(128, 0, 128);
      colorMid = p.color(255, 255, 0);
      colorHigh = p.color(255, 0, 0);
    };

    function gasColor(TK) {
      const t = p.constrain(TK, 0, 1000);
      if (t <= 500) {
        return p.lerpColor(colorLow, colorMid, t / 500);
      } else {
        return p.lerpColor(colorMid, colorHigh, (t - 500) / 500);
      }
    }

    function mapVolumeToGasHeight(V) {
      const vClamped = p.constrain(V, 0, 2000);
      return p.map(vClamped, 0, 2000, 8, innerH);
    }

    function drawCylinder() {
      p.noStroke();
      p.fill('#ffffff');
      p.rect(innerX, baseY - innerH, innerW, innerH); // cavity background

      p.fill('#2f6fb6'); // walls
      p.rect(innerX - wall, baseY - innerH - baseH, wall, innerH + baseH);
      p.rect(innerX + innerW, baseY - innerH - baseH, wall, innerH + baseH);
      p.rect(innerX - wall, baseY, innerW + 2 * wall, baseH);
    }

    function drawArrows(yTop) {
      p.stroke(230, 120, 20);
      p.fill(230, 120, 20);
      const n = 7;
      const margin = 14;
      const spacing = (innerW - 2 * margin) / (n - 1);
      const y0 = yTop - 24;
      for (let i = 0; i < n; i++) {
        const x = innerX + margin + i * spacing;
        p.line(x, y0, x, yTop - 6);
        p.triangle(x - 4, yTop - 10, x + 4, yTop - 10, x, yTop - 2);
      }
    }

    p.draw = function() {
      p.background(250);

      // Geometry
      const hGas = mapVolumeToGasHeight(state.V);
      const gasTopY = baseY - hGas;

      // Cylinder
      drawCylinder();

      // Gas
      p.noStroke();
      p.fill(gasColor(state.T_K));
      p.rect(innerX, gasTopY, innerW, hGas);

      // Piston plate at gasTopY
      p.fill(120);
      p.rect(innerX, gasTopY - plateH, innerW, plateH);

      // Piston block above plate
      const blockW = 36;
      const blockH = 48;
      const blockX = innerX + (innerW - blockW) / 2;
      const blockY = gasTopY - plateH - blockH;
      p.rect(blockX, blockY, blockW, blockH, 4);

      // Pressure arrows
      drawArrows(gasTopY - plateH);
    };
  };

  // Start
  init();
  new p5(pistonSketch);

})();
</script>
</body>
</html>
