<script>
  // State
  let angleA = 306;
  let angleB = 54;
  let angleC = 180;
  let selectedVertex = 'C'; // default checked

  // DOM references
  const sliderA = document.getElementById('slider-a');
  const sliderB = document.getElementById('slider-b');
  const sliderC = document.getElementById('slider-c');

  const radioA = document.getElementById('radio-a');
  const radioB = document.getElementById('radio-b');
  const radioC = document.getElementById('radio-c');
  const radioNone = document.getElementById('radio-none');

  // Info panel spans
  const ids = {
    arcLabel: [document.getElementById('arc-label-1'), document.getElementById('arc-label-2'), document.getElementById('arc-label-3')],
    arcValue: [document.getElementById('arc-value-1'), document.getElementById('arc-value-2'), document.getElementById('arc-value-3')],
    vertLabel:[document.getElementById('vertex-label-1'), document.getElementById('vertex-label-2'), document.getElementById('vertex-label-3')],
    vertValue:[document.getElementById('vertex-value-1'), document.getElementById('vertex-value-2'), document.getElementById('vertex-value-3')],
    arcTotal: document.getElementById('arc-total'),
    vertTotal: document.getElementById('vertex-total'),
  };

  function modDeg(d){ return ( (d % 360) + 360 ) % 360; }
  function toRad(d){ return d * Math.PI / 180; }
  function toDeg(r){ return r * 180 / Math.PI; }
  function degStr(v){ return `${(Math.round(v*10)/10).toFixed(1)}°`; }

  function computeArcs(){
    const arcAB = modDeg(angleB - angleA);
    const arcBC = modDeg(angleC - angleB);
    const arcCA = modDeg(angleA - angleC);
    return { arcAB, arcBC, arcCA };
  }
  function computeVertexAngles(arcs){
    return {
      A: arcs.arcBC / 2,
      B: arcs.arcCA / 2,
      C: arcs.arcAB / 2
    };
  }

  function clearRow(i){
    ids.arcLabel[i].innerText = '';
    ids.arcValue[i].innerText = '';
    ids.vertLabel[i].innerText = '';
    ids.vertValue[i].innerText = '';
  }
  function fillRow(i, arcLabel, arcVal, vertLabel, vertVal){
    ids.arcLabel[i].innerText = arcLabel;
    ids.arcValue[i].innerText = degStr(arcVal);
    ids.vertLabel[i].innerText = vertLabel;
    ids.vertValue[i].innerText = degStr(vertVal);
  }

  function updateInfoPanel(){
    const arcs = computeArcs();
    const verts = computeVertexAngles(arcs);

    // Reset all rows
    clearRow(1); clearRow(2);

    if(selectedVertex === 'A'){
      fillRow(0, 'Arc BC', arcs.arcBC, 'Vertex A', verts.A);
      ids.arcTotal.innerText = '?';
      ids.vertTotal.innerText = '?';
    }else if(selectedVertex === 'B'){
      fillRow(0, 'Arc CA', arcs.arcCA, 'Vertex B', verts.B);
      ids.arcTotal.innerText = '?';
      ids.vertTotal.innerText = '?';
    }else if(selectedVertex === 'C'){
      fillRow(0, 'Arc AB', arcs.arcAB, 'Vertex C', verts.C);
      ids.arcTotal.innerText = '?';
      ids.vertTotal.innerText = '?';
    }else{ // None: show all
      fillRow(0, 'Arc AB', arcs.arcAB, 'Vertex C', verts.C);
      fillRow(1, 'Arc BC', arcs.arcBC, 'Vertex A', verts.A);
      fillRow(2, 'Arc CA', arcs.arcCA, 'Vertex B', verts.B);
      ids.arcTotal.innerText = '360°';
      ids.vertTotal.innerText = '180°';
    }
  }

  // p5 sketch
  let cnv, W=640, H=520;
  let cx, cy, R;
  let dragging = null;

  function setupCanvasSize(){
    const cont = document.getElementById('canvas-container');
    const pad = 0;
    const maxW = cont.clientWidth - 2; // borders
    W = Math.max(360, Math.min(720, maxW));
    H = Math.round(W * 0.78) + 10; // room for aesthetics
    if(cnv) resizeCanvas(W, H);
    cx = W*0.48;
    cy = H*0.46;
    R  = Math.min(W, H)*0.28;
  }

  function setup(){
    setupCanvasSize();
    cnv = createCanvas(W, H);
    cnv.parent('canvas-container');
    pixelDensity(2);
    noLoop();
    drawScene();
  }

  function windowResized(){
    setupCanvasSize();
    redraw();
  }

  function drawArrowhead(x, y, angle, col){
    push();
    translate(x,y);
    rotate(angle);
    fill(col);
    noStroke();
    const L = 12, Wd = 7;
    beginShape();
    vertex(0,0);
    vertex(-L, -Wd);
    vertex(-L,  Wd);
    endShape(CLOSE);
    pop();
  }

  function drawArcWithArrow(cx,cy,r,startRad,endRad,col,thick=6){
    noFill();
    stroke(col);
    strokeWeight(thick);
    arc(cx,cy, 2*r, 2*r, startRad, endRad);
    // arrow at end, tangent direction (CCW): angle + PI/2
    const ex = cx + r * Math.cos(endRad);
    const ey = cy + r * Math.sin(endRad);
    const tangent = endRad + Math.PI/2;
    drawArrowhead(ex, ey, tangent, col);
  }

  function pointFromAngle(deg, radius=R){
    const r = toRad(deg);
    return { x: cx + radius * Math.cos(r), y: cy + radius * Math.sin(r) };
  }

  function angleAtPoint(px, py){
    return modDeg(toDeg(Math.atan2(py - cy, px - cx)));
  }

  function smallestArc(aRad, bRad){
    // returns [start, end] such that moving CCW from start to end is the smaller span (<= PI)
    const ccw = ((bRad - aRad) % (2*Math.PI) + 2*Math.PI) % (2*Math.PI);
    if(ccw <= Math.PI){
      return [aRad, aRad + ccw];
    }else{
      const ccw2 = (aRad - bRad + 2*Math.PI) % (2*Math.PI);
      return [bRad, bRad + ccw2];
    }
  }

  function drawBackgroundSectors(arcs){
    noStroke();
    const bigR = Math.max(W, H)*0.95;
    const aA = toRad(angleA);
    const aB = toRad(angleB);
    const aC = toRad(angleC);
    // AB (greenish)
    fill('#e0f0e0');
    arc(cx,cy,bigR,bigR, aA, aA + toRad(arcs.arcAB), PIE);
    // BC (yellowish)
    fill('#f5f5d5');
    arc(cx,cy,bigR,bigR, aB, aB + toRad(arcs.arcBC), PIE);
    // CA (blueish)
    fill('#e0e8f8');
    arc(cx,cy,bigR,bigR, aC, aC + toRad(arcs.arcCA), PIE);
  }

  function drawTriangleAndCircle(PA, PB, PC){
    // Circle
    stroke('#666666');
    strokeWeight(2);
    fill('#aaccde');
    circle(cx, cy, R*2);
    // center dot
    noStroke();
    fill('#7b7b7b');
    circle(cx, cy, 6);

    // Triangle
    stroke('#666666');
    strokeWeight(3);
    fill('rgba(173,216,230,0.5)');
    beginShape();
    vertex(PA.x, PA.y);
    vertex(PB.x, PB.y);
    vertex(PC.x, PC.y);
    endShape(CLOSE);

    // Vertex labels
    noStroke();
    fill('#00008B');
    textAlign(CENTER, CENTER);
    textStyle(BOLD);
    textSize(Math.max(14, Math.round(R*0.22)));
    const oa = pointFromAngle(angleA, R*1.16);
    const ob = pointFromAngle(angleB, R*1.16);
    const oc = pointFromAngle(angleC, R*1.16);
    text('A', oa.x, oa.y);
    text('B', ob.x, ob.y);
    text('C', oc.x, oc.y);
  }

  function drawHighlights(PA, PB, PC, arcs, verts){
    if(selectedVertex === 'None') return;

    let startDeg, spanDeg, arcLabelText, vertex, vertTextPos, ray1, ray2, vPoint;
    if(selectedVertex === 'A'){
      startDeg = angleB; spanDeg = arcs.arcBC; arcLabelText = `θ = ${degStr(arcs.arcBC)}`;
      vPoint = PA;
      ray1 = Math.atan2(PB.y - PA.y, PB.x - PA.x); // A->B
      ray2 = Math.atan2(PC.y - PA.y, PC.x - PA.x); // A->C
    }else if(selectedVertex === 'B'){
      startDeg = angleC; spanDeg = arcs.arcCA; arcLabelText = `θ = ${degStr(arcs.arcCA)}`;
      vPoint = PB;
      ray1 = Math.atan2(PC.y - PB.y, PC.x - PB.x); // B->C
      ray2 = Math.atan2(PA.y - PB.y, PA.x - PB.x); // B->A
    }else{ // 'C'
      startDeg = angleA; spanDeg = arcs.arcAB; arcLabelText = `θ = ${degStr(arcs.arcAB)}`;
      vPoint = PC;
      ray1 = Math.atan2(PA.y - PC.y, PA.x - PC.x); // C->A
      ray2 = Math.atan2(PB.y - PC.y, PB.x - PC.x); // C->B
    }

    // Central angle on circumference (outer arc with arrow)
    const startRad = toRad(startDeg);
    const endRad   = startRad + toRad(spanDeg);
    drawArcWithArrow(cx, cy, R*1.02, startRad, endRad, '#008000', 6);

    // Label at mid of big arc
    const mid = (startRad + endRad)/2;
    noStroke();
    fill('#008000');
    textAlign(CENTER, CENTER);
    textSize(Math.max(12, Math.round(R*0.17)));
    textStyle(NORMAL);
    const tx = cx + Math.cos(mid) * R * 1.30;
    const ty = cy + Math.sin(mid) * R * 1.30;
    text(arcLabelText, tx, ty);

    // tiny center arc for visual cue
    noFill();
    stroke('#008000');
    strokeWeight(4);
    arc(cx, cy, R*0.75, R*0.75, startRad, endRad);

    // Inscribed red angle at selected vertex (small arc between the two sides)
    const [s, e] = smallestArc(ray1, ray2);
    const innerR = R*0.22;
    stroke('#FF0000');
    strokeWeight(5);
    noFill();
    arc(vPoint.x, vPoint.y, innerR*2, innerR*2, s, e);
    // label θ/2
    const midLocal = (s + e) / 2;
    const lx = vPoint.x + Math.cos(midLocal) * (innerR + 16);
    const ly = vPoint.y + Math.sin(midLocal) * (innerR + 16);
    noStroke();
    fill('#FF0000');
    textSize(Math.max(12, Math.round(R*0.17)));
    text(`θ\n— = ${degStr(spanDeg/2)}`, lx, ly - 6); // simple stacked fraction look
  }

  function drawScene(){
    background('#ffffff');

    const arcs = computeArcs();
    const verts = computeVertexAngles(arcs);

    // Background sectors
    drawBackgroundSectors(arcs);

    // Geometry points
    const PA = pointFromAngle(angleA);
    const PB = pointFromAngle(angleB);
    const PC = pointFromAngle(angleC);

    // Core graphics
    drawTriangleAndCircle(PA, PB, PC);

    // Highlights according to selection
    drawHighlights(PA, PB, PC, arcs, verts);
  }

  // Interaction: sliders
  function setAngleFromSlider(which, value){
    const v = Number(value);
    if(which==='A'){ angleA = modDeg(v); sliderA.value = angleA; }
    if(which==='B'){ angleB = modDeg(v); sliderB.value = angleB; }
    if(which==='C'){ angleC = modDeg(v); sliderC.value = angleC; }
    updateInfoPanel();
    redraw();
  }

  sliderA.addEventListener('input', e => setAngleFromSlider('A', e.target.value));
  sliderB.addEventListener('input', e => setAngleFromSlider('B', e.target.value));
  sliderC.addEventListener('input', e => setAngleFromSlider('C', e.target.value));

  // Radio buttons
  function onRadioChange(){
    if(radioA.checked) selectedVertex = 'A';
    else if(radioB.checked) selectedVertex = 'B';
    else if(radioC.checked) selectedVertex = 'C';
    else if(radioNone.checked) selectedVertex = 'None';
    updateInfoPanel();
    redraw();
  }
  [radioA, radioB, radioC, radioNone].forEach(r => r.addEventListener('change', onRadioChange));

  // Mouse dragging on canvas (p5)
  function mousePressed(){
    // Check near vertices
    const pA = pointFromAngle(angleA);
    const pB = pointFromAngle(angleB);
    const pC = pointFromAngle(angleC);
    const eps = Math.max(10, R*0.08);
    if(dist(mouseX, mouseY, pA.x, pA.y) < eps) dragging = 'A';
    else if(dist(mouseX, mouseY, pB.x, pB.y) < eps) dragging = 'B';
    else if(dist(mouseX, mouseY, pC.x, pC.y) < eps) dragging = 'C';
  }
  function mouseDragged(){
    if(!dragging) return;
    const ang = angleAtPoint(mouseX, mouseY);
    if(dragging==='A'){ angleA = ang; sliderA.value = angleA; }
    if(dragging==='B'){ angleB = ang; sliderB.value = angleB; }
    if(dragging==='C'){ angleC = ang; sliderC.value = angleC; }
    updateInfoPanel();
    redraw();
  }
  function mouseReleased(){ dragging = null; }

  // Initialize display
  updateInfoPanel();
</script>
</body>
