          <button id="btn-inc-xi">+</button>
        </div>
        <div class="control-row" style="justify-content: flex-end;">
          <span class="value-span" id="value-xi">0.400</span>
        </div>
      </div>
    </div>

    <div id="visualization-panel" style="flex-grow: 1;">
      <div id="plot-concentration"></div>
      <div id="plot-temperature"></div>
    </div>
  </div>

<script>
(function(){
  // ---- Global state ----
  const Nx = 100;
  const x = new Float64Array(Nx);
  const dx = 1.0 / (Nx - 1);
  const dx2 = dx * dx;
  for (let i=0;i<Nx;i++) x[i] = i * dx;

  let state = {
    c: new Float64Array(Nx),
    th: new Float64Array(Nx),
    tau: 1.0,
    phi: 2.0,
    beta: 0.2,
    gamma: 20,
    xi: 0.4,
    yMinTheta: 0.95,
    yMaxTheta: 1.2
  };

  // DOM refs
  const el = {
    tau: document.getElementById('slider-tau'),
    phi: document.getElementById('slider-phi'),
    beta: document.getElementById('slider-beta'),
    gamma: document.getElementById('slider-gamma'),
    xi: document.getElementById('slider-xi'),
    vTau: document.getElementById('value-tau'),
    vPhi: document.getElementById('value-phi'),
    vBeta: document.getElementById('value-beta'),
    vGamma: document.getElementById('value-gamma'),
    vXi: document.getElementById('value-xi'),
    plotC: document.getElementById('plot-concentration'),
    plotT: document.getElementById('plot-temperature'),
    decTau: document.getElementById('btn-dec-tau'),
    incTau: document.getElementById('btn-inc-tau'),
    decPhi: document.getElementById('btn-dec-phi'),
    incPhi: document.getElementById('btn-inc-phi'),
    decBeta: document.getElementById('btn-dec-beta'),
    incBeta: document.getElementById('btn-inc-beta'),
    decGamma: document.getElementById('btn-dec-gamma'),
    incGamma: document.getElementById('btn-inc-gamma'),
    decXi: document.getElementById('btn-dec-xi'),
    incXi: document.getElementById('btn-inc-xi')
  };

  // ---- Utilities ----
  function fmtTau(v){ return (+v).toFixed(2); }
  function fmtPhi(v){ return (+v).toFixed(1); }
  function fmtBeta(v){ return (+v).toFixed(2); }
  function fmtGamma(v){ return Math.round(+v).toString(); }
  function fmtXi(v){ return (+v).toFixed(3); }
  function fmtVal(v){ return (+v).toFixed(5); }
  function clamp(val, lo, hi){ return Math.max(lo, Math.min(hi, val)); }

  function interpLinear(arr, xi) {
    if (xi <= 0) return arr[0];
    if (xi >= 1) return arr[Nx-1];
    const idx = Math.floor(xi / dx);
    const x0 = idx * dx;
    const w = (xi - x0) / dx;
    const a = arr[idx];
    const b = arr[idx+1];
    return a * (1 - w) + b * w;
  }

  // ---- Model: RK4 MOL solver ----
  const invdx2 = 1.0 / dx2;

  function computeRHS(c, th, dc, dth, phi2, beta, gamma) {
    // i = 0 (Neumann symmetry)
    let i = 0;
    let t0 = Math.max(th[0], 1e-8);
    let exp0 = Math.exp(gamma * (1.0 - 1.0 / t0));
    let r0  = phi2 * c[0] * exp0;
    dc[0]   = 2.0 * (c[1] - c[0]) * invdx2 - r0;
    dth[0]  = 2.0 * (th[1] - th[0]) * invdx2 + beta * r0;

    // interior
    for (i = 1; i < Nx-1; i++) {
      const lapC = (c[i-1] - 2*c[i] + c[i+1]) * invdx2;
      const ti   = Math.max(th[i], 1e-8);
      const expi = Math.exp(gamma * (1.0 - 1.0 / ti));
      const r    = phi2 * c[i] * expi;
      dc[i]      = lapC - r;

      const lapT = (th[i-1] - 2*th[i] + th[i+1]) * invdx2;
      dth[i]     = lapT + beta * r;
    }

    // i = Nx-1 (Dirichlet at surface)
    dc[Nx-1]  = 0.0;
    dth[Nx-1] = 0.0;
  }

  // Preallocate work arrays
  const k1c = new Float64Array(Nx), k2c = new Float64Array(Nx), k3c = new Float64Array(Nx), k4c = new Float64Array(Nx);
  const k1t = new Float64Array(Nx), k2t = new Float64Array(Nx), k3t = new Float64Array(Nx), k4t = new Float64Array(Nx);
  const tmpC = new Float64Array(Nx), tmpT = new Float64Array(Nx);

  function rk4Integrate(tEnd, phi, beta, gamma) {
    // Initial conditions
    const c = state.c;
    const th = state.th;
    for (let i=0;i<Nx-1;i++) c[i] = 0.0;
    c[Nx-1] = 1.0;
    for (let i=0;i<Nx;i++) th[i] = 1.0; // includes boundary at 1

    const phi2 = phi * phi;

    // time step (tuned for stability and interactivity)
    const dtBase = 2.0 * dx2; // RK4 allows a relatively larger step than FE
    let t = 0.0;
    while (t < tEnd - 1e-12) {
      const dt = Math.min(dtBase, tEnd - t);

      // k1
      computeRHS(c, th, k1c, k1t, phi2, beta, gamma);

      // tmp for k2
      for (let i=0;i<Nx-1;i++) { tmpC[i] = c[i] + 0.5*dt*k1c[i]; tmpT[i] = th[i] + 0.5*dt*k1t[i]; }
      tmpC[Nx-1] = 1.0; tmpT[Nx-1] = 1.0;
      computeRHS(tmpC, tmpT, k2c, k2t, phi2, beta, gamma);

      // tmp for k3
      for (let i=0;i<Nx-1;i++) { tmpC[i] = c[i] + 0.5*dt*k2c[i]; tmpT[i] = th[i] + 0.5*dt*k2t[i]; }
      tmpC[Nx-1] = 1.0; tmpT[Nx-1] = 1.0;
      computeRHS(tmpC, tmpT, k3c, k3t, phi2, beta, gamma);

      // tmp for k4
      for (let i=0;i<Nx-1;i++) { tmpC[i] = c[i] + dt*k3c[i]; tmpT[i] = th[i] + dt*k3t[i]; }
      tmpC[Nx-1] = 1.0; tmpT[Nx-1] = 1.0;
      computeRHS(tmpC, tmpT, k4c, k4t, phi2, beta, gamma);

      // advance
      const dt6 = dt/6.0;
      for (let i=0;i<Nx-1;i++) {
        c[i]  += dt6 * (k1c[i] + 2*k2c[i] + 2*k3c[i] + k4c[i]);
        th[i] += dt6 * (k1t[i] + 2*k2t[i] + 2*k3t[i] + k4t[i]);
        th[i] = Math.max(th[i], 1e-8); // keep positive
        c[i]  = Math.max(c[i], 0.0);
      }
      c[Nx-1] = 1.0;
      th[Nx-1] = 1.0;

      t += dt;
    }
  }

  // ---- Plotting ----
  function buildAnnotationsC(tau, xi, cxi) {
    const text = `&tau; = ${fmtTau(tau)}<br>&xi; = ${fmtXi(xi)}<br>c = ${fmtVal(cxi)}`;
    return [{
      xref: 'paper', yref: 'paper', x: 0.85, y: 0.6, text, showarrow: false,
      font: {size: 12}
    }];
  }
  function buildAnnotationsT(tau, xi, thxi) {
    const text = `&tau; = ${fmtTau(tau)}<br>&xi; = ${fmtXi(xi)}<br>&theta; = ${fmtVal(thxi)}`;
    return [{
      xref: 'paper', yref: 'paper', x: 0.85, y: 0.6, text, showarrow: false,
      font: {size: 12}
    }];
  }

  function drawPlots() {
    const { c, th, tau, xi } = state;

    // For temperature axis range estimate (used also for marker y_min)
    let tmin = Infinity, tmax = -Infinity;
    for (let i=0;i<Nx;i++){ if (th[i] < tmin) tmin=th[i]; if (th[i] > tmax) tmax=th[i]; }
    const pad = 0.05 * Math.max(1e-12, tmax - tmin);
    state.yMinTheta = tmin - pad;
    state.yMaxTheta = tmax + pad;

    const cxi = interpLinear(c, xi);
    const thxi = interpLinear(th, xi);

    // Concentration plot
    const traceC = {
      type: 'scatter', mode: 'lines',
      x: Array.from(x), y: Array.from(c),
      line: {color: '#1f77b4', width: 2},
      name: 'c(ξ)'
    };
    const traceCmarker = {
      type: 'scatter', mode: 'lines',
      x: [xi, xi, 0], y: [0, cxi, cxi],
      line: {color: '#333333', width: 3},
      hoverinfo: 'skip',
      showlegend: false
    };
    const layoutC = {
      margin: {l: 60, r: 20, t: 10, b: 40},
      xaxis: {title: 'ξ', range: [0,1.0], zeroline: false},
      yaxis: {title: 'c', range: [0, 1.1], zeroline: false},
      annotations: buildAnnotationsC(tau, xi, cxi)
    };

    // Temperature plot
    const traceT = {
      type: 'scatter', mode: 'lines',
      x: Array.from(x), y: Array.from(th),
      line: {color: '#2ca02c', width: 2},
      name: 'θ(ξ)'
    };
    const yMinForMarker = state.yMinTheta;
    const traceTmarker = {
      type: 'scatter', mode: 'lines',
      x: [xi, xi, 0], y: [yMinForMarker, thxi, thxi],
      line: {color: '#333333', width: 3},
      hoverinfo: 'skip',
      showlegend: false
    };
    const layoutT = {
      margin: {l: 60, r: 20, t: 10, b: 40},
      xaxis: {title: 'ξ', range: [0,1.0], zeroline: false},
      yaxis: {title: 'θ', autorange: true, zeroline: false},
      annotations: buildAnnotationsT(tau, xi, thxi)
    };

    Plotly.react(el.plotC, [traceC, traceCmarker], layoutC, {displayModeBar: false});
    Plotly.react(el.plotT, [traceT, traceTmarker], layoutT, {displayModeBar: false});
  }

  function updateMarkersOnly() {
    const xi = state.xi;
    const cxi = interpLinear(state.c, xi);
    const thxi = interpLinear(state.th, xi);

    // Update only marker traces and annotations
    Plotly.restyle(el.plotC, {
      x: [[xi, xi, 0]],
      y: [[0, cxi, cxi]]
    }, [1]);
    Plotly.relayout(el.plotC, {annotations: buildAnnotationsC(state.tau, xi, cxi)});

    const yMinForMarker = state.yMinTheta; // estimated from last render
    Plotly.restyle(el.plotT, {
      x: [[xi, xi, 0]],
      y: [[yMinForMarker, thxi, thxi]]
    }, [1]);
    Plotly.relayout(el.plotT, {annotations: buildAnnotationsT(state.tau, xi, thxi)});
  }

  function runSimulationAndRender() {
    // update state from sliders
    state.tau   = parseFloat(el.tau.value);
    state.phi   = parseFloat(el.phi.value);
    state.beta  = parseFloat(el.beta.value);
    state.gamma = parseFloat(el.gamma.value);

    // Integrate PDEs
    rk4Integrate(state.tau, state.phi, state.beta, state.gamma);

    // Render plots
    drawPlots();

    // Update value display spans
    el.vTau.textContent   = fmtTau(state.tau);
    el.vPhi.textContent   = fmtPhi(state.phi);
    el.vBeta.textContent  = fmtBeta(state.beta);
    el.vGamma.textContent = fmtGamma(state.gamma);
    el.vXi.textContent    = fmtXi(state.xi);
  }

  // ---- Event wiring ----
  function attachStepButtons(slider, decBtn, incBtn) {
    decBtn.addEventListener('click', () => {
      const step = parseFloat(slider.step);
      const min  = parseFloat(slider.min);
      const val  = parseFloat(slider.value) - step;
      slider.value = Math.max(min, +(val.toFixed(10)));
      slider.dispatchEvent(new Event('input', {bubbles: true}));
    });
    incBtn.addEventListener('click', () => {
      const step = parseFloat(slider.step);
      const max  = parseFloat(slider.max);
      const val  = parseFloat(slider.value) + step;
      slider.value = Math.min(max, +(val.toFixed(10)));
      slider.dispatchEvent(new Event('input', {bubbles: true}));
    });
  }

  // Input handlers
  el.tau.addEventListener('input', runSimulationAndRender);
  el.phi.addEventListener('input', runSimulationAndRender);
  el.beta.addEventListener('input', runSimulationAndRender);
  el.gamma.addEventListener('input', runSimulationAndRender);

  el.xi.addEventListener('input', () => {
    state.xi = parseFloat(el.xi.value);
    el.vXi.textContent = fmtXi(state.xi);
    updateMarkersOnly();
  });

  attachStepButtons(el.tau,   el.decTau,   el.incTau);
  attachStepButtons(el.phi,   el.decPhi,   el.incPhi);
  attachStepButtons(el.beta,  el.decBeta,  el.incBeta);
  attachStepButtons(el.gamma, el.decGamma, el.incGamma);
  attachStepButtons(el.xi,    el.decXi,    el.incXi);

  // ---- Initialize page ----
  function init() {
    // Set initial display values
    el.vTau.textContent   = fmtTau(el.tau.value);
    el.vPhi.textContent   = fmtPhi(el.phi.value);
    el.vBeta.textContent  = fmtBeta(el.beta.value);
    el.vGamma.textContent = fmtGamma(el.gamma.value);
    el.vXi.textContent    = fmtXi(el.xi.value);

    state.tau   = parseFloat(el.tau.value);
    state.phi   = parseFloat(el.phi.value);
    state.beta  = parseFloat(el.beta.value);
    state.gamma = parseFloat(el.gamma.value);
    state.xi    = parseFloat(el.xi.value);

    runSimulationAndRender();
  }

  init();
})();
</script>
</body>
</html>
