<script>
(function() {
  // Static data for table and data points
  const ages = [30, 35, 40, 45, 50, 55];
  const volumes = [289, 445, 565, 704, 833, 924];

  // Populate data table
  function populateTable() {
    const tbody = document.getElementById('data-body');
    tbody.innerHTML = '';
    for (let i = 0; i < ages.length; i++) {
      const tr = document.createElement('tr');
      const tdAge = document.createElement('td');
      tdAge.textContent = ages[i];
      const tdVol = document.createElement('td');
      tdVol.textContent = volumes[i];
      tr.appendChild(tdAge);
      tr.appendChild(tdVol);
      tbody.appendChild(tr);
    }
  }

  // Utility formatters for slider readouts
  function fmt0(x) { return Number(x).toFixed(0); }
  function fmt1(x) { return Number(x).toFixed(1); }
  function fmt3(x) { return Number(x).toFixed(3); }
  function fmt4(x) { return Number(x).toFixed(4); }

  let plotCreated = false;

  function update() {
    // Read slider values
    const bmax = parseFloat(document.getElementById('slider-bmax').value);
    const b0   = parseFloat(document.getElementById('slider-b0').value);
    const r    = parseFloat(document.getElementById('slider-r').value);
    const beta = parseFloat(document.getElementById('slider-beta').value);

    // Update value displays
    document.getElementById('value-bmax').textContent = fmt0(bmax);
    document.getElementById('value-b0').textContent   = (Math.abs(b0 - Math.round(b0)) < 1e-9) ? fmt0(b0) : fmt1(b0);
    document.getElementById('value-r').textContent    = fmt3(r);
    document.getElementById('value-beta').textContent = fmt3(beta);

    // t axis for curves
    const tMin = 15, tMax = 80, dt = 0.5;
    const tArr = [];
    for (let t = tMin; t <= tMax + 1e-9; t += dt) tArr.push(t);

    // Compute Richards curve
    let yRichards = new Array(tArr.length).fill(null);
    let M = Math.pow(bmax / b0, beta) - 1;
    if (isFinite(M) && M > -0.999999) {
      for (let i = 0; i < tArr.length; i++) {
        const t = tArr[i];
        const denom = Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
        yRichards[i] = bmax / denom;
      }
    }

    // Inflection properties
    let u = NaN, p = NaN;
    let yLaw = new Array(tArr.length).fill(null);

    const lnArg = ((Math.pow(bmax / b0, beta) - 1) * beta);
    if (lnArg > 0 && isFinite(lnArg) && isFinite(r) && r > 0 && beta > 0) {
      const ti = Math.log(lnArg) / r;
      const Vi = bmax * Math.pow((beta + 1) / (2 * beta), 1 / beta);
      const Vip = (r / beta) * Vi * (1 - Math.pow(Vi / bmax, beta));
      if (isFinite(ti) && isFinite(Vi) && isFinite(Vip) && Vip !== 0) {
        u = ti - (4 / 5) * (Vi / Vip);
        const denom = ti - u;
        if (denom > 0) {
          p = Vi / Math.pow(denom, 4 / 5);
          // 4/5 law curve
          for (let i = 0; i < tArr.length; i++) {
            const t = tArr[i];
            yLaw[i] = (t > u) ? p * Math.pow(t - u, 4 / 5) : null;
          }
        }
      }
    }

    // Update results display
    const resU = document.getElementById('result-u');
    const resP = document.getElementById('result-p');
    if (isFinite(u)) {
      resU.textContent = 'rejuvenation bias u = ' + fmt4(u);
    } else {
      resU.textContent = 'rejuvenation bias u = —';
    }
    if (isFinite(p)) {
      resP.textContent = 'site productivity p = ' + fmt4(p);
    } else {
      resP.textContent = 'site productivity p = —';
    }

    // Traces
    const dataPoints = {
      x: ages,
      y: volumes,
      type: 'scatter',
      mode: 'markers',
      marker: { color: 'blue', size: 10, line: { color: 'navy', width: 1 } },
      name: 'data'
    };
    const traceRichards = {
      x: tArr,
      y: yRichards,
      type: 'scatter',
      mode: 'lines',
      line: { color: '#f39c12', width: 3 },
      name: 'Richards curve'
    };
    const traceLaw = {
      x: tArr,
      y: yLaw,
      type: 'scatter',
      mode: 'lines',
      line: { color: '#1f77b4', width: 3 },
      name: '4/5 law'
    };

    const layout = {
      title: 'forest growth curve',
      plot_bgcolor: '#FFFFF0',
      showlegend: false,
      margin: { t: 40, r: 60, b: 60, l: 80 },
      xaxis: {
        title: 'stand age (years)',
        range: [15, 80],
        showgrid: true,
        gridcolor: 'lightgrey',
        zeroline: false
      },
      yaxis: {
        title: 'wood volume, m³ ha⁻¹',
        range: [0, Math.max(1600, Math.ceil(bmax * 1.05 / 50) * 50)],
        showgrid: true,
        gridcolor: 'lightgrey',
        zeroline: false
      },
      annotations: [
        {
          xref: 'paper',
          yref: 'paper',
          x: 1.06,
          y: 0.5,
          text: 'Richards curve vs 4/5 law',
          showarrow: false,
          textangle: -90,
          font: { color: '#666' }
        }
      ]
    };

    const config = { responsive: true, displayModeBar: false };

    if (!plotCreated) {
      Plotly.newPlot('plot-container', [dataPoints, traceRichards, traceLaw], layout, config);
      plotCreated = true;
    } else {
      Plotly.react('plot-container', [dataPoints, traceRichards, traceLaw], layout, config);
    }
  }

  // Initialize
  document.addEventListener('DOMContentLoaded', () => {
    populateTable();

    // Attach update to sliders
    document.querySelectorAll('#control-panel input[type="range"]').forEach(sl => {
      sl.addEventListener('input', update);
    });

    // Initial plot render
    update();
  });
})();
</script>
</body>
</html>
