  <script>
  // three.js setup
  const canvas = document.getElementById('main-canvas');
  const container = document.getElementById('canvas-container');
  const renderer = new THREE.WebGLRenderer({ canvas, antialias: true, alpha: false });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
  renderer.setClearColor(0xffffff, 1);

  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);

  const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 2000);
  camera.position.set(0, 0, 400);
  camera.lookAt(0, 0, 0);

  // Lighting
  const ambient = new THREE.AmbientLight(0xffffff, 0.6);
  scene.add(ambient);

  const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
  dirLight.position.set(200, 200, 300);
  scene.add(dirLight);

  // Main group for all segments
  const pineGroup = new THREE.Group();
  scene.add(pineGroup);

  // Utility: resize renderer and camera
  function resizeRendererToDisplaySize() {
    const w = container.clientWidth;
    const h = container.clientHeight;
    if (w === 0 || h === 0) return false;
    renderer.setSize(w, h, false);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    return true;
  }

  window.addEventListener('resize', () => {
    resizeRendererToDisplaySize();
    renderer.render(scene, camera);
  });

  // Controls
  const sliders = {
    units: document.getElementById('slider-units'),
    angle: document.getElementById('slider-angle'),
    conical: document.getElementById('slider-conical'),
    rotateX: document.getElementById('slider-rotate-x'),
    rotateZ: document.getElementById('slider-rotate-z')
  };

  // Plus buttons logic
  function wirePlus(btnId, sliderEl) {
    document.getElementById(btnId).addEventListener('click', () => {
      const step = parseFloat(sliderEl.step || '1');
      const max = parseFloat(sliderEl.max);
      let v = parseFloat(sliderEl.value);
      v = Math.min(max, v + step);
      sliderEl.value = (Math.round(v / step) * step).toString();
      redraw();
    });
  }
  wirePlus('btn-units-plus', sliders.units);
  wirePlus('btn-angle-plus', sliders.angle);
  wirePlus('btn-conical-plus', sliders.conical);
  wirePlus('btn-rotate-x-plus', sliders.rotateX);
  wirePlus('btn-rotate-z-plus', sliders.rotateZ);

  // Input events: sliders redraw on input
  Object.values(sliders).forEach(sl => sl.addEventListener('input', redraw));

  // Radio group change
  document.querySelectorAll('input[name="base-polygon"]').forEach(r => {
    r.addEventListener('change', redraw);
  });

  // Create a regular polygon THREE.Shape with n sides and given radius
  function makeRegularPolygonShape(n, radius) {
    const shape = new THREE.Shape();
    for (let i = 0; i < n; i++) {
      const a = (i / n) * Math.PI * 2 - Math.PI / 2;
      const x = radius * Math.cos(a);
      const y = radius * Math.sin(a);
      if (i === 0) shape.moveTo(x, y);
      else shape.lineTo(x, y);
    }
    shape.closePath();
    return shape;
  }

  // Global cache for the current base geometry to reuse across segments
  let currentBaseGeometry = null;

  function getSelectedPolygonSides() {
    const r = document.querySelector('input[name="base-polygon"]:checked');
    return parseInt(r.value, 10);
    }

  // Redraw function per spec
  function redraw() {
    resizeRendererToDisplaySize();

    // 1. Read values
    const n = getSelectedPolygonSides();
    const units = parseInt(sliders.units.value, 10);
    const angularAdvanceRad = parseFloat(sliders.angle.value) * Math.PI / 180;
    const conical = parseFloat(sliders.conical.value);
    const rotX = parseFloat(sliders.rotateX.value) * Math.PI / 180;
    const rotZ = parseFloat(sliders.rotateZ.value) * Math.PI / 180;

    // 2. Remove previous meshes
    while (pineGroup.children.length > 0) {
      const obj = pineGroup.children.pop();
      // Materials will be disposed to avoid WebGL leaks
      if (obj.material && obj.material.dispose) obj.material.dispose();
      // Geometry is shared; skip disposing here
    }

    // 3. Base geometry for n-gon (radius 10)
    if (currentBaseGeometry) {
      currentBaseGeometry.dispose();
      currentBaseGeometry = null;
    }
    const baseShape = makeRegularPolygonShape(n, 10);
    currentBaseGeometry = new THREE.ShapeGeometry(baseShape);
    currentBaseGeometry.computeVertexNormals();

    // 4. Loop to create segments
    for (let i = 0; i < units; i++) {
      const angle = i * angularAdvanceRad;
      const radius = 4 * Math.sqrt(i);
      const scale = 0.5 * Math.log(i + 2);

      const x = radius * Math.cos(angle);
      const y = radius * Math.sin(angle);
      const z = conical * radius;

      const material = new THREE.MeshStandardMaterial({
        color: 0xffffff,
        metalness: 0.15,
        roughness: 0.85,
        side: THREE.DoubleSide
      });
      material.color.setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6);

      const mesh = new THREE.Mesh(currentBaseGeometry, material);
      mesh.position.set(x, y, z);
      mesh.scale.set(scale, scale, 1);
      mesh.rotation.z = angle;

      pineGroup.add(mesh);
    }

    // 5. Apply master rotations
    pineGroup.rotation.set(rotX, 0, rotZ);

    // 6. Render scene
    renderer.render(scene, camera);
  }

  // Initial draw
  redraw();
  </script>
</body>
