  <!-- three.js + OrbitControls via ES modules -->
  <script type="module">
    import * as THREE from 'https://unpkg.com/three@0.150.1/build/three.module.js';
    import { OrbitControls } from 'https://unpkg.com/three@0.150.1/examples/jsm/controls/OrbitControls.js';

    // DOM elements
    const btnSC  = document.getElementById('btn-sc');
    const btnBCC = document.getElementById('btn-bcc');
    const btnFCC = document.getElementById('btn-fcc');
    const inputH = document.getElementById('input-h');
    const inputK = document.getElementById('input-k');
    const inputL = document.getElementById('input-l');
    const sliderOpacity = document.getElementById('slider-opacity');
    const opacityValue = document.getElementById('opacity-value');
    const canvasContainer = document.getElementById('canvas-container');

    // three.js setup
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xeeeeee);

    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
    camera.position.set(2.2, 1.8, 2.2);

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
    renderer.setPixelRatio(window.devicePixelRatio || 1);
    canvasContainer.appendChild(renderer.domElement);

    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.08;
    controls.target.set(0,0,0);

    // Lights
    scene.add(new THREE.AmbientLight(0xffffff, 0.45));
    const dir1 = new THREE.DirectionalLight(0xffffff, 0.6);
    dir1.position.set(2, 3, 4);
    scene.add(dir1);
    const dir2 = new THREE.DirectionalLight(0xffffff, 0.4);
    dir2.position.set(-3, -2, -1);
    scene.add(dir2);

    // Groups for easy clearing
    const atomsGroup  = new THREE.Group(); atomsGroup.name  = 'atomsGroup';
    const planesGroup = new THREE.Group(); planesGroup.name = 'planesGroup';
    scene.add(atomsGroup);
    scene.add(planesGroup);

    // Static visuals: unit cell edges and axes
    addUnitCellWireframe();
    addAxes();

    function addUnitCellWireframe() {
      const g = new THREE.BoxGeometry(1,1,1);
      const edges = new THREE.EdgesGeometry(g);
      const line = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x333333, linewidth: 1 }));
      scene.add(line);
    }
    function addAxes() {
      const len = 0.9;
      const o = new THREE.Vector3(0,0,0);
      const x = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), o, len, 0xff3333, 0.06, 0.04);
      const y = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), o, len, 0x33aa33, 0.06, 0.04);
      const z = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), o, len, 0x3377ff, 0.06, 0.04);
      scene.add(x,y,z);
    }

    // State
    let latticeType = 'SC'; // default active
    let H = 1, K = 1, L = 1;

    // Utilities
    const EPS = 1e-9;

    function gcd(a, b) {
      a = Math.abs(a); b = Math.abs(b);
      while (b !== 0) { const t = b; b = a % b; a = t; }
      return a;
    }
    function gcd3(a,b,c) {
      return gcd(gcd(a,b), c);
    }

    function setActiveButton(id) {
      [btnSC, btnBCC, btnFCC].forEach(b => b.classList.remove('active'));
      document.getElementById(id).classList.add('active');
    }

    // UI events
    btnSC.addEventListener('click', () => { latticeType='SC'; setActiveButton('btn-sc'); updateScene(); });
    btnBCC.addEventListener('click', () => { latticeType='BCC'; setActiveButton('btn-bcc'); updateScene(); });
    btnFCC.addEventListener('click', () => { latticeType='FCC'; setActiveButton('btn-fcc'); updateScene(); });

    function onMillerChange() {
      H = parseInt(inputH.value, 10) || 0;
      K = parseInt(inputK.value, 10) || 0;
      L = parseInt(inputL.value, 10) || 0;
      updateScene();
    }
    ['change','input'].forEach(evt => {
      inputH.addEventListener(evt, onMillerChange);
      inputK.addEventListener(evt, onMillerChange);
      inputL.addEventListener(evt, onMillerChange);
    });

    sliderOpacity.addEventListener('input', () => {
      const op = parseFloat(sliderOpacity.value);
      opacityValue.textContent = op.toFixed(2);
      // Only update material opacity without rebuilding geometry
      planesGroup.children.forEach(mesh => {
        if (mesh.material && 'opacity' in mesh.material) {
          mesh.material.opacity = op;
        }
      });
    });

    // Resize handling
    function resizeRenderer() {
      const rect = canvasContainer.getBoundingClientRect();
      const w = Math.max(320, rect.width);
      const h = Math.max(200, rect.height);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h, false);
    }
    window.addEventListener('resize', resizeRenderer);

    // Build scene according to state
    function updateScene() {
      // Clear groups
      while (atomsGroup.children.length) atomsGroup.remove(atomsGroup.children[0]);
      while (planesGroup.children.length) planesGroup.remove(planesGroup.children[0]);

      // Read state
      const h = parseInt(inputH.value, 10) || 0;
      const k = parseInt(inputK.value, 10) || 0;
      const l = parseInt(inputL.value, 10) || 0;
      const op = parseFloat(sliderOpacity.value);

      // Draw atoms
      drawAtoms(latticeType);

      // Draw planes
      if (!(h === 0 && k === 0 && l === 0)) {
        const g = gcd3(h,k,l) || 1;
        const hp = Math.trunc(h / g), kp = Math.trunc(k / g), lp = Math.trunc(l / g);
        drawPlanes(hp, kp, lp, op);
      }

      renderer.render(scene, camera);
    }

    function drawAtoms(type) {
      const atomRadius = 0.045;
      const geom = new THREE.SphereGeometry(atomRadius, 32, 16);
      const material = new THREE.MeshPhongMaterial({
        color: 0x2c2c2c,
        specular: 0x62d1c7,
        shininess: 60
      });

      // Corner positions for the unit cell [-0.5,0.5]^3
      const s = 0.5;
      const corners = [
        new THREE.Vector3(-s,-s,-s), new THREE.Vector3( s,-s,-s),
        new THREE.Vector3(-s, s,-s), new THREE.Vector3( s, s,-s),
        new THREE.Vector3(-s,-s, s), new THREE.Vector3( s,-s, s),
        new THREE.Vector3(-s, s, s), new THREE.Vector3( s, s, s),
      ];

      const positions = [];

      // SC: 8 corners
      positions.push(...corners);

      if (type === 'BCC') {
        positions.push(new THREE.Vector3(0,0,0));
      } else if (type === 'FCC') {
        positions.push(
          new THREE.Vector3( s, 0, 0), new THREE.Vector3(-s, 0, 0),
          new THREE.Vector3( 0, s, 0), new THREE.Vector3( 0,-s, 0),
          new THREE.Vector3( 0, 0, s), new THREE.Vector3( 0, 0,-s)
        );
      }
      // Create meshes
      positions.forEach(p => {
        const m = new THREE.Mesh(geom, material.clone());
        m.position.copy(p);
        atomsGroup.add(m);
      });
    }

    // Plane construction
    // Precompute cube vertices and edges for [-0.5,0.5]^3
    const CUBE_S = 0.5;
    const cubeVertices = [
      new THREE.Vector3(-CUBE_S,-CUBE_S,-CUBE_S), // 0
      new THREE.Vector3( CUBE_S,-CUBE_S,-CUBE_S), // 1
      new THREE.Vector3(-CUBE_S, CUBE_S,-CUBE_S), // 2
      new THREE.Vector3( CUBE_S, CUBE_S,-CUBE_S), // 3
      new THREE.Vector3(-CUBE_S,-CUBE_S, CUBE_S), // 4
      new THREE.Vector3( CUBE_S,-CUBE_S, CUBE_S), // 5
      new THREE.Vector3(-CUBE_S, CUBE_S, CUBE_S), // 6
      new THREE.Vector3( CUBE_S, CUBE_S, CUBE_S)  // 7
    ];
    const cubeEdges = [
      [0,1],[2,3],[4,5],[6,7], // along X
      [0,2],[1,3],[4,6],[5,7], // along Y
      [0,4],[1,5],[2,6],[3,7]  // along Z
    ];

    function drawPlanes(h, k, l, opacity) {
      const nVec = new THREE.Vector3(h,k,l);
      const nLen = nVec.length();
      if (nLen < EPS) return;

      // Range of n: over the corners of the cube
      let minS = Infinity, maxS = -Infinity;
      for (const v of cubeVertices) {
        const s = h*v.x + k*v.y + l*v.z;
        if (s < minS) minS = s;
        if (s > maxS) maxS = s;
      }
      const nStart = Math.floor(minS);
      const nEnd   = Math.ceil(maxS);

      for (let m = nStart; m <= nEnd; m++) {
        const pts = [];
        for (const [i0, i1] of cubeEdges) {
          const a = cubeVertices[i0], b = cubeVertices[i1];
          const da = h*a.x + k*a.y + l*a.z;
          const db = h*b.x + k*b.y + l*b.z;
          const denom = db - da;
          if (Math.abs(denom) < EPS) continue; // parallel edge
          const t = (m - da) / denom;
          if (t > -EPS && t < 1+EPS) {
            const tClamped = Math.min(1, Math.max(0, t));
            const p = new THREE.Vector3(
              a.x + (b.x - a.x)*tClamped,
              a.y + (b.y - a.y)*tClamped,
              a.z + (b.z - a.z)*tClamped
            );
            // Deduplicate close points
            let unique = true;
            for (const q of pts) {
              if (p.distanceToSquared(q) < 1e-8) { unique = false; break; }
            }
            if (unique) pts.push(p);
          }
        }

        if (pts.length >= 3) {
          // Sort angularly around centroid using a stable plane basis
          const centroid = pts.reduce((acc, v) => acc.add(v), new THREE.Vector3()).multiplyScalar(1/pts.length);

          const n = nVec.clone().normalize();
          // Build in-plane orthonormal basis (u, v)
          let tmp = Math.abs(n.x) < 0.9 ? new THREE.Vector3(1,0,0) : new THREE.Vector3(0,1,0);
          const u = new THREE.Vector3().crossVectors(n, tmp).normalize();
          const v = new THREE.Vector3().crossVectors(n, u).normalize();

          // Compute angles for sorting
          const withAngles = pts.map(p => {
            const d = p.clone().sub(centroid);
            const x = d.dot(u), y = d.dot(v);
            const angle = Math.atan2(y, x);
            return { p, x, y, angle };
          }).sort((a,b) => a.angle - b.angle);

          // Create a 2D shape in (u,v)-coordinates centered at origin,
          // then transform geometry to 3D with basis [u,v,n] and translation = centroid.
          const shape = new THREE.Shape();
          shape.moveTo(withAngles[0].x, withAngles[0].y);
          for (let i=1;i<withAngles.length;i++) {
            shape.lineTo(withAngles[i].x, withAngles[i].y);
          }
          shape.closePath();

          const geom2D = new THREE.ShapeGeometry(shape);
          const basis = new THREE.Matrix4().makeBasis(u, v, n);
          basis.setPosition(centroid);
          geom2D.applyMatrix4(basis);

          const mat = new THREE.MeshBasicMaterial({
            color: 0x3758ff,
            transparent: true,
            opacity: opacity,
            side: THREE.DoubleSide,
            depthWrite: false
          });
          const mesh = new THREE.Mesh(geom2D, mat);
          planesGroup.add(mesh);
        }
      }
    }

    // Initial build
    resizeRenderer();
    updateScene();

    // Animation loop
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

  </script>
</body>
</html>
