      <label for="slider-eccentricity">orbit eccentricity</label>
      <input id="slider-eccentricity" type="range" min="0" max="0.99" step="0.000001" value="0.016722" />
      <span id="value-eccentricity" class="value-box">0.016722</span>
    </div>
  </div>

  <div id="viz-section">
    <div id="orbit-host" class="canvas-holder" aria-label="Orbit sketch area"></div>
    <div id="plot-host" class="canvas-holder" aria-label="Equation of Time plot area"></div>
