<!-- p5.js library -->
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
(function(){
  // Grab sliders and value displays
  const sAngle = document.getElementById('slider-angle');
  const sLon   = document.getElementById('slider-longitude');
  const sEcc   = document.getElementById('slider-eccentricity');

  const vAngle = document.getElementById('value-angle');
  const vLon   = document.getElementById('value-longitude');
  const vEcc   = document.getElementById('value-eccentricity');

  // Formatting helpers
  const fmt = {
    angle: v => Number(v).toFixed(2),
    lon:   v => Number(v).toFixed(2),
    ecc:   v => Number(v).toFixed(6),
  };
  const deg2rad = d => d * Math.PI / 180;
  const rad2deg = r => r * 180 / Math.PI;

  function getParams(){
    return {
      epsilonDeg: parseFloat(sAngle.value),
      omegaDeg:   parseFloat(sLon.value),
      e:          parseFloat(sEcc.value)
    };
  }

  function updateValueBoxes(){
    vAngle.textContent = fmt.angle(sAngle.value);
    vLon.textContent   = fmt.lon(sLon.value);
    vEcc.textContent   = fmt.ecc(sEcc.value);
  }

  // Compute responsive canvas sizes
  function computeSizes(){
    const container = document.getElementById('viz-section');
    const maxW = Math.min(860, container.clientWidth - 24);
    const orbitW = maxW;
    const orbitH = Math.max(90, Math.round(maxW * 0.16)); // compact, like screenshots
    const plotW  = maxW;
    const plotH  = Math.max(260, Math.round(maxW * 0.55));
    return { orbitW, orbitH, plotW, plotH };
  }

  // Equation of Time computation for a set of points
  function computeEOTPoints(epsilonDeg, e, omegaDeg, N){
    const eps = deg2rad(epsilonDeg);
    const omega = deg2rad(omegaDeg);
    const twoPI = Math.PI * 2;

    const pts = [];
    let prevEOT = null;
    for(let i=0;i<=N;i++){
      const M = i / N * twoPI; // mean anomaly [0, 2π]
      // Solve Kepler's equation: M = E - e sin E
      let E = M;
      for(let k=0;k<8;k++){
        const f = E - e*Math.sin(E) - M;
        const fp = 1 - e*Math.cos(E);
        E = E - f/fp;
      }
      // True anomaly ν
      const nu = 2 * Math.atan2(
        Math.sqrt(1+e)*Math.sin(E/2),
        Math.sqrt(1-e)*Math.cos(E/2)
      );
      // True longitude λ
      const lambda = nu + omega;

      // Right ascension α
      const alpha = Math.atan2(Math.cos(eps) * Math.sin(lambda), Math.cos(lambda));

      // Equation of Time in radians (unwrap to avoid ±2π jumps)
      let eot = M - alpha;
      if(prevEOT !== null){
        const diff = eot - prevEOT;
        if(Math.abs(diff) > Math.PI){
          eot -= Math.sign(diff) * 2*Math.PI;
        }
      }
      prevEOT = eot;

      // Convert to minutes
      const minutes = eot * (720 / Math.PI);
      pts.push({ M, minutes });
    }
    return pts;
  }

  // Make nice y-ticks
  function niceStep(range){
    if(range <= 0 || !isFinite(range)) return 1;
    const rough = range / 6;
    const mag = Math.pow(10, Math.floor(Math.log10(rough)));
    const r = rough / mag;
    let nice;
    if (r >= 5)      nice = 10;
    else if (r >= 2) nice = 5;
    else if (r >= 1) nice = 2;
    else             nice = 1;
    return nice * mag;
  }

  // p5 instance: Orbit Sketch
  let orbitP5 = new p5((p)=>{
    let cnv;

    function ensureSize(){
      const {orbitW, orbitH} = computeSizes();
      if(!cnv){
        cnv = p.createCanvas(orbitW, orbitH);
        cnv.id('orbit-canvas');
        cnv.parent('orbit-host');
        p.noLoop();
      }else{
        p.resizeCanvas(orbitW, orbitH);
      }
    }

    p.setup = function(){
      ensureSize();
    };

    p.windowResized = function(){
      ensureSize();
      p.redraw();
    };

    p.draw = function(){
      const { e, omegaDeg } = getParams();
      const omega = deg2rad(omegaDeg);

      p.background('#f0f0f0');

      p.push();
      p.translate(p.width/2, p.height/2);
      p.rotate(omega); // rotate ellipse by longitude of periapsis (ω)

      const a = Math.min(p.width, p.height) * 0.36; // semi-major axis
      const b = a * Math.sqrt(Math.max(0, 1 - e*e)); // semi-minor axis

      p.noFill();
      p.stroke('#a0522d');
      p.strokeWeight(3);
      p.ellipse(0, 0, 2*a, 2*b);
      p.pop();
    };
  });

  // p5 instance: Equation of Time Plot
  let plotP5 = new p5((p)=>{
    let cnv;

    function ensureSize(){
      const {plotW, plotH} = computeSizes();
      if(!cnv){
        cnv = p.createCanvas(plotW, plotH);
        cnv.id('plot-canvas');
        cnv.parent('plot-host');
        p.noLoop();
      }else{
        p.resizeCanvas(plotW, plotH);
      }
    }

    function drawAxesAndGrid(bounds, yTicks){
      const {left, right, top, bottom} = bounds;
      const w = right - left;
      const h = bottom - top;

      // Background
      p.push();
      p.noStroke();
      p.fill(255);
      p.rect(left, top, w, h);
      p.pop();

      // Major vertical gridlines at x=1..6
      p.push();
      p.stroke('#e0e0e0');
      p.strokeWeight(1);
      for(let xi=1; xi<=6; xi++){
        const x = left + (xi/(2*Math.PI)) * w;
        p.line(x, top, x, bottom);
      }
      // Horizontal gridlines
      for(const t of yTicks){
        const y = bounds.mapY(t);
        p.line(left, y, right, y);
      }
      p.pop();

      // Axes: box + zero line
      p.push();
      p.stroke('#000');
      p.strokeWeight(1);
      p.noFill();
      p.rect(left, top, w, h);
      // y=0 line
      const y0 = bounds.mapY(0);
      p.line(left, y0, right, y0);
      p.pop();

      // Labels
      p.push();
      p.fill(0);
      p.noStroke();
      p.textSize(12);
      p.textAlign(p.CENTER, p.TOP);
      for(let xi=1; xi<=6; xi++){
        const x = left + (xi/(2*Math.PI)) * w;
        p.text(String(xi), x, bottom + 6);
      }
      // y ticks and label
      p.textAlign(p.RIGHT, p.CENTER);
      for(const t of yTicks){
        const y = bounds.mapY(t);
        p.text(niceNumberString(t), left - 6, y);
      }
      // y-axis label "minutes"
      p.push();
      p.translate(left - 42, (top + bottom)/2);
      p.rotate(-Math.PI/2);
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text("minutes", 0, 0);
      p.pop();
      p.pop();
    }

    function niceNumberString(x){
      // Format to sensible decimals depending on magnitude
      const ax = Math.abs(x);
      if(ax >= 1000) return Math.round(x).toString();
      if(ax >= 100)  return x.toFixed(0);
      if(ax >= 10)   return x.toFixed(0);
      if(ax >= 1)    return x.toFixed(1);
      return x.toFixed(2);
    }

    p.setup = function(){
      ensureSize();
      p.textFont('sans-serif');
    };

    p.windowResized = function(){
      ensureSize();
      p.redraw();
    };

    p.draw = function(){
      p.background(255);

      const { epsilonDeg, e, omegaDeg } = getParams();
      const N = 600;
      const pts = computeEOTPoints(epsilonDeg, e, omegaDeg, N);

      // Determine y-range
      let yMin = Infinity, yMax = -Infinity;
      for(const pt of pts){
        if(pt.minutes < yMin) yMin = pt.minutes;
        if(pt.minutes > yMax) yMax = pt.minutes;
      }
      if(!isFinite(yMin) || !isFinite(yMax) || yMin === yMax){
        yMin = -1; yMax = 1;
      }
      // Add padding
      const pad = Math.max(2, 0.08 * (yMax - yMin));
      yMin -= pad; yMax += pad;

      // Plot bounds (margins)
      const left = 64, right = p.width - 20, top = 18, bottom = p.height - 46;
      const bounds = {
        left, right, top, bottom,
        mapX: (M) => left + (M / (2*Math.PI)) * (right-left),
        mapY: (min) => top + (yMax - min) / (yMax - yMin) * (bottom - top)
      };

      // Y ticks
      const step = niceStep(yMax - yMin);
      const yTicks = [];
      const start = Math.ceil(yMin / step) * step;
      for(let y=start; y<=yMax+1e-9; y+=step) yTicks.push(+y.toFixed(10));

      drawAxesAndGrid(bounds, yTicks);

      // Plot curve in orange, breaking on large jumps
      p.push();
      p.noFill();
      p.stroke('#ff8c00');
      p.strokeWeight(3);

      const jumpThreshold = (yMax - yMin) * 0.55; // break segments on large jumps
      let prev = null;
      for(let i=0;i<pts.length;i++){
        const pt = pts[i];
        const x = bounds.mapX(pt.M);
        const y = bounds.mapY(pt.minutes);
        if(prev === null){
          p.beginShape();
          p.vertex(x,y);
        }else{
          const dy = Math.abs(pt.minutes - prev.minutes);
          if(dy > jumpThreshold){
            // end current segment and start a new one
            p.endShape();
            p.beginShape();
            p.vertex(x,y);
          }else{
            p.vertex(x,y);
          }
        }
        prev = pt;
      }
      p.endShape();
      p.pop();
    };
  });

  // Update visuals when sliders change
  function updateAll(){
    updateValueBoxes();
    orbitP5.redraw();
    plotP5.redraw();
  }
  sAngle.addEventListener('input', updateAll);
  sLon.addEventListener('input', updateAll);
  sEcc.addEventListener('input', updateAll);

  // Initial render
  updateAll();
})();
</script>
</body>
</html>
