<script>
(function(){
  // Utility: read current UI state
  function getParams() {
    const c = parseFloat(document.getElementById('slider-c').value);
    const hC = parseFloat(document.getElementById('slider-h_C').value);
    const delta = parseFloat(document.getElementById('slider-delta').value);
    const plotRange = parseFloat(document.getElementById('slider-plot-range').value);
    const step = parseInt(document.querySelector('input[name="steps"]:checked').value, 10);
    const verification = document.getElementById('check-verification').checked;
    return { c, hC, delta, plotRange, step, verification };
  }

  // Update numeric readouts
  function hookSliderValue(sliderId, valueId, formatter = (v)=>v) {
    const s = document.getElementById(sliderId);
    const v = document.getElementById(valueId);
    const update = () => { v.textContent = formatter(s.value); redrawScene(); };
    s.addEventListener('input', update);
  }
  hookSliderValue('slider-c', 'c-value', v => Number(v).toFixed(2).replace(/\.00$/,''));
  hookSliderValue('slider-h_C', 'h_C-value', v => Number(v).toFixed(2).replace(/\.00$/,''));
  hookSliderValue('slider-delta', 'delta-value', v => Number(v).toFixed(2));
  hookSliderValue('slider-plot-range', 'plot-range-value', v => Number(v).toFixed(2).replace(/\.00$/,''));

  // Events for radios and checkbox
  document.getElementById('steps-group').addEventListener('change', () => redrawScene());
  document.getElementById('check-verification').addEventListener('change', () => redrawScene());

  // p5 sketch (global mode)
  let scaleFactor = 60; // base scale (pixels per unit), will be multiplied by plotRange
  let worldStrokePx = 1.5; // default world line thickness in pixels

  function solveM(c, h, delta) {
    // From plan:
    // x = (2*h*y - h^2 + c^2/4) / c = a*y + b
    const a = (2*h)/c;
    const b = ( (c*c)/4 - h*h )/c;
    const coeff2 = 1 + a*a;
    const coeff1 = 2*a*b - 1.5*c*a;
    const coeff0 = b*b - 1.5*c*b + 0.5*c*c;
    const t = Math.tan(delta);

    const A = t*coeff2;
    const B = t*coeff1 - c/2;
    const C = t*coeff0;

    let ySolutions = [];
    if (Math.abs(A) < 1e-10) {
      if (Math.abs(B) < 1e-12) {
        ySolutions = [0]; // degenerate; arbitrary
      } else {
        ySolutions = [-C/B];
      }
    } else {
      let disc = B*B - 4*A*C;
      if (disc < 0) disc = 0;
      const s = Math.sqrt(disc);
      ySolutions = [(-B + s)/(2*A), (-B - s)/(2*A)];
    }
    // Select the negative root (as in the diagram); fallback to first.
    let yM = ySolutions.filter(v => isFinite(v)).sort((x,y)=>x-y).find(v => v < 0);
    if (yM === undefined) yM = ySolutions[0];
    const xM = a*yM + b;
    return {x:xM, y:yM};
  }

  function worldToScreen(p) {
    // using the same transform as used in drawing: translate(width/2, height*0.6); scale(s, -s)
    const w = width, h = height;
    const originX = w/2;
    const originY = h*0.6;
    const s = scaleFactor;
    return { x: originX + p.x * s, y: originY - p.y * s };
  }

  // Points helpers
  function drawPointLabel(p, label, dx=6, dy=14) {
    // draw point and label in screen (pixel) space to keep sizes constant
    const sp = worldToScreen(p);
    push();
    resetMatrix();
    noStroke();
    fill(0);
    ellipse(sp.x, sp.y, 7, 7);
    textSize(14);
    textAlign(LEFT, BOTTOM);
    text(label, sp.x + dx, sp.y - dy + 18); // fine-tuned baseline
    pop();
  }

  function drawPointOnly(p) {
    const sp = worldToScreen(p);
    push(); resetMatrix(); noStroke(); fill(0); ellipse(sp.x, sp.y, 7, 7); pop();
  }

  function dashedLineWorld(ax, ay, bx, by, dash=[6,6], weightPx=1.3) {
    push();
    const wUnit = weightPx/scaleFactor;
    strokeWeight(wUnit);
    drawingContext.setLineDash(dash);
    line(ax, ay, bx, by);
    drawingContext.setLineDash([]);
    pop();
  }

  function solidLineWorld(ax, ay, bx, by, weightPx=1.3) {
    push();
    const wUnit = weightPx/scaleFactor;
    strokeWeight(wUnit);
    line(ax, ay, bx, by);
    pop();
  }

  // p5 globals
  let cnv;
  function setup() {
    // Replace placeholder canvas with p5 canvas so only one canvas remains
    const viz = document.getElementById('viz-area');
    const ph = document.getElementById('plemelj-canvas');
    const w = viz.clientWidth;
    const h = Math.max(520, Math.floor(window.innerHeight - 24));
    if (ph && ph.parentNode) ph.parentNode.removeChild(ph);
    cnv = createCanvas(w, h);
    cnv.parent(viz);
    cnv.id('plemelj-canvas');

    noLoop();
    textFont(getComputedStyle(document.body).fontFamily);
    redrawScene();
  }

  function windowResized() {
    const viz = document.getElementById('viz-area');
    resizeCanvas(viz.clientWidth, Math.max(520, Math.floor(window.innerHeight - 24)));
    redrawScene();
  }

  function draw() {
    renderScene();
  }

  function renderScene() {
    const { c, hC, delta, plotRange, step, verification } = getParams();

    // background and setup
    background('#E0E0E0');
    stroke(0);
    fill(0);

    // world scale
    scaleFactor = 60 * plotRange;
    const wUnitThin = 1.2/scaleFactor;
    const wUnitThick = 3.0/scaleFactor;

    // Define basic points
    const F = {x:0, y:0};
    const C = {x:0, y:hC};
    const D = {x:c/2, y:0};
    const K = {x:c, y:0};

    // Solve for M
    const M = solveM(c, hC, delta);

    // Circle radius R = |MC|
    const R = Math.hypot(M.x - C.x, M.y - C.y);

    // Intersection with x-axis gives B
    const xB = 2*M.x - c/2;
    const B = {x:xB, y:0};

    // A such that |AB| = c (to the left of B on λ)
    const A = {x:xB - c, y:0};

    // Verification point E: D is midpoint of E and B
    const E = {x: (2*D.x - B.x), y:(2*D.y - B.y)};

    // Prepare transform: origin center-bottom and y up
    push();
    translate(width/2, height*0.6);
    scale(scaleFactor, -scaleFactor);

    // Helper: X-span for line λ
    const L = (width * 0.9) / (2*scaleFactor);

    // Step 1: Draw λ, F, C, D, and FC
    if (step >= 1) {
      // Infinite line λ
      stroke(0);
      strokeWeight(wUnitThin);
      line(-L, 0, L, 0);

      // Label λ (in pixel space)
      pop();
      drawTextAtWorld({x: -L*0.9, y: -0.02}, 'λ');
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);

      // Segment FC
      solidLineWorld(F.x, F.y, C.x, C.y, 1.2);

      // Points and labels in pixel space
      pop();
      drawPointLabel(F, 'F', 6, 8);
      drawPointLabel(C, 'C', 6, 14);
      drawPointLabel(D, 'D', 6, 8);
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);
    }

    // Step 2: K, CD, DK (dashed), M, MC, MD, MK (dashed), circle κ
    if (step >= 2) {
      // Dashed CD and DK
      stroke(0);
      dashedLineWorld(C.x, C.y, D.x, D.y, [6,6], 1.2);
      dashedLineWorld(D.x, D.y, K.x, K.y, [6,6], 1.2);

      // Dashed MC, MD, MK
      dashedLineWorld(M.x, M.y, C.x, C.y, [6,6], 1.2);
      dashedLineWorld(M.x, M.y, D.x, D.y, [6,6], 1.2);
      dashedLineWorld(M.x, M.y, K.x, K.y, [6,6], 1.2);

      // Circle κ (draw as large arc above the axis)
      noFill();
      stroke(0);
      strokeWeight(wUnitThin);
      // Draw a wide arc from slightly below 0 to slightly above π so it looks like the snapshots
      arc(M.x, M.y, 2*R, 2*R, -0.15, Math.PI + 0.15);

      // Labels for K and M
      pop();
      drawPointLabel(K, 'K', 6, 8);
      drawPointLabel(M, 'M', 6, -2);
      // κ label at a point on the arc
      const arcPoint = { x: M.x - R*0.85, y: M.y + R*0.52 };
      drawTextAtWorld(arcPoint, 'κ');
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);
    }

    // Step 3: point B
    if (step >= 3) {
      pop();
      drawPointLabel(B, 'B', 6, 8);
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);
    }

    // Step 4: point A
    if (step >= 4) {
      pop();
      drawPointLabel(A, 'A', 6, 8);
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);
    }

    // Step 5: triangle ABC with thick lines
    if (step >= 5) {
      stroke(0);
      strokeWeight(wUnitThick);
      noFill();
      line(A.x, A.y, B.x, B.y);
      line(B.x, B.y, C.x, C.y);
      line(C.x, C.y, A.x, A.y);
    }

    // Verification geometry (toggle)
    if (verification) {
      // E point, and dashed CE and KB
      dashedLineWorld(C.x, C.y, E.x, E.y, [6,6], 1.2);
      dashedLineWorld(K.x, K.y, B.x, B.y, [6,6], 1.2);

      pop();
      drawPointLabel(E, 'E', 6, 8);
      push();
      translate(width/2, height*0.6);
      scale(scaleFactor, -scaleFactor);
    }

    pop(); // end world transform
  }

  function drawTextAtWorld(p, txt) {
    const sp = worldToScreen(p);
    push(); resetMatrix(); noStroke(); fill(0); textSize(14); text(txt, sp.x + 4, sp.y - 4); pop();
  }

  // Expose redrawScene per requirements
  window.redrawScene = function(){ if (typeof redraw === 'function') redraw(); };

})();
</script>
</body>
</html>
