      padding: 6px 6px 14px;
    }
    #control-panel label {
      color: #666;
    }
    #slider-z {
      flex: 1 1 auto;
    }
    #label-z {
      min-width: 84px;
      display: inline-block;
      padding: 2px 6px;
      border: 1px solid #ccc;
      border-radius: 4px;
      background: #fafafa;
      text-align: right;
      color: #555;
      font-variant-numeric: tabular-nums;
    }
    #plots-container > div {
      height: 240px;
      margin-bottom: 18px;
    }
    @media (max-width: 700px) {
      #plots-container > div { height: 260px; }
    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <label for="slider-z">z (m)</label>
      <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
      <span id="label-z">0.</span>
    </div>
    <div id="plots-container">
      <div id="plot-intensity"></div>
      <div id="plot-population"></div>
      <div id="plot-coherence"></div>
    </div>
  </div>

  <script>
    // Common time axis: t from -50 to 250 ns (301 points)
    const t_values = Array.from({ length: 301 }, (_, i) => i - 50);

    // Helper to format z with trailing decimal if integer
    function formatZ(z) {
      const zNum = +z;
      const isInt = Math.abs(zNum - Math.round(zNum)) < 1e-12;
      return isInt ? `${Math.round(zNum)}.` : zNum.toFixed(5);
    }

    // Red (z=0) base curves (computed once)
    function I_red_fn(t) {
      if (t < -15 || t > 170) return 0;
      return t > 15 ? 1 : 0.5 * (1 + Math.tanh(t / 4));
    }
    function P_red_fn(t) {
      if (t < -15) return 0;
      return 4.5 * (1 - Math.exp(-(t + 15) / 50));
    }
    function C_red_fn(t) {
      if (t < -15) return 0;
      return 2.2 * (1 - Math.exp(-(t + 15) / 50));
    }

    const I_red = t_values.map(I_red_fn);
    const P_red = t_values.map(P_red_fn);
    const C_red = t_values.map(C_red_fn);

    // Blue (dynamic) curves as functions of z
    function computeBlueIntensity(z) {
      const delay = z / 0.3; // ns
      return t_values.map(t => {
        const tp = t - delay;
        let I_base;
        if (tp < -15) I_base = 0;
        else if (tp > 15) I_base = 1;
        else I_base = 0.5 * (1 + Math.tanh(tp / 4));
        const envelope = (tp > 0) ? Math.exp(-0.035 * z * tp / 32) : 1;
        let val = I_base * envelope;
        if (t > 170 + delay) val = 0; // clip after trailing edge
        return val;
      });
    }

    function computeBluePopulation(z) {
      const delay = z / 0.3;
      const scale = 1 - 0.45 * (z / 32);
      const capVal = scale * (4.5 * (1 - Math.exp(-(170 + 15) / 50))); // value at clip edge
      return t_values.map(t => {
        const tp = t - delay;
        let val = (tp < -15) ? 0 : scale * (4.5 * (1 - Math.exp(-(tp + 15) / 50)));
        if (t > 170 + delay) val = capVal; // clipped plateau
        return val;
      });
    }

    function computeBlueCoherence(z) {
      const delay = z / 0.3;
      const scale = 1 - 0.45 * (z / 32);
      const capVal = scale * (2.2 * (1 - Math.exp(-(170 + 15) / 50)));
      return t_values.map(t => {
        const tp = t - delay;
        let val = (tp < -15) ? 0 : scale * (2.2 * (1 - Math.exp(-(tp + 15) / 50)));
        if (t > 170 + delay) val = capVal; // clipped plateau
        return val;
      });
    }

    // Common layout bits
    function verticalLinesAt(xMoving) {
      return [
        {
          type: 'line', x0: 0, x1: 0, y0: 0, y1: 1, yref: 'paper',
          line: { color: 'black', width: 2, dash: 'dash' }
        },
        {
          type: 'line', x0: xMoving, x1: xMoving, y0: 0, y1: 1, yref: 'paper',
          line: { color: 'black', width: 2, dash: 'dash' }
        }
      ];
    }

    const commonLayout = {
      xaxis: { title: 'time (ns)', range: [-50, 250], zeroline: true },
      margin: { l: 80, r: 20, t: 40, b: 40 },
      showlegend: true,
      legend: { x: 0.7, y: 0.9 }
    };

    // Initial z = 0
    const initialZ = 0;
    const initialZLabel = formatZ(initialZ);
    const I_blue_0 = computeBlueIntensity(initialZ);
    const P_blue_0 = computeBluePopulation(initialZ);
    const C_blue_0 = computeBlueCoherence(initialZ);

    // Plot (a) Intensity
    const dataIntensity = [
      { x: t_values, y: I_red, name: 'z = 0 meters', mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: I_blue_0, name: `z = ${initialZLabel} meters`, mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ];
    const layoutIntensity = {
      ...commonLayout,
      yaxis: { title: 'intensity (arb)', range: [-0.1, 1.1], zeroline: true },
      shapes: verticalLinesAt(0),
      annotations: [
        { x: -45, y: 0.9, xref: 'x', yref: 'y', text: '(a)', showarrow: false },
        { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
      ]
    };

    // Plot (b) Population
    const dataPopulation = [
      { x: t_values, y: P_red, name: 'z = 0 meters', mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: P_blue_0, name: `z = ${initialZLabel} meters`, mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ];
    const layoutPopulation = {
      ...commonLayout,
      yaxis: { title: 'excited-state population × 10<sup>4</sup>', range: [-0.5, 5.5], zeroline: true },
      shapes: verticalLinesAt(0),
      annotations: [
        { x: -45, y: 4.9, xref: 'x', yref: 'y', text: '(b)', showarrow: false }
      ]
    };

    // Plot (c) Coherence
    const dataCoherence = [
      { x: t_values, y: C_red, name: 'z = 0 meters', mode: 'lines', line: { color: 'red', width: 2 } },
      { x: t_values, y: C_blue_0, name: `z = ${initialZLabel} meters`, mode: 'lines', line: { color: 'blue', width: 2, dash: 'dash' } }
    ];
    const layoutCoherence = {
      ...commonLayout,
      yaxis: { title: 'coherence × 10<sup>2</sup>', range: [-0.2, 2.4], zeroline: true },
      shapes: verticalLinesAt(0),
      annotations: [
        { x: -45, y: 2.15, xref: 'x', yref: 'y', text: '(c)', showarrow: false }
      ]
    };

    const config = { responsive: true, displayModeBar: false };

    Plotly.newPlot('plot-intensity', dataIntensity, layoutIntensity, config);
    Plotly.newPlot('plot-population', dataPopulation, layoutPopulation, config);
    Plotly.newPlot('plot-coherence', dataCoherence, layoutCoherence, config);

    // Interaction: slider drives everything
    const slider = document.getElementById('slider-z');
    const labelZ = document.getElementById('label-z');

    function onZChange() {
      const z = parseFloat(slider.value);
      labelZ.textContent = formatZ(z);

      const iBlue = computeBlueIntensity(z);
      const pBlue = computeBluePopulation(z);
      const cBlue = computeBlueCoherence(z);

      const legendName = `z = ${formatZ(z)} meters`;
      const t_delay = z / 0.3;

      // Update blue traces (index 1)
      Plotly.restyle('plot-intensity', { y: [iBlue], name: [legendName] }, [1]);
      Plotly.restyle('plot-population', { y: [pBlue], name: [legendName] }, [1]);
      Plotly.restyle('plot-coherence', { y: [cBlue], name: [legendName] }, [1]);

      // Update moving vertical dashed lines
      const relayoutObj = { 'shapes[1].x0': t_delay, 'shapes[1].x1': t_delay };
      Plotly.relayout('plot-intensity', relayoutObj);
      Plotly.relayout('plot-population', relayoutObj);
      Plotly.relayout('plot-coherence', relayoutObj);
    }

    slider.addEventListener('input', onZChange);

    // Ensure initial state is correct if the browser autofills differently
    window.addEventListener('load', () => {
      slider.value = "0";
      labelZ.textContent = "0.";
      onZChange(); // this keeps all traces consistent on load
    });
  </script>
</body>
</html>
