<body>
<div id="demo-container">
  <div id="control-panel">
    <div>
      <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
      <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
      <span id="ua-value">0.0</span>
    </div>
    <div>
      <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
      <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
      <span id="kr0-value">0.0 × 10¹²</span>
    </div>
    <div>
      <label for="slider-tf">feed temperature (K)</label>
      <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
      <span id="tf-value">265</span>
    </div>
    <div>
      <label for="slider-tau">residence time (s)</label>
      <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
      <span id="tau-value">400</span>
    </div>
  </div>

  <div id="plot-container"></div>
</div>

<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script>
(function(){
  // Constants
  const C_A0 = 10;           // mmol/dm^3
  const R = 1.987;           // cal/mol/K
  const E_f = 25000;         // cal/mol
  const dH_rxn = -200000;    // cal/mol
  const E_r = E_f - dH_rxn;  // 225000 cal/mol
  const k_f0 = 1e15;         // 1/s
  const rhoCp = 20000;       // cal/(dm^3 K)

  // DOM elements
  const uaSlider  = document.getElementById('slider-ua');
  const kr0Slider = document.getElementById('slider-kr0');
  const tfSlider  = document.getElementById('slider-tf');
  const tauSlider = document.getElementById('slider-tau');

  const uaVal  = document.getElementById('ua-value');
  const kr0Val = document.getElementById('kr0-value');
  const tfVal  = document.getElementById('tf-value');
  const tauVal = document.getElementById('tau-value');

  const plotDiv = document.getElementById('plot-container');

  // Helpers
  function clamp(v, lo, hi){ return Math.max(lo, Math.min(hi, v)); }
  function formatKr0Slider(x){ return x.toFixed(1) + ' × 10¹²'; }

  function computeCurves(UA, kr0_slider, Tf, tau){
    const T = [];
    const CB_mass = [];
    const CB_energy = [];
    const k_r0 = kr0_slider * 1e12; // actual reverse pre-exponential

    for(let t=250; t<=400; t+=1){
      const kf = k_f0 * Math.exp(-E_f / (R * t));
      const kr = k_r0 * Math.exp(-E_r / (R * t));
      const cb_m = (tau * kf * C_A0) / (1 + tau * (kf + kr));
      const cb_e = ((rhoCp + UA * tau) * (t - Tf)) / (-dH_rxn);
      T.push(t);
      CB_mass.push(cb_m);
      CB_energy.push(cb_e);
    }
    return {T, CB_mass, CB_energy};
  }

  function update(){
    // Read slider values
    const UA   = parseFloat(uaSlider.value);
    const kr0s = parseFloat(kr0Slider.value);
    const Tf   = parseFloat(tfSlider.value);
    const tau  = parseFloat(tauSlider.value);

    // Update displays
    uaVal.textContent  = UA.toFixed(1);
    kr0Val.textContent = formatKr0Slider(kr0s);
    tfVal.textContent  = Tf.toString();
    tauVal.textContent = tau.toString();

    // Compute curves
    const {T, CB_mass, CB_energy} = computeCurves(UA, kr0s, Tf, tau);

    // Determine annotation positions
    const T_mass_label = 360; // near top-right
    const idxMass = Math.round(T_mass_label - 250);
    const yMass = CB_mass[clamp(idxMass, 0, CB_mass.length-1)];

    const T_energy_label = clamp(Tf + 15, 255, 360);
    const idxEnergy = Math.round(T_energy_label - 250);
    const yEnergy = CB_energy[clamp(idxEnergy, 0, CB_energy.length-1)];

    const traces = [
      {
        x: T,
        y: CB_mass,
        mode: 'lines',
        line: {color: '#008000', width: 3},
        name: 'mass balance',
        hovertemplate: 'T: %{x} K<br>C_B: %{y:.2f} mmol/dm³<extra>mass balance</extra>'
      },
      {
        x: T,
        y: CB_energy,
        mode: 'lines',
        line: {color: '#0000FF', width: 3},
        name: 'energy balance',
        hovertemplate: 'T: %{x} K<br>C_B: %{y:.2f} mmol/dm³<extra>energy balance</extra>'
      }
    ];

    const layout = {
      margin: {l: 70, r: 20, t: 10, b: 60},
      xaxis: {
        title: 'temperature (K)',
        range: [250, 400],
        gridcolor: '#e5e5e5',
        zeroline: false
      },
      yaxis: {
        title: 'product concentration (mmol/dm³)',
        range: [0, 12],
        gridcolor: '#e5e5e5',
        zeroline: false
      },
      showlegend: false,
      annotations: [
        {
          x: T_energy_label,
          y: yEnergy,
          text: 'energy balance',
          font: {color: '#0000FF', size: 14},
          showarrow: false,
          xanchor: 'left',
          ay: -20
        },
        {
          x: T_mass_label,
          y: yMass,
          text: 'mass balance',
          font: {color: '#008000', size: 14},
          showarrow: false,
          xanchor: 'left',
          ay: -20
        }
      ]
    };

    Plotly.react(plotDiv, traces, layout, {displayModeBar:false, responsive:true});
  }

  // Initial plot
  Plotly.newPlot(plotDiv, [{
    x:[250, 400],
    y:[0, 0],
    mode:'lines',
    line:{color:'#fff', width:0.1}
  }], {
    xaxis:{title:'temperature (K)', range:[250,400]},
    yaxis:{title:'product concentration (mmol/dm³)', range:[0,12]},
    showlegend:false
  }, {displayModeBar:false, responsive:true});

  // Bind events
  [uaSlider, kr0Slider, tfSlider, tauSlider].forEach(el => {
    el.addEventListener('input', update);
  });

  // First update with defaults
  update();
})();
</script>
</body>
</html>
