<script>
/*
  Wankel Rotary Engine: Epitrochoidal Envelopes
  Rendering with p5.js using the component IDs and logic defined in the plan.
*/

(function() {
  // UI Elements
  const sliderK = document.getElementById('slider-eccentricity');
  const labelK  = document.getElementById('label-eccentricity');
  const sliderPhi = document.getElementById('slider-rotation');
  const labelPhi  = document.getElementById('label-rotation');

  const refRadios = () => document.querySelector('input[name="ref-frame"]:checked').value;
  const envRadios = () => document.querySelector('input[name="envelope-type"]:checked').value;

  // Initialize numeric labels
  function updateLabels() {
    labelK.textContent = Number(sliderK.value).toFixed(6);
    labelPhi.textContent = Number(sliderPhi.value).toFixed(5);
  }
  sliderK.addEventListener('input', updateLabels);
  sliderPhi.addEventListener('input', updateLabels);
  document.querySelectorAll('input[name="ref-frame"]').forEach(el => el.addEventListener('change', () => {}));
  document.querySelectorAll('input[name="envelope-type"]').forEach(el => el.addEventListener('change', () => {}));
  updateLabels();

  // p5 Sketch
  let sketch = (p) => {
    let canvas;
    let pxPerUnit = 150;   // base; will be adjusted dynamically
    let viewRange = 1.6;   // world coordinate half-span initially

    const SAMPLES_HOUSING = 800;
    const SAMPLES_ROTOR   = 600;

    function worldToScreen(x, y) {
      const ox = p.width / 2;
      const oy = p.height / 2;
      return [ ox + x * pxPerUnit, oy - y * pxPerUnit ];
    }

    function drawAxes() {
      const R = viewRange;
      const [x0,y0] = worldToScreen(-R, 0);
      const [x1,y1] = worldToScreen( R, 0);
      const [x2,y2] = worldToScreen( 0,-R);
      const [x3,y3] = worldToScreen( 0, R);

      p.stroke(180);
      p.strokeWeight(1);
      p.line(x0, y0, x1, y1); // x-axis
      p.line(x2, y2, x3, y3); // y-axis

      // Ticks
      p.stroke(160);
      const step = niceTickStep(R);
      const tickPix = Math.max(4, Math.min(8, pxPerUnit * 0.04));

      // x ticks
      for (let x = -Math.floor(R/step)*step; x <= R + 1e-9; x += step) {
        if (Math.abs(x) < 1e-9) continue; // skip center tick label on x (will be labeled by y-axis)
        const [tx0, ty0] = worldToScreen(x, 0);
        p.line(tx0, ty0 - tickPix, tx0, ty0 + tickPix);
        drawText(formatTick(x), tx0 + 2, ty0 + 14); // below axis
      }
      // y ticks
      for (let y = -Math.floor(R/step)*step; y <= R + 1e-9; y += step) {
        if (Math.abs(y) < 1e-9) continue;
        const [tx0, ty0] = worldToScreen(0, y);
        p.line(tx0 - tickPix, ty0, tx0 + tickPix, ty0);
        drawText(formatTick(y), tx0 + 6, ty0 - 6); // right of axis
      }
      // Origin label
      drawText("0", ...worldToScreen(0,0).map((v,i,arr)=> i? v+14 : v+4));
    }

    function niceTickStep(R) {
      const targetTicks = 6; // aim for around this many in half-span
      let ideal = R / targetTicks; // step for half-range
      const mag = Math.pow(10, Math.floor(Math.log10(Math.max(ideal, 1e-9))));
      const norm = ideal / mag;
      let stepNorm;
      if (norm <= 1) stepNorm = 1;
      else if (norm <= 2) stepNorm = 2;
      else if (norm <= 2.5) stepNorm = 2.5;
      else if (norm <= 5) stepNorm = 5;
      else stepNorm = 10;
      return stepNorm * mag;
    }

    function formatTick(v) {
      // nice formatting like -1.0, -0.5, etc.
      const s = Math.abs(v) < 1e-10 ? 0 : v;
      const abs = Math.abs(s);
      let str;
      if (abs >= 1) str = s.toFixed(1);
      else if (abs >= 0.1) str = s.toFixed(1);
      else str = s.toFixed(2);
      return str;
    }

    function epitrochoidPoints(k) {
      const pts = [];
      const twoPi = Math.PI * 2;
      for (let i = 0; i <= SAMPLES_HOUSING; i++) {
        const t = (i / SAMPLES_HOUSING) * twoPi;
        const x = Math.cos(t) - k * Math.cos(3 * t);
        const y = Math.sin(t) - k * Math.sin(3 * t);
        pts.push([x, y]);
      }
      return pts;
    }

    function rotorInnerPoints(k) {
      const R = 1; // base radius for the housing equations; rotor uses 2/3*R term
      const pts = [];
      const twoPi = Math.PI * 2;
      for (let i = 0; i <= SAMPLES_ROTOR; i++) {
        const t = (i / SAMPLES_ROTOR) * twoPi;
        const x = (2/3)*R*Math.cos(t) + k*Math.cos(2*t);
        const y = (2/3)*R*Math.sin(t) - k*Math.sin(2*t);
        pts.push([x, y]);
      }
      return pts;
    }

    function rotorOuterPoints(k) {
      const R = 1;
      const pts = [];
      const twoPi = Math.PI * 2;
      for (let i = 0; i <= SAMPLES_ROTOR; i++) {
        const t = (i / SAMPLES_ROTOR) * twoPi;
        const x = (2/3)*R*Math.cos(t) - k*Math.cos(2*t);
        const y = (2/3)*R*Math.sin(t) + k*Math.sin(2*t);
        pts.push([x, y]);
      }
      return pts;
    }

    function rotatePoint(pt, angle) {
      const [x,y] = pt;
      const c = Math.cos(angle), s = Math.sin(angle);
      return [ c*x - s*y, s*x + c*y ];
    }

    function translatePoint(pt, dx, dy) {
      return [ pt[0] + dx, pt[1] + dy ];
    }

    function transformPoints(pts, angle, dx, dy) {
      const out = new Array(pts.length);
      const c = Math.cos(angle), s = Math.sin(angle);
      for (let i = 0; i < pts.length; i++) {
        const x = pts[i][0], y = pts[i][1];
        const xr = c*x - s*y;
        const yr = s*x + c*y;
        out[i] = [ xr + dx, yr + dy ];
      }
      return out;
    }

    function inverseTransformPoints(pts, angle, dx, dy) {
      // apply inverse of rotate(angle) then translate by (dx,dy)
      // inverse is: translate by -dx,-dy then rotate by -angle
      const out = new Array(pts.length);
      const c = Math.cos(-angle), s = Math.sin(-angle);
      for (let i = 0; i < pts.length; i++) {
        const x = pts[i][0] - dx;
        const y = pts[i][1] - dy;
        const xr = c*x - s*y;
        const yr = s*x + c*y;
        out[i] = [ xr, yr ];
      }
      return out;
    }

    function drawPolyline(pts, color, weight=2) {
      if (!pts || pts.length < 2) return;
      p.stroke(color);
      p.noFill();
      p.strokeWeight(weight);
      p.beginShape();
      for (let i = 0; i < pts.length; i++) {
        const [sx, sy] = worldToScreen(pts[i][0], pts[i][1]);
        p.vertex(sx, sy);
      }
      p.endShape();
    }

    function computeBounds(listOfPointArrays) {
      let minX = Infinity, maxX = -Infinity, minY = Infinity, maxY = -Infinity;
      for (const pts of listOfPointArrays) {
        if (!pts) continue;
        for (let i = 0; i < pts.length; i++) {
          const x = pts[i][0], y = pts[i][1];
          if (x < minX) minX = x;
          if (x > maxX) maxX = x;
          if (y < minY) minY = y;
          if (y > maxY) maxY = y;
        }
      }
      // Use origin-centered range for axes
      const maxAbs = Math.max(Math.abs(minX), Math.abs(maxX), Math.abs(minY), Math.abs(maxY), 1.0);
      const margin = Math.max(0.08 * maxAbs, 0.06);
      return maxAbs + margin;
    }

    function drawText(txt, x, y) {
      p.push();
      p.noStroke();
      p.fill(80);
      p.textAlign(p.LEFT, p.TOP);
      p.textSize(12);
      // x,y are in screen coords
      p.text(txt, x, y);
      p.pop();
    }

    function recomputeAndRender() {
      const k = parseFloat(sliderK.value);
      const phi = parseFloat(sliderPhi.value);

      // Kinematics
      const phi_shaft = 2 * phi;
      const theta = -phi_shaft / 3; // rotor rotation
      const Cx = k * Math.cos(phi_shaft);
      const Cy = k * Math.sin(phi_shaft);

      // Base curves in their canonical positions
      const housingBase = epitrochoidPoints(k);
      const rotorInnerBase = rotorInnerPoints(k);
      const rotorOuterBase = rotorOuterPoints(k);

      // Select envelope(s)
      const envType = envRadios();
      let rotorA = null, rotorB = null;
      if (envType === 'inner') rotorA = rotorInnerBase;
      else if (envType === 'outer') rotorA = rotorOuterBase;
      else { rotorA = rotorInnerBase; rotorB = rotorOuterBase; }

      // Reference frame transforms
      const ref = refRadios();
      let housingDraw = null;
      let rotorADraw = null;
      let rotorBDraw = null;

      if (ref === 'epitrochoid' || ref === 'fixed centers') {
        housingDraw = housingBase; // blue fixed at origin
        rotorADraw = transformPoints(rotorA, theta, Cx, Cy);
        if (rotorB) rotorBDraw = transformPoints(rotorB, theta, Cx, Cy);
      } else if (ref === 'envelope') {
        // Make the rotor stationary at the origin; move the housing with inverse transform
        housingDraw = inverseTransformPoints(housingBase, theta, Cx, Cy);
        rotorADraw = rotorA;
        if (rotorB) rotorBDraw = rotorB;
      }

      // Determine dynamic scale
      viewRange = computeBounds([housingDraw, rotorADraw, rotorBDraw]);
      const usableW = p.width, usableH = p.height;
      pxPerUnit = Math.min(usableW, usableH) / (2 * viewRange);

      // Draw
      p.background('#ffffff');
      drawAxes();

      // Curves
      drawPolyline(housingDraw, p.color(50,100,200), 2); // blue
      drawPolyline(rotorADraw,  p.color(230,150,0), 2);  // orange
      if (rotorBDraw) drawPolyline(rotorBDraw, p.color(230,150,0, 180), 2);
    }

    p.setup = function() {
      // Create a square canvas that fits the container width
      const container = document.getElementById('canvas-container');
      const W = Math.min(container.clientWidth - 2, 820);
      const size = Math.max(420, Math.min(640, W));
      canvas = p.createCanvas(size, size);
      canvas.parent('canvas-container');
      p.pixelDensity(1.5);
      p.frameRate(60);

      // Redraw on resize
      window.addEventListener('resize', () => {
        const W2 = Math.min(container.clientWidth - 2, 820);
        const size2 = Math.max(420, Math.min(680, W2));
        p.resizeCanvas(size2, size2);
      });
    };

    p.draw = function() {
      recomputeAndRender();
    };
  };

  new p5(sketch);

})();
</script>

</body>
</html>
