<script>
(() => {
  // Constants
  const C_UNIT_CONVERSION = 1000; // to kg/m^3
  const K_DEPLETION = 0.137;
  const PHI_MIN = 0.0001;

  // Elements
  const el = id => document.getElementById(id);
  const plotId = 'plot-container';

  const sliders = {
    phi: el('slider-phi'),
    gamma: el('slider-gamma-sat'),
    r: el('slider-r'),
    rpe: el('slider-rpe'),
    mw: el('slider-mw'),
    xMax: el('slider-phi-axis'),
    yMax: el('slider-c-axis'),
  };

  const displays = {
    phi: el('phi-value-display'),
    gamma: el('gamma-sat-value-display'),
    r: el('r-value-display'),
    rpe: el('rpe-value-display'),
    mw: el('mw-value-display'),
    xMax: el('phi-axis-value-display'),
    yMax: el('c-axis-value-display'),
    formula: el('formula-display'),
    phimax: el('phimax-display'),
    range: el('range-display'),
  };

  function stateFromUI() {
    const s = {
      phi: parseFloat(sliders.phi.value),
      gamma: parseFloat(sliders.gamma.value),
      r: parseFloat(sliders.r.value),
      rpe: parseFloat(sliders.rpe.value),
      mw: parseFloat(sliders.mw.value),
      xMax: parseFloat(sliders.xMax.value),
      yMax: parseFloat(sliders.yMax.value),
    };
    // Derived constants
    s.A = (3 * s.gamma / s.r) * C_UNIT_CONVERSION;
    s.B = (K_DEPLETION * s.mw) / Math.pow(s.rpe, 3);
    s.phi_max = Math.sqrt(s.B / s.A);
    return s;
  }

  // Mathematical functions
  const y1 = (phi, A) => A * phi;
  const y2 = (phi, A) => A * phi / (1 - phi);
  const y3 = (phi, A, B) => A * phi + (B / phi);

  const linspace = (a, b, n=400) => {
    const arr = [];
    const step = (b - a) / (n - 1);
    for (let i = 0; i < n; i++) arr.push(a + step * i);
    return arr;
  };

  function buildTraces(s) {
    const xmax = Math.max(PHI_MIN, s.xMax);
    const xs = linspace(PHI_MIN, xmax, 500);
    const y1_full = xs.map(x => y1(x, s.A));
    const y2_full = xs.map(x => y2(x, s.A));
    const y3_full = xs.map(x => y3(x, s.A, s.B));

    // Shaded region up to phi_max
    const shadeMax = Math.max(PHI_MIN, s.phi_max);
    const xsShade = linspace(PHI_MIN, shadeMax, 250);
    const y2_shade = xsShade.map(x => y2(x, s.A));
    const y3_shade = xsShade.map(x => y3(x, s.A, s.B));

    const traces = [
      {
        name: 'y1',
        type: 'scatter',
        mode: 'lines',
        line: {color: 'blue', width: 3},
        x: xs, y: y1_full
      },
      {
        name: 'y2',
        type: 'scatter',
        mode: 'lines',
        line: {color: 'red', width: 3},
        x: xs, y: y2_full
      },
      {
        name: 'y3',
        type: 'scatter',
        mode: 'lines',
        line: {color: 'green', width: 3},
        x: xs, y: y3_full
      },
      // shaded region (y2 then y3 with tonexty)
      {
        name: 'y2_shade',
        type: 'scatter',
        mode: 'lines',
        line: {color: 'rgba(0,0,0,0)'},
        x: xsShade,
        y: y2_shade,
        hoverinfo: 'skip'
      },
      {
        name: 'y3_shade',
        type: 'scatter',
        mode: 'lines',
        line: {color: 'rgba(0,0,0,0)'},
        fill: 'tonexty',
        fillcolor: 'rgba(173, 216, 230, 0.5)',
        x: xsShade,
        y: y3_shade,
        hoverinfo: 'skip'
      },
      {
        name: 'markers',
        type: 'scatter',
        mode: 'markers',
        marker: {size: 10, color: ['blue','red','green']},
        x: [s.phi, s.phi, s.phi],
        y: [y1(s.phi, s.A), y2(s.phi, s.A), y3(s.phi, s.A, s.B)]
      }
    ];
    return traces;
  }

  function buildLayout(s){
    return {
      xaxis: {title: 'ϕ (volume fraction)', range: [0, s.xMax], zeroline: false},
      yaxis: {title: 'C (kg/m³)', range: [0, s.yMax], zeroline: false},
      shapes: [
        {
          type: 'line',
          x0: s.phi, x1: s.phi,
          y0: 0, y1: s.yMax,
          line: {color: 'grey', width: 2, dash: 'dash'}
        }
      ],
      margin: {l: 70, r: 15, t: 10, b: 55},
      showlegend: false
    };
  }

  function updateInfo(s){
    // Color-coded function names
    displays.formula.innerHTML =
      '<span style="color:blue;">\\(y_1(\\phi)\\)</span>, ' +
      '<span style="color:red;">\\(y_2(\\phi)\\)</span>, and ' +
      '<span style="color:green;">\\(y_3(\\phi)\\)</span>';

    displays.phimax.innerHTML =
      '\\(\\phi_{max}\\) = ' + s.phi_max.toFixed(4);

    const lower = y2(s.phi, s.A);
    const upper = y3(s.phi, s.A, s.B);
    displays.range.innerHTML =
      `${lower.toFixed(3)} < \\(C_{\\text{secondary emulsion}}\\) < ${upper.toFixed(3)}`;

    if (window.MathJax && MathJax.typesetPromise) MathJax.typesetPromise([displays.formula, displays.phimax, displays.range]);
  }

  function updateValueDisplays(){
    displays.phi.textContent = parseFloat(sliders.phi.value).toFixed(4);
    displays.gamma.textContent = parseFloat(sliders.gamma.value).toFixed(1);
    displays.r.textContent = parseFloat(sliders.r.value).toFixed(0);
    displays.rpe.textContent = parseFloat(sliders.rpe.value).toFixed(0);
    displays.mw.textContent = parseFloat(sliders.mw.value).toFixed(0);
    displays.xMax.textContent = parseFloat(sliders.xMax.value).toFixed(2);
    displays.yMax.textContent = parseFloat(sliders.yMax.value).toFixed(1);
  }

  function renderAll(){
    const s = stateFromUI();
    updateValueDisplays();
    const traces = buildTraces(s);
    const layout = buildLayout(s);
    Plotly.react(plotId, traces, layout, {responsive: true});
    updateInfo(s);
  }

  // Event handlers
  sliders.phi.addEventListener('input', () => {
    // Update value text
    displays.phi.textContent = parseFloat(sliders.phi.value).toFixed(4);
    const s = stateFromUI();

    // Update markers
    const newX = [[s.phi, s.phi, s.phi]];
    const newY = [[y1(s.phi, s.A), y2(s.phi, s.A), y3(s.phi, s.A, s.B)]];
    Plotly.restyle(plotId, {x: newX, y: newY}, [5]);

    // Update vertical line
    Plotly.relayout(plotId, {
      'shapes[0].x0': s.phi,
      'shapes[0].x1': s.phi,
      'shapes[0].y1': s.yMax
    });

    // Update info display (range)
    updateInfo(s);
  });

  // Physical parameter sliders -> full recompute and re-render
  [sliders.gamma, sliders.r, sliders.rpe, sliders.mw].forEach(inp => {
    inp.addEventListener('input', () => {
      renderAll();
    });
  });

  // Axis controls
  sliders.xMax.addEventListener('input', () => {
    // Update display and link phi slider max
    updateValueDisplays();
    sliders.phi.max = sliders.xMax.value;
    // Clamp phi if needed
    if (parseFloat(sliders.phi.value) > parseFloat(sliders.xMax.value)) {
      sliders.phi.value = sliders.xMax.value;
      displays.phi.textContent = parseFloat(sliders.phi.value).toFixed(4);
    }
    // Recompute and redraw so curves extend across new range
    renderAll();
    // Also ensure relayout for emphasis
    Plotly.relayout(plotId, {'xaxis.range':[0, parseFloat(sliders.xMax.value)]});
  });

  sliders.yMax.addEventListener('input', () => {
    updateValueDisplays();
    const s = stateFromUI();
    // Update axis range and vertical line height
    Plotly.relayout(plotId, {
      'yaxis.range': [0, s.yMax],
      'shapes[0].y1': s.yMax
    });
    updateInfo(s);
  });

  // Initial setup
  function init(){
    // Set initial numeric displays and phi-slider max link
    sliders.phi.max = sliders.xMax.value;
    updateValueDisplays();
    renderAll();
  }

  init();

})();
</script>
</body>
</html>
