  }
</style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <label for="slider-time">time relative to collision</label>
      <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
      <span id="label-time">-10 s</span>

      <h4>object 1</h4>
      <label for="slider-m1">mass</label>
      <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
      <span id="label-m1">5 kg</span>

      <label for="slider-v1">speed</label>
      <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
      <span id="label-v1">10 m/s</span>

      <h4>object 2</h4>
      <label for="slider-m2">mass</label>
      <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
      <span id="label-m2">3 kg</span>

      <label for="slider-v2">speed</label>
      <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
      <span id="label-v2">5 m/s</span>
      <div class="small-note">Note: v₂ slider is for display; momentum uses m₁,m₂,v₁.</div>

      <label>observer velocity (m/s)</label>
      <div id="joystick-container">
        <div id="joystick-knob"></div>
      </div>
      <div>
        <span>v0x: </span><span id="label-v0x">1.1</span>
      </div>
      <div>
        <span>v0y: </span><span id="label-v0y">1.1</span>
      </div>
    </div>

    <div id="canvas-container">
      <div id="canvas-holder"></div>
    </div>
  </div>

<script>
/* ---------- State ---------- */
const state = {
  t: -10,
  m1: 5,
  m2: 3,
  s1_cm: 10, // given
  s2_cm: 5 * (5/3), // computed
  v0x: 1.1,
  v0y: 1.1,

  // derived (pre-collision observer-frame metrics)
  metrics: {
    v0: 0,
    v1: 0,
    v2: 0,
    energy: 0
  }
};
const JOY_MAX = 5;

/* ---------- DOM Elements ---------- */
const el = {
  time: document.getElementById('slider-time'),
  m1: document.getElementById('slider-m1'),
  v1: document.getElementById('slider-v1'),
  m2: document.getElementById('slider-m2'),
  v2: document.getElementById('slider-v2'),
  labelTime: document.getElementById('label-time'),
  labelM1: document.getElementById('label-m1'),
  labelV1: document.getElementById('label-v1'),
  labelM2: document.getElementById('label-m2'),
  labelV2: document.getElementById('label-v2'),
  labelV0x: document.getElementById('label-v0x'),
  labelV0y: document.getElementById('label-v0y'),
  joy: document.getElementById('joystick-container'),
  knob: document.getElementById('joystick-knob')
};

/* ---------- Helpers ---------- */
function toNumber(v){ return typeof v === 'number' ? v : parseFloat(v); }
function fmt(n, d){ return Number(n).toFixed(d); }
function vecAdd(a,b){ return {x:a.x+b.x, y:a.y+b.y}; }
function vecScale(a,s){ return {x:a.x*s, y:a.y*s}; }
function vecMag(a){ return Math.hypot(a.x, a.y); }
function rotate(v, rad){
  return {x: v.x*Math.cos(rad) - v.y*Math.sin(rad),
          y: v.x*Math.sin(rad) + v.y*Math.cos(rad)};
}

/* ---------- Controls <-> State ---------- */
function updateFromControls(){
  state.t = toNumber(el.time.value);
  state.m1 = toNumber(el.m1.value);
  state.m2 = toNumber(el.m2.value);
  state.s1_cm = toNumber(el.v1.value);
  // s2_cm from momentum in CM frame
  state.s2_cm = (state.m1/state.m2) * state.s1_cm;

  // Update labels
  el.labelTime.textContent = `${fmt(state.t, (Math.abs(state.t) < 10 ? 1 : 0))} s`;
  el.labelM1.textContent = `${fmt(state.m1, 2)} kg`;
  el.labelV1.textContent = `${fmt(state.s1_cm, 1)} m/s`;
  el.labelM2.textContent = `${fmt(state.m2, 2)} kg`;
  el.labelV2.textContent = `${fmt(toNumber(el.v2.value), 1)} m/s`; // display only

  // Recompute metrics (pre-collision)
  recalcMetrics();
}

function recalcMetrics(){
  const u1_cm = {x: state.s1_cm, y: 0};
  const u2_cm = {x: -state.s2_cm, y: 0};
  const v0 = {x: state.v0x, y: state.v0y};

  const u1_obs = vecAdd(u1_cm, v0);
  const u2_obs = vecAdd(u2_cm, v0);

  state.metrics.v0 = Math.hypot(state.v0x, state.v0y);
  state.metrics.v1 = vecMag(u1_obs);
  state.metrics.v2 = vecMag(u2_obs);
  state.metrics.energy = 0.5*state.m1*state.metrics.v1*state.metrics.v1 +
                         0.5*state.m2*state.metrics.v2*state.metrics.v2;
}

/* ---------- Joystick ---------- */
(function initJoystick(){
  const pad = el.joy;
  const knob = el.knob;

  function setKnobFromV0(){
    const rect = pad.getBoundingClientRect();
    const cx = rect.width/2;
    const cy = rect.height/2;
    const x = (state.v0x/JOY_MAX)*cx + cx;
    const y = (-state.v0y/JOY_MAX)*cy + cy; // invert so up is +y
    knob.style.left = `${x}px`;
    knob.style.top  = `${y}px`;
    el.labelV0x.textContent = fmt(state.v0x, 2);
    el.labelV0y.textContent = fmt(state.v0y, 2);
  }

  function updateFromPointer(clientX, clientY){
    const rect = pad.getBoundingClientRect();
    let x = clientX - rect.left;
    let y = clientY - rect.top;
    x = Math.max(0, Math.min(rect.width, x));
    y = Math.max(0, Math.min(rect.height, y));
    const cx = rect.width/2;
    const cy = rect.height/2;
    // map to [-JOY_MAX, JOY_MAX]
    state.v0x = ((x - cx)/cx) * JOY_MAX;
    state.v0y = (-(y - cy)/cy) * JOY_MAX;
    setKnobFromV0();
    recalcMetrics();
  }

  let dragging = false;
  function startDrag(e){
    dragging = true;
    knob.style.cursor = 'grabbing';
    if(e.type === 'mousedown'){
      updateFromPointer(e.clientX, e.clientY);
    }else if(e.type === 'touchstart'){
      const t = e.touches[0];
      updateFromPointer(t.clientX, t.clientY);
    }
  }
  function onMove(e){
    if(!dragging) return;
    if(e.type === 'mousemove'){
      updateFromPointer(e.clientX, e.clientY);
    }else if(e.type === 'touchmove'){
      const t = e.touches[0];
      updateFromPointer(t.clientX, t.clientY);
    }
  }
  function endDrag(){
    dragging = false;
    knob.style.cursor = 'grab';
  }

  pad.addEventListener('mousedown', startDrag);
  knob.addEventListener('mousedown', startDrag);
  document.addEventListener('mousemove', onMove);
  document.addEventListener('mouseup', endDrag);

  pad.addEventListener('touchstart', startDrag, {passive:false});
  document.addEventListener('touchmove', onMove, {passive:false});
  document.addEventListener('touchend', endDrag);

  // initialize knob position
  setKnobFromV0();
})();

/* ---------- Slider listeners ---------- */
['time','m1','v1','m2','v2'].forEach(id=>{
  el[id].addEventListener('input', updateFromControls);
});
updateFromControls();

/* ---------- p5 Visualization ---------- */
let sketch = (p)=>{
  let canvas;
  function createSizedCanvas(){
    const holder = document.getElementById('canvas-holder');
    const rect = holder.getBoundingClientRect();
    if(canvas){ canvas.remove(); }
    canvas = p.createCanvas(rect.width, rect.height);
    canvas.parent('canvas-holder');
  }
  p.setup = ()=>{
    createSizedCanvas();
    p.frameRate(60);
    p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Helvetica Neue, Arial, sans-serif');
  };
  p.windowResized = ()=> createSizedCanvas();

  function worldToScreen(pt){
    const cx = p.width/2;
    const cy = p.height/2;
    const S = metersToPixels();
    return {x: cx + pt.x*S, y: cy - pt.y*S};
  }

  function metersToPixels(){
    // dynamic scale tuned for clarity; clamps to avoid extremes
    // choose time horizon 12 s for trajectories
    const Smin = 0.5, Smax = 8; // px per meter
    const vmax = Math.max(state.s1_cm, state.s2_cm, Math.hypot(state.v0x, state.v0y)) + 1;
    const est = Math.min(p.width, p.height) / (2 * 12 * vmax);
    return Math.max(Smin, Math.min(Smax, est));
  }

  function drawDashedLine(a, b, col){
    p.push();
    p.stroke(col);
    p.strokeWeight(2);
    p.drawingContext.setLineDash([6,6]);
    p.line(a.x, a.y, b.x, b.y);
    p.pop();
  }

  function drawArrow(x, y, vx, vy, color='black'){
    const len = Math.hypot(vx, vy);
    if(len < 1e-6) return;
    const nx = vx/len, ny = vy/len;
    const head = 10;
    p.push();
    p.stroke(color);
    p.fill(color);
    p.strokeWeight(3);
    p.line(x, y, x+vx, y+vy);
    // arrowhead
    p.translate(x+vx, y+vy);
    p.rotate(Math.atan2(vy, vx));
    p.triangle(0,0, -head, 4, -head,-4);
    p.pop();
  }

  p.draw = ()=>{
    p.background(255);

    // Velocities in CM frame
    const u1_cm = {x: state.s1_cm, y: 0};
    const u2_cm = {x: -state.s2_cm, y: 0};
    const rot = -Math.PI/4;
    const v1_cm = rotate(u1_cm, rot);
    const v2_cm = rotate(u2_cm, rot);

    // Current (piecewise) velocities in CM based on t
    const t = state.t;
    const v1_cm_now = (t < 0) ? u1_cm : v1_cm;
    const v2_cm_now = (t < 0) ? u2_cm : v2_cm;

    // Observer transform
    const v0 = {x: state.v0x, y: state.v0y};

    const v1_obs_now = vecAdd(v1_cm_now, v0);
    const v2_obs_now = vecAdd(v2_cm_now, v0);
    const p1_obs = vecScale(v1_obs_now, t);
    const p2_obs = vecScale(v2_obs_now, t);

    // Draw trajectories (-T..0) and (0..+T) with kink at 0, in observer frame
    const T = 12; // seconds horizon for drawing
    const segs = [
      {v_cm: u1_cm, color: p.color(255,0,0)},
      {v_cm: v1_cm, color: p.color(255,0,0)},
      {v_cm: u2_cm, color: p.color(0,102,255)},
      {v_cm: v2_cm, color: p.color(0,102,255)}
    ];
    // pre-collision lines: [-T,0] with u*_cm; post: [0,+T] with v*_cm
    const lines = [
      // particle 1
      {t0:-T, t1:0, v_cm:u1_cm, color:p.color(255,0,0)},
      {t0:0,  t1:T, v_cm:v1_cm, color:p.color(255,0,0)},
      // particle 2
      {t0:-T, t1:0, v_cm:u2_cm, color:p.color(0,102,255)},
      {t0:0,  t1:T, v_cm:v2_cm, color:p.color(0,102,255)}
    ];

    const S = metersToPixels();

    for(const L of lines){
      const a_obs = vecAdd(vecScale(L.v_cm, L.t0), vecScale(v0, L.t0));
      const b_obs = vecAdd(vecScale(L.v_cm, L.t1), vecScale(v0, L.t1));
      const a = worldToScreen(a_obs);
      const b = worldToScreen(b_obs);
      drawDashedLine(a,b,L.color);
    }

    // Draw particles
    const center1 = worldToScreen(p1_obs);
    const center2 = worldToScreen(p2_obs);

    const r1 = 6 * Math.sqrt(state.m1);
    const r2 = 6 * Math.sqrt(state.m2);

    p.noStroke();
    p.fill(255,0,0);
    p.circle(center1.x, center1.y, r1*2);
    p.fill(0,102,255);
    p.circle(center2.x, center2.y, r2*2);

    // Draw observer velocity arrow (-v0) from a fixed anchor
    const anchor = {x: p.width*0.35, y: p.height*0.7};
    const arrowScale = 14; // px per (m/s)
    drawArrow(anchor.x, anchor.y, -state.v0x*arrowScale, state.v0y*arrowScale, 'black');

    // Labels / metrics
    p.fill(0);
    p.noStroke();
    p.textSize(14);
    const v0mag = state.metrics.v0;
    const txtLeftX = anchor.x - 35;
    const txtLeftY = anchor.y + 24;
    p.text(`v₀ = ${fmt(v0mag,5)} m/s`, txtLeftX, txtLeftY);

    const rightX = p.width*0.55;
    const rightY = p.height*0.55;
    p.text(`v₁ = ${fmt(state.metrics.v1,5)} m/s`, rightX, rightY);
    p.text(`v₂ = ${fmt(state.metrics.v2,5)} m/s`, rightX, rightY+20);
    p.text(`energy: ${fmt(state.metrics.energy,2)} joules`, rightX, rightY+40);

    // Collision point marker at observer's origin at t=0
    const origin_obs = worldToScreen({x:0, y:0}); // since CM collision at (0,0), plus v0*0 = 0
    p.push();
    p.stroke(0,0,0,30);
    p.strokeWeight(1);
    p.line(origin_obs.x-6, origin_obs.y, origin_obs.x+6, origin_obs.y);
    p.line(origin_obs.x, origin_obs.y-6, origin_obs.x, origin_obs.y+6);
    p.pop();
  };
};
new p5(sketch);

/* ---------- End p5 ---------- */
</script>
</body>
</html>
